INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (1,'2023-02-19 09:34:48.522784','2023-02-19 09:34:48.522784','京杭大运河运输忙','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：京杭大运河运输忙</div><p style=\"text-align: center;\">\n	<a href=\"/n1/2023/0217/c1008-32625666-2.html\"><img alt=\"\" src=\"/mediafile/pic/20230217/88/17078054500044156404.jpg\" /></a></p>\n<p class=\"pictext\" style=\"text-align: center;\">\n	　　2月16日，货船行驶在京杭大运河江苏苏州段。</p>\n<p>\n	　　时值春耕备耕时节，京杭大运河上装载化肥、种子、建材等货物的船舶在航道上有序航行，来往如梭，一派繁忙景象。</p>\n<p>\n	　　新华社发（杭兴微摄）</p>\n<div class=\"zdfy clearfix\"><a href=\"/n1/2023/0217/c1008-32625666.html\">【1】</a><a href=\"/n1/2023/0217/c1008-32625666-2.html\">【2】</a><a href=\"/n1/2023/0217/c1008-32625666-3.html\">【3】</a><a href=\"/n1/2023/0217/c1008-32625666-4.html\">【4】</a><a href=\"/n1/2023/0217/c1008-32625666-5.html\">【5】</a><a href=\"/n1/2023/0217/c1008-32625666-6.html\">【6】</a><a href=\"/n1/2023/0217/c1008-32625666-7.html\">【7】</a><a href=\"/n1/2023/0217/c1008-32625666-8.html\">【8】</a></div><center><table border=\"0\" align=\"center\" width=\"40%\"><tr><td width=\"50%\" align=\"center\"><a href=\"/n1/2023/0217/c1008-32625666-2.html\"><img src=\"/img/next_page.jpg\" border=\"0\" /></a></td></tr></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0217/c1008-32625666.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (2,'2023-02-19 09:34:48.532125','2023-02-19 09:34:48.532125','上海豫园灯会落幕','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：上海豫园灯会落幕</div><p style=\"text-align: center;\">\n	<a href=\"/n1/2023/0216/c1008-32624870-2.html\"><img alt=\"\" src=\"/mediafile/pic/20230216/78/16980939606487689686.jpg\" /></a></p>\n<p class=\"pictext\" style=\"text-align: center;\">\n	　　2月15日，市民和游客在上海豫园九曲桥赏灯。</p>\n<p>\n	　　当日，上海市豫园灯会落幕。据悉，灯会自2022年12月26日开幕，举办期间共吸引约400万人次参观。</p>\n<p>\n	　　新华社记者 金立旺 摄</p>\n<div class=\"zdfy clearfix\"><a href=\"/n1/2023/0216/c1008-32624870.html\">【1】</a><a href=\"/n1/2023/0216/c1008-32624870-2.html\">【2】</a><a href=\"/n1/2023/0216/c1008-32624870-3.html\">【3】</a><a href=\"/n1/2023/0216/c1008-32624870-4.html\">【4】</a></div><center><table border=\"0\" align=\"center\" width=\"40%\"><tr><td width=\"50%\" align=\"center\"><a href=\"/n1/2023/0216/c1008-32624870-2.html\"><img src=\"/img/next_page.jpg\" border=\"0\" /></a></td></tr></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0216/c1008-32624870.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (3,'2023-02-19 09:34:48.532125','2023-02-19 09:34:48.532125','春来水暖鸟先知','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：春来水暖鸟先知</div><p style=\"text-align: center;\">\n	<a href=\"/n1/2023/0215/c1008-32624060-2.html\"><img alt=\"\" src=\"/mediafile/pic/20230215/80/16746018824549420916.jpg\" /></a></p>\n<p class=\"pictext\" style=\"text-align: center;\">\n	　　各种鸟类在北京园博湖栖息（2月14日摄）。</p>\n<p>\n	　　随着气温升高，在北京园博园的园博湖上，大批鸟类到水面上嬉戏。近年来，北京加大河湖湿地环境治理和动物保护力度，许多野生鸟类把北京市境内的湿地、河湖作为栖息地。</p>\n<p>\n	　　新华社记者 李欣 摄</p>\n<div class=\"zdfy clearfix\"><a href=\"/n1/2023/0215/c1008-32624060.html\">【1】</a><a href=\"/n1/2023/0215/c1008-32624060-2.html\">【2】</a><a href=\"/n1/2023/0215/c1008-32624060-3.html\">【3】</a><a href=\"/n1/2023/0215/c1008-32624060-4.html\">【4】</a><a href=\"/n1/2023/0215/c1008-32624060-5.html\">【5】</a></div><center><table border=\"0\" align=\"center\" width=\"40%\"><tr><td width=\"50%\" align=\"center\"><a href=\"/n1/2023/0215/c1008-32624060-2.html\"><img src=\"/img/next_page.jpg\" border=\"0\" /></a></td></tr></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0215/c1008-32624060.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (4,'2023-02-19 09:34:48.542269','2023-02-19 09:34:48.542269','蒙山雪霁美如画','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：蒙山雪霁美如画</div><p style=\"text-align: center;\">\n	<a href=\"/n1/2023/0214/c1008-32623317-2.html\"><img alt=\"\" src=\"/mediafile/pic/20230214/73/5664238145729702921.jpg\" /></a></p>\n<p class=\"pictext\" style=\"text-align: center;\">\n	　　这是2月13日拍摄的蒙山雪景（无人机照片）。</p>\n<p>\n	　　当日，雪后初晴的山东蒙山银装素裹，美如画卷。</p>\n<p>\n	　　新华社发（武纪全摄）</p>\n<div class=\"zdfy clearfix\"><a href=\"/n1/2023/0214/c1008-32623317.html\">【1】</a><a href=\"/n1/2023/0214/c1008-32623317-2.html\">【2】</a><a href=\"/n1/2023/0214/c1008-32623317-3.html\">【3】</a><a href=\"/n1/2023/0214/c1008-32623317-4.html\">【4】</a><a href=\"/n1/2023/0214/c1008-32623317-5.html\">【5】</a><a href=\"/n1/2023/0214/c1008-32623317-6.html\">【6】</a></div><center><table border=\"0\" align=\"center\" width=\"40%\"><tr><td width=\"50%\" align=\"center\"><a href=\"/n1/2023/0214/c1008-32623317-2.html\"><img src=\"/img/next_page.jpg\" border=\"0\" /></a></td></tr></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0214/c1008-32623317.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (5,'2023-02-19 09:34:48.542269','2023-02-19 09:34:48.542269','哈尔滨：企鹅“巡游”引客来','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-align: center;\">\n	<a href=\"/n1/2023/0213/c1008-32622407-2.html\"><img alt=\"\" src=\"/mediafile/pic/20230213/70/15082820339390635202.jpg\" /></a></p>\n<p align=\"center\" class=\"pictext\">\n	　　2月12日，哈尔滨极地公园的企鹅外出“巡游”。</p>\n<p>\n	　　时下，“冰城”哈尔滨市正值冰雪旅游季。哈尔滨极地公园的企鹅每天都会外出“巡游”，吸引许多游客前来观赏。</p>\n<p>\n	　　新华社记者 王建威 摄</p>\n<div class=\"zdfy clearfix\"><a href=\"/n1/2023/0213/c1008-32622407.html\">【1】</a><a href=\"/n1/2023/0213/c1008-32622407-2.html\">【2】</a><a href=\"/n1/2023/0213/c1008-32622407-3.html\">【3】</a><a href=\"/n1/2023/0213/c1008-32622407-4.html\">【4】</a><a href=\"/n1/2023/0213/c1008-32622407-5.html\">【5】</a><a href=\"/n1/2023/0213/c1008-32622407-6.html\">【6】</a><a href=\"/n1/2023/0213/c1008-32622407-7.html\">【7】</a><a href=\"/n1/2023/0213/c1008-32622407-8.html\">【8】</a><a href=\"/n1/2023/0213/c1008-32622407-9.html\">【9】</a><a href=\"/n1/2023/0213/c1008-32622407-10.html\">【10】</a></div><center><table border=\"0\" align=\"center\" width=\"40%\"><tr><td width=\"50%\" align=\"center\"><a href=\"/n1/2023/0213/c1008-32622407-2.html\"><img src=\"/img/next_page.jpg\" border=\"0\" /></a></td></tr></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：薄晨棣、温璐)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0213/c1008-32622407.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (6,'2023-02-19 09:34:48.552065','2023-02-19 09:34:48.552065','海南迈湾水利枢纽工程现雏形','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-align: center;\">\n	<a href=\"/n1/2023/0213/c1008-32622439-2.html\"><img alt=\"\" src=\"/mediafile/pic/20230213/22/14800244677593874846.jpg\" /></a></p>\n<p align=\"center\" class=\"pictext\">\n	　　这是2月12日拍摄的迈湾水利枢纽工程（无人机照片）。</p>\n<p>\n	　　近日，位于海南南渡江中游的迈湾水利枢纽工程已现雏形，正在进行大坝主体浇筑、副坝边坡护理等施工。该工程是我国“十四五”规划的国家水网骨干工程之一，也是海南自贸港水网建设核心工程。工程建成后将用于防洪、供水、灌溉和发电，助力海南自贸港建设。</p>\n<p>\n	　　新华社记者 蒲晓旭 摄</p>\n<div class=\"zdfy clearfix\"><a href=\"/n1/2023/0213/c1008-32622439.html\">【1】</a><a href=\"/n1/2023/0213/c1008-32622439-2.html\">【2】</a><a href=\"/n1/2023/0213/c1008-32622439-3.html\">【3】</a><a href=\"/n1/2023/0213/c1008-32622439-4.html\">【4】</a><a href=\"/n1/2023/0213/c1008-32622439-5.html\">【5】</a></div><center><table border=\"0\" align=\"center\" width=\"40%\"><tr><td width=\"50%\" align=\"center\"><a href=\"/n1/2023/0213/c1008-32622439-2.html\"><img src=\"/img/next_page.jpg\" border=\"0\" /></a></td></tr></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：薄晨棣、温璐)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0213/c1008-32622439.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (7,'2023-02-19 09:34:48.552065','2023-02-19 09:34:48.552065','全面提升质量水平 统筹推进质量强国建设','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：全面提升质量水平 统筹推进质量强国建设</div><p>\n	　　 近日，中共中央、国务院印发《质量强国建设纲要》（以下简称《纲要》），明确了质量建设工作的总体要求和任务，掀开了新时代建设质量强国的新篇章。2月16日，在国务院新闻办公室举行的新闻发布会上，国家市场监管总局、国家发改委、工业和信息化部等部门相关负责人对《纲要》进行了深入解读。</p>\n<p>\n	　　<strong>为统筹推进质量强国提供行动指南</strong></p>\n<p>\n	　　 “《纲要》是首个由党中央、国务院印发的中长期质量纲领性文件，确立了新时期质量工作的全新方位，为统筹推进质量强国建设提供了行动指南、注入了强大动力。贯彻实施好《纲要》是推进中国式现代化建设、促进我国经济由大向强转变、实现经济社会高质量发展的重要举措，是更好满足人民美好生活需要的重要途径。”国家市场监督管理总局副局长、国家标准化管理委员会主任田世宏说。</p>\n<p>\n	　　 20世纪90年代以来，我国先后制定实施了《质量振兴纲要》《质量发展纲要》两个规划文件，探索出了一条中国特色的质量发展之路。《纲要》的接续出台与此前的政策一脉相承，更加凸显了质量在经济社会发展全局中的基础性、战略性地位和作用。“此次的《纲要》从微观的产品、工程、服务质量领域，向经济、产业、区域质量等宏观层面拓展，首次对推动经济质量效益型发展、增强产业和区域质量竞争力等作出了专门部署，更加突出推动质量变革创新。”田世宏指出。</p>\n<p>\n	　　 《纲要》提出了2025年和2035年两个阶段的发展目标。“到2025年，是质量强国建设的关键时期。”田世宏分析道，到2025年的目标可以从六个方面来理解：一是经济发展质量效益明显提升；二是产业质量竞争力持续增强；三是产品、工程、服务质量水平显著提升；四是品牌建设取得更大进展；五是质量基础设施更加现代高效；六是质量治理体系更加完善。</p>\n<p>\n	　　<strong>持续推进品牌培育建设</strong></p>\n<p>\n	　　 品牌是经济高质量发展的重要象征，也是质量强国的内在支撑。近年来，在有关部门的推动下，推进品牌建设政策文件陆续出台，“中国品牌日”活动影响力增强，我国品牌建设工作取得了显著成效。</p>\n<p>\n	　　 此次《纲要》提出了“增强企业质量和品牌发展能力”“争创国内国际知名品牌”等要求，并提出“完善品牌培育发展机制”“开展中国品牌创建行动”“鼓励企业实施质量品牌战略”“持续办好‘中国品牌日’系列活动”等具体举措，着力推动品牌建设发展。</p>\n<p>\n	　　 国家发展和改革委员会产业发展司负责人龚桢梽指出，下一步，将结合《纲要》内容，进一步推动品牌高质量发展。要高起点办好品牌日活动，持续放大活动效应，积极推动中华优秀传统文化和社会主义先进文化融入品牌创建实践工作中来，不断提升品牌的文化内涵；要进一步健全活动长效机制，在全社会、全年广泛传播品牌发展理念、凝聚品牌发展共识、营造品牌培育的氛围，形成政府搭台、企业参与、消费者期待的可持续发展机制，完善服务支撑体系。</p>\n<p>\n	　　<strong>进一步推动制造业高质量发展</strong></p>\n<p>\n	　　 增强产业质量竞争力、提高制造业质量水平、推动工业品质量迈向中高端，是建设质量强国的重要方面。在《纲要》提出到2025年的主要目标中，包括“产业质量竞争力持续增强”“制约产业发展的质量瓶颈不断突破”等内容。</p>\n<p>\n	　　 “近年来，工业和信息化部协同推进质量强国与制造强国、网络强国、数字中国建设，加快推进新型工业化，扎实推动制造业高端化、智能化、绿色化发展。”工业和信息化部科技司负责人毕开春说。</p>\n<p>\n	　　 结合《纲要》部署，毕开春表示，将锚定新型工业化及实现产业质的有效提升这一战略目标，不断推动制造业高质量发展。将实施制造业卓越质量工程，持续开展制造业“三品”专项行动、质量管理数字化“深度行”行动、全国质量标杆示范等，激发企业提质增效的内生动力；实施质量标准品牌赋值中小企业专项行动，培育更多专精特新中小企业；提高以可靠性为核心的产品质量水平，加强质量与可靠性技术创新和推广应用；夯实产业技术基础。（记者 鲁元珍）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0217/c1008-32625610.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (8,'2023-02-19 09:34:48.562461','2023-02-19 09:34:48.562461','吉林今年将重点抓好50项民生实事','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　本报长春2月16日电  （记者郑智文）日前，吉林印发《中共吉林省委吉林省人民政府关于2023年全省民生实事安排的意见》（以下简称《意见》）。《意见》提出：2023年，吉林省委、省政府将重点抓好促进就业、社会保障、困难群体救助、教育惠民、卫生健康护理服务、环境保护、交通出行、文体服务、城乡建设、公共安全服务等10个方面、50项民生实事任务。</p><p>　　《意见》就民生实事任务做了具体安排，在促进就业方面，包括城镇新增就业、维护农民工合法权益等任务；在社会保障方面，包括提高城乡特困人员基本生活标准和城乡居民医保补助标准、建设社区居家养老服务中心和老年食堂等任务；在困难群体救助方面，包括扩大采暖补助范围，提高补助标准、开展重点居家老年人巡访关爱服务等任务；在城乡建设方面，包括打造和美乡村示范村、推进农村24小时供水、改造棚户区和城镇老旧小区等任务。</p><p>　　据悉，“十四五”以来，吉林省委、省政府聚焦就业、社保、教育、卫生、环保、交通、住房等重点民生领域，每年确定50件民生实事，集中财力物力全力推进实施，持续把好事办好实事办实。</p><p>　　据介绍，2022年吉林省50项民生实事全面完成。在就业方面，城镇新增就业25.17万人，完成年度计划的109.43%。零就业家庭实现动态清零；在社会保障方面，退休人员养老保险待遇较上年提高约4%；全省城乡特困供养对象基本生活标准达到月人均1031元和749元，均达到上年城乡低保标准的1.68倍。城乡居民基本医疗保险政府补助标准提高到每人每年610元。</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月17日   12 版）</span>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：胡永秋、白宇)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0217/c1008-32625419.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (9,'2023-02-19 09:34:48.562461','2023-02-19 09:34:48.572322','云南全力提升农村饮水安全保障水平','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　本报昆明2月16日电  （记者李茂颖）为巩固拓展脱贫攻坚农村饮水安全成果，全面推进乡村振兴，云南加快推进农村供水保障工程建设，加大农村供水工程维修养护力度，并通过包保责任制等措施，全力提升农村群众饮水安全和供水保障水平。</p><p>　　据介绍，云南目前正全力推进总投资208亿元的农村供水保障3年专项行动。通过3年时间，实施集中式供水工程3322件，着力解决中度干旱条件下100.4万农村人口因旱缺水问题，提升72.6万依靠水窖供水和84.1万靠水窖辅助供水农村人口供水保障水平。截至2023年2月1日，农村供水保障3年专项行动累计开工项目1969件，完工1338件；改变了25.94万依靠水窖供水人口和26.54万水窖辅助供水人口的供水方式，解决了72.28万因旱拉水送水人口的供水保障问题，同步提升了280.66万城乡人口的供水保障水平。</p><p>　　同时，云南将常态化开展农村供水保障排查工作，按照《关于建立农村饮水安全管理责任体系的通知》，明确地方政府的主体责任、水行政主管部门的行业监管责任和供水单位的运行管理责任，以及农户、村民小组、村委会（社区）、乡（镇）人民政府的农村供水工程管护职责。根据每件农村集中供水工程至少配备1名管水员的要求，目前云南全省已配备管水员7.4万余人，为农村供水工程正常运行提供了人员保障。</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月17日   12 版）</span>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：胡永秋、白宇)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0217/c1008-32625420.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (10,'2023-02-19 09:34:48.575574','2023-02-19 09:34:48.575574','栎阳城遗址发现目前最早的都城轴线','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：栎阳城遗址发现目前最早的都城轴线</div><p style=\"text-align: center;\">\n	<img alt=\"\" src=\"/mediafile/pic/20230217/80/13740482399897381136.jpg\" /></p>\n<p class=\"pictext\" style=\"text-align: center;\">\n	　　栎阳城遗址三号建筑西南院落内瓦砾堆积 阿房宫与上林苑考古队供图</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"/mediafile/pic/20230217/74/13237059267192372758.jpg\" /></p>\n<p style=\"text-align: center;\">\n	　　栎阳城遗址三号建筑出土的可冲水陶便器与弯管组合情况 阿房宫与上林苑考古队供图</p>\n<p>\n	　　 本报西安2月16日电 由中国社会科学院考古研究所与西安市文物保护考古研究院联合组成的阿房宫与上林苑考古队，近年在栎阳城遗址考古取得丰硕成果。从2018年开始，考古队在三号古城内开展较大面积发掘，2019年至2020年发掘了三号古城的四号到九号建筑，将临近城内北墙的“后宫”区域进行较为完整的揭露。</p>\n<p>\n	　　 2022年，考古队对前述发掘区南侧的三号建筑和十一号建筑进行了完整揭露，并在其中发现了冲水式陶便器，这是中国历代宫城考古中发现的唯一一例厕类遗存，也是我国考古发现的第一个冲水式厕所。</p>\n<p>\n	　　 三号建筑整体坐北朝南，呈长方形，由台基、散水等组成。在发掘时，考古工作人员在三号建筑台基四周发现散水，台基北侧、西侧散水外发现屋檐滴水形成的滴水坑，表明建筑原为瓦顶。在散水外缘之内，发现连续分布、间距不等的陶立管。发掘过程中，还在三号台基西北、东北、西南、东南角各发现一枚俗称“瓦当王”的大半圆瓦当，为解决1956年发现第一枚瓦当王以来争论未解的大半圆瓦当如何使用等学术问题，提供了重要资料。</p>\n<p>\n	　　 三号建筑台基之上为原建筑空间，受晚期遗迹破坏，东侧未发现建筑使用期间地面，在台基中部偏西分布有1座半地下建筑（编号F1，2016年探沟发掘中清理），向西新清理出1处遗迹（编号F2）。F1坐北朝南，由门道、通道和地下空间组成，整体呈不规则甲字形，使用面积29.27平方米。F2根据形制判断，其由台基上的室内厕位和房外排污坑两部分组成。受晚期遗存破坏，厕位上部结构不存。发掘显示，厕所可分早晚两期。</p>\n<p>\n	　　 十一号建筑位于发掘区东侧，通过北侧廊道西折后与十号建筑连接。其台基呈长方形。台基西侧、南侧、北侧发现散水，北侧廊道以东的残存散水，在内外两条立瓦内用卵石平铺，残长2.6米、宽0.67米，残存卵石9排，东西长2.52米，呈南高北低。卵石为椭圆形，直径8~10厘米，在台基北侧、南侧、西侧的散水与台基间均分布有陶立管。</p>\n<p>\n	　　 通过考古清理和进一步解剖发掘揭示，三号古城的三号、十一号等建筑从战国中期建成之后，一直延续使用到西汉前期，建筑形制、布局未有改变。</p>\n<p>\n	　　 在考古工作人员清理过程中，于三号、十一号建筑之上，还清理出成片分布的由田间道路、沟渠、水井、田地等组成的汉代农田，它们与农田中较为规律分布的犁沟、牛蹄等一起，共同构成了迄今为止第一次在秦汉都城附近发现的农业景观。</p>\n<p>\n	　　 在栎阳城遗址三号古城的三号、十一号建筑的考古发掘中，第一次完整揭示出战国中期至西汉前期都城核心区重要建筑的平面形制，是战国秦汉考古的重要发现。从三号至十一号等建筑发掘的情况看，不同功能、等级的建筑，无论是建筑平面与布局，还是建筑材料的使用、散水位置与组合等都存在明显的等级差异。</p>\n<p>\n	　　 根据勘探、发掘资料，结合三号建筑东侧南北两台阶可确定，那里存在一条贯穿整个三号古城核心宫殿建筑的南北向轴线。作为目前考古发现并确定的时代最早的都城轴线，它的发现和确定，对战国秦汉都城乃至我国古代都城的布局研究均有重要价值。</p>\n<p>\n	　　 栎阳城遗址是国家文物局公布的“十四五”时期大遗址之一，2019年以来在其三号古城核心区宫殿建筑的大面积连续性考古发掘，是近年来我国战国中期列国都城核心建筑的重要发掘，随着一系列大型建筑的清理，我们对战国时期，特别是对从战国中期延续到西汉初期的从王国到帝国时代主要核心建筑的特点、形制和演变有了越来越清晰的认识。（记者李洁、张哲浩）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0217/c1008-32625615.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (11,'2023-02-19 09:34:48.582086','2023-02-19 09:34:48.582086','今年春运全社会人员流动人次约47.33亿','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：今年春运全社会人员流动人次约47.33亿</div><p>\n	　　 本报北京2月16日电 16日，历时40天的2023年春运正式收官。交通运输部数据显示，春运期间，全社会人员流动量约47.33亿人次。其中，营业性客运量约15.95亿人次，比2022年同期增长50.5%，恢复至2019年同期的53.5%。铁路、民航分别发送旅客3.48亿、5523万人次，分别恢复至2019年同期的85.5%、76%。在全国铁路运输方面，其中有11天单日旅客发送量超千万，铁路客流实现大幅回升。总的来看，2023年春运运力供给充足，运输组织有序，安全形势稳定。</p>\n<p>\n	　　 今年春运期间，高速公路小客车流量创历史新高。公众选择自驾出行意愿显著增强，春节假期7座以下小客车高速公路免费通行政策继续实行，春运期间自驾出行人数创历年最高，1月27日（正月初六）高速公路小客车流量达6029.1万辆次，为历史峰值。</p>\n<p>\n	　　 此外，营业性客流量大幅回升。回乡过年、探亲访友、旅游度假、商务旅行等出行需求集中释放，带动客流迅速攀升，全社会人员流动量、营业性客运量均达到2020年春运以来的新高。</p>\n<p>\n	　　 各类重点物资运输需求旺盛。“乙类乙管”实施正值春运伊始，全国医疗物资运输需求急剧增长，能源、民生等重点物资处于保供保运的关键时期，春运期间各类重点物资运输需求交织。国家铁路累计发送货物4.15亿吨，持续保持高位运行。</p>\n<p>\n	　　 今年春运期间，交通运输系统落实落细疫情防控措施，从业人员严格开展防护，社会公众积极主动落实个人健康防护责任，在人流物流大规模流动的情况下，最大限度防止了交叉感染，保障了旅客身体健康。（记者姚亚奇）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0217/c1008-32625617.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (12,'2023-02-19 09:34:48.582086','2023-02-19 09:34:48.582086','人民法院一站式化解纠纷惠及更多群众','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：人民法院一站式化解纠纷惠及更多群众</div><p>\n	　　 本报北京2月16日电 碰上难解的矛盾纠纷，很多人希望到法院讨个“说法”，可打官司往往耗时费力。自2019年起，人民法院一站式多元纠纷解决和诉讼服务体系建设从无到有、从有到优，让公平正义更加可触可感可及。</p>\n<p>\n	　　 2月15日，最高人民法院举行新闻发布会，通报一站式建设有关情况。据介绍，2022年，人民法院诉前调解纠纷1307.2万件，平均时长13.5天，每分钟有75件纠纷在线成功化解，大大减轻了群众和企业的时间成本。</p>\n<p>\n	　　 最高法立案庭庭长钱晓晨介绍，近年来，起诉到人民法院的矛盾纠纷年均增幅达10%以上。人民法院深入推进诉源治理工作，通过源头减量、诉前调解，以诉讼方式解决的民事行政纠纷占法院纠纷总量比例从2019年的90%下降到2022年的64%。</p>\n<p>\n	　　 最高人民法院会同12家中央行政机关、全国性行业协会建立“总对总”在线多元解纷机制，实现“一平台调解、全流程在线、菜单式服务、一体化解纷”。各地法院在金融、家事、道路交通、劳动争议、知识产权等重点领域一站式多元解纷工作成效明显。截至2022年年底，对接人民法院的调解组织、调解员达9.6万家、37.2万名，在线调解纠纷量累计3832万件。其中，2022年诉前调解成功895万件，平均每个工作日有5.4万件纠纷在线调解，每分钟就有75件成功化解在诉前。</p>\n<p>\n	　　 各级法院以调解平台为支撑，以人民法庭为“前哨堡垒”，广泛建设乡村调解室、社区微法庭等基层解纷站点，开展风险预警、矛盾排查、民意沟通、就地化解、视频调解、立案审判等。2022年，9677家人民法庭入驻调解平台，在线对接派出所、司法所、街道办、村委会等基层治理单位，就地预防化解纠纷33.21万件，“家门口”解纷服务新模式有力促进基层善治。</p>\n<p>\n	　　 各级法院全面推进一窗集成、一站通办、一号响应、一次办好工作，努力实现当事人“进一个门，到一个窗，办全部事”。2022年，全国法院12368热线接听群众来电1465.3万件，日均帮助解决诉求5.9万个。（记者靳昊）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0217/c1008-32625584.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (13,'2023-02-19 09:34:48.582086','2023-02-19 09:34:48.582086','安徽省亳州市创新政务“一键申报”','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：“办事像网购一样方便”</div><p>\n	　　 张青决定尝试一把。他想开办一家装修公司，市场监管局的朋友告诉他，手机上就能一键搞定。</p>\n<p>\n	　　 他将信将疑地打开安徽政务服务网，进入企业开办专题，按照提示勾选、提交材料，10分钟后，他欣喜不已：“办完了，说是下午就能审核通过。”张青接着选择营业执照、公司公章直接邮寄到家，“这样我一趟都不要跑，太方便了，跟网购一样”。</p>\n<p>\n	　　 给张青带来便利的是安徽省亳州市自2022年3月创新开展的政务“一键申报”改革。</p>\n<p>\n	　　 在亳州市，除了开办企业，公积金业务、货运车辆年审等271类证照业务都可以通过安徽省政务服务网亳州分厅网上办理。目前，已实现市县两级2602个事项“一键申报”，超过市级有改造权限事项的50%。</p>\n<p>\n	　　 “从生育服务登记卡登记发放，到80岁以上高龄津贴发放等全生命周期一揽子事项，都可以‘一键申报’。”亳州市数据资源管理局局长王宇告诉记者，“一键申报”充分运用大数据核查手段，辅以简单的人工审批，压缩了权力设租寻租空间，让政务服务从“面对面”走向“键对键”，从根本上杜绝了关系审批、人情办事、暗箱操作、“吃拿卡要”、反复驳回等现象，节约了企业办事成本，提升了政务服务水平，优化了营商环境。</p>\n<p>\n	　　 “以前要跑窗口、跑银行，取一次公积金最少得跑三次，因为窗口的上班时间和自己的上班时间一样，还要请假去办理。”10年前，市民陈琳贷款购买了一套住房，自那以后，每年都要提取一次公积金，她对公积金提取业务的办理变化感触颇深。</p>\n<p>\n	　　 “第一次提取公积金是到市住房公积金管理中心现场办理的，先拿一个申请表，回单位填表盖章，再到银行打印流水，然后拿着申请表、银行流水、身份证、房产证、银行卡等一堆材料到窗口办理。后来在网上办事大厅，把这些材料拍照上传到就可以申请办理。”陈琳介绍，现在她登录“皖事通”账号后，直接进入“公积金专题”，点击“我要提取”，选取提取类型，系统就自动调取了她的房产、银行卡信息，一分钟就能完成申报，第二天钱款就直接打到银行卡了。</p>\n<p>\n	　　 2022年，亳州市继打造政务服务“一站式”网上平台后，全面梳理市县两级7913个政务服务事项，将2602个可实现“一键申报”事项的材料，分为数据核查类、表单开发类、证章制作类进行改造，实现群众办事“申请一键通，材料零提交”，政务服务进入智能申报审批阶段。</p>\n<p>\n	　　 “‘一键申报’不是不要表格和材料，而是改变申报方式，企业、群众提交后，系统会按相关规定，再自动生成所需的表格、材料。”王宇告诉记者，以前网上办事还需要企业和群众到现场最后签字确认，“一键申报”改革后，通过制作电子证照、配置电子签章功能等，推进电子证照、电子云签、电子印章、电子档案在“一键申报”事项的应用，能够关联电子证照的事项一律免于提交实体证照、能够电子签章的事项一律免于提交纸质签字材料、能够电子印章审批的事项一律免于线下盖章审批、能够生成电子档案的事项一律免于纸质存档，切实减少企业群众跑现场的次数，提高审批部门工作效率。</p>\n<p>\n	　　 通过改革，目前，亳州2602个政务服务事项平均申报材料由3.59个压缩至0个，平均申报时间由1至2天压缩至5分钟以内，平均审批时间由3至5天压缩至4小时以内，每个事项节约材料成本约3元，企业、群众获得感和满意度明显提高。</p>\n<p>\n	　　 作为一家货运公司的工作人员，汪影每年都要办理货运车辆年审。以前，她要准备登记表、检测报告、各种证件的复印件等，手续繁多，还要到政务服务大厅的窗口排队，审核时间也长。“现在只要把车辆送到检测站，检测合格后，就可以手机登录安徽政务服务网根据提示填写信息，‘一键申报’，当天就能办好。”汪影告诉记者：“再也不用跑来跑去到窗口排队了，也不会耽误车辆出车了。”</p>\n<p>\n	　　 数据联通共享是“互联网+”政务服务的基础，为有效破解各部门之间的“信息孤岛”，亳州市强力整合公安、人社、房产等53个部门、25.1亿条数据，建立人口、法人和电子证照三大数据库，实现数据同步更新、信息真实有效，将8651项政务服务事项全部网上运行，除法律法规要求必须现场检验、采集指纹人像等事项外，一律不让群众、企业到现场办理。</p>\n<p>\n	　　 2602个事项流程再造并非易事，需要相关部门通力合作，一一想办法解决。记者了解到，由于工作涉及市县两级，为避免重复建设，亳州市统筹协调，市级和4个县区各负责500多个事项，成功后相互复制推广，有效节约了财政资金。</p>\n<p>\n	　　 “申报人实名认证，相关信息系统可直接调取核查，我们不用再费时费力核查信息真实性。”亳州市住房公积金管理中心服务大厅首席代表朱瑞清告诉记者，大数据智能核查既降低了人工核查容易出错的风险，又减轻了审批人员工作量，各项业务审批效率大大提高，“现在退休人员提取公积金已经可以实现秒到账”。</p>\n<p>\n	　　 “以办理不动产登记业务为例，原来需要到国土、房产、地税、测绘4个单位的9个窗口，提交38种材料，缴纳4次费用。有了网上办事大厅后，数据互联互通、共享互信，群众只需要在网上提交1次材料，缴纳1次费用，证件就可以免费快递到家。”王宇介绍说，网上办事大厅实现了数据实时交换、同步更新、动态共享，推动“互联网+”与政务服务深度融合，“一次上网，事就办成”让群众充分感受到了政务服务的速度和温度。（记者 常河 马荣瑞 通讯员 张珍）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0217/c1008-32625580.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (14,'2023-02-19 09:34:48.592179','2023-02-19 09:34:48.592179','安徽:进一步提高司法公信力和群众满意度','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：安徽：进一步提高司法公信力和群众满意度</div><p>\n	　　本报合肥2月16日电 （记者 周瑞平）今天，安徽全省法院工作会议在合肥召开。</p>\n<p>\n	　　安徽省委常委、秘书长、政法委书记张韵声指出，全省法院要把“两个确立”“两个维护”作为最高政治原则和根本政治规矩，坚持党对司法工作的绝对领导，坚持服务大局、司法为民、公正司法，加快推进审判体系和审判能力现代化。</p>\n<p>\n	　　安徽省高级人民法院党组书记、院长田云鹏出席会议并讲话。会议对2023年和今后一个时期全省法院工作作出部署：坚持以习近平新时代中国特色社会主义思想凝心铸魂，深入学习贯彻党的二十大精神，树牢新时代社会主义司法理念，努力提高司法公信力和群众满意度，推进安徽法院工作高质量发展。坚持依法能动履职，助力营造法治化营商环境，服务高质量发展和高水平平安安徽建设。坚持以人民为中心，用心用情化解涉诉信访，探索案访比质效分析管理制度，加强访源治理；持续推进执行攻坚，兑现当事人胜诉权益。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0217/c1008-32625630.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (15,'2023-02-19 09:34:48.602114','2023-02-19 09:34:48.602114','广东茂名法院打通服务群众“最后一公里”','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：打通服务群众“最后一公里”</div><p>\n	　　“还以为要跑上几个月，没想到一个多小时就解决了！太谢谢你们了！”2022年8月2日上午，由广东省茂名市电白区人民法院牵头设立的一站式涉渔解纷中心成功调解了一起渔民伤害赔偿纠纷，当天接到纠纷、当天调解成功、现场支付完毕，当事人蔡某赞不绝口。</p>\n<p>\n	　　这是茂名法院建立一站式司法惠民中心便利群众参与诉讼服务的一个缩影。</p>\n<p>\n	　　2019年以来，茂名法院创新建设遍及村镇的司法惠民服务中心158个，发挥“法治宣传、诉前化解、便捷诉讼、以案释法”四大功能，将司法惠民服务延伸到老百姓家门口，为基层社会治理和乡村振兴提供强有力的司法服务和保障。</p>\n<p>\n	<strong>　　整合司法惠民力量 推动联合解纷源头防控</strong></p>\n<p>\n	　　“我们对他没信心，必须一个月内付清！”2022年5月7日上午，信宜市人社局来了19名工人，他们来自信宜市某饰品加工厂，由于老板姚某拖欠工资所以来投诉。</p>\n<p>\n	　　人社局工作人员了解情况后，立即将工人引导到人社局司法惠民服务中心，通过远程视频平台与信宜市人民法院沟通。</p>\n<p>\n	　　信宜法院立案庭法官助理黄明军接到视频连线后，确认19名工人均有姚某签字的工资条证据，事实清楚，随即拨通了姚某的电话了解情况。</p>\n<p>\n	　　“加工的饰品主要经销地因疫情影响货物一时间难以销售出去，工厂资金难以周转，现在也在想办法筹借资金。”姚某表示。从双方的实际情况考虑，调解是最节约成本的办法，黄明军便立马联合人社局调解员开展“背对背”调解。</p>\n<p>\n	　　“如果进入正常诉讼程序，加工厂经营肯定会受到影响，工商营业信息上也会注明你的加工厂存在诉讼风险。相信这也不是你想看到的……”黄明军从经营角度出发做起了姚某的思想工作。人社局调解员也劝导工人换位思考，在疫情影响下，体谅姚某的难处，适当给予缓冲时间，共渡难关。</p>\n<p>\n	　　当天下午，在人社局司法惠民服务中心，姚某和19名工人通过远程视频连线达成调解协议：7月底前全部支付完毕，随后进行网上司法确认。</p>\n<p>\n	　　建立“法院+人社+工会”劳动争议诉调对接机制，是茂名法院依托司法惠民服务中心推动联合解纷的举措之一。</p>\n<p>\n	　　2019年以来，茂名法院坚持推动司法惠民服务中心与妇联、人社局、镇（街）综治中心、派出所、司法所等部门整合力量，与各社会治理平台互联互通，累计化解各类诉前纠纷2.6万余件，解决争议标的额4.3亿余元。</p>\n<p>\n	<strong>　　优化司法惠民方式 推动服务集约化云端化</strong></p>\n<p>\n	　　2022年7月29日，郑女士因为一起民间借贷纠纷来到了化州市杨梅镇司法惠民服务中心。中心调解员第一时间招呼她坐下慢慢说。</p>\n<p>\n	　　“我看到宣传资料，说每个镇上都设有司法惠民服务中心了，可以到这里来网上立案？”因为借钱给一位朋友，过了约定的时间也一直不还，郑女士准备好了材料将起诉对方。</p>\n<p>\n	　　“如果您确定要起诉了，这里就可以网上立案，您有准备起诉状吗？没有的话，请到这边智能诉状生成系统操作，我们联系化州法院帮您进行网上立案。”调解员一边指导着郑女士操作电脑，一边介绍着每一个步骤。</p>\n<p>\n	　　茂名每个司法惠民服务中心，都配置了智能诉状生成系统，系统内置126种表单模板，涵盖民事、执行、保全等业务范围，通过问答形式能辅助填写诉状、诉讼风险评估、类案法条推送，中心及时联系法院审查立案，实现调解与诉讼无缝对接，真正将司法服务快捷高效送到百姓身边。</p>\n<p>\n	　　“自动生成诉状后，请依次上传身份证复印件、户口本复印件……提交后，稍等几分钟，我们来确认。”化州市人民法院立案庭庭长陈青春接到杨梅镇司法惠民服务中心的电话邀请，远程视频指导郑女士使用诉讼服务一体机。5分钟后，网上立案成功。</p>\n<p>\n	　　司法惠民服务中心通过配置多元化远程调解平台、智能诉状生成系统、自助立案平台、多媒体播放平台等系统联网，一站式提供网上立案、跨域立案、网上缴费、网上阅卷、电子送达等诉讼业务。</p>\n<p>\n	　　同时，司法惠民服务中心还实现与人民法庭、基层法院、茂名市中级人民法院的多方位联通，并开通“粤公正”“广东移动微法院”“云上法庭”等，让群众享受“指尖上”的便利。</p>\n<p>\n	<strong>　　延伸司法惠民网络 推动普法精准化全域化</strong></p>\n<p>\n	　　茂名是全球最大的连片荔枝生产基地，全市荔枝种植面积约145万亩，年产量约50万吨，全球每5颗荔枝就有1颗产自茂名。高州根子镇则被誉为“中国荔枝第一乡”。茂名司法惠民中心就经常针对荔枝产业进行精准普法。</p>\n<p>\n	　　“叔，您多看看这个水果购销合同参考样本，别不小心闹了矛盾。”2022年5月，根子镇司法惠民中心来到柏桥村荔枝贡园，开展“服务果农 送法下乡”活动。</p>\n<p>\n	　　经营着一家生态农业公司的马先生眉头紧皱地走近了咨询台。马先生表示，在荔枝收购销售过程中有些烦恼不知道该怎么处理，听镇上司法惠民服务中心宣传，今天有法官来贡园送法下乡，专门过来咨询了解。</p>\n<p>\n	　　对于农产品如何规避预售价格等法律风险，法官为马先生支起了招。“原来可以这样提前避免法律风险，幸亏来一趟！”听完法官的普法介绍，马先生竖起大拇指。</p>\n<p>\n	　　为了助力乡村振兴，司法惠民中心为当地果农、购销商提供菜单式法律服务，并建议当地政府和行业协会组织农业专家提前研判，及时向果农果商发布预判价格，服务保障近两年产值超百亿元的水果购销市场秩序良好。</p>\n<p>\n	　　近两年来，茂名法院组建了普法讲师团，普法讲师团联合司法惠民中心开展以案释法和巡回审判，累计开展精准普法、专题讲座、送法五进等活动3800多场，向乡村、企业和学校发放普法资料13万多份，接待群众法律咨询6.9万余人次。（记者 吁青 通讯员 梁良）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0217/c1008-32625636.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (16,'2023-02-19 09:34:48.602114','2023-02-19 09:34:48.602114','北京西城：依法监督规范财产刑执行工作','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：被告人名下有财产，被害人为何拿不到退赔款</div><p>\n	　　“非常感谢你们，我被骗的钱款已全部到账了。”近日，北京市西城区检察院检察官郭玉芳接到了一起诈骗案被害人曹某打来的电话。在这起财产刑执行监督案中，检察机关能动履职，曹某拿回了自己的损失，该案画上句号。</p>\n<p>\n	　　转账后卖家“人间消失”</p>\n<p>\n	　　曹某是北京某邮币卡市场上的个体户，因工作性质，经常从网上淘换一些纪念币、纪念卡，再倒手出售。2018年3月，狗年纪念币的行情不错，曹某便在网上联系了卖家刘某，购买狗年纪念币，双方约定转账付款后发货。曹某在约定当天便给刘某转账4520元。三天后，因想再多买些纪念币，曹某又给刘某转账6720元。谁知第二次转账后，刘某玩起了“人间消失”。感到不妙的曹某向派出所报了案。</p>\n<p>\n	　　同年10月，民警电话通知曹某，刘某归案了，让其去派出所补充信息。“这之后的4年，我没有收到任何退赔的消息，直到2022年9月的一天，西城区检察院检察官给我打来电话，问我被骗的钱退还了吗？我当时想，事情过去这么久了，这个钱可能也要不回来了。”曹某说。</p>\n<p>\n	　　检察官发现财产刑执行监督案件线索</p>\n<p>\n	　　2022年7月，西城区检察院收到北京市检察院发来的区法院2021年财产刑执行立案、结案数据。如何开展有针对性的监督？经分析讨论，西城区检察院决定缩小范围，从终结本次执行程序结案的案件入手，一件件核查，确认被执行人是否确无财产可供执行。</p>\n<p>\n	　　“终结本次执行程序结案的，虽然案结了，但财产刑实际可能没有执行到位或没有完全执行到位。”郭玉芳说，判断被执行人的财产情况，一方面需要查阅原案件办理卷宗，了解被执行人的情况；另一方面通过查阅执行机关财产刑执行卷宗，了解实际执行情况。但把众多执行卷宗都调来似乎不现实，于是办案团队决定选择“小切口”——从执行案件立案金额入手，将1000多件以终结本次执行程序结案的财产刑执行案与检察业务应用系统中原案判决情况逐一进行比对。就这样，刘某财产刑执行监督案线索进入了检察官的视线。为了进一步核实情况，郭玉芳专门联系了被害人曹某……</p>\n<p>\n	　　“郭检察官在电话里问我被骗的情况，核实我被骗的钱是否退回来了……”曹某回忆起与郭玉芳第一次通电话的情景，他表示，后来检察官又多次打来电话，询问退赔情况。</p>\n<p>\n	　　被害人拿到退赔款</p>\n<p>\n	　　郭玉芳和办案团队在将财产刑的执行情况与原案判决情况比对后发现：法院以诈骗罪判处被告人刘某有期徒刑八个月，并处罚金1万元；责令刘某退赔1万余元返还被害人；在案扣押手机二部予以变卖，变卖款并入本判决主文第二项一并执行。</p>\n<p>\n	　　调取到的财产刑执行卷宗显示：查询到刘某名下共有存款1.2万余元。也就是说，当时刘某名下的财产达不到罚金与退赔数额的总数，但超过了单独一项的数额。2020年12月，执行机关将刘某名下的1万元存款予以划拨。因此，执行人员是按照原刑事判决书第一项罚金数额执行了刘某的财产。</p>\n<p>\n	　　这样执行合适吗？被害人的钱是否已退还？带着疑问，2022年9月，郭玉芳拨通了曹某的电话，了解到曹某既没有收到判决书，也没有得到退赔款。</p>\n<p>\n	　　“执行机关除了立案金额、结案金额与实际不符外，执行顺位也不符合法律规定。”郭玉芳表示，调取到相关证据后，西城区检察院向执行机关制发了纠正违法通知书，通知该机关恢复刘某对应刑事判决书涉财产部分的执行程序；将该案执行到位的款项优先退赔给被害人；继续调查了解被执行人刘某的财产状况，穷尽财产调查措施，确保该案涉财产部分得到执行。</p>\n<p>\n	　　收到纠正违法通知书后，被监督单位及时对案件启动了复查，并书面回复，表示将立即恢复案件执行，重新调查被执行人的财产情况并依法采取措施，同时进一步细化涉刑事财产执行的工作流程，杜绝此类问题的发生。</p>\n<p>\n	　　此后，郭玉芳从该案恢复执行开始持续跟踪监督。经执行机关对刘某的财产进行网络查控和冻结后，同年12月，财产被顺利划拨到法院账户上。目前，曹某已经收到被骗的1万余元。（简洁 李浩）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：温璐、薄晨棣)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0217/c1008-32625650.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (17,'2023-02-19 09:34:48.602114','2023-02-19 09:34:48.602114','孩子生长迟缓？请看这份指南','<div class=\"artDet\">\n                        \n                        <table class=\"pci_c\" width=\"399\">\n	<tbody>\n		<tr>\n			<td align=\"center\">\n				<img src=\"http://paper.people.com.cn/rmrbhwb/images/1/20230217/1676575876635_1.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n				<p>\n					　　图片来源：国家卫生健康委官网</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	　　孩子比同龄人长得慢，个子长不高，体重偏轻……这些在孩子成长中遇到的问题困扰着不少家长。</p>\n<p>\n	　　孩子生长迟缓，如何通过“食养”来改善？近日，国家卫生健康委办公厅印发《儿童青少年生长迟缓食养指南（2023年版）》（以下简称“指南”），旨在促进儿童青少年营养状况改善，进一步降低生长迟缓率，帮助儿童青少年培养健康饮食习惯，充分发挥生长潜力。</p>\n<p>\n	　　</p>\n<p>\n	　　每天12种以上、每周25种以上食物</p>\n<p>\n	　　什么是生长迟缓？指南给出了明确定义：生长迟缓是指由于膳食的蛋白质或能量、维生素、矿物质摄入不足等原因，导致身高低于筛查标准的年龄别身高（以身高为基础评价儿童青少年体格营养状况的一项指标）界值范围，属长期性营养不良。</p>\n<p>\n	　　《中国居民营养与慢性病状况报告（2020年）》显示，6岁以下儿童生长迟缓率为4.8%，6—17岁儿童青少年生长迟缓率为1.7%。儿童青少年生长迟缓率城乡差异一直较为明显，农村约为城市的2至3倍。</p>\n<p>\n	　　生长迟缓影响儿童青少年期的体格和智力发育，出现体能下降、学习表现不佳等，严重时容易导致罹患腹泻、肺炎等疾病，增加感染性疾病发病率和死亡率。此外，还可能影响其成年后的身高，增加肥胖、心血管疾病、糖尿病等慢性病的发病风险，降低劳动生产能力。儿童青少年生长迟缓仍是我国当前面临的重要公共卫生问题之一。</p>\n<p>\n	　　根据儿童青少年生长迟缓的特点，指南给出了食养原则和建议，详细描述了生长迟缓儿童青少年的食物选择和食养方例子，不同证型食药物质推荐以及不同地区、不同季节的食谱示例和常见食物交换表等工具。</p>\n<p>\n	　　如果出现生长迟缓，在膳食上需要注意什么？指南建议，生长迟缓儿童青少年食物选择应全面、均衡、多样。保证一日三餐、定时定量、饮食规律，能量和营养素摄入充足。每餐的膳食应包括谷薯类、蔬菜水果、畜禽鱼蛋、奶和大豆等食物中的3类及以上；每天食物种类达到12种以上，每周达到25种以上。尤其应重视选择高营养素密度食物。鱼虾类食物的优质蛋白质、维生素D、B族维生素、矿物质等营养素密度较高；深绿色叶菜的矿物质、维生素等营养素密度较高；蛋黄是多种营养素的重要来源，如维生素A、维生素D、维生素E、磷、铁、胆碱等，也是营养素密度较高的食物之一。同时，在合理膳食的基础上，可针对不同证型选用食药物质。</p>\n<p>\n	　　一些儿童青少年生长迟缓，常常与挑食、偏食有关。为此，指南建议，日常配餐过程中要注重同类食物互换，丰富食物品种，如可用杂粮或薯类部分替代米或面，避免长期食用1 种主食，畜肉与禽肉互换，鱼与虾互换，各种蛋类互换等，注重蔬菜的颜色和品种搭配。</p>\n<p>\n	　　可针对不同证型选“药食同源”食养方</p>\n<p>\n	　　与其他膳食指南不同，此次指南充分发挥现代营养学和传统食养的中西医各方优势，根据我国传统医学的理念和调养方案，将食药物质融入合理膳食中，辅助预防和改善儿童青少年生长迟缓。</p>\n<p>\n	　　“食养方中选用的食药物质为国家卫生健康委公布的既是食品又是药品的物品。”国家卫生健康委有关负责人说，可在医师或营养指导人员的建议下，根据儿童青少年具体情况选择具有健脾、开胃、消滞、益肾等作用的食药物质，如麦芽、山楂、莲子、芡实、山药等。</p>\n<p>\n	　　指南建议，在平衡膳食原则的基础上，遵循中医食养原则。合理搭配食物，体现“五谷为养、五菜为充、五果为助、五畜为益”的传统膳食结构特点。以健脾增食为食养总则，根据不同症状，采取不同食养方法。注意食补不能太过，以防阻滞脾胃。</p>\n<p>\n	　　指南强调，要因人因地因时食养。在因人制宜进行食养方面，针对不同证型，膳食中加入适宜的食药物质，丰富儿童青少年食谱，改善消化吸收功能；在因地制宜进行食养方面，根据各地地理环境特点、饮食习惯和食物供应特点，选择适宜的食物。中医既重视人体自身的整体性，又重视人与自然的相互关系，如季节气候对人体的影响。儿童青少年也应遵循春夏养阳、秋冬养阴的因时调养原则。春天寒气渐退，阳气生发，应适当摄入辛温之物助阳气之生发，如葱、蒜、韭菜等。夏季阳气外越而体内阳虚，应少食生冷。秋季干燥，应选择甘润养肺类食物，如雪梨、银耳。冬季寒冷，在温补之余，应不忘养阴，可适当食用鸭肉、猪肉等。</p>\n<p>\n	　　倡导食养与规律运动、充足睡眠相结合</p>\n<p>\n	　　孩子出现生长迟缓，吃好吃够就行了吗？指南强调，除了食养，还要注意保持适宜的身体活动，关注睡眠和心理健康。</p>\n<p>\n	　　适宜的身体活动可增强食欲，促进胃肠蠕动，改善消化功能；对于生长发育关键期的儿童青少年，可促进骨骼和肌肉的生长发育，有助于身高增长。为此，指南建议，生长迟缓儿童青少年通过中等强度的身体活动（如跳绳、篮球、游泳等），结合传统健身方式（如八段锦、五禽戏、武术等），促进生长发育。</p>\n<p>\n	　　充足的睡眠也是儿童青少年身体活动和生长发育的重要保证。“胃不和则卧不安”，消化功能不良会影响睡眠，进而影响生长激素的分泌，出现身材矮小。指南提出，应让儿童青少年养成良好睡眠习惯，保证睡眠时间和质量。例如，2—5岁儿童每天睡眠时长应为10—13小时，其中包括1—2小时午睡时间；6—12岁儿童每天睡眠时长应为9—12小时，13—17岁儿童青少年应为8—10小时。</p>\n<p>\n	　　与此同时，指南建议，要重视儿童青少年情绪和行为管理，关注情绪性少食、限制性进食等危害身体健康的行为，以合理膳食为基础，结合心理疏导、沟通交流等心理和情绪干预措施，改善儿童青少年生长迟缓。</p>\n<p>\n	　　如何及时发现孩子生长迟缓？指南指出，定期监测儿童青少年体格发育，有助于及时了解儿童青少年生长发育指标的动态变化，建议依据儿童青少年性别、年龄和营养状况，至少动态纵向追踪观察6个月（最好12个月）以上，根据儿童青少年的生长速率或生长曲线的偏离情况，评估其生长状况，及时调整指导方案。</p>\n<p>\n	　　国家卫生健康委有关负责人表示，指南主要面向基层卫生工作者（包括营养指导人员）、2—17岁儿童青少年的家长和校医等，为儿童青少年生长迟缓的辅助预防和改善提供食养指导，不可替代药物治疗。指南提示，对于微量营养素缺乏的儿童青少年，必要时在医师或营养指导人员的指导下合理使用营养素补充剂。长期生长发育不理想、改善效果不明显或疾病原因导致生长迟缓的儿童青少年，应到医院就诊。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0217/c14739-32625686.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (18,'2023-02-19 09:34:48.612448','2023-02-19 09:34:48.612448','为何门诊共济保障改革后个人医保账户钱少了?','<div class=\"artDet\">\n                        \n                        <p>\n	　　今年元旦，一些省份的地级市职工基本医保门诊共济保障机制落地实施。当地职工医保参保人发现，去医院看门诊就医购药能报销了。提升医疗保障待遇，这本是大家都期待的好事。但与此同时，自己个人医保账户里的资金减少了，也让一些人有点疑惑。</p>\n<p>\n	　　职工医保门诊共济保障机制改革意义何在，参保人的权益是否受到影响？对此，记者采访了有关专家。</p>\n<p>\n	<strong>　　“个人账户保门诊小病”局限日益凸显，共济保障改革势在必行</strong></p>\n<p>\n	　　“职工个人医保账户的资金减少，是因为实施门诊共济保障改革后的门诊保障机制发生了变化，将单位缴费全部计入医保统筹基金，不再将其中一部分划入个人账户，同时参保人个人门诊费用纳入医保统筹基金的保障范围，从而在整体上提高广大职工的门诊保障水平。”浙江大学国家制度研究院副院长金维刚告诉记者，我国现行职工医保制度已有20多年历史，实行社会统筹与个人账户相结合的制度模式。其中，统筹基金来源于用人单位缴费，主要提供住院、门诊大病和部分慢性病的医疗保障；个人账户来源于个人缴费和单位缴费的一部分划拨，主要用于一般的门诊医疗费用支出。这种“统账结合”的制度模式是特定历史时期的产物，有其特殊的历史作用，但这种制度模式也存在着不足之处。</p>\n<p>\n	　　金维刚表示，由于职工医保个人账户缺乏社会互济性，个人账户在门诊保障方面的功能具有局限性，出现了参保职工“有病的不够花，没病的用不了”的状况。一方面，在医保基金累计结余中有将近40%是个人账户结余资金，其中80%以上的个人账户累计结余资金主要集中在健康状况较好的青壮年参保人员的个人账户中，有些收入高的参保人员个人账户累计结余达数万元；另一方面，不少参保患者特别是广大退休人员个人账户资金在支付门诊费用方面不够用，门诊个人负担较重。在这种情况下，迫切需要通过建立职工门诊共济保障机制来提高医保在门诊保障方面的能力，以减轻个人在门诊医疗保障方面的负担。</p>\n<p>\n	　　国家医保局公布数据显示，2021年，全国参保职工在医疗机构中发生的费用中，在职职工平均花费2097元，退休职工花费8002元，后者是前者的3.82倍。在职职工住院率为9.90%，退休职工为39.50%，后者是前者的3.99倍。</p>\n<p>\n	　　“很明显，老年人生病多、花费多。”华中师范大学劳动与社会保障系副教授王超群表示，调取某个城市约500万人参保职工个人账户数据发现，在60岁以上、70岁以上和80岁以上的参保职工中，50%以上参保人的个人账户余额分别低于730元、325元和162元。也就是说，年龄越大，个人账户的结余金额越低，看一两次病就用完了。依靠个人账户完全无法解决老年人尤其是高龄老年人的门诊就医和购药需求。</p>\n<p>\n	　　“建立健全职工门诊共济保障机制是深化医保改革、提高参保职工门诊保障水平的客观需要。”金维刚表示，职工医保中的统筹基金是全体参保职工之间实现互助共济的基础，体现了医疗保险作为一种社会保障制度的再分配功能。从发展趋势来看，医保统筹基金需要承担更多的医疗保险责任。通过推行职工医保门诊共济保障机制改革，强化职工医保统筹基金，将职工门诊医疗费用全面纳入职工医保统筹基金的保障范围，有利于增强职工医保在门诊方面的保障功能，有利于完善医保筹资机制，有利于提高医保基金的使用效率，有利于职工医保更加公平可持续发展。</p>\n<p>\n	<strong>　　改革就是让最有需要的人受益，要算大账、长远账</strong></p>\n<p>\n	　　“一些人认为个人账户里的钱就是个人自己的，这是概念上的误解。”王超群介绍，个人账户里的钱在法律性质上是医保基金，由政府管理。只不过，医保基金按照每个参保职工的工资或者退休金水平，给每个人分别设置了一个可以使用的资金额度。这个资金额度划到个人账户户头上，就成了个人账户里的钱。既然属于医保基金，这些个人账户里的钱必须遵照社会保险法的规定，只能用于支付医药费用。</p>\n<p>\n	　　“个人账户里钱少了，我就吃亏了！”事实果真如此吗？</p>\n<p>\n	　　从全国看，实施普通门诊统筹后，互助共济功能显著提高，门诊需求多、患病多的参保职工明显获益。之前不报销的门诊常见病、多发病医疗费纳入统筹基金支付范围，将有效减轻普通门诊就诊费用负担。特别是患病多和罹患慢病的参保职工，获益远超个人账户减计额，仅仅减计数百元，就能报销千余元甚至数千元。2022年，全国职工医保普通门诊统筹待遇惠及11.8亿就诊人次，参保人政策范围内报销比例在50%左右。</p>\n<p>\n	　　当然，也有部分身体健康的职工，很少去医院就诊，个人账户资金减少，对他们来说确实有损失。对此，金维刚表示，改革本身就是利益调整的过程，让最有需要的人受益是基本原则。改革后，大部分参保人个人账户当期计入会减少，但改革后门诊待遇更好，真正患病的群众和老年人更受益。</p>\n<p>\n	　　有关专家详解了“待遇更好”：一是调减个人账户当期计入的同时建立了共济保障，通过权益置换提升普通门诊保障水平，增加的普通门诊统筹报销待遇比个人账户减少要高，随着制度健全完善还将逐步提高门诊报销水平，让老百姓充分享受改革红利。二是保留了个人账户这一制度设置，且历史上的结存仍然由个人支配使用，可继续用于个人在门诊就医、在药店购药，原来能保障的改革后仍然可以保障，还拓展了使用范围。三是计入办法更加公平，在职职工个人缴费继续计入个人账户，退休人员个人账户由统筹基金统一按定额划入，不再与本人养老金水平或者退休前工资一定比例挂钩，实现了同一地区内公平统一，同一人群内基本一致。</p>\n<p>\n	　　专家表示，在社会医疗保险的大框架下，个人账户的资金是“看病钱”，不是工资收入，也不是福利。年轻健康的，虽然现在看病不多，但疾病带来的经济风险是长期存在的。年老多病的时候，个人账户和个人积累有限，需要有坚实可持续的医疗保险实现社会互助共济来化解疾病风险。长远来看，每个人都会在改革中获益。“大家既要算小账，也要算大账；既要算眼前账，又要算长远账。”</p>\n<p>\n	<strong>　　门诊共济保障改革将细化配套措施，促参保职工获得感提升</strong></p>\n<p>\n	　　除了对个人账户资金减少不理解，也有一些职工提出：因为附近药店没有自己需要的常用药，要享受报销待遇还得常跑医院，耽误时间，还搭上了门诊费交通费。</p>\n<p>\n	　　“医保改革关系到人民群众的切身利益，各地经济发展水平、职工收入水平、医保基金统筹层次和收支状况以及参保人员对医保的理解认识等方面都存在着显著差异。因此，在目前职工医保以市级统筹为主的条件下，这项改革还需要由各统筹地区政府根据国家要求，结合本地实际情况制定出台具体政策和实施细则。”金维刚认为，目前，有的地方参保人对当地职工医保门诊共济保障的具体政策措施有不理解，不仅反映了当地有关政策宣传解读工作做得不到位，而且也提醒地方政府有关部门，要把制定和实施上述改革的具体政策措施以及经办服务工作做得更细更扎实。</p>\n<p>\n	　　为确保各地改革顺利推进，国家医保局早前已出台相关规定，包括职工医保个人账户家庭共济、适当拓展个人账户使用范围等等。</p>\n<p>\n	　　针对群众提出的药店购药难享受医保待遇的问题，国家医保局明确回应，改革后，参保患者在定点零售药店购药既可以使用个人账户，也可以凭外配处方享受统筹基金报销。为更好方便参保群众待遇享受，还将实施支付方面的创新。一是支持患者持外配处方在定点零售药店结算和配药，享受与在就诊医疗机构同等的报销待遇。二是探索将符合条件的“互联网+”医疗服务纳入保障范围，提高享受待遇的便捷性。三是探索推进电子处方流转，更好解决购药结算的需要。此外，还将协同相关部门在家庭医生签约服务、基层医疗卫生机构和定点药店配药联动上，进一步完善支持措施。</p>\n<p>\n	　　职工医保门诊共济保障制度建立之后，武汉等地也相继出台了一系列医疗机构便民服务政策。目前，武汉市682家定点医疗机构、1029家定点零售药店已纳入门诊统筹保障范围。</p>\n<p>\n	　　“后续，医保、卫健、市场监管、财政等部门将不断深化职工医保门诊共济保障机制改革配套措施，比如增加职工医保门诊统筹定点医疗机构数量，增加医保门诊慢性病特殊疾病保障病种，完善日间手术医保报销政策，推行电子处方流转等等，在定点医疗机构设立便民门诊，免收便民门诊的挂号费、诊疗费，确保参保职工看病购药更方便、医疗费用保障更充分、获得感更强。”武汉市政府相关人士表示。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月16日 04 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624928.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (19,'2023-02-19 09:34:48.622440','2023-02-19 09:34:48.622440','领电子消费券，一起“嗨购”吧','<div class=\"artDet\">\n                        \n                        <table class=\"pci_c\" width=\"400\"><tbody><tr><td align=\"center\"><img src=\"http://paper.people.com.cn/rmrbhwb/images/1/20230215/1676401220294_1.jpg\" /></td></tr> <tr><td><p>　　在北京举行的服贸会首钢园区金融服务专题展上，一名观众下载数字人民币APP并开通钱包。<br />　　新华社记者  李  鑫摄</p></td></tr></tbody></table><p>　　美食套餐五折享、新款冰箱七折购、热门景区免费逛……你最近领到电子消费券了吗？近期，全国多地通过线上平台发放电子消费券，涵盖餐饮、文旅、家电等多个消费领域。各地商户积极参与，消费者热情高涨，掀起了消费新热潮。</p><p>　　</p><p>　　“买单之前先领券”</p><p>　　“平时300多元的自助套餐，现在只要210元，感谢消费券帮忙把价格‘打下来’。”北京市民朱女士与朋友相约在朝阳区大望路附近的一家餐厅吃饭。用餐后，她们又“转战”附近的咖啡馆，用“满50元减15元”的消费券点了一份“网红蛋糕”，并在朋友圈推荐分享。</p><p>　　近期，北京市多区通过大众点评平台，向市民发放数轮餐饮消费券，带动了市民聚餐消费的热情。朱女士告诉记者，这些消费券面值15元至90元不等，可根据相应消费金额抵扣，叠加商家折扣相当于五到七折的优惠。“我们几个好朋友都领到了消费券，大家都在约着一起吃饭，讨论怎么用券最划算。”</p><p>　　如今，“买单之前先领券”已经成为广大消费者的常规操作。这些电子消费券不仅涵盖了餐饮行业，也在文旅、汽车等消费领域不断上新。浙江杭州近期通过网络平台，陆续发放了汽车消费券等不同类型的专项消费券，西湖区、灵隐寺、千岛湖等景区也实行免门票入场，吸引各地游客；贵州省针对电影等发放专项消费券，为本地居民和游客带来丰富的消费优惠。</p><p>　　记者了解到，去年12月份以来，已有超过40个城市宣布发放消费券，覆盖汽车、餐饮、文旅、冰雪运动等多个领域。</p><p>　　同步推广数字人民币</p><p>　　近日，不少地方将数字人民币推广与“扩内需”相结合，与线上、线下商家合作发放数字人民币消费券，用数字人民币成为多个地区市民的购物支付新风尚。例如，天津市在京东等线上消费平台设立数字人民币消费券专区，深圳发放200万个餐饮数字人民币红包。</p><p>　　山东济南将数字人民币优惠融入近年来热度较高的冰雪运动消费，推出“冰雪体育消费季·数字人民币冰雪特色惠民活动”。市民和游客在指定滑雪场消费时，通过数字人民币钱包进行支付，即可在使用济南冰雪体育消费券的同时，额外享受“满50元减20元”优惠。</p><p>　　走在城市街头巷尾，小到个体商店、大到百货商场，接入数字人民币交易系统的商家也越来越多。今年1月，数字人民币APP还上线了“无网无电支付”功能，部分手机机型在关机时可通过“碰一碰”收款终端完成数字人民币支付，进一步提升便捷性。</p><p>　　据悉，数字人民币APP上的子钱包已支持美团、京东、淘宝等90多个商户平台接收数字人民币，覆盖吃喝玩乐、衣食住行等多个场景。专家认为，数字人民币与电子消费券的深度融合，不仅为消费者带来真正的实惠，同时也加速了人们对数字人民币的认知和推广进程。</p><p>　　让消费者使用更方便</p><p>　　电子消费券不仅帮消费者省了钱，也让商家多获益。在北京石景山一家烧烤店门前，经理张宏招呼伙计给等位的顾客多搬几把椅子，自己则忙着端上茶水小吃。</p><p>　　“这段时间顾客多了不少，忙碌却也充实。”张宏告诉记者，近期餐厅的客流量较以往增长了三成以上，其中不少顾客都使用了消费券。为此，餐厅也根据消费券门槛金额推出了不同价位的套餐和优惠，吸引食客前来消费。“只要大家吃得满意，回头客多一些，我们多让点利也乐意。”</p><p>　　国家税务总局相关数据显示，刚刚过去的兔年春节假期，全国消费相关行业销售收入同比增长12.2%，消费市场强劲复苏。业内人士认为，各类电子消费券的发放在一定程度上提振了市民消费的信心和意愿，对于短期提升消费活跃度和商业活力有着积极作用。以海南为例，截至1月底，各类消费券核销率达到95.3％，有力促进了当地旅游消费市场回暖。</p><p>　　专家认为，在电子消费券发放过程中，还存在领用规则不清晰、使用场景和方式受限等问题。有关部门应重视消费者的反馈和诉求，与商家紧密配合，因地制宜，不断丰富电子消费券种类和使用场景，充分释放市场消费潜力。同时要规范精简电子消费券发放使用流程，加强消费券使用便捷性，让消费者用得方便、买得放心。</p>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0215/c14739-32624083.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (20,'2023-02-19 09:34:48.622440','2023-02-19 09:34:48.622440','村医李红霞：我用蒙医护健康','<div class=\"artDet\">\n                        \n                        <table class=\"pci_c\" width=\"400\"><tbody><tr><td align=\"center\"><img src=\"http://paper.people.com.cn/rmrbhwb/images/2023-02/14/09/rmrbhwb2023021409p17_b.jpg\" /></td></tr> <tr><td><p>　　2月3日，李红霞给村民把脉。<br />　　新华社记者  连  振摄</p></td></tr></tbody></table><table class=\"pci_c\" width=\"400\"><tbody><tr><td align=\"center\"><img src=\"http://paper.people.com.cn/rmrbhwb/images/2023-02/14/09/rmrbhwb2023021409p14_b.jpg\" /></td></tr> <tr><td><p>　　2月3日，李红霞到村民家给老人体检。<br />　　新华社记者  刘懿德摄</p></td></tr></tbody></table><table class=\"pci_c\" width=\"400\"><tbody><tr><td align=\"center\"><img src=\"http://paper.people.com.cn/rmrbhwb/images/1/20230214/1676314607754_1.jpg\" /></td></tr> <tr><td><p>　　2月3日，李红霞骑电动车去村民家随访。<br />　　新华社记者  连  振摄</p></td></tr></tbody></table><p>　　李红霞是内蒙古通辽市科尔沁左翼中旗架玛吐镇前新艾勒嘎查村的村医。在做好日常基本诊疗工作的基础上，李红霞经常入户巡诊，给村里的老人体检，为村民建立健康档案。从医16年来，她凭借自身高超的蒙医技术，成为远近闻名的乡村医生，不仅守护着全村400多名村民的健康，周边省份也不乏慕名而来的患者向她问诊。</p>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0214/c14739-32623247.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (21,'2023-02-19 09:34:48.632495','2023-02-19 09:34:48.632495','“小改造”承载“大幸福”','<div class=\"artDet\">\n                        \n                        <table class=\"pci_c\" width=\"400\">\n	<tbody>\n		<tr>\n			<td align=\"center\">\n				<img src=\"http://paper.people.com.cn/rmrbhwb/images/1/20230213/1676228159692_1.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n				<p>\n					　　在天津市和平区文化村社区，楼道内加装了扶手和折叠椅便于老人上下楼。<br />\n					　　新华社记者 李 然摄</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<table class=\"pci_c\" width=\"400\">\n	<tbody>\n		<tr>\n			<td align=\"center\">\n				<img src=\"http://paper.people.com.cn/rmrbhwb/images/1/20230213/1676228167247_1.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n				<p>\n					　　在江苏省盐城市亭湖区五星街道万户新村老年助浴服务中心，工作人员为前来洗浴的老人测量血压。<br />\n					　　新华社记者 李 博摄</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	　　在家走路怕摔、洗澡怕滑，线上挂不上号、打不到车……日常生活中，老年人在各类高频事项和服务场景中都有可能遇到难处。“适老化改造”应运而生。</p>\n<p>\n	　　中共中央、国务院印发的《关于加强新时代老龄工作的意见》提出，将无障碍环境建设和适老化改造纳入城市更新、城镇老旧小区改造、农村危房改造、农村人居环境整治提升统筹推进，让老年人参与社会活动更加安全方便。前不久密集召开的地方两会上，多地也在政府工作报告中对适老化改造作出部署。</p>\n<p>\n	　　随着我国人口老龄化加速、老年人口规模扩大，如何通过适老化“小改造”，承载老年人“大幸福”，成为社会日益关注的话题。</p>\n<p>\n	　　</p>\n<p>\n	　　改善生活设施设备——</p>\n<p>\n	　　让老年人不再“磕磕碰碰”</p>\n<p>\n	　　家住湖北省宜昌市西陵区四方堰社区的龙大妈腿脚不太方便。几个月前，在当地民政局和社区的帮助下，龙大妈家里迎来一些新变化：卧室床头添了感应小夜灯，床边加了起床护栏，地上铺了多个防滑垫……</p>\n<p>\n	　　“凳子既不滑也稳当，拉手多且方便，一拉就能站起来。”对在卫生间加装的助浴设施，龙大妈十分满意。</p>\n<p>\n	　　2022年末，我国60岁及以上人口28004万人，占总人口的19.8%。其中，绝大多数老年人选择居家养老。如何为老年人打造安全、便利的生活环境，成为重要的民生课题。</p>\n<p>\n	　　2020年7月，民政部等9部门联合出台《关于加快实施老年人居家适老化改造工程的指导意见》，提出顺应广大老年人居家养老的意愿与趋势，以满足其居家生活照料、起居行走、康复护理等需求为核心，改善居家生活照护条件，增强居家生活设施设备安全性、便利性和舒适性，提升居家养老服务品质。</p>\n<p>\n	　　为了对家里进行适老化改造，在北京工作的刘先生过去一年没少忙活。他告诉记者：“母亲80多岁了，日常生活原本能自理。可谁知后来在家里摔了一跤，现在都没恢复好，只能借助辅具活动。”</p>\n<p>\n	　　数据显示，我国每年有4000多万老年人至少发生1次跌倒，其中约一半发生在家中。基于此，刘先生挑选了专业团队对家里进行适老化改造，主要目的就是预防跌倒。很快，团队对地面做了防滑处理，在老人经常走过的墙边安装扶手，在卫生间也添置了助力架和洗澡椅。为了更安心，刘先生给母亲配备了紧急呼叫器，一旦遇到问题可随时呼救。</p>\n<p>\n	　　“一千个老年人家庭，就需要一千个适老化改造方案。”90后设计师小罗近年为身边许多老人设计了各类适老化产品与方案。他认为，居家适老化改造并非“简单装修”，要真正了解清楚老年人的具体需求，根据其身体特点、生活习惯等有针对性地定制方案。</p>\n<p>\n	　　家里需要改造，公共环境也要改造。如今，越来越多的地方将适老化改造融入老旧小区改造和市政配套建设中。</p>\n<p>\n	　　重庆市渝北区飞湖雅园小区的李大爷年过七旬，喜欢下棋。每天一早，他都要爬40多步楼梯，到家对面的益寿园益寿亭下棋。“之前走这段路觉得特别漫长。自从益寿亭通往居民区的路上有了手扶栏杆，出门轻松多了！”李大爷说。</p>\n<p>\n	　　让李大爷感到轻松的，还有益寿园内不少契合老年人需求的暖心设计，吸引着他走出家门。比如，增设适老化弧形椅子及扶手，座椅靠背避开尖角、扶手位置可靠放拐杖和水杯等。李大爷表示，园内有约50米长的“康复栏杆”，下完棋还能顺便压压腿、扭扭腰。</p>\n<p>\n	　　加速数字技术适老化改造——</p>\n<p>\n	　　助老年人跨越“数字鸿沟”</p>\n<p>\n	　　“信息化时代，我们不能落伍！”河北的贾大妈一边拿着手机看新闻，一边说，之前不会用智能手机，仿佛和社会脱节了。</p>\n<p>\n	　　儿子为贾大妈买了一部智能手机后，她感觉像打开了一片新天地：“现在我能通过手机了解国内外大事，也学会了微信聊天。接着，我还要尝试线上购物，以后生活就会更方便！”</p>\n<p>\n	　　现实中，并非所有老人都如贾大妈一样解锁“新技能”。碰到各行各业的线上业务，遇到日常生活中的扫码支付，很多老人犯了难。以打车为例，数据显示，目前74%的老年人仍在路边招手打车。即便在使用过手机叫车的老年人中，不求助他人独立叫车的仅占20%。</p>\n<p>\n	　　不少老年人面临着“数字鸿沟”。在清华大学人因工程与智能交互研究所研究员刘雪茜看来，这条“鸿沟”并非不可逾越。问题的关键，在于加速数字技术的适老化改造。</p>\n<p>\n	　　为帮助老年人更好适应并融入数字生活，《关于切实解决老年人运用智能技术困难的实施方案》《移动互联网应用（App）适老化通用设计规范》等文件相继出台，648家网站和App完成适老化改造。</p>\n<p>\n	　　记者发现，多款手机App切换成“长辈模式”或“关怀模式”后，字体变大，色彩变强，界面简化。此外，应用场景更丰富，部分App推出语音搜索、内容朗读、一键订票、一键叫车等功能，使用门槛降低。</p>\n<p>\n	　　在政策推动下，互联网应用适老化改造取得显著成效，但也存在一些问题。有网友反映，有的App在首页把字体、图标大小调整为适合老人阅读的模式，但进入二级界面后又跟正常版一样，还有的App适老化模式切入口不易找到等。</p>\n<p>\n	　　湖北老年教育研究中心专家张智敏表示，App适老化改造应更细化。在对老年人互联网需求进行充分调研的基础上，建立“数字惠老”长效机制，并落实好监督和测评责任。</p>\n<p>\n	　　当下，智能家居正走入老年人的日常。市场上不少智能家电具备语音交互功能，甚至还能识别动作、自动感应。科技感十足的同时，一些家居设备操作复杂，无形中增加了老年群体使用的难度。</p>\n<p>\n	　　刚参加工作的小王最近给姥姥买了台智能电视，可姥姥不太会操作，时常找不到想看的节目。调查显示，目前49.6%的老年人受困于“开机后不能直达想看的电视节目”。</p>\n<p>\n	　　“家电适老化改造要从关怀老年人这一初衷出发，各项功能和产品设计应符合老年人的使用习惯，同时保障老年人的安全。”奥维云网总裁助理兼研究创新部总经理赵梅梅表示，可以有更简单的操作界面，更人性化的设计，如无须弯腰或踮脚即可操作，防漏电、防烫伤、防划伤等。</p>\n<p>\n	　　细化标准规范 拓宽资金渠道——</p>\n<p>\n	　　为老年人减轻适老化改造经济负担</p>\n<p>\n	　　江苏省苏州工业园区的邓女士家中添置了各式智能设备：一键报警器、定时提醒服药的智能药盒、可自动定位的手表……父亲患有残疾，有了这些“神器”，她可以更好地照顾父亲。</p>\n<p>\n	　　据了解，江苏近几年将老年人家庭适老化改造纳入民生实事，增加了对困难老人家庭的补贴支持，邓女士一家能享受80%的补贴。</p>\n<p>\n	　　去年2月，民政部等4部门联合印发《关于推进“十四五”特殊困难老年人家庭适老化改造工作的通知》，提出“十四五”时期支持200万户特殊困难高龄、失能、残疾老年人家庭实施适老化改造。</p>\n<p>\n	　　当前，老年人居家适老化改造范围主要集中在由国家财政补贴的特殊困难老年人家庭。这就意味着，对广大老年人的覆盖能力还不够。扩大覆盖面，需要稳定的资金支持。资金从何而来？</p>\n<p>\n	　　“适老化改造涉及面广，资金需求量大，应加快建立社会筹资为主、财政资助为辅、多方出资的资金筹措机制。”中国法学会副会长姜伟指出，政府不仅要为居家和社区适老化改造提供必要资金支持，更要通过优惠政策吸引社会资本参与。</p>\n<p>\n	　　北京市投资促进服务中心副主任苏宏表示，对设备供应商、研发商提供专项支持，激发市场参与活力，鼓励企业及专业人才投身适老化改造。可考虑将部分适老化产品纳入社保范畴，减轻老年人自行购买适老化改造服务的负担。</p>\n<p>\n	　　总体上，我国适老化改造仍处于起步阶段。除了资金来源，其他方面也面临挑战。“各地推进适老化改造，很多企业依靠自己的企业标准，于是造成了‘一企一标、一地一策’的现象，很多适老化产品和服务的质量参差不齐。”中国老龄协会政策研究部主任李志宏建议，在国家层面，实施更为细化的行业标准和强制标准，促进适老化改造标准化与规范化。</p>\n<p>\n	　　长期以来，社会对适老化改造的重要性与必要性认知不足，在一定程度上阻碍了适老化改造进程。记者发现，不少老年人家庭对适老化改造缺乏了解，很多老年人，尤其是低龄老人持有“不服老”心态，认为自己根本不需要适老化产品及服务。</p>\n<p>\n	　　“推行适老化改造，首要在转变观念，提升全社会的适老化意识。”浙江大学经济学院百人计划研究员张川川说，对适老化改造所涉领域应有更全面的认识，不仅包括加装电梯、增设无障碍设施等硬件改造，各类产品的开发应用及公共服务也要考虑在内。</p>\n<p>\n	　　适老化改造不是短期行为，而是长期过程。中国人民大学智慧养老研究所所长左美云指出，要摒弃“运动式适老化”。将适老化改造纳入日常更新维护，使其从“可用”和“能用”，发展成“好用”和“爱用”。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0213/c14739-32622518.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (22,'2023-02-19 09:34:48.642282','2023-02-19 09:34:48.642282','积极促进健康老龄化','<div class=\"artDet\">\n                        \n                        <p>　　春节前夕，习近平总书记通过视频连线看望慰问基层干部群众时指出：“尊老爱老是中华民族的优良传统和美德。一个社会幸福不幸福，很重要的是看老年人幸福不幸福。”</p><p>　　人口老龄化是社会发展的重要趋势，是人类文明进步的重要体现，也是我国今后较长一个时期的基本国情。让老年人拥有一个幸福的晚年，必须把健康老龄化摆在突出位置。调查显示，我国老年人整体健康状况不容乐观，给家庭和社会带来沉重的照护负担。健康是保障老年人独立自主和参与社会的基础，健康状况是衡量老年人晚年生活是否幸福的重要指标。因此，促进健康老龄化，是积极应对人口老龄化最核心的问题，也是积极应对人口老龄化的长久之计。</p><p>　　促进健康老龄化，需要完善老年健康服务体系。要以满足老年人的健康需求为导向，大力发展老年健康事业，着力构建包括健康教育、预防保健、疾病诊治、康复护理、长期照护、安宁疗护在内的老年健康服务体系，为老年人提供优质高效的整合型医疗卫生服务。推进二级及以上综合性医院设立老年医学科，鼓励支持通过新建改扩建、转型发展，加强老年医院、康复医院、护理院建设。提高老年人家庭医生签约服务覆盖率，扩大医联体提供家庭病床、上门巡诊等居家医疗服务，推动医疗服务向居家社区延伸，发展“互联网+照护服务”，完善从专业机构到社区、家庭的长期照护服务模式。发挥中医药在健康管理、疾病预防和治疗康复方面的作用，让更多老年人方便看中医、看上好中医。</p><p>　　促进健康老龄化，需要优化老年人的居住环境。一项调查显示，跌倒成为我国65岁以上老年人因伤致死的首位原因。平均每10位老人，就有3至4人发生过跌倒，其中一半以上跌倒事件发生在家里，最常见的场景是在浴室、上厕所和起身下蹲。因此，要加快社区和居家的适老化改造，为老年人提供舒适安全的居住环境。尤其是要化解独居、空巢、留守、失能、重残等特殊困难老年人的居家养老安全风险，加快居家环境的适老化改造。例如，地面进行防滑处理、床边加装护栏把手、厕所和浴室安装扶手……这些防护措施，可以有效降低老年人跌倒风险。</p><p>　　促进健康老龄化，需要加快老龄产业发展。在老龄健康领域，现有服务和产品单一，难以满足老年人多层次、多样化需求。要推动老龄事业和产业高质量发展，激活老年用品和服务市场。深入挖掘老年人的真实需求，积极开发相适应的产业、产品和服务，不断提升产品和服务的适老化水平，让老年人的需求得到充分满足。引导和支持社会力量和民间资本进入老龄产业，促进养老服务与教育培训、健康、体育、文化、旅游等产业融合发展。鼓励发展具有比较优势的老龄特色产业，打造具有示范带动效应的老龄产业品牌。调动社会力量的积极性和创造性，有效扩大医养结合服务供给，推动医养结合服务多元化发展。</p><p>　　健康是人民幸福生活最重要的指标。实践证明，健康老龄化，是积极应对人口老龄化成本最低、效益最好的手段和途径。做好新时代老龄工作，必须把积极老龄观、健康老龄化理念融入经济社会发展全过程，营造有利于老年健康的社会支持和生活环境，延长健康预期寿命，维护健康功能，提高健康水平，全方位改善老年人生活品质，让老年人安享健康幸福的晚年。</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月10日   19 版）</span>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0210/c14739-32621027.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (24,'2023-02-19 09:34:48.642282','2023-02-19 09:34:48.642282','新研究：吸电子烟对DNA损伤不亚于香烟','<div class=\"artDet\">\n                        \n                        <p>\n	　　新华社北京2月18日电 《参考消息》日前刊登合众国际社网站报道《研究显示吸电子烟对DNA损伤不亚于香烟》。报道摘要如下：</p>\n<p>\n	　　美国南加州大学开展的一项新研究发现，经常吸电子烟的人与经常吸烟的人，二者脱氧核糖核酸(DNA)的受损程度相同，从而驳斥有关电子烟是香烟较安全替代品的说法。</p>\n<p>\n	　　在近期发表在《尼古丁与烟草研究》杂志上的一项研究中，南加州大学凯克医学院的研究人员从吸电子烟者、吸香烟者和从未吸过这两种烟的人口中提取上皮细胞进行分析。</p>\n<p>\n	　　他们发现，与不吸烟者相比，吸电子烟和香烟者的DNA受损程度是前者的两倍多。他们还发现，吸香烟和吸电子烟造成的DNA损伤性质相似。</p>\n<p>\n	　　这种对口腔内膜DNA造成的损害往往与癌症和炎性疾病等多种慢性疾病的风险增加有关。</p>\n<p>\n	　　凯克医学院的艾哈迈德·贝萨拉提尼亚博士在一份声明中说：“我们首次证明，电子烟使用者吸电子烟的次数越多、时间越长，口腔细胞DNA受到的损伤就越大。同样情况也发生在吸烟者身上。”</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0219/c14739-32626716.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (25,'2023-02-19 09:34:48.652420','2023-02-19 09:34:48.652420','川渝将加快推动公立医院检验结果互认','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	17日，川渝医疗管理高质量发展座谈会在四川省广安市举行，川渝两地将分步骤加快推动两地二级甲等以上公立医院开展检查检验结果互认，以控制不必要的重复检查、减轻患者就医负担。</p>\n<p style=\"text-indent: 2em;\">\n	2023年川渝两地重点推动重庆市中心城区、万州、涪陵等29个区(县)和四川省成都、广安、泸州等15个市相邻地区开展二级甲等以上公立医院检查检验结果互认。此前，川渝两地已实现所有三级甲等公立医院45项临床检验、67项医学影像检查项目结果互认，互认工作已惠及两地672.24万例次患者，累计为患者节约费用约3.88亿元。</p>\n<p style=\"text-indent: 2em;\">\n	近年来，川渝医疗管理协同发展取得了显著成效。以四川离重庆主城区最近的地级市广安市为例，渝广两地每年互认检查检验结果2.8万例次，邻水县人民医院、广安区人民医院等县级医疗卫生机构得到重医附二院等名院驻点帮扶指导，广安与重庆市渝北区、合川区签订《毗邻区域120应急救援合作协议》，联动区域内实现就近调度救护车，西南医院、新桥医院等均对广安协议医院开通了双向转诊绿色通道。</p>\n<p style=\"text-indent: 2em;\">\n	据悉，该座谈会由广安市人民政府主办。根据会议共识，接下来，川渝两地将加快构建优势互补、高效整合、协同共赢的卫生健康发展新格局。通过建立健全医疗服务、公共卫生、健康产业等方面联动协作机制，实现资源、人才、技术、信息等要素良性流动、合理配置。到2025年，成渝地区一体化公共卫生体系、品质化医疗卫生服务体系、高端化健康产业体系健全完善，区域卫生健康协作高水平样板初步建成，“大病不出成渝”全面实现。(王爵 王林)</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0219/c14739-32626717.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (26,'2023-02-19 09:34:48.652420','2023-02-19 09:34:48.652420','“名医走基层 志愿服务行”活动的四个镜头','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	由国家卫生健康委组织的“名医走基层 志愿服务行”活动，2023年第一站来到四川省凉山彝族自治州。2月16日、17日连续两天，专家们到大山里开展义诊，在当地医院带教查房，马不停蹄。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>“大专家看病，‘瓦吉瓦’”</strong></p>\n<p style=\"text-indent: 2em;\">\n	16日，凉山州普格县螺髻山镇的小广场上，来自北京、上海、广州、重庆、乌鲁木齐的多名志愿服务队成员开始义诊。他们都曾当选“中国好医生、中国好护士”月度人物。</p>\n<p style=\"text-indent: 2em;\">\n	心血管、肝胆、肾脏、眼科、妇科、儿科、护理、中医……专家们的服务领域覆盖面广。同行的还有来自四川大学华西医院、华西第二医院的12名专家和凉山州的44名医务人员。</p>\n<p style=\"text-indent: 2em;\">\n	初春山桃花次第绽开，闻讯而来的村民排成长队。</p>\n<p style=\"text-indent: 2em;\">\n	北京大学第一医院院长助理、肾脏内科副主任周福德耐心地解答着每一名病患的提问，有时语言不通，就请当地医务人员帮忙翻译。</p>\n<p style=\"text-indent: 2em;\">\n	阿得伍友带着小儿子的脑部核磁片子问诊。“医生告诉我说之前的诊断没问题，可以适当增加康复训练。”阿得伍友的心里踏实了，“大专家看病，‘瓦吉瓦’（好）！”</p>\n<p style=\"text-indent: 2em;\">\n	天色渐黑，首都医科大学附属北京中医医院院长刘清泉的诊台前仍有不少村民排队。“中医应当普及到镇、村，让更多老百姓真正体验到中医药给生活带来的帮助。”刘清泉感慨。</p>\n<p style=\"text-indent: 2em;\">\n	目送远道而来的“好医生、好护士”上车返程，村民挥手告别：“谢谢你们给大山里送来‘专家号’！”</p>\n<p style=\"text-indent: 2em;\">\n	<strong>“我会按时量血压，‘卡沙沙’”</strong></p>\n<p style=\"text-indent: 2em;\">\n	进村后，志愿服务队成员沿着山路再绕两道弯，来到一个独门小院。听见脚步声，82岁的何安贵由家人和村干部陪同，迎到门口。</p>\n<p style=\"text-indent: 2em;\">\n	何安贵是螺髻山镇德育村10组村民，患有冠心病、心肌梗塞；其妻阿列么丁都67岁，患有高血压。陆军军医大学第二附属医院（新桥医院）心血管外科主任医师肖颖彬拿出听诊器，给何安贵快速做了检查，又取出血压计测量：“高压210了，您要特别注意啊！”</p>\n<p style=\"text-indent: 2em;\">\n	肖大夫把一台便携式血压计留下，并给何安贵的家人演示如何使用。详细询问夫妻双方的病情和病史后，肖大夫说：“用药都没问题，但要坚持定时服用。”</p>\n<p style=\"text-indent: 2em;\">\n	送医生出门时，何安贵黝黑的脸上皱纹笑得舒展开来：“我会按时量血压，定时吃药，‘卡沙沙’（谢谢）！”</p>\n<p style=\"text-indent: 2em;\">\n	<strong>“尝试建设双向转诊平台、技术交流平台”</strong></p>\n<p style=\"text-indent: 2em;\">\n	17日，志愿服务队一行人来到凉山彝族自治州第一人民医院。</p>\n<p style=\"text-indent: 2em;\">\n	门诊、病房、重症监护室……整洁敞亮，光是儿科就有100多张床位。</p>\n<p style=\"text-indent: 2em;\">\n	不过，该院儿科主任石艳说，一些孩子如果诊断不明确，其父母出于对交通、语言交流的顾虑，往往不会选择到外地更好的医院就医。</p>\n<p style=\"text-indent: 2em;\">\n	首都医科大学附属北京儿童医院血液二科主任吴润晖接话说：“基层医生的服务面更‘广’，我们的专科诊疗水平更‘高’，大家携手，一定能做好边远地区儿童专科医疗工作。”</p>\n<p style=\"text-indent: 2em;\">\n	两位医生约定，2023年一起尝试建设双向转诊平台、技术交流平台。遇到诊断疑难的病人，州医院推荐到北京儿童医院，北京的医生接待、诊断并制定好治疗方案后，再转基层医院开展后续治疗，并对病人保持双向动态随诊。同时，州医院选派年轻的专科医生到北京进修交流。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>“‘名医走基层 志愿服务行’中国好医生、中国好护士志愿服务总队正式成立”</strong></p>\n<p style=\"text-indent: 2em;\">\n	在数百名凉山群众的见证下，授旗仪式举行。一面旗帜，由中宣部志愿服务促进中心副主任张其胜郑重交到国家卫生健康委宣传司负责人米锋的手中。</p>\n<p style=\"text-indent: 2em;\">\n	国家卫生健康委副主任李斌在现场宣布：“‘名医走基层 志愿服务行’中国好医生、中国好护士志愿服务总队正式成立！”</p>\n<p style=\"text-indent: 2em;\">\n	这支志愿服务总队，成员来自2017年以来中央文明办、国家卫生健康委联合举办的“中国好医生、中国好护士”群众推荐评议活动产生的月度人物等。</p>\n<p style=\"text-indent: 2em;\">\n	米锋介绍，成立志愿服务总队，旨在进一步发挥更多先进典型的示范带动作用，带动更多医疗卫生工作者投身志愿服务，为革命老区、边远地区、乡村振兴重点地区群众送健康。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0219/c14739-32626718.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (27,'2023-02-19 09:34:48.662207','2023-02-19 09:34:48.662207','全国名中医陈宝贵谈雨水节气与养生','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	<span style=\"text-indent: 2em;\">北京时间2月19日6时34分将迎来雨水节气，雨水是二十四节气中的第二个节气，也是春季的第二个节气。因为气温回升、冰雪融化、降水增多而取名为雨水。《月令七十二候集解》中说：“正月中，天一生水。春始属木，然生木者必水也，故立春后继之雨水。且东风既解冻，则散而为雨矣。”说的就是雨水节气前后，万物开始萌动，春天的脚步近了。</span></p>\n<p style=\"text-indent: 2em;\">\n	每年雨水节气前后，就是所谓“龙抬头”之时。中国华北地区自古就有“二月二，龙抬头；大仓满，小仓流”说法。</p>\n<p style=\"text-indent: 2em;\">\n	为了弘扬中医养生与防病文化，让公众了解和掌握节气养生的知识，天津中医药大学特邀请全国名中医陈宝贵教授谈“雨水节气与养生”。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>生活起居</strong></p>\n<p style=\"text-indent: 2em;\">\n	陈宝贵教授指出，“春捂”是古人根据春季气候变化特点提出的穿衣方面的养生原则。但是“春捂”到底要“捂”哪里呢？陈教授建议，要将保暖的重心放在下身。腿脚的保暖工作做好了，才能防止春季疾病的入侵。春季多风，运动后容易出汗，春风一吹容易感冒。尤其是老年人出门要记得戴帽子，正如中医经典所讲“头为诸阳之会”，戴帽子正是春季顾护阳气的做法。另外，老年人在春季洗漱、洗碗、洗衣时，尽量用温水，以免寒湿之气挟风侵袭关节，造成关节疼痛。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>饮食养生</strong></p>\n<p style=\"text-indent: 2em;\">\n	陈宝贵教授指，出食物和药物可以按照“四气”的属性划分，即寒、热、温、凉。在日常食疗养生中可以参考药食四气的属性加以选择。中医认为，春季与五脏中的肝脏相对应，人在春季肝气容易过旺，肝木旺则克脾土，对脾胃产生不良影响，妨碍食物正常的消化吸收。因此在雨水节气，在饮食方面人们要注意补脾。粥被古人誉为“天下第一补人之物”。粥以米为主，水为辅，水米交融，香甜可口，便于消化吸收，补脾养胃。比如扁豆红枣粥、山药粥、栗子桂圆粥都是很好的选择。(糖尿病人要做好食物搭配，不宜单纯喝白米粥。)</p>\n<p style=\"text-indent: 2em;\">\n	<strong>运动养生</strong></p>\n<p style=\"text-indent: 2em;\">\n	陈宝贵教授指出，自雨水节气开始，人们就要从“冬藏”的养生方式中解放出来，让“窝”了一冬的身体活动起来。春三月应该早睡早起，可以散步、可以踏青，通过活动肢体以达到疏通气血、调节经络、舒畅情绪的作用，但是要注意劳逸结合，运动量不宜过大。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0219/c14739-32626719.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (28,'2023-02-19 09:34:48.662207','2023-02-19 09:34:48.662207','长三角多地最高温创今年新高','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	18日，南方开启“升温模式”，长三角多地气温来到20℃以上，上海、杭州等地最高温创今年新高，提前体验春季般的暖意。不过受冷空气和降雨影响，19日起南方气温将迎来“大跳水”，局地累计降温幅度或超15℃。</p>\n<p style=\"text-indent: 2em;\">\n	今天是近期以来长三角地区最暖的一天，仿佛一脚从冬季迈入春季。不少地方气温升到近期高点，江南、华南大片区域最高气温回升到20℃以上，多地甚至达到25℃左右，在立春时节提前感受清明时节的暖。</p>\n<p style=\"text-indent: 2em;\">\n	在暖气团控制下，气温回暖，连早间的寒意也“收敛”了。18日10时，浙江全省大部地区气温已达13℃至15℃，白天气温更是节节攀升，浙北北部和东南沿海地区最高气温达19℃至21℃，其他地区最高气温22℃至24℃。</p>\n<p style=\"text-indent: 2em;\">\n	今天（18日）白天，杭州的最高气温高达23.8℃，为当地有历史记录以来同期最温暖的一天。周六遇上这样的好天气，不少市民选择了外出游玩，西湖景区及杭州市中心商圈再次开启“堵车模式”，也有部分市民选择“错峰”前往市郊露营，在山水间玩个尽兴。</p>\n<p style=\"text-indent: 2em;\">\n	在阳光助力下，上海市的午后气温也如火箭般往上蹿，今日上海全市最高气温都定格在了“2”字头，其中徐家汇站最高气温达21.0℃，这也是上海今年以来气温最高的一天。</p>\n<p style=\"text-indent: 2em;\">\n	不过随着冷空气到来，19日起，气温进入“过山车”下降通道，“虚高”的气温将迅速“跳水”，中国西南地区东部到江南、华南东部一带累计降温幅度普遍达到8℃至12℃。目前，北方较强冷空气已开始影响浙北北部地区，受其影响，浙江全省将有一次明显的降温、大风和弱降水过程。</p>\n<p style=\"text-indent: 2em;\">\n	降温过后，21日至22日，长江沿线一带地区最高气温会降到10℃左右，低于常年同期。其中浙江早晨温度较低，西北山区气温将达到零度左右，局部有薄冰，体感上又重回寒冬时节。与此同时，暖湿气流也将明显增强，冷暖气流交汇之下，长三角多地有时会有小雨光顾。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0219/c14739-32626723.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (29,'2023-02-19 09:34:48.672248','2023-02-19 09:34:48.672248','亲子沟通：《狂飙》里有个反面典型','<div class=\"artDet\">\n                        \n                        <p>\n	　　“《狂飙》里高晓晨成年后为什么会那么叛逆？因为高启强是‘捧杀’，陈书婷是‘棒杀’，从心理学角度来看，这对‘父母’演示了两种最糟糕的亲子沟通方式。”随着各地中小学校陆续开学，各种开学“大戏”纷纷“登场”。为帮助学生和家长们平稳度过开学阶段，南京市教育部门组织的公益讲堂“第三间教室”，邀请南京中医药大学心理学教授、博士生导师曾智，给广大学生家长“开课”，为开学季的亲子沟通支招。</p>\n<p>\n	　　寒假作业没补完怎么办？身体开学了心理还在放假怎么办？开学就定目标会不会压力过大？……连日来，各地家长“喜忧参半”，喜的是家里的“神兽”终于可以回归校园，忧的是开学就意味着新的“起跑线”，孩子和家长心里都没底。</p>\n<p>\n	　　“每到新学期，很多学生和家长都会有些紧张和焦虑。其实，家长和孩子们需要明白，走进一个新的生活或者学习状况，在适应过程中表现出焦虑和紧张是正常现象。对家长们来说，要对此给予理解和接纳，不必过度担忧，给孩子们一点时间，他们会慢慢适应开学的节奏，找到符合自己的学习方式和方向。”曾智说，对于孩子来说，高质量的陪伴、安全感、爱和尊重是比提供物质更为重要的养分，“虽然是老生常谈，但很多家长在实际操作中却会‘变形走样’。”</p>\n<p>\n	　　“比如说，现在很多家长都知道要给孩子高质量的陪伴，但什么是高质量的陪伴呢？‘陪’的不是时间，更重要的是关系，如何理解孩子很重要。父母要懂孩子，但又不要太懂孩子。孩子们需要家长的理解，但一位‘无所不知’的终极权威却会遏制孩子自身的探索和发展。”曾智介绍，“比如，在教育心理学研究中，我们就发现家长擅长的科目，有时候未必是其孩子的强项。为什么会出现这样的情况？因为这样的家长可能无法体会孩子成长中的困境，甚至还不能与孩子产生共情，孩子被困在家长津津乐道的‘当年勇’之中无所适从。”</p>\n<p>\n	　　“再说尊重和爱，最近很火的一部电视剧《狂飙》中，叛逆的高晓晨让很多观众都怒其不争，他为何会变成这样。”在曾智看来，“这就是必然的结果，在剧中，高启强对高晓晨是一味的宠和溺爱，而母亲陈书婷对高晓晨则只有贬低和斥责。”</p>\n<p>\n	　　曾智以剧中一个场景举例，“高晓晨正在练钢琴，高启强一味地夸他学得快、学得好，而母亲陈书婷却在一旁冷言打压：‘练了一个月才练成这样，还有脸说！’听到妈妈说出这句话，高晓晨脸上原本有的笑容瞬间就消失了，情绪也变得低落，甚至导致练琴不自觉地分神弹错，结果陈书婷狠狠地打了他一下，还抛出一句‘手型不对，学钢琴不打怎么练！’”</p>\n<p>\n	　　“相信在很多家庭中都有过类似的情景，这样一对‘父母’展示的正是亲子沟通与互动中的大忌，‘捧场教育’和‘贬低教育’。父母应尽可能用正面语言开展亲子沟通和互动，不要把自己的负面情绪或认知、观点强行投射到孩子身上。”曾智建议，新学期，家长可以跟孩子一起制定一些合适的学习目标，“在孩子完成这个目标的过程中，父母需要做到保持一致的态度，给予正面反馈和鼓励。”</p>\n<p>\n	　　据南京市鼓楼区委教育工委书记侯正永介绍，这个“第三间教室”是为家长们打造的专门课堂。“孩子成长的第一间教室是家庭，第二间教室是学校，‘第三间教室’就是希望把第一间教室(家庭)和第二间教室(学校)打通，让孩子、教师、家长在一起学习。在这里，我们定期邀请各领域有丰富经验的专家为家庭和学校教育提供方方面面的建议和提醒，经过几年的建设，已成为深受学生家长和学校教师欢迎的家校社合作沟通平台。”(记者 申冉)</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0219/c14739-32626725.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (30,'2023-02-19 09:34:48.677816','2023-02-19 09:34:48.677816','北京雨雪过后迎来晴天 气温加速攀升','<div class=\"artDet\">\n                        \n                        <p>\n	　　昨天（2月18日），北京多地迎来雨雪和大风天气，气温低迷。今天进入我国二十四节气的“雨水”，北京却是雨过天晴，风力也较昨天明显减弱，气温快速攀升，预计白天的最高气温可达10℃，适宜外出活动和开窗通风。</p>\n<p>\n	　　昨天，北京迎来今冬第五轮降水过程，其中西部和北部地区降水较为明显，以降雪为主，城区及南部地区相对较弱，以雨或雨夹雪为主；下午到傍晚前后，大风也紧随其后上线，全市普遍出现3~4级左右的偏北风，部分地区的阵风甚至达到6~7级左右，北京市气象台于昨天16时40分发布大风蓝色预警信号。</p>\n<p>\n	　　今天，雨雪过后天空迅速放晴，北京将迎来阳光在线、天朗气清的一天。根据北京市气象台今晨6时预报，今天白天，北京晴，北转南风3级左右，最高气温10℃；夜间晴，南转北风3级左右，最低气温-4℃。</p>\n<p>\n	　　明天，北京晴，有偏北风2~3级，平原地区最高气温6℃，山区最高气温2～5℃。</p>\n<p>\n	　　气象部门提醒，北京今天阳光在线，气温回升明显，公众适宜外出活动和开窗通风；不过早晚时段大部地区的气温仍在0℃以下，寒意在线，公众仍需继续做好防寒保暖工作。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0219/c14739-32626673.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (31,'2023-02-19 09:34:48.682322','2023-02-19 09:34:48.682322','喝酒防不了新冠 还会加重症状','<div class=\"artDet\">\n                        \n                        <p>\n	　　“喝白酒杀菌能预防新冠”一度被不少人信以为真。这些人的逻辑是“病毒怕酒精，天天喝酒的人不容易感染”。那么喝酒真的能够预防新冠感染吗？当然不可能！不仅不能防新冠，喝酒还会加重“中招儿”后的症状。</p>\n<p>\n	　　酒精是常用的消毒剂，75%的酒精外部使用可以起到灭杀病毒的作用，但这和喝酒可是两码事。</p>\n<p>\n	　　饮用白酒，在体内主要在胃肠道吸收，通过肝肾代谢，根本接触不到感染呼吸道的新冠病毒。且饮用白酒完全达不到75%酒精的灭杀病毒作用。不仅如此，长期饮酒对免疫系统和呼吸系统都存在着各种影响，反而会增加感染新冠的风险。</p>\n<p>\n	　　“天天喝酒”，一部分这样的人已经开始生病了——“酒精依赖综合征”。酒精的成瘾会导致无法停止饮酒，停止或减少饮酒后便会出现手抖、心慌、出汗等酒精戒断症状，严重情况下甚至会危及生命。新冠感染之后，大部分人会出现发热的过程，也可能感觉乏力、干咳等等，有些长期饮酒的人，便会停止或减少饮酒，一天后，甚至更短到几个小时之后，戒断症状便会显现。并且，长期饮酒的患者，因酒精的长期影响，身体情况也更容易出现问题。</p>\n<p>\n	　　不论是肝脏代谢酒精带来的负担，还是长期饮酒引起的营养不良等，都会影响机体的免疫力，加重新冠感染的症状，增加重症肺炎出现的概率。那这种情况，难道还要继续喝酒吗？当然不是。当酒精已经达到依赖综合征情况时，只有在专科医院的治疗护理下，才能够安全地把酒戒掉。 文/邢笑萌（北京安定医院）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0218/c14739-32626408.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (32,'2023-02-19 09:34:48.682322','2023-02-19 09:34:48.682322','\"阳康\"后如何养肺?','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	肺作为重要的呼吸器官，负责人体氧气与二氧化碳的交换，维持人体新陈代谢。“阳康”后如何养护肺脏成为人们关注的话题。</p>\n<p style=\"text-indent: 2em;\">\n	“加强肺脏的养护，对增强机体免疫力非常重要。”河南中医药大学第一附属医院副主任医师张海龙在接受人民网记者采访时介绍，中医称肺为“华盖”，其在五脏六腑的最上方，具有保护诸脏免受外邪侵袭的作用；肺为“娇脏”，且由于肺位与外界相通，有外邪侵袭时最易被犯，尤其是在新冠病毒感染后的恢复期，更应注意养护肺脏。</p>\n<p style=\"text-indent: 2em;\">\n	张海龙表示，养护肺脏首先要有良好的生活方式。合理规划饮食，“阳康”后可多食用补益肺气、滋阴润肺的食物，如百合、银耳、白萝卜、雪梨、莲藕、山药、蜂蜜等，并注意规律饮食，多饮水，多食用新鲜蔬菜、水果；注意作息规律、充足睡眠，避免熬夜；保持心情愉悦，根据自身的喜好和身体差异，选择合适的锻炼方法，如太极拳、八段锦等，以改善体质，增强心肺功能，避免正气虚弱再次感染病毒。</p>\n<p style=\"text-indent: 2em;\">\n	另外，要注意避风防寒。中医理论认为，养护肺脏应重视固护肌表，特别是老年人、儿童和患有慢性呼吸道疾病者，更应注意天气变化，及时增减衣服防寒保暖。</p>\n<p style=\"text-indent: 2em;\">\n	同时，张海龙还介绍了几个中医养肺小妙招。</p>\n<p style=\"text-indent: 2em;\">\n	艾灸可振奋阳气，顾护人体一身之阳。可选穴大椎、肺俞、孔最等，具有温肺散寒、宣通肺气、润肺理气之功效。</p>\n<p style=\"text-indent: 2em;\">\n	穴位按摩有助于改善气虚，促进疾病康复，可选穴太渊、列缺、中府等，能调补肺脏之气，清泻肺之余热。</p>\n<p style=\"text-indent: 2em;\">\n	呼吸六字诀，可调节脏腑经络气血运行，有助于正气的恢复。该方法不仅适用于轻度脏腑功能疾病患者的康复，也适用于健康人群调理脏腑，预防疾病。“嘘(xu)、呵(he)、呼(hu)、呬（si）、吹（chui）、嘻（xi）”，依次每个字6秒，反复6遍，腹式呼吸方式，建议每天1至2组。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624988.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (33,'2023-02-19 09:34:48.693594','2023-02-19 09:34:48.693594','运动装点乡村生活','<div class=\"artDet\">\n                        \n                        <p>\n	　　随着全民健身战略深入推进，运动健身在乡间日益普遍，丰富的体育活动绘成一幅幅生机勃勃的画卷</p>\n<p>\n	　　前不久，一场乡村攀岩登高赛在浙江省新塘边镇毛村山头村开展，高高的人工岩壁、色彩缤纷的岩点、安全到位的保护装置……比赛不仅令村民跃跃欲试，也让许多从外地回村过年的人眼前一亮。虽然攀爬速度、技术动作比不了专业选手，但对他们来说，在家乡参与一场攀岩登高赛，既是运动健身，也讨个“节节高”的好彩头。</p>\n<p>\n	　　如今，随着全民健身战略深入推进，运动健身在乡间日益普遍，丰富的体育活动绘成一幅幅生机勃勃的画卷。</p>\n<p>\n	　　防守、抢断、奔跑……随着球员奋力一投，人们屏气凝神，篮球在空中划过一道弧线，稳稳落入篮筐，观众的热情瞬间被点燃。正月初一，甘肃省平凉市李河村有着多年历史的篮球友谊赛如期而至。场上，赛况瞬息万变，球员赛得尽兴；场下，掌声欢呼不绝于耳，观众看得过瘾。除了篮球赛，平凉市各乡镇还安排了舞龙舞狮、社火秧歌等民俗体育文化表演。通过这些活动，村民收获了更健康的生活方式，增进了对当地文化的了解，增强了集体意识和归属感。观看村里的直播后，不少网友感慨，农村的体育设施变多了，生活变好了，既有“烟火气”，更有“精气神”。</p>\n<p>\n	　　不仅如此，一些乡村体育活动渐渐发展壮大，助力乡村振兴。</p>\n<p>\n	　　在江苏省徐州市沛县八堡村，“村界杯”乡村足球赛举办3年，吸引了众多爱好者甚至专业球员前来参赛、观赛；在河北省张家口市玉狗梁村，瑜伽助村民强身健体，为这个老人居多的村子带来新气象，不少人慕名前来感受“农村瑜伽”的魅力；在广东省佛山市南海西樵镇，传统体育项目醒狮成为向世界展示中国传统文化的窗口……体育聚揽人气，展现乡村之美，也逐渐为广大乡村打造了别具一格的新名片。</p>\n<p>\n	　　如今，社会各界对乡村体育愈发重视，体育设施不断完善，越来越多体育项目落地生根，并逐渐发挥品牌效应，带动地域发展。未来，随着乡村体育与传统节日、当地习俗、农产品售卖、文旅活动等不断深度交融，乡村发展将迎来更多机遇。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月16日 15 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624780.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (34,'2023-02-19 09:34:48.693594','2023-02-19 09:34:48.693594','警惕记忆丢失！','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	近日，一则“19岁男孩患阿尔茨海默病”的消息冲上热搜。这一案例也让很多人意识到，阿尔茨海默病不是老年人的“专利”，年轻人也要提高认知和早防早治的意识。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>19岁患者确诊 短期记忆丧失明显</strong></p>\n<p style=\"text-indent: 2em;\">\n	不久前，19岁男孩小程在父母陪同下来到医院求助。原来，小程17岁时开始出现注意力难以集中的情况，一年后短期记忆丧失明显。学习成绩也从班级中等以上水平，滑落到了末位，最终无法完成学业，不得不退学。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>首都医科大学宣武医院神经疾病高创中心主任 神经内科主任医师 贾建平：</strong>最短昨天吃的什么饭也记不住，东西放在什么位置他也经常忘记，经常找不见东西。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>无家族史无基因突变 患者发病年龄罕见 </strong></p>\n<p style=\"text-indent: 2em;\">\n	经过头部核磁扫描等全面检查和评估后，小程被诊断为阿尔茨海默病。专家表示，这位患者没有家族史，现有手段也无法查到基因突变，发病年龄十分罕见。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>首都医科大学宣武医院神经疾病高创中心主任 神经内科主任医师 贾建平：</strong>双侧的海马它都有萎缩的现象。看这个地方有一个黑的一个长条状的缝隙，本来它应该很饱满的，不应该有这个缝隙。你看这个年龄他也出现了萎缩，这也是一个缝隙。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>爱忘事不是判断标准 警惕情景记忆障碍</strong></p>\n<p style=\"text-indent: 2em;\">\n	阿尔茨海默病根据国际标准，一般以65岁为界分为早发型和晚发型。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>首都医科大学宣武医院神经疾病高创中心主任 神经内科主任医师 贾建平：</strong>确确实实现在看病的过程中，我发现中年人或者是四五十岁出现记忆下降的现象，确实还是比较多的。</p>\n<p style=\"text-indent: 2em;\">\n	记忆力下降是阿尔茨海默病最典型的症状，不少人担心，自己生活中也经常会忘事。记忆力衰退，是否是阿尔茨海默病前兆呢？</p>\n<p style=\"text-indent: 2em;\">\n	<strong>首都医科大学宣武医院神经疾病高创中心主任 神经内科主任医师 贾建平：</strong>忘不等于就是阿尔茨海默病。阿尔茨海默病的忘主要是情景记忆障碍，所谓的情景记忆障碍就是时间、地点、人物发生了一个严重的记忆丢失，把过去的事、近期的事都慢慢地忘掉了。偶尔忘一件事儿，我觉得问题不是那么太大，如果总是忘事，总是固定在某一个认知域当中的忘事，我觉得就应该提高警惕，到专业医院进行检查。</p>\n<p style=\"text-indent: 2em;\">\n	提高记忆力，目前全世界尚无特效药物，如何攻克记忆力衰退的难题，可以说预防远大于救治。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>【中国科协】阿尔茨海默病无症状期诊断列为十大前沿问题</strong></p>\n<p style=\"text-indent: 2em;\">\n	专家介绍，阿尔茨海默病出现明显的症状之前有一个中间阶段，叫轻度认知障碍，而在此之前就是无症状期。去年，阿尔茨海默病无症状期的诊断，被中国科协列为十大前沿问题之首。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>【高危因素】不良生活习惯 心脑血管疾病等</strong></p>\n<p style=\"text-indent: 2em;\">\n	专家介绍，通常60岁以后，阿尔茨海默病的发病率会逐年增高，重点人群可以提前进行专业检测，早发现早干预。除了家族史、遗传因素等，阿尔茨海默病还与环境因素和其他不良生活习惯有关。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>首都医科大学宣武医院神经疾病高创中心主任 神经内科主任医师 贾建平：</strong>阿尔茨海默病大部分是与自己的环境因素的暴露或者是不良的生活习惯以及共病，比如说一个人同时有好几种病，有糖尿病、高血压、心脏病、脑血管病等等，这几个病是与阿尔茨海默病非常相关的。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>【健康生活方式】管住嘴迈开腿 勤动脑多社交 记忆好</strong></p>\n<p style=\"text-indent: 2em;\">\n	想要延缓记忆力衰退，专家建议，要“管住嘴，迈开腿，勤动脑，多社交，记忆好”。即坚持食用7种以上推荐饮食，推荐饮食包括谷类、蔬菜、肉类、鱼类、饮茶等共12种，每周不少于150分钟的中等强度或不少于75分钟的高强度体育锻炼、每周不少于两次的社会活动、每周不少于两次阅读思考和书写等。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0212/c14739-32622018.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (35,'2023-02-19 09:34:48.702139','2023-02-19 09:34:48.702139','每天睡多久才够？年龄是重要影响因素','<div class=\"artDet\">\n                        \n                        <p style=\"text-align: center;\">\n	<script src=\"http://tv.people.com.cn/img/player/v.js\" /><script>showPlayer({id:\"/pvservice/xml/2023/2/15/946d150c-03ba-435a-a209-96bddab7849f.xml\",width:640,height:360});</script></p>\n<p style=\"text-indent: 2em;\">\n	普通人的一生约有1/3的时间是在睡觉中度过，但有的人表示，自己每天只睡4个小时就够了。那么，不同人群每天到底应该睡多少个小时？补觉有用吗？在本期《1分钟养生课》上，<span style=\"text-indent: 2em;\">同济大学附属同济医院精神医学科主任医师李清伟分享相关知识。</span></p>\n<p style=\"text-indent: 2em;\">\n	<span style=\"text-indent: 2em;\">李清伟表示，睡眠的时长是随着年龄变化而不断变化的。</span>一般而言，小学生要保证每天睡眠10个小时左右，初中生每天睡眠9个小时左右，高中生每天睡眠8个小时左右。</p>\n<p style=\"text-indent: 2em;\">\n	“对于成年人来说，每天睡眠6-8小时，60岁以上老年人适当减少，也需每天睡眠6个小时，这有助于减少痴呆的风险。”李清伟说。</p>\n<p style=\"text-indent: 2em;\">\n	是否存在每天睡眠4个小时就足够的情况呢？对此，李清伟强调，这只是极个别例子，并不是科学的睡眠方法，容易对身体健康造成损害。</p>\n<p style=\"text-indent: 2em;\">\n	在日常生活中，总有不能有效睡眠，导致第二天疲乏的情况，有些人就会采用补觉的方法来改善睡眠。那补觉有用吗？对此，李清伟表示，偶尔一次的失眠，可以采用补觉的方法来改善，有助于缓解疲乏。</p>\n<p style=\"text-indent: 2em;\">\n	“如果经常睡眠不好，采用补觉的方法就会弊大于利，可能会加剧失眠。”李清伟说，<span style=\"text-indent: 2em;\">一周有3天或3天以上的失眠，连续1个月，需及时寻求医生的帮助。</span></p>\n<p style=\"text-align: center;\">\n	<span style=\"text-indent: 2em; font-size: 20px; display: block;\"><strong>点击下方图片进入【</strong><strong style=\"color: rgb(178, 34, 34); text-indent: 2em;\">1分钟养生课</strong><strong style=\"text-indent: 2em;\">】专题，获取更多</strong><span style=\"color: rgb(178, 34, 34); text-indent: 2em; font-weight: bold; display: block;\">精彩内容</span></span></p>\n<p style=\"text-align: center; text-indent: 2em;\">\n	<span style=\"color: rgb(0, 0, 205); text-indent: 2em; display: block;\"><span style=\"text-indent: 2em; font-size: 28px; display: block;\"><strong>↓↓↓</strong></span></span></p>\n<p style=\"text-align: center; ;\">\n	<a href=\"http://health.people.com.cn/GB/443192/index.html\" target=\"_blank\"><img alt=\"\" src=\"/NMediaFile/2022/0406/MAIN202204062023000440366917591.jpeg\" width=\"550\" height=\"167\" /></a></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624673.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (36,'2023-02-19 09:34:48.702139','2023-02-19 09:34:48.702139','隔日禁食＋锻炼=脂肪肝克星','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	美国伊利诺伊大学芝加哥分校的营养研究人员对80名非酒精性脂肪肝患者进行的研究发现，那些遵循隔日禁食和锻炼的人能够改善他们的健康状况。研究成果发表在最近的《细胞代谢》杂志上。</p>\n<p style=\"text-indent: 2em;\">\n	研究人员报告说，在3个月的时间里，锻炼并隔日禁食（一天饮食不受限制，第二天摄入500卡路里或更少）的人胰岛素敏感性增加、肝脏脂肪减少、体重和ALT（丙氨酸转氨酶）下降。</p>\n<p style=\"text-indent: 2em;\">\n	大约65%的肥胖成年人患有非酒精性脂肪肝，其与胰岛素抵抗和Ⅱ型糖尿病的发展密切相关。如果不加以控制，脂肪肝疾病会导致更严重的并发症，如肝硬化或肝功能衰竭。</p>\n<p style=\"text-indent: 2em;\">\n	临床试验的参与者被随机分配到4组中的一组：隔日禁食组、有氧运动组、联合组和对照组。饮食组的参与者被跟踪他们的食物摄入量，运动组的参与者每天使用椭圆机1小时，每周5天。研究发现，只节食或只运动的人没有看到同样的改善，这加强了这两种相对便宜的生活方式对整体健康和对抗脂肪肝等慢性病的重要性。</p>\n<p style=\"text-indent: 2em;\">\n	除了看到代谢指标的改善外，研究还发现，在试验期间没有发生严重的安全事件。研究人员称，这是第一项调查间歇性禁食结合运动对非酒精性脂肪肝疾病预后影响的研究。与对照组相比，联合干预对于减轻肥胖和非酒精性脂肪肝患者的体重、脂肪量、腰围、丙氨酸转氨酶、空腹胰岛素、胰岛素抵抗和增加胰岛素敏感性是有效的。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624807.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (37,'2023-02-19 09:34:48.712368','2023-02-19 09:34:48.712368','“减毒”放疗提高鼻咽癌患者生活质量','<div class=\"artDet\">\n                        \n                        <p>\n	　　科技日报讯 （记者龙跃梅）近日，科技日报记者获悉，中山大学肿瘤防治中心常务副主任、鼻咽癌多学科诊断与治疗国家重点实验室主任马骏教授团队牵头，联合佛山市第一人民医院、梧州市红十字会医院共3家单位，共同完成了一项鼻咽癌患者内侧组咽后淋巴结区豁免放疗对比标准放疗的前瞻性Ⅲ期临床研究，首次创新了鼻咽癌患者内侧组咽后淋巴结区豁免放疗，在确保疗效的同时，显著改善了患者的生活质量。相关研究成果近日在线发表于国际顶级医学期刊《英国医学杂志》。</p>\n<p>\n	　　放射治疗是鼻咽癌的主要治疗方法，但毒副反应明显，容易损伤鼻咽周围的重要器官。在患者治疗时，既往推荐鼻咽癌全咽后淋巴结区预防照射。由于相关毒性大，放疗后晚期吞咽困难发生率为35.4%，而且这部分患者中有66.9%会发生误吸，12%会有吸入性肺炎，10%不能正常饮食、需要依赖鼻饲管或者胃造瘘，生活质量较差。</p>\n<p>\n	　　联合团队于2017年开展了一项前瞻性、随机、多中心的Ⅲ期临床试验，比较了鼻咽癌内侧组咽后淋巴区豁免放疗与标准放疗（内、外侧组均接受放疗）的临床结果。</p>\n<p>\n	　　数据显示，3年无局部复发生存率、3年总生存率、无区域复发生存率和无远处转移生存率在两组间均相似。但内侧组咽后淋巴结区豁免放疗组的放疗相关毒副反应，包括急性黏膜炎、急性吞咽困难、体重下降以及晚期吞咽困难等，发生率较标准放疗组降低约10%，而且内侧组咽后淋巴结区豁免放疗组的生活质量明显优于标准放疗组。</p>\n<p>\n	　　这一结果表明，鼻咽癌患者内侧组咽后淋巴结区的豁免放疗，不仅有效减少了咽缩肌及声门上喉等吞咽相关结构的照射，而且在确保患者治疗效果不降低的同时，还可提高患者生活质量。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0215/c14739-32624161.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (38,'2023-02-19 09:34:48.712368','2023-02-19 09:34:48.712368','中药注册管理专门规定发布','<div class=\"artDet\">\n                        \n                        <p>\n	　　新华社北京2月10日电（记者戴小河）国家药监局10日发布中药注册管理专门规定，自2023年7月1日起施行。</p>\n<p>\n	　　专门规定共11章82条，进一步对中药研制相关要求进行细化，加强了中药新药研制与注册管理，促进中药传承创新发展。</p>\n<p>\n	　　国家药监局表示，“临床—实验室—临床”是中药新药研发的主要特点。因此，专门规定强化“以临床价值为导向、重视人用经验、全过程质量控制”等研制理念，将中药的生产工艺、质量标准、药效学、毒理学、临床研究等各研制内容有机结合，结合药品安全性、有效性、质量可控性的基本要求，建立起兼顾药品基本要求，并具有中药特点的审评审批体系。</p>\n<p>\n	　　专门规定鼓励用新兴科学技术研究中药的作用机理，借鉴国际先进理念，鼓励将新型生物标志物、替代终点决策等用于中药疗效评价，在此基础上推动中药新药研制创新。</p>\n<p>\n	　　专门规定充分重视“人用经验”对中药安全性、有效性的支撑，设立专章，对中药人用经验的具体内涵，作为支持中药安全性、有效性证据的合规性、药学研究要求，以及人用经验证据支持注册申请的情形等进行明确，加快促进了中药评价方法与体系的建立和完善。同时，还明确注册申请人可根据中药人用经验对中药安全性、有效性的支持程度和不同情形，在研制时可选择直接从Ⅰ期或Ⅱ期或Ⅲ期开始临床试验研究。</p>\n<p>\n	　　国家药监局表示，专门规定是药品注册管理制度与中医药特点规律的深度融合，是中药传承创新发展的时代体现和具体实践。专门规定全面落实《中共中央 国务院关于促进中医药传承创新发展的意见》，吸收近年来中药审评审批制度改革成熟经验，并结合疫情防控中药成果转化实践探索，借鉴国内外药品监管科学研究成果，全方位、成体系地构建中药注册管理体系，推进中国式药品监管现代化建设。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0212/c14739-32621972.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (39,'2023-02-19 09:34:48.722281','2023-02-19 09:34:48.722281','首款国产3CL新冠药先诺欣今起供…','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	2月11日，首款国产3CL靶点抗新冠创新药先诺欣上市投产仪式分别在江苏、海南两地举行。即日起，先诺欣将陆续供应各医疗机构，满足新冠感染者急需。</p>\n<p style=\"text-indent: 2em;\">\n	2023年1月28日，先诺欣获国家药品监督管理局(NMPA)批准在中国附条件上市，先声药业立即启动生产程序并迅速完成所有生产环节。在产能爬坡阶段，先声药业在相关数据支持下，将首批先诺欣优先用于国内用药需求较大的区域。</p>\n<p style=\"text-indent: 2em;\">\n	在投产上市仪式现场，临床专家表示，当前国内新冠疫情虽处于“低位、多地零星散发”态势，但不应掉以轻心，当群体免疫屏障随体内抗体浓度衰减而消失后，还存在下一波新冠疫情的可能，我们必须严阵以待。期待先诺欣将成为抗击下一阶段新冠疫情的又一有效手段。现在经济发展态势强劲，社会生活渐趋正常，希望先诺欣成为助力中国经济蓬勃发展、免除社会大众后顾之忧的强大科技力量。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>降低病毒载量超96%，先诺欣“抗病毒”优于进口同类3CL口服药</strong></p>\n<p style=\"text-indent: 2em;\">\n	先诺欣于1月28日获国家药品监督管理局(NMPA)特别审评审批在中国附条件上市，批准文号国药准字H20230001，成为2023年国家药监局批准的第一个1类创新药，也是我国首款自主研发、具备自主知识产权的3CL靶点抗新冠病毒药物。该药物由先声药业与中科院上海药物所、武汉病毒所合作研发，在中日友好医院、复旦大学中山医院、武汉金银潭医院、深圳三院、山东千佛山医院等全国各地43家医院开展临床研究，是国内迄今为止覆盖最广泛感染新冠奥密克戎毒株的中国患者人群的注册临床。</p>\n<p style=\"text-indent: 2em;\">\n	据公开披露的临床研究数据，先诺欣与进口3CL抗新冠口服药具有相似的安全性、更为卓越的有效性证据，具有快速和大幅度降低病毒载量、缩短新冠感染者病程和转阴时间、3CL靶点更为安全可靠等三大特点。在接受完整的5天疗程治疗后，与安慰剂组相比，先诺欣组病毒载量显著下降1.43log10拷贝/mL(96.28%)；而进口同类3CL靶点抗新冠药在类似实验中病毒载量下降0.868log10拷贝/mL(86.4%)，比先诺欣低了近10个百分点。同时，安全性数据亦显示，先诺欣在中国轻中度新冠患者中安全耐受性良好。</p>\n<p style=\"text-indent: 2em;\">\n	中国科学院饶子和院士介绍，“先诺欣靶向冠状病毒复制过程中关键蛋白酶3CL，尽早服用能够快速并大幅降低病毒载量，因为其极强的抗病毒活性，先诺欣有助于缩短新冠感染者的病程，帮助新冠感染者核酸更快转阴”。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>“先声药业国家重点实验室”聚焦国家重大战略需求的成果</strong></p>\n<p style=\"text-indent: 2em;\">\n	先诺欣研发由先声药业国家重点实验室主导。据了解，该实验室是由科技部2015年批准组建、由企业牵头建设的国家重点实验室。近年来，该实验室聚焦国家重大战略需求，持续加大人才引进和研发投入，新冠疫情发生后，立即组织科研攻关小组，针对多个抗新冠病毒靶点进行研究，确定了以3CL蛋白酶抑制为主要研究方向的攻坚计划。</p>\n<p style=\"text-indent: 2em;\">\n	经过一年多的科技攻关，将一个处于临床前的苗头化合物经由临床I期安全性试验，Ib期初步疗效探索试验，以及II/III期疗效安全性确证试验，研发出先诺欣，并最终于2023年1月28日获国家药监局附条件批准上市。</p>\n<p style=\"text-indent: 2em;\">\n	“先诺欣从立项到成功获批仅用了短短14个月，成为首款国产3CL靶点的1类创新药，说明中国生物医药产业的创新能力正在快速提档升级”，业内人士分析认为。中国工程院王广基院士也表示，先声药业国家重点实验室快速响应国家重大战略需求，及时组织科研攻关团队，高效推动先诺欣项目研发，“是把论文写在祖国大地上的生动践行”。</p>\n<p style=\"text-indent: 2em;\">\n	参加上市投产仪式的南京市委常委、江北新区党工委书记杨学鹏表示，“先诺欣打破了进口药主导市场的局面，为广大新冠患者带来更有效的治疗手段。”。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>今日起陆续供应各医疗机构</strong></p>\n<p style=\"text-indent: 2em;\">\n	北京、湖北、四川等多省市疾控中心近日发布提示，当地新冠处于病例散发状态。据悉，先诺欣获批后，先声药业聚力全集团制药力量，12天后即完成投产上市。</p>\n<p style=\"text-indent: 2em;\">\n	“先诺欣的成功获批并快速投产上市，彰显先声药业社会责任和担当”，海口市长丁晖在上市仪式上表示，希望企业以市场需求为导向，进一步挖掘潜力、扩大产能，尽力服务保障下一阶段防疫药品市场需求。</p>\n<p style=\"text-indent: 2em;\">\n	据先诺欣临床数据提示，尽早使用疗效更优。为帮助患者在“黄金72小时”内用到先诺欣，特别是老年人、免疫力低下、基础疾病患者等脆弱人群、以及医疗条件较差的区域和偏远地区药物可及性，在初期产能爬坡阶段，先声药业在相关数据支持下，将首批先诺欣优先用于国内用药需求较大的区域，尽最大努力提升先诺欣可及性。</p>\n<p style=\"text-indent: 2em;\">\n	作为首款国产3CL靶点抗新冠创新药，先诺欣的医保政策受到高度关注。2月8日，国家医保局发布通知：先诺欣等国产新冠药属于第十版诊疗方案范围。先诺欣进入国家临时医保，纳入本省份基本医保基金支付范围，支付至2023年3月31日。目前，先诺欣的定价为750元每盒/疗程，同类靶点产品辉瑞Paxlovid为1890元/疗程。</p>\n<p style=\"text-indent: 2em;\">\n	“新冠奥密克戎毒株的持续变异，为我国疫情防控带来挑战，先诺欣因为其极强的抗病毒活性，有助于缩短新冠感染者的病程，为我国下一阶段疫情防控提供了有效应对手段，也增强了我们的信心。”中国科学院饶子和院士表示。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0212/c14739-32622058.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (40,'2023-02-19 09:34:48.722281','2023-02-19 09:34:48.722281','什么是诺如病毒？','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	人民网北京2月14日电 （记者孙红丽）<span style=\"text-indent: 2em;\">中国疾控中心微信公众号14日发布诺如病毒胃肠炎健康提示称</span><span style=\"text-indent: 2em;\">，</span><span style=\"text-indent: 2em;\">诺如病毒胃肠炎全年均可发生，一般10月到次年3月是诺如病毒感染高发季节。人群普遍易感。聚集性疫情主要发生在学校和托幼机构等人群聚集场所。</span></p>\n<p style=\"text-indent: 2em;\">\n	中疾控提示个人和家庭，预防诺如病毒感染，要<span style=\"text-indent: 2em;\">保持手卫生；注意饮食饮水卫生；病例应尽量居家隔离，避免传染他人；做好环境清洁和消毒工作；保持健康生活方式。</span></p>\n<p style=\"text-indent: 2em;\">\n	<span style=\"text-indent: 2em;\">另外，</span><span style=\"text-indent: 2em;\">托幼机构和学校要及时发现和规范管理病例；加强饮水和食品卫生的管理；加强厨工、保育员等从业人员健康管理；做好环境清洁消毒；科学规范处理呕吐物等。</span></p>\n<p style=\"text-indent: 2em;\">\n	<strong>诺如病毒极容易发生变异</strong></p>\n<p style=\"text-indent: 2em;\">\n	<span style=\"text-indent: 2em;\">据介绍，诺如病毒属于杯状病毒科，是引起急性胃肠炎常见的病原体之一。诺如病毒具有感染剂量低、排毒时间长、外环境抵抗力强等特点，容易在学校、托幼机构等相对封闭环境引起胃肠炎暴发。诺如病毒为RNA病毒，极容易发生变异，每隔几年就有新的变异株出现，引起全球或区域性暴发流行。</span></p>\n<p style=\"text-indent: 2em;\">\n	对于诺如病毒胃肠炎的主要症状，中疾控表示，<span style=\"text-indent: 2em;\">诺如病毒感染潜伏期为12-72小时，通常为24-48小时。常见症状主要为恶心、呕吐、发热、腹痛和腹泻，部分患者有头痛、畏寒和肌肉酸痛等。儿童以呕吐为主，成人则腹泻居多，粪便为稀水便或水样便。</span></p>\n<p style=\"text-indent: 2em;\">\n	<strong>目前尚无有效抗病毒药物</strong></p>\n<p style=\"text-indent: 2em;\">\n	中疾控表示，<span style=\"text-indent: 2em;\">病例和隐性感染者为诺如病毒感染的传染源。传播途径多样，主要通过摄入被粪便或呕吐物污染的食物或水、接触患者粪便或呕吐物、吸入呕吐时产生的气溶胶、以及间接接触被粪便或呕吐物污染的物品和环境等感染。</span></p>\n<p style=\"text-indent: 2em;\">\n	治疗方面，<span style=\"text-indent: 2em;\">诺如病毒胃肠炎属于自限性疾病，目前尚无有效的抗病毒药物。多数患者发病后症状轻，无需治疗，休息2-3天即可康复，可口服糖盐水或口服补液盐补充呕吐和腹泻消耗的水分。对于婴幼儿、老人，特别是伴有基础性疾病的老人，如因频繁呕吐或腹泻，出现脱水等较严重的症状时，应及时治疗。</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0214/c14739-32623703.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (41,'2023-02-19 09:34:48.722281','2023-02-19 09:34:48.722281','呵护消化系统健康','<div class=\"artDet\">\n                        \n                        <table class=\"pci_c\" width=\"400\"><tbody><tr><td align=\"center\"><img src=\"http://paper.people.com.cn/rmrbhwb/images/1/20230210/1675976392852_1.jpg\" /></td></tr> <tr><td><p>　　何玉琦（右一）正在为患者进行诊治。<br />　　丛  林摄</p></td></tr></tbody></table><table class=\"pci_c\" width=\"400\"><tbody><tr><td align=\"center\"><img src=\"http://paper.people.com.cn/rmrbhwb/images/1/20230210/1675976400114_1.jpg\" /></td></tr> <tr><td><p>　　新华社发 <br />　　王  鹏作</p></td></tr></tbody></table><p>　　春节期间，亲朋好友聚会频繁，餐桌上美味佳肴诱惑不断。在大快朵颐的同时，更要关注消化系统的健康。如何保护我们的消化系统健康呢？</p><p>　　近日，首都医科大学附属北京胸科医院消化内科、消化内镜中心开诊，本报记者采访了该院消化内科主任何玉琦教授，为您解读消化系统健康的注意事项。</p><p>　　</p><p>　　调整饮食正当时</p><p>　　正如不少网友的调侃——“每逢佳节胖三斤，消化疾病要当心”。春节长假刚过，消化内科成了患者前来就诊的热门科室之一。“某种程度上可以说，消化道疾病多数是吃出来的。”何玉琦提醒，春节期间由于饮食中大鱼大肉明显增多、蔬菜摄入较少，易导致消化道疾病高发，节后要注意调整饮食结构，保护消化系统健康。</p><p>　　具体应如何调整饮食？“要少食多餐，以容易消化的食物为主，多吃青菜，主食和肉类适量即可，注意要清淡少盐少油腻。”何玉琦提醒，要少吃动物脂肪和内脏，例如蛋黄、鱼子、肥肉含胆固醇较多，高胆固醇人群不宜食用。同时，不吃辛辣刺激、生冷食物，少喝酒、浓茶、咖啡，晚餐避免油腻。</p><p>　　良好的饮食习惯究竟有多重要？专家介绍，有研究显示，2017年全球成年人总死亡人数中，约有1000多万人是由于不良饮食习惯引起的，而其中70岁以下的人群就有500万例，“这个数字大约占全球成年人总死亡人数的22%，已经接近因吸烟而导致的死亡人数。”何玉琦说。</p><p>　　常见的不良饮食习惯主要有哪些？“饮食不规律，暴饮暴食，进食速度较快，喜食辛辣、油腻、甜食、过烫或生冷食物，长期大量饮酒等。”何玉琦说，预防消化道疾病应注意清淡饮食，从低盐、低脂、低糖三个方面做好膳食调整；保证新鲜果蔬的足量摄入，注意饮食多样性；选择合理的烹饪方式，油炸、油煎、烧烤等烹饪方式做出的食物味道虽好，却不健康，应多选择更为柔和的烹饪方式，例如蒸、煮等方式不仅能够更好地保留食物的营养，还不产生有害物质。</p><p>　　早诊早治勿拖延</p><p>　　“食物经过口腔的咀嚼吞咽，经过食管、胃、十二指肠，这就是上消化道，然后经由空肠、回肠、结直肠，最终以粪便形式排出体外，肝脏、胆囊、胰腺这些消化道腔外的器官也参与了消化过程。”何玉琦介绍，常见的消化系统疾病有胃食管反流、胃炎、消化性溃疡、肝胆疾病、胰腺疾病、肠道疾病等。此外，消化道肿瘤有很多种，分为良性肿瘤和恶性肿瘤，公众较为关注的恶性肿瘤主要包括食管癌、胃癌、结直肠癌等。</p><p>　　研究显示，我国癌症死亡率排名前五位中，除了排在第一位的肺癌，剩下4类都是消化系统癌症——肝癌、胃癌、食道癌、结直肠癌。为什么消化道肿瘤的死亡率排名如此靠前？</p><p>　　“消化道肿瘤与饮食有着非常直接的相关性，因此不良的饮食习惯与环境、遗传等因素的作用导致了消化道肿瘤发病率居高不下。”何玉琦说，遗传因素是消化道肿瘤的主要病因之一，而同一家庭内的成员一般是共同进食的，如果一定程度上是饮食导致了肿瘤的发生，那么家庭内其他成员也更容易发病。</p><p>　　相关研究显示，消化道肿瘤的发生呈年轻化趋势。专家介绍，1990—2019年中国主要消化系统恶性肿瘤发病变化的分析显示，2019年15—49岁年龄组人群中，结直肠癌、胃癌和胰腺癌的发病率相比1990年上升。“这可能与饮酒、大量食用红肉和加工肉类、肥胖、久坐以及缺乏身体活动等有关。”何玉琦说，临床研究发现，早诊早治是提高癌症治愈率的关键。由于绝大部分的消化道肿瘤在早期并没有典型症状，当出现吞咽困难、腹痛、消瘦、黑便等症状时，可能已经发展到肿瘤晚期。</p><p>　　值得注意的是，肿瘤从发生到发展会经历一个比较长的时间过程，这就给早期筛查、早期诊断创造了条件。如何尽早发现消化道疾病？如何提高消化道肿瘤的早诊早治水平？</p><p>　　“对患者而言，首先要关注消化道肿瘤的一些常见症状，例如食欲减退、消化不良、腹胀、腹部不适或腹泻、便秘、排便习惯改变等，如有以上不适要及时就医。此外，高危人群应该注意定期进行消化内镜筛查，从而做到早发现、早治疗。”何玉琦介绍，由于消化道肿瘤包括数种疾病，其高危人群也有差异。例如食管癌的高危人群包括食管癌高发地区人群、有食管癌家族史、有食管癌前病变等，而胃癌的高危因素包括胃溃疡、胃息肉、萎缩性胃炎、幽门螺杆菌感染等。结肠癌的好发人群包括饮食中以红肉（牛羊肉）为主、有烟酒等不良嗜好的人群，有结肠癌家族史的人群，大便长期不规律（包括便秘或者腹泻）的人群、长期久坐人群。但是，这些消化道肿瘤的高危人群都与年龄相关，例如食管癌和结直肠癌的高危人群为40岁以上人群，而胃癌的高危人群为60岁以上人群。</p><p>　　用好筛查“金标准”</p><p>　　“近年来，我国一直在致力于提高消化道肿瘤的早诊早治水平。”何玉琦说，2018年，我国国家消化道早癌防治中心联盟正式。早癌的诊治需要发挥基层医院的作用，通过深入带教、指导和培训等，提高消化内镜医师的基本功，运用新技术提高早癌诊治水平。与此同时，提高病理科医师的诊断水平至关重要，应加强内镜与病理密切协同，开展消化道早癌MDT（多学科会诊）工作，从而做到共同进步。此外，需要加强健康科普宣传教育，不断宣传早癌筛查知识，引导公众树立正确的早筛理念。</p><p>　　消化道肿瘤常见的筛查和诊断手段有哪些？专家介绍，常见的筛查和诊断手段可分为无创和有创。常见的无创检查手段主要包括化验检查（血、粪便等）、碳13呼气试验、胶囊内镜以及B超、CT、核磁等影像学检查；有创检查主要包括胃肠镜、小肠镜、超声内镜等检查。其中，胃肠镜是尽早发现消化道疾病最精准的检查方法，胃镜活检病理诊断也是胃癌诊断的“金标准”。在疾病早诊和预防方面，胃肠镜检查的作用不可小觑。</p><p>　　提到胃肠镜检查，很多人的第一反应就是难受、恐惧，甚至逃避、拒绝检查。胃肠镜检查像人们想象的那样可怕吗？</p><p>　　“胃镜检查时因镜身通过咽部引起咽反射，会引起恶心、咳嗽等反应，但检查前一般会口服局麻药来麻醉咽部，可在一定程度上减少不适。”何玉琦说，肠镜检查过程中需要充气使肠腔扩张，患者会感觉腹胀，而这些不适感绝大多数人都可以耐受。近年来，随着内窥镜技术的进步，目前的胃肠镜镜身较细、柔软，再加上内镜医生的熟练操作，一般不需要过度担心。</p><p>　　专家介绍，胃肠镜检查主要适用于40岁以上人群、一些有症状人群、高危人群、不良生活习惯及体检发现异常的人群等。“如果难以克服内心的恐惧，经过评估符合条件的患者可以选择无痛胃肠镜检查。”何玉琦说，无痛胃肠镜是在普通胃肠镜检查的基础上，先通过静脉给予一定剂量的短效麻醉剂，帮助患者迅速进入镇静、睡眠状态，患者可在毫无知觉中完成胃肠镜检查，并在检查完毕后可迅速苏醒，“一些患者说，感觉做无痛胃肠镜就像‘睡了一觉’，大大减少了传统检查带来的不适感。”</p>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0210/c14739-32620945.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (42,'2023-02-19 09:34:48.732560','2023-02-19 09:34:48.732560','警惕抑郁早期信号','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	近日，备受关注的胡某宇事件新闻发布会召开。发布会上，江西省、市、县公安机关联合工作专班及市县相关部门，公布了事件的最新调查情况。经调查，所有关键证据都指向：失踪的铅山少年胡某宇系死于自杀。调查结果公布后，青少年心理健康问题再度引发社会关注。那么，青少年可能出现哪些心理问题？又该如何应对呢？</p>\n<p style=\"text-indent: 2em;\">\n	哈尔滨市第一专科医院心理卫生中心主任徐佳在接受人民网记者采访时介绍，青春期是人个性发展的关键期，同时也是矛盾时期。此时，青少年从生理角度已趋于成熟，但在心理角度又尚未完全成熟，处于一种既要求独立又无法完全独立的阶段。青少年行为问题高发，但是可塑性极大，这一时期加强心理卫生干预和道德规范等方面教育的重要性不言而喻。</p>\n<p style=\"text-indent: 2em;\">\n	“青少年常见的心理疾病包括，抑郁症、焦虑症、强迫症，双相情感障碍、睡眠障碍等。”徐佳表示，以抑郁症为例，青少年抑郁症的诊断标准从医学角度讲和成人抑郁症的诊断标准没有区别，但是又有相对特殊的表现。患者除了会感到总是高兴不起来、兴趣减低、思维反应速度减慢、注意力不集中、倾向于自我否定的认知，还会出现明显的烦躁情绪，疲劳感、头痛、胃肠不适等不明原因的躯体不适感。</p>\n<p style=\"text-indent: 2em;\">\n	“青少年抑郁早期有3大预警信号，即持续的情绪低落或烦躁，不明原因的躯体不适，性格改变。在此时干预，可降低青少年抑郁发生率。”徐佳提示，父母需要及时发现孩子行为上的异常。比如不愿意和朋友、同学来往，不愿意和人交流，喜欢独处；不愿意参加集体活动，经常流露出不安、焦虑、悲伤等情绪；夜间失眠，进食障碍；学习成绩下降，注意力不集中等。</p>\n<p style=\"text-indent: 2em;\">\n	当出现负面情绪时，青少年应如何进行自我心理调适？徐佳建议，青少年应学会倾诉，可以和信任的朋友、老师或家长分享感受，也可以拨打专业的心理援助热线电话，或找心理咨询师沟通；学习自我调节情绪的方法，坏情绪需要发泄，可以大哭一场排解情绪，可以通过运动、画画、听音乐释放情绪，也可以尝试把此刻的感受写下来；学会接纳自己，对自己的能力合理预期，及时调整心态。</p>\n<p style=\"text-indent: 2em;\">\n	青少年心理健康离不开家庭成员彼此间的良性互动。徐佳表示，家长应学会倾听，掌握一定的沟通技巧，以关怀的角度去倾听孩子的讲述，与孩子建立互信的关系。家长要尊重孩子并关注其感受，共同解决问题，不独断专行。协助青少年进行自我调节，鼓励其参与艺术和体育活动等，进行情绪行为调节。同时，家长要学习一定的心理知识，掌握抑郁症、焦虑症等心理疾病的一般症状表现，不要讳疾忌医应学会及时求助。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0206/c14739-32618458.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (43,'2023-02-19 09:34:48.742519','2023-02-19 09:34:48.742519','从抑郁情绪到抑郁症有多远？专家教…','<div class=\"artDet\">\n                        \n                        <p>\n	<script src=\"http://tv.people.com.cn/img/player/v.js\" /><script>showPlayer({id:\"/pvservice/xml/2023/2/14/8b0264ed-3ff0-4336-a802-81379a2a01cb.xml\",width:640,height:360});</script></p>\n<p style=\"text-indent: 2em;\">\n	近年来，青少年心理健康越来越受到社会广泛关注，尤其是青少年抑郁症，需要早发现早干预。那么，如何预防青少年从抑郁情绪发展为抑郁症？在本期《1分钟养生课》上，北京师范大学党委学生工作部心理健康教育与咨询中心心教练张蔚分享相关知识。</p>\n<p style=\"text-indent: 2em;\">\n	有些家长会担心，孩子看上去好好的，但有时候也不知道他的小脑瓜里在想什么，会不会也出现抑郁？对此，张蔚表示，其实6到12岁是抑郁易发的阶段，只是孩子对自己的身心不是很了解，所以他们抑郁的表现和成年人有很大区别，这就需要家长更细心的观察，并具备一定的相关常识。</p>\n<p style=\"text-indent: 2em;\">\n	“如果一个人的抑郁情绪持续两周没有缓解，那么就有可能出现了抑郁症的症状，就需要及时去医院检查。”张蔚说。</p>\n<p style=\"text-indent: 2em;\">\n	那么，有没有办法可以预防抑郁症？张蔚说，抑郁症的成因复杂，当一个人呈现出抑郁症时，往往从躯体、到关系、到情绪、再到认知，包括生活状况，都会出现一定的问题。那么，我们的预防也可以从这几个方面去入手。具体来说包括以下方面：</p>\n<p style=\"text-indent: 2em;\">\n	首先，从躯体角度看，要养成一定的运动习惯，只要是自己感兴趣的运动都可以，让出汗成为调节身心的便捷手段；</p>\n<p style=\"text-indent: 2em;\">\n	其次，从关系角度看，至少有一两个可以倾吐心事的亲人或朋友，遇到痛苦时，结实的关系往往是心灵安全的庇护所；</p>\n<p style=\"text-indent: 2em;\">\n	第三，从情绪角度看，注意自己的负面情绪信号，不压抑，用对自己管用的方式去调节；</p>\n<p style=\"text-indent: 2em;\">\n	第四，从认知角度看，完美主义、绝对化思维往往会导致抑郁，多察觉自己认知当中此类思维，学会换个视角看问题；</p>\n<p style=\"text-indent: 2em;\">\n	第五，从生活状况角度看，形成规律的生活，早起早睡，健康饮食，制订可行性计划，不把自己的生活搞得一团糟，都对预防抑郁症有积极作用。</p>\n<p style=\"text-indent: 2em;\">\n	<span style=\"color:#0000cd;\"><strong>关注青少年抑郁系列</strong></span></p>\n<p style=\"text-indent: 2em;\">\n	<a href=\"http://health.people.com.cn/n1/2023/0214/c14739-32623176.html\" target=\"_blank\"><span style=\"color:#0000ff;\">这些可能是抑郁症苗头 家长如何早发现？</span></a></p>\n<p style=\"text-indent: 2em;\">\n	<a href=\"http://health.people.com.cn/n1/2023/0215/c14739-32623997.html\" target=\"_blank\"><span style=\"color:#0000ff;\">靠自我调节就能治好抑郁症？专家提示这些误区不要踩</span></a></p>\n<p style=\"text-align: center;\">\n	<span style=\"text-indent: 2em; font-size: 20px; display: block;\"><strong>点击下方图片进入【</strong><strong style=\"color: rgb(178, 34, 34); text-indent: 2em;\">1分钟养生课</strong><strong style=\"text-indent: 2em;\">】专题，获取更多</strong><span style=\"color: rgb(178, 34, 34); text-indent: 2em; font-weight: bold; display: block;\">精彩内容</span></span></p>\n<p style=\"text-align: center; text-indent: 2em;\">\n	<span style=\"color: rgb(0, 0, 205); text-indent: 2em; display: block;\"><span style=\"text-indent: 2em; font-size: 28px; display: block;\"><strong>↓↓↓</strong></span></span></p>\n<p style=\"text-align: center; ;\">\n	<a href=\"http://health.people.com.cn/GB/443192/index.html\" target=\"_blank\"><img alt=\"\" src=\"/NMediaFile/2022/0406/MAIN202204062023000440366917591.jpeg\" width=\"550\" height=\"167\" /></a></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624672.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (44,'2023-02-19 09:34:48.742519','2023-02-19 09:34:48.742519','以更科学方式教育引导下一代','<div class=\"artDet\">\n                        \n                        <p>\n	　　教育必须遵循基本规律。基础教育是全社会的事业，需要学校、家庭、社会密切配合</p>\n<p>\n	　　一段时间以来，多地医院依托心理科室开设“学习困难”门诊，引发社会关注。不少家长带孩子前来问诊，有时甚至出现“一号难求”的现象。孩子学习情况始终是父母关注的一个重点，“学习困难”门诊的出现，回应了许多家庭的现实需求。</p>\n<p>\n	　　据调查，在前来问诊的孩子中，有的是“不愿学”，由于出现焦虑等情况因而注意力不集中、缺乏自信，属于情绪和精神心理障碍。有的则是“不能学”，存在多动症、自闭症、阅读书写障碍、精神发育迟缓等状况，属于神经系统发育问题。前者可以通过心理干预、家校配合，引导孩子重拾信心；后者则必须采取药物治疗、行为训练、运动辅助等方式，帮助孩子重回正轨。一位医生表示：原本并没有“学习困难”这个诊断类别，之所以用这一名称开设诊室，是因为部分家长、老师不能分辨学习困难的源头是生理还是心理因素。使用相对通俗的诊室名称，有助于提示社会更早、更好关注孩子身心健康发展，防止“药不对症”“有病乱投医”。</p>\n<p>\n	　　需要注意的是，“成绩不好”不等于“学习困难”。导致“成绩不好”的原因多种多样，未必都能通过“学习困难”门诊治愈。通过诊疗发现，一些学生学习能力没问题，成绩不理想更多是因为不恰当的教育方式和不适宜的学习环境。有的老师给学生太多压力，挤占休息和运动时间；有的家长信奉“棍棒之下出高分”，导致孩子对学习产生抵触情绪；有的家庭麻将声不绝于耳等，分散了孩子的注意力。对于类似情况，与其从孩子身上找原因，不如从家长、老师身上找问题；与其依赖门诊治疗，不如在日常生活中给予孩子更多关心和尊重。</p>\n<p>\n	　　许多人之所以不理解学习困难的成因，是教育理念出了偏差。时下，仍然有人存在“学习不好就是懒”“叛逆不听话，所以考得差”“好学生一定没心理问题”“多花点时间一定能提高成绩”等偏见。教育是一门科学。学习成绩受能力、心态、环境等多方面因素共同影响，不能一概而论。“学习困难”门诊的出现，有助于打破刻板印象，提醒我们以更科学的方式教育下一代。</p>\n<p>\n	　　教育必须遵循基本规律，“学习困难”门诊也不能解决所有学习问题，更没有变“学渣”为“学霸”的魔力。基础教育是全社会的事业，需要学校、家庭、社会密切配合。孩子学业不理想，需要各方分工合作、凝聚合力，通过营造良好环境、优化教学方法等，让祖国的花朵在共同呵护下茁壮成长。对于学习困难，则要分清哪些是医疗问题，哪些是教育问题，找准症结、对症下药，为他们克服困难提供坚实的依靠、有效的帮助。</p>\n<p>\n	　　学习固然重要，但学习不是全部。除了学习成绩，健全的人格、强健的体魄、正常的认知对于孩子走好未来的人生路也至关重要。从这个意义上说，开设“学习困难”门诊不仅仅聚焦于学习问题，也要通过科学诊疗，启发家长、学校、社会更新教育观念，合理调整对成绩的预期，让孩子在合适的年龄里做合适的事，成长为拥有健全人格的人、快乐生活的人。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月16日 05 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624787.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (45,'2023-02-19 09:34:48.752095','2023-02-19 09:34:48.752095','用心用情为儿童打造优质产品','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	加好友、线上支付、益智游戏、运动PK、心率监测……随着可穿戴智能设备的兴起，儿童手表的功能日趋丰富，成为不少孩子的生活伙伴。不过，有调查显示，41.95%的家长担忧儿童手表的复杂功能会分散儿童注意力。如何引导儿童合理使用儿童手表等产品，引发社会关注。</p>\n<p style=\"text-indent: 2em;\">\n	近年来，不少与儿童有关的产品都曾引发讨论。这些产品，在某方面满足了儿童及家长的需求，但也对儿童成长产生了不良影响。这提醒我们，儿童产品基于儿童、为了儿童，应始终以儿童为本位，以健康、安全为原则。</p>\n<p style=\"text-indent: 2em;\">\n	我国未成年人保护法规定，智能终端产品的制造者、销售者应当在产品上安装未成年人网络保护软件，或者以显著方式告知用户未成年人网络保护软件的安装渠道和方法。从监管层面看，多部门已开展专项行动，强化对专门供未成年人使用的智能手表等智能设备信息内容监管。下一步，相关部门应继续加强行业审核与监管，规范健康指引标准，并根据新情况不断完善相关制度。</p>\n<p style=\"text-indent: 2em;\">\n	监管之外，提高孩子们正确使用网络产品的能力同样重要。家长要关注孩子的使用频率和身心健康状况，学校也应加强网络素养教育。更重要的是，全社会应共同营造良好氛围，当好孩子健康成长的护航者。用心用情为儿童打造合适优质的产品，引导儿童科学合理使用相关产品，孩子们成长路上的“好帮手”才会越来越多、作用才会越来越大。</p>\n<p style=\"text-indent: 2em;\">\n	（原载于《人民日报》 作者：周山吟 摘编：项锋、杨心悦）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624821.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (46,'2023-02-19 09:34:48.756570','2023-02-19 09:34:48.756570','开展饮食教育 促进健康成长','<div class=\"artDet\">\n                        \n                        <table class=\"pci_c\" width=\"400\">\n	<tbody>\n		<tr>\n			<td align=\"center\">\n				<img src=\"http://paper.people.com.cn/rmrb/images/1/20230214/1676324785562_1.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n				<p>\n					　　2023年1月，在柏林国际绿色周上，孩子们在听营养师介绍食品的营养成分。<br />\n					　　本报记者 李 强摄</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	　　食育，是有关饮食知识和良好饮食习惯的培养教育，有助于改善青少年饮食行为、营养状况和健康素养。许多国家在推行食育的过程中，针对性地出台一些政策措施，开展各具特色的活动，有效提高青少年儿童的健康意识、观念及素养，使其受益一生。</p>\n<p>\n	　　</p>\n<p>\n	　　德国——</p>\n<p>\n	　　在全国中小学推行“公共厨房”项目</p>\n<p>\n	　　本报记者  李  强</p>\n<p>\n	　　“有机蔬菜有什么特点？同学们请看这箱胡萝卜，每一颗的形态和颜色都不一样，完全不像超市里整齐划一的盒装胡萝卜。”在德国举办的“柏林国际绿色周”有机食品企业Bio Company展位前，一群小学生正在认真倾听工作人员讲解。</p>\n<p>\n	　　一年一度的“柏林国际绿色周”是全球知名的农业和食品展会，每年吸引来自全球70多个国家和地区的40万名观众。“柏林国际绿色周”上除了买家、卖家和专业观众以外，成群结队的小朋友格外引人注目。</p>\n<p>\n	　　在完成从一个蔬菜筐里分选出有机蔬菜和普通蔬菜的任务后，学生们进入了最喜欢的教学环节：一边现场品尝有机粗粮面包，一边听营养师介绍有机面包的原料产地、营养成分，以及有机食品对环境和气候保护产生哪些积极影响。</p>\n<p>\n	　　在畜牧业展厅，德国各州的农业协会直接搭起牛棚和羊圈，展出饲养的牲畜，这里也成为低年级小朋友最喜欢的地方。他们不但可以和小动物接近，尝试亲自喂小牛，还可以体验黄油和奶酪的生产实践。高年级的学生则对德国食品和饮料雇主协会的展位更感兴趣，通过与从业者的对话，他们能够获得诸如“有哪些职业岗位”“需要什么技能和兴趣”“未来有哪些发展机会”等行业信息，完成一次有价值的职业规划探索。    </p>\n<p>\n	　　这些丰富多彩的活动都隶属于绿色周针对学校设计的“年轻一代”项目。“只是‘换个教室’，体验动手教学”——每年参展商都会以此为题，为德国当地学校提供700多种课外活动，涉及农业和食品工业、健康和园艺等相关主题。在德国政府的支持下，每名学生只需支付4欧元，就可以享受到这场“课外教学”，德国联邦食品和农业部也设置了大型展位，邀请农业等行业从业者为学生们介绍相关知识。</p>\n<p>\n	　　近年来，为提高学生对健康食物的认识，培养他们的健康饮食习惯，德国联邦卫生部、联邦食品和农业部、各级教育部门以及德国营养学会等共同合作，在全国中小学推行“公共厨房”项目，将学校厨房变为帮助中小学生培养健康饮食习惯的饮食教室，学校的食育课也借助“公共厨房”来进行体验式教学。此外，学校还邀请家长和政府部门代表参加“公共厨房”活动，共同参与健康饮食教育。</p>\n<p>\n	　　作为食育的主要实施者，德国各中小学推出丰富多彩的实践活动，比如开辟菜地，让学生在植物专家和菜农的指导下学习种菜；或是由拥有职业营养师资格的老师带孩子们去市场实地认识、感受食物。专家认为，食育课有利于帮助学生树立健康的饮食和烹饪观念，养成均衡的饮食习惯。</p>\n<p>\n	　　2018年，德国食品和农业部等政府部门与相关行业协会出台一份国家战略，明确关注加工食品，特别是针对儿童和青少年的加工食品，以减少加工食品中糖、脂肪和盐的含量，促进学生的健康饮食。同时，德国联邦卫生健康宣传中心还在各地设立了1万多个健康教育点，由饮食健康宣传员到居民家中讲解营养配餐，宣传健康的饮食习惯。德国营养协会指出，在政府和社会的共同努力下，近年来，德国少年儿童的肥胖问题得到了一定控制。未来，政府仍需进一步出台全面的政策和法规，帮助青少年儿童养成良好的饮食习惯。</p>\n<p>\n	　　</p>\n<p>\n	　　巴西——</p>\n<p>\n	　　将健康饮食列为必修课</p>\n<p>\n	　　本报记者  毕梦瀛</p>\n<p>\n	　　在巴西里约州尼特洛伊市的马里利亚·马托索学校，用各类水果制作动物造型的水果沙拉是孩子们最喜欢的活动之一。苹果切片充当小猫的脸，橘子瓣当作猫耳朵，蓝莓做眼睛，再用沙拉酱挤出一个灿烂的微笑……一个个充满童趣的水果沙拉作品在课堂上诞生，孩子们兴奋地与自己的作品合影。</p>\n<p>\n	　　在这所学校担任营养顾问的科斯塔介绍，学校面向低龄学童开设了美食游戏体验课，通过寓教于乐的形式，增强学生对健康食物的了解，鼓励学生改善饮食习惯。活动中，孩子们能够了解到绿色蔬菜、时令水果的相关知识，学习清洗果蔬的正确方式，还可以体验如何将其做成沙拉、三明治等方便又健康的食物。“保证充足的营养摄入对儿童的健康成长非常重要。食品安全教育是学校教育中不可或缺的一环，在这方面，学校大有可为。”科斯塔说。</p>\n<p>\n	　　世界卫生组织2022年的数据显示，目前全世界约有3.8亿儿童和青少年出现肥胖问题，这一数字仍呈不断上升趋势。在这种背景下，巴西越来越多的学校和家长将目光投向健康饮食，希望通过加强饮食教育，培养孩子们的健康饮食习惯。</p>\n<p>\n	　　在圣保罗州的一家幼儿园，孩子们每天都会学习各种食物的名称、特征以及其对身体的作用。在该园开展的一系列食品教育活动中，孩子们最喜欢的是“模拟集市”。“肉类、果蔬、蜂蜜……孩子们通过扮演菜农和顾客，挑选、品尝、模拟交易各色农产品，在游戏中了解与食品相关的词汇及知识。”幼儿园老师布鲁尼说。 </p>\n<p>\n	　　一直以来，巴西政府十分重视学生的饮食健康。自上世纪50年代以来，巴西在全国范围内实行供餐计划，覆盖包括日托所、幼儿园、小学和初高中在内的所有公立和社区学校，为在校学生提供营养均衡的餐食。2012年，巴西推出公共政策食品和营养教育参考框架，在学校课程中增加、推广食品教育相关内容。2018年，该国政府颁布法令，将食品及营养教育列为巴西全国公立和私立中小学的必修内容。</p>\n<p>\n	　　不少巴西民间组织也参与其中，积极推动饮食教育的发展。非营利组织玛尼瓦厨艺学院创建于2007年，旨在为农民、厨师、消费者搭建起一座沟通的桥梁，加强公众对本土饮食文化的了解与保护意识。该学院推出木薯项目，通过在公立学校举办木薯工作坊的形式，把木薯的栽培历史与生长条件等地理知识与和木薯相关的音乐和民俗故事相结合，向学生们展示这种食物在巴西饮食历史及饮食结构中的重要性。学生们通过观看纪录片，掌握了木薯被制成木薯粉，又变身为餐桌上常见的各类美味食物的过程。</p>\n<p>\n	　　来自里约州的学生玛丽亚看完纪录片后受益匪浅，跃跃欲试。“我第一次知道原来木薯可以制成这么多营养美味的食物。回家后，我要用木薯粉为爸爸妈妈亲手制作一份健康的美食。”</p>\n<p>\n	　　</p>\n<p>\n	　　新加坡——</p>\n<p>\n	　　从小养成健康饮食习惯</p>\n<p>\n	　　本报记者  刘  慧</p>\n<p>\n	　　赫玛是新加坡一所公立小学二年级学生艾希莉的妈妈。因为工作繁忙，她不能经常为女儿准备午餐盒饭。“好在学校对食堂餐饮品质要求严格，同时注意孩子们饮食的营养均衡。学校还会定期举办健康饮食讲座，向学生们介绍加工食品的成分，告诉他们长期吃不健康食品的危害，让孩子们大受触动。”</p>\n<p>\n	　　新加坡非常重视饮食教育。近年来，针对不同年龄层的学生，新加坡政府推出学前班健康膳食计划和学校健康膳食计划，制定相应的健康膳食准则，指导学校为学生提供更健康的食物。以学校健康膳食计划为例，根据该计划，学校食堂、咖啡馆或自动售货机提供的食物要满足几个标准，包括减少脂肪、糖和钠的摄入；提供包含糙米、全麦面包、肉、蔬菜和水果的健康套餐等，以帮助学生获得生长发育需要的全面营养。此外，在新加坡保健促进局的指导下，学校食堂开始将油炸食物、加工食品等从学校菜单中删去，转而提供健康套餐。保健促进局指出，良好的营养可以帮助青少年儿童健康成长。从小让孩子们养成健康的饮食习惯至关重要，学校在这之中应发挥重要作用。</p>\n<p>\n	　　这些健康膳食计划不仅有助于改进学生在校的饮食健康，还把健康的饮食习惯带回了家庭。“如今，女儿在家里不再吵着要吃油炸食品和加工食品了。”赫玛说，女儿经常会主动询问“晚餐里蔬菜分量有多少”；逛超市时，也会要求选择带有“较健康的选择”标签的食物。在女儿的影响下，赫玛现在做饭也开始尝试少油、少盐。“对于孩子们来说，从小养成的健康饮食习惯在他们长大后也将一直影响他们，这也是政府和学校共同努力的初衷。”</p>\n<p>\n	　　受限于自然条件，新加坡90%以上的粮食依赖进口，保障粮食安全是新加坡加强食育的目的之一。该国政府有关部门通过在学校、公共场所等地设置展板，介绍食物安全相关知识，并针对小学高年级到大学预科阶段的学生提供专门讲座，介绍新加坡的农业发展史、粮食安全战略、食品安全问题等方式，帮助学生了解该国目前的粮食供应情况以及面临的挑战。</p>\n<p>\n	　　2013年以来，新加坡政府针对小学三年级学生开展“给孩子一颗种子”计划。在这个计划中，每名学生都会获得一份空心菜、向日葵或玫瑰茄的种子，以及配套的花盆和肥料土壤。“种下种子后，我每天给它浇水，每周给它施肥。当看到它长出第一片叶子时，我非常兴奋。当它长到一定高度时，爸爸妈妈帮我把菜叶剪下来，妈妈真的用它做了晚餐！”参与该计划的学生凯拉说，“这个经历让我感到食物的来之不易，我们应该珍惜食物。”</p>\n<p>\n	　　</p>\n<p>\n	　　英国——</p>\n<p>\n	　　教学大纲要求十四岁学生能做出二十道菜</p>\n<p>\n	　　本报记者  邢  雪</p>\n<p>\n	　　在英国斯劳市赫歇尔文法学校，孩子们的课表上有一门富有创意的课程——食物准备与营养课。以8年级为例，十二三岁的学生们在这门烹饪课中要学会做5道菜式，其中的入门菜是杂蔬炒面。老师给学生们提供所有需要的食材、调味料及烹饪的详细步骤，学生要学会安全准确地使用刀具和削皮器，并掌握烹饪火候。这门课的家庭作业也很有趣，学生需写出洋葱、豆腐、豆芽、面条的主要营养成分及功用，分辨低盐、低糖、低脂的调味料，回答为什么胡萝卜炒了颜色会变深、豆芽要保存在冰箱里，最后还要写出烹饪这道菜的感受。</p>\n<p>\n	　　食品行业在英国具有重要地位，该国的食品与营养教育也经历了长时间的发展。英国教育与技能部在1999年发起全国健康学校项目，制订若干标准，对全国学校实施考评，要求学校从饮食等多方面加强学生的健康教育。从2008年开始，英国的公立中学开始推行烹饪课；2013年，又将该课程从中学扩大到小学，成为全体英国孩子都要学习的必修科目。 </p>\n<p>\n	　　目前，食品与营养学是英国中学会考科目之一。按照教学大纲要求，到14岁时，学生要能做出20道不同种类的菜，比如馅饼、炒菜、咖喱饭等。从2015年9月起，英国政府还规定，4—5岁的幼儿也需对烹饪有简单了解，应教授孩子大致识别食物的营养价值和热量等。</p>\n<p>\n	　　长期以来，英国政府有关部门不断推动校园食育课程改革，改进教学目标与课程设计。比如，将食物原产国的知识融入地理课程、食材的种植加工则被纳入科学等理科课程……为推动中小学加强健康饮食教育，在数学课和英语课等多个学科的教学中都融入健康饮食的内容。为鼓励学生参与，学校设计了栽培、烹饪等主题，使得孩子们对食物和营养有更直观的理解，并将健康饮食知识运用到日常生活中。一项调查结果显示，84%的受访者认为，在学校开设食品相关课程十分必要。</p>\n<p>\n	　　同时，英国政府有关部门与民间团体合作，推出多个项目，帮助学生对农业、营养、食品安全、耕作、烹饪等生活化议题有更深刻的认识。该国教育部门还联合相关部门共同推出学校食物计划，保障在校生饮食的营养健康搭配；土壤协会、健康教育信托、有机庭园等社会组织则共同创办生活饮食伙伴联盟，推动学生、家长及全社会提升健康饮食意识。</p>\n<p>\n	　　一家位于剑桥的营养研究所指出，加强孩子们对营养和食物的正确认识，有助于在全社会范围内塑造并推广健康的饮食文化。英国未来还需要继续加强青少年儿童的食育，促进他们的健康成长和全面发展。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月14日 18 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0214/c14739-32623227.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (47,'2023-02-19 09:34:48.762078','2023-02-19 09:34:48.762078','一开学就生病？做好这几点摆脱开学…','<div class=\"artDet\">\n                        \n                        <p style=\"text-align: center;\">\n	<script src=\"http://tv.people.com.cn/img/player/v.js\" /><script>showPlayer({id:\"/pvservice/xml/2023/2/13/95a877f3-9d48-4e1e-86d6-e331cdbe6a3b.xml\",width:640,height:360});</script></p>\n<p style=\"text-indent: 2em;\">\n	近日，全国各地大中小学陆续开学，家长们终于迎来“神兽归笼”的时刻。但对部分家长来说，怎么在新学期开始，帮孩子快速摆脱开学综合征，成为摆在他们面前的一大难题。</p>\n<p style=\"text-indent: 2em;\">\n	天津市第一中心医院营养科主任谭桂军在接受人民网记者采访时介绍，开学综合征是指在开学前后，部分儿童青少年表现出焦虑、紧张，甚至恐惧等情绪反应，还可能伴有头疼、胃疼、恶心、腹泻等症状。患者以中小学生居多。</p>\n<p style=\"text-indent: 2em;\">\n	谭桂军表示，部分学生放假期间饮食作息不规律、暴饮暴食、过度食用不健康零食、不注意食物合理搭配等，导致营养状况及免疫力下降，开学后难以适应规律的学习生活。再加上开学时正值季节交替之际，气温变化大，且学生集中返校后，加大了病毒细菌传播和交叉感染的机会，为呼吸道、肠道和传染性疾病的传播与流行创造了条件。</p>\n<p style=\"text-indent: 2em;\">\n	那么，家长怎么做才能帮孩子摆脱开学综合征呢？谭桂军给出以下建议。</p>\n<p style=\"text-indent: 2em;\">\n	<strong><strong>营养均衡</strong></strong><strong><strong> 食物</strong></strong><strong><strong>多样</strong></strong></p>\n<p style=\"text-indent: 2em;\">\n	儿童青少年生长发育迅速，代谢相对旺盛，开学后用脑增加、运动量增大，需要摄入充足的营养素。建议每日保证12种以上的食物摄入，包含谷薯、果蔬、鱼、禽、蛋、奶、瘦肉、大豆、坚果等。每餐合理搭配各类食物，确保食物摄入充足。</p>\n<p style=\"text-indent: 2em;\">\n	<strong><strong>饮食规律</strong></strong><strong> </strong><strong><strong>不暴饮暴食</strong></strong></p>\n<p style=\"text-indent: 2em;\">\n	饮食不规律容易导致营养摄入不均衡，易引发身体不适，如胃肠道功能紊乱，出现腹痛、腹泻等。饮食要定时定量，规律进餐，每餐七分饱即可，避免暴饮暴食。</p>\n<p style=\"text-indent: 2em;\">\n	<strong><strong>早餐要吃好 质量很重要</strong></strong></p>\n<p style=\"text-indent: 2em;\">\n	不吃早餐或者早餐质量不好，会导致精神差、反应迟钝、注意力无法集中，从而影响学习。长期不吃早餐还容易引发胆结石等疾病的发生。早餐不但要吃，还要保证质量。早餐的营养应全面均衡，要摄入充足的优质蛋白，如牛奶、鸡蛋、豆浆、瘦肉等，同时搭配一定量的谷类食物。</p>\n<p style=\"text-indent: 2em;\">\n	<strong><strong>充足饮水 饮料不能代替水</strong></strong></p>\n<p style=\"text-indent: 2em;\">\n	水是人体正常代谢所必需的物质。建议6至10岁学生每日饮水800至1000毫升，11至17岁学生每日饮水1100至1400毫升，尽量选择白开水，也可以通过汤、粥等补充水分。不喝或少喝含糖饮料，多数饮料含有大量的添加糖，不能用饮料替代水。一瓶500毫升的含糖饮料含糖量可达50克，摄入过量含糖饮料已成为青少年超重或肥胖的重要因素。</p>\n<p style=\"text-indent: 2em;\">\n	<strong><strong>选择健康零食</strong></strong><strong> </strong><strong><strong>零食</strong></strong><strong><strong>不可</strong></strong><strong><strong>代替正餐</strong></strong></p>\n<p style=\"text-indent: 2em;\">\n	学生尽量不吃或少吃零食。如需食用可以选择卫生、营养丰富的食物，如水果、奶类、坚果等。油炸、高盐或高糖的食品不宜作零食。吃零食的量以不影响正餐为宜，更不能用零食代替正餐。</p>\n<p style=\"text-indent: 2em;\">\n	“除此之外，开学后儿童青少年要多晒太阳、多运动、规律作息，坚持做好个人防护，科学佩戴口罩、勤洗手、遵守呼吸道礼仪等。”谭桂军说。（视频制作：实习生赵嘉欣）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0214/c14739-32623289.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (48,'2023-02-19 09:34:48.765831','2023-02-19 09:34:48.765831','解决线上消费和服务维权新问题 中…','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	<span style=\"text-indent: 2em;\">中消协15日发布的2022年全国消协组织受理投诉情况分析显示，消费者对互联网服务的投诉位居服务大类投诉量前列。网购售后服务不畅，在线培训退费承诺难履行，在线会员服务“套路”多……针对消费者在线上消费和购买服务时遇到的新问题，中消协给出建议和消费提示。</span></p>\n<p style=\"text-indent: 2em;\">\n	一些不良商家利用小程序，在无第三方资金托管的情况下进行销售或诱导私信交易，售后发生纠纷便失联。一些不法经营者利用个人社交账号，以自制官方店铺头像，诱导消费者与之交易，有部分老年消费者支付费用后发现上当受骗。有的电商平台对外推出所谓平台特卖店，而特卖店是在平台内“嵌入”的另一个平台，一旦出现纠纷就以此为由逃避自身责任。有的平台“国际购”业务售后渠道不畅，往往以“国际购”经营者在海外注册不适用中国法律为由拒绝协调售后，让消费者陷入维权困境。</p>\n<p style=\"text-indent: 2em;\">\n	中消协建议，加强对网络消费新业态、新模式的研究，特别是小程序购物、平台内“嵌入”平台、频繁变更注册地址逃避监管、“国际购”售后不畅等新问题，明确相关经营者主体责任。</p>\n<p style=\"text-indent: 2em;\">\n	当前，各类在线培训服务多以预付费形式进行销售，具有消费群体广、销售金额大、服务次数多等特点，存在较大的违约和跑路风险。一些培训机构在课程中宣传“考不过退费”，但后续不履行退费承诺，引发消费者大量投诉。部分培训机构编造虚假成功案例，诱导学员贷款购买高价培训课程。</p>\n<p style=\"text-indent: 2em;\">\n	中消协建议，创新监管方式和手段，利用大数据、人工智能加强对在线培训机构的监管，加大对虚假承诺等不良营销手段的处罚力度。</p>\n<p style=\"text-indent: 2em;\">\n	消费者在购买线上会员服务时，额外支付会员费是为了更专业的服务内容和更优质的消费体验，但有的商家却一味追求利润，“套路”收割消费者。</p>\n<p style=\"text-indent: 2em;\">\n	有的智能电视系统内各模块资源相互独立，消费者连续充值却仍无法观看全部视频内容，这种“套娃式”收费引发消费者不满。一些婚恋平台收取高额服务费，但实际服务达不到约定标准，有的对会员信息审核把关不严，甚至提供“婚托”虚构交友、结婚需求欺骗消费者。一些网络会员服务平台先诱导消费者免费使用，到期后未向消费者充分告知或有效提醒便自动续费。</p>\n<p style=\"text-indent: 2em;\">\n	中消协提示，消费者在选择会员服务时要注意会员具体权益内容，对于模糊表述要与商家确认并留存证据，自身权益受到侵害时及时向相关行政主管部门或消协组织投诉。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624852.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (49,'2023-02-19 09:34:48.772465','2023-02-19 09:34:48.772465','全国消协组织：2022年共受理消…','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	据中国消费者协会15日发布的《2022年全国消协组织受理投诉情况分析》显示，2022年全国消协组织共受理消费者投诉1151912件，解决915752件，解决率79.5%，为消费者挽回经济损失137767万元。其中，因经营者有欺诈行为得到加倍赔偿的投诉18032件，加倍赔偿金额453万元。</p>\n<p style=\"text-indent: 2em;\">\n	2022年，共接待消费者来访和咨询149万人次。与2021年相比，售后服务问题投诉比重上升较高。在具体服务投诉中，投诉量居前五位的分别为经营性互联网服务、餐饮服务、培训服务、移动电话服务、美容美发。与2021年相比，移动电话服务进入服务类投诉前十。</p>\n<p style=\"text-indent: 2em;\">\n	2022年，受国际国内经济形势和疫情因素影响，涉疫类消费纠纷和经营者关停跑路类投诉明显增多。与此同时，大额商品、在线服务领域新老问题交织，困扰广大消费者。如房地产消费领域的捆绑销售、烂尾问题，汽车消费领域的不公平格式条款问题，培训服务领域的赔本式承诺圈钱、培训贷问题，以及会员服务领域的“套路”频出问题等。</p>\n<p style=\"text-indent: 2em;\">\n	其中，以智能电视平台“套娃式”收费为代表的在线会员服务乱象屡现，成为消费者投诉重灾区。据介绍，在线会员服务领域问题主要有四大类：一是智能电视平台“套娃式”收费引发消费者不满。由于智能电视系统内各模块资源相互独立，消费者连续充值却仍无法观看全部视频内容。二是视频平台会员服务体验不佳。视频平台限制原会员用户手机投屏功能，若解除限制需购买更贵的增值会员服务。消费者支付的费用越来越高，但体验却大打折扣。三是婚恋平台会员服务存乱象。一些婚恋平台收取高额服务费，但实际服务达不到约定标准，有的对会员信息审核把关不严，甚至提供“婚托”虚构交友、结婚需求欺骗消费者。四是网络会员服务默认自动续费。一些网络会员服务平台先诱导消费者免费使用，到期后未向消费者充分告知或有效提醒消费者情况下自动续费。</p>\n<p style=\"text-indent: 2em;\">\n	对此，中国消费者协会指出，消费者额外支付会员费购买的是商家更专业的服务内容和更优质的消费体验，但一些商家却背离会员服务的本质和初衷，一味追求利润“套路”收割消费者，大幅降低了消费者的消费感受和体验。消费者在选择会员服务时要注意会员具体权益内容，对于模糊表述要与商家确认并留存证据，自身权益受到侵害时及时向相关行政主管部门或消协组织投诉。</p>\n<p style=\"text-indent: 2em;\">\n	针对去年投诉情况，中国消费者协会建议，监管部门要加大监管执法力度、提振消费信心，市场主体要优化产品服务供给、增强消费活力，消费者要树立正确消费观念、助力消费升级。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624826.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (50,'2023-02-19 09:34:48.776054','2023-02-19 09:34:48.776054','中消协：2022年涉疫类投诉激增…','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	人民网北京2月15日电 （记者孙红丽）15日，中国消费者协会发布《2022年全国消协组织受理投诉情况分析》（以下简称《情况分析》）。<span style=\"text-indent: 2em;\">其中提到，2022年，涉疫类消费纠纷投诉明显增多。</span></p>\n<p style=\"text-indent: 2em;\">\n	<strong>防疫产品投诉激增</strong></p>\n<p style=\"text-indent: 2em;\">\n	《情况分析》显示，在疫情反复期间，相关防疫用品和退烧药等医疗物资供不应求，一些不良商家借机涨价、囤积居奇或进行捆绑销售，扰乱了正常市场秩序。</p>\n<p style=\"text-indent: 2em;\">\n	一是借需求增加哄抬物价。部分医疗器械产品和抗疫药品相关经营者肆意涨价或恶意砍单。二是囤积药品或防疫用品。部分线下药店在退烧药或抗原有货情况下，无理由拒绝向消费者出售，引发消费者强烈不满。三是强制捆绑搭售其他药品。一些线下药店为规避禁止“哄抬物价”监管规定，利用消费者需求急迫心理，强制消费者捆绑购买并不需要的高价中成药等。四是夸大防疫用品实际功能。部分商家将普通口罩宣传为医用外科口罩，或在口罩中添加抗菌剂等宣称能够“灭”病毒，诱导消费者购买。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>“新冠”保险套路繁多</strong></p>\n<p style=\"text-indent: 2em;\">\n	《情况分析》显示，疫情发生后，国内不少保险公司推出“新冠”隔离和感染相关保险，并在网络平台上售卖，成为网络保险领域的“网红”产品。但有不少消费者反映随着感染人数的激增，在理赔时遭遇各种“高门槛”。</p>\n<p style=\"text-indent: 2em;\">\n	<span style=\"text-indent: 2em;\">一是宣传容易赔付难。一些保险公司为吸引消费者投保，宣称核酸阳性即可赔付，消费者实际感染后又设置苛刻条件，如要求提供CT检测报告和医生确诊通知书等才能理赔。</span><span style=\"text-indent: 2em;\">二是为拒赔玩“文字游戏”。部分保险公司以消费者确诊的是“新冠病毒感染”而非“新冠肺炎”为由不予赔付。三是逃避赔付责任。有些保险公司通过下架相关保险产品、保险理赔员失联、客服电话不通等方式故意逃避赔付责任。</span></p>\n<p style=\"text-indent: 2em;\">\n	对此，中国消费者协会表示，<span style=\"text-indent: 2em;\">消费者在购买相关保险产品时，要认真阅读合同条款，不轻信保险销售人员的口头承诺，避免因误导而购买与实际需要不相符的保险产品。</span></p>\n<p style=\"text-indent: 2em;\">\n	<strong>涉疫服务纠纷频发</strong></p>\n<p style=\"text-indent: 2em;\">\n	《情况分析》显示，由于新冠变异病毒传播能力强，“快速过峰”和大面积人员感染造成部分行业的正常运转受到一定影响，特别是线下聚集性、接触性服务行业短期内陷入“停滞”状态，相关消费纠纷因此大量增加。</p>\n<p style=\"text-indent: 2em;\">\n	一是因疫情原因消费者无法按照原计划出行，在相关网络平台预付的定金或合同价款遭遇退费难。二是快递配送超时。疫情反复期间，各类外卖平台由于人手不够、运力下降等原因，导致大量快递网点瘫痪，包裹积压或丢失。</p>\n<p style=\"text-indent: 2em;\">\n	对此，中国消费者协会表示，对于打着疫情影响的旗号逃避经营者责任、侵害消费者权益的各类违法违规行为，相关监管部门应当予以坚决打击、严厉查处。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0215/c14739-32624384.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (51,'2023-02-19 09:34:48.782374','2023-02-19 09:34:48.782374','中消协称商品房消费领域“痼疾难除”','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	人民网北京2月15日电 （记者孙红丽）15日，中国消费者协会发布《2022年全国消协组织受理投诉情况分析》。<span style=\"text-indent: 2em;\">其中提到，商品房消费领域的问题尽管经过多年整顿，但依然“痼疾难除”。</span></p>\n<p style=\"text-indent: 2em;\">\n	一是房屋质量问题。既有墙面开裂、地面空鼓等“显性”质量问题，也有卫生间渗漏、地暖破裂等后继使用后才能暴露发现的“隐形”问题。</p>\n<p style=\"text-indent: 2em;\">\n	二是不符条件强行交房。部分开发商在合同约定交房日到来时，楼盘尚未全部完工且不具备交付条件情况下，为逃避违约责任强行交房。</p>\n<p style=\"text-indent: 2em;\">\n	三是开发商强制捆绑销售。如开发商捆绑销售地下仓储或车位；或以不妥协则不交付钥匙为要挟，强迫业主接受其指定的中介机构代办房屋产权证或代缴契税，并从中收取中介费。</p>\n<p style=\"text-indent: 2em;\">\n	四是有的精装修房配套家电产品交房时已过或快过“三包”期，开发商拒绝承担“三包”责任。</p>\n<p style=\"text-indent: 2em;\">\n	五是交房时实测房屋面积大于合同约定面积，开发商要求消费者补差价引纠纷。</p>\n<p style=\"text-indent: 2em;\">\n	六是楼盘烂尾消费者“房财”两空。部分开发商挪用资金导致楼盘后续烂尾，消费者花费大量积蓄却未能如期入住，还面临还贷压力。</p>\n<p style=\"text-indent: 2em;\">\n	中国消费者协会表示，房地产开发商违背消费者意愿，强制捆绑销售地下仓储、车位或强制代办房屋产权证、代缴契税以及附加其他不合理条件等行为属于强制消费行为。“<span style=\"text-indent: 2em;\">建议相关行政部门加大对房地产消费监督执法力度，特别是要严厉打击房地产市场捆绑销售、强制交易、挪用预付资金等违法违规行为，加强对购房消费者的保护。”</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0215/c14739-32624425.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (52,'2023-02-19 09:34:48.782374','2023-02-19 09:34:48.782374','这15批次化妆品检出禁用原料！含…','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	人民网北京2月14日电 （记者孙红丽）国家药监局14日发布的《关于15批次化妆品检出禁用原料的通告》（以下简称《通告》）显示， 在2022年国家化妆品监督抽检工作中，经上海市食品药品检验研究院等单位检验，产品标签标示为委托方济南赤道经贸有限公司、被委托方广州市碧莹化妆品有限公司生产的韓聖伊薰衣草祛痘原浆液等15批次化妆品，检出《化妆品安全技术规范（2015年版）》中规定的禁用原料。</p>\n<p style=\"text-indent: 2em;\">\n	其中，标签标示名称为清诗帕润净洁发乳等2批次化妆品检出禁用原料的同时，还存在甲基氯异噻唑啉酮和甲基异噻唑啉酮与氯化镁及硝酸镁的混合物（甲基氯异噻唑啉酮：甲基异噻唑啉酮为3：1）检出结果不符合规定的问题。</p>\n<p style=\"text-indent: 2em;\">\n	记者注意到，此次通报的15批次检出禁用原料化妆品多为祛痘、洗发类产品，检出的禁用原料包括环丙沙星、克林霉素、西咪替丁、地索奈德、甲硝唑、三氯生等。</p>\n<p style=\"text-indent: 2em;\">\n	如，产品标签标示为委托方云南清茹生物科技有限公司、受托方广州市西美生物科技有限公司生产的妮予儿净肤清颜祛痘霜，检出禁用原料甲硝唑、克林霉素、林可霉素。</p>\n<p style=\"text-indent: 2em;\">\n	产品标签标示为委托方成都渼沦雅生物科技有限公司、受托方广州市梵蔻化妆品有限公司生产的安珂羽思桂花净痘液，检出禁用原料地索奈德、克林霉素。</p>\n<p style=\"text-indent: 2em;\">\n	产品标签标示为广州博氏化妆品有限公司生产的金紫雨丝盈舒焕洗发露，检出禁用原料三氯生。</p>\n<p style=\"text-indent: 2em;\">\n	据了解，甲硝唑是抗生素类药物，人体长期接触含抗生素的化妆品，易引起接触性皮炎、抗生素过敏等症状，易产生耐药性。药物残留还可能导致过敏反应等。《化妆品安全技术规范》（2015年版）规定其为化妆品中禁用物质。</p>\n<p style=\"text-indent: 2em;\">\n	地索奈德是一种糖皮质激素，长期使用含有糖皮质激素类的化妆品可能导致面部皮肤产生黑斑、萎缩变薄等问题，还可能出现激素依赖性皮炎等后果。《化妆品安全技术规范》（2015年版）规定其为化妆品中禁用组分。</p>\n<p style=\"text-indent: 2em;\">\n	根据《化妆品监督管理条例》《化妆品生产经营监督管理办法》，国家药监局已要求山东、广东、四川、云南省药品监督管理部门对上述不符合规定的化妆品涉及的注册人、备案人、受托生产企业依法立案调查，责令相关企业立即依法采取风险控制措施并自查整改；各省（区、市）药品监督管理部门责令相关化妆品经营者立即停止经营上述化妆品，依法调查其进货查验记录等情况，对违法产品进行追根溯源；发现违法行为的，依法严肃查处；涉嫌犯罪的，依法移送公安机关。</p>\n<p style=\"text-indent: 2em;\">\n	<a href=\"https://www.nmpa.gov.cn/directory/web/nmpa/images/1676360183069075685.doc\" target=\"_blank\"><span style=\"color:#000080;\">附件：15批次检出禁用原料的化妆品信息</span></a></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0214/c14739-32623579.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (53,'2023-02-19 09:34:48.792376','2023-02-19 09:34:48.792376','国家药监局通报51批次不合规化妆…','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	人民网北京2月13日电 （记者孙红丽）国家药监局13日发布的《关于51批次不符合规定化妆品的通告》（以下简称《通告》）显示，在2022年国家化妆品监督抽检工作中，经江苏省食品药品监督检验研究院等单位检验，产品标签标示为广州市申强实业有限公司生产的NNBIS染发膏（黑色12号）等51批次化妆品不符合规定。</p>\n<p style=\"text-indent: 2em;\">\n	其中，根据《通告》，<span style=\"text-indent: 2em;\">产品标签标示为广州市兰姿化妆品有限公司生产的海维斯染发焗油膏（自然黑）（标示批号：20211117），</span>不符合规定项目为成分比对。</p>\n<p style=\"text-indent: 2em;\">\n	产品标签标示为巴黎欧莱雅（香港）化妆品有限公司（监制）、广州总都国际生物科技有限公司生产的mewell美维兰 丝润-弱酸修护发膜（标示批号：ZD2D2205），甲基氯异噻唑啉酮和甲基异噻唑啉酮与氯化镁及硝酸镁的混合物（甲基氯异噻唑啉酮：甲基异噻唑啉酮为3:1）检出结果不符合规定。</p>\n<p style=\"text-indent: 2em;\">\n	产品标签标示为法国兰蔻国际集团药妆有限公司（商标持有人）、广州金婵生物科技有限公司（委托方）、广州玉婕生物科技有限公司（被委托方）生产的<span style=\"text-indent: 2em;\">YIGAR海元素储水霜</span>（标示批号：20210105CD），不符合规定项目为丙烯酰胺。</p>\n<p style=\"text-indent: 2em;\">\n	记者注意到，此次通报的51批次不符合规定化妆品多为染发、洗发、防晒、面膜类产品，抽检发现的主要问题是成分比对、菌落总数等不符合规定。</p>\n<p style=\"text-indent: 2em;\">\n	据了解，《化妆品标签管理办法》规定，化妆品标签应当在销售包装可视面标注化妆品全部成分的原料标准中文名称，以“成分”作为引导语引出，并按照各成分在产品配方中含量的降序列出。</p>\n<p style=\"text-indent: 2em;\">\n	如产品标签标示为广州市艾娜斯化妆品有限公司（委托方）、广州市卓依娜化妆品有限公司（被委托方）生产的苾美斯染发膏（栗棕色）（标示批号：20220305），不符合规定项目为成分比对，检验结果为检出产品标签及注册资料载明的技术要求未标示的染发剂：间氨基苯酚。</p>\n<p style=\"text-indent: 2em;\">\n	另外，菌落总数是指示性微生物指标，指示产品生产或流通过程中受污染程度，间接反映出化妆品生产加工或流通储存环节卫生状况的优劣。《化妆品安全技术规范》（2015年版）规定眼部化妆品、口唇化妆品和儿童化妆品中菌落总数应≤500（CFU/g或CFU/ml），其他化妆品中菌落总数应≤1000（CFU/g或CFU/ml）。</p>\n<p style=\"text-indent: 2em;\">\n	根据《化妆品监督管理条例》《化妆品生产经营监督管理办法》，国家药监局要求北京、内蒙古、辽宁、江西、广东省（区、市）药品监督管理部门对上述51批次不符合规定化妆品涉及的注册人、备案人、受托生产企业依法立案调查，责令相关企业立即依法采取风险控制措施并开展自查整改；各省（区、市）药品监督管理部门责令相关化妆品经营者立即停止经营上述化妆品，依法调查其进货查验记录等情况，对违法产品进行追根溯源；发现违法行为的，依法严肃查处；涉嫌犯罪的，依法移送公安机关。</p>\n<p style=\"text-indent: 2em;\">\n	<a href=\"https://www.nmpa.gov.cn/directory/web/nmpa/images/1676272599975061382.docx\" target=\"_blank\"><span style=\"color:#0000cd;\">附件：51批次不符合规定化妆品信息</span></a></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0213/c14739-32622840.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (54,'2023-02-19 09:34:48.801170','2023-02-19 09:34:48.801170','电影院3D眼镜要收费，合理吗','<div class=\"artDet\">\n                        \n                        <p>\n	　　春节假期，贺岁档影片掀起了观影热潮。然而，不少观众到电影院才发现，原本免费提供的3D眼镜如今需要自行购买了。</p>\n<p>\n	　　四川成都市读者黄敏前不久和全家人一起去看电影，取票后却被告知3D眼镜需要自费购买或租赁。“3D电影本来就比普通电影要贵，电影院不提供眼镜，无形中又增加了票价。而且，‘3D眼镜需要付费’仅以小字在购票页面标注，消费者根本注意不到。到了电影院，临开场才被工作人员告知要买，这太不合理了。”黄敏说。</p>\n<p>\n	　　记者搜索了某城市57家正在放映3D电影的影院，其中12家显示3D眼镜需要收费，有些影院需要观众自行准备或到影院购买眼镜，一副5元至30元不等；有些影院则通过租赁的方式，收取押金，按小时收费。而没有提示3D眼镜需要收费的影院也并非默认免费提供：有的影院标注“免押金”使用的同时，声称提供品质更好、佩戴更舒服的付费3D眼镜；有的仅免费提供框架式3D眼镜，夹片式或儿童款眼镜则需要购买或租用；还有的影院只提供有限的免费眼镜，观影人数多的情况下，部分观众不得不购买付费眼镜。</p>\n<p>\n	　　江西南昌市读者邹燕购票后发现影院并不提供3D眼镜，只好前往租赁。但租赁眼镜的观众很多，排起了长长的队伍，这也让邹燕错过了影片开场。“租眼镜要排队收押金，退还又要排队退押金，很影响观影体验。”邹燕说。</p>\n<p>\n	　　记者调查发现，很多需要观众付费购买或租赁3D眼镜的影院，所标注原因大都出于健康卫生考虑。“我认为这样的理由实际上并不成立。如果是出于卫生考虑，影院可以对眼镜进行集中消毒，而不是强迫消费者出钱购买。而且，消费者购买后也很少重复使用，因为大部分时候是临时起意看电影。我家里已经存了好几副3D眼镜，都是去看电影的时候买回来的，这也是一种浪费。”北京市读者杨女士说。</p>\n<p>\n	　　付费使用眼镜，经营成本是影院考虑的重要因素。据业内人士介绍，3D眼镜规格种类繁杂，一般影院使用的普通3D眼镜成本价从几元到十几元不等。还有一些技术含量较高的眼镜，早期成本价高达五六百元。而且，后续眼镜断腿、掉镜片、丢失等都是易发情况，免费向观众提供眼镜无形中增加了影院经营成本。</p>\n<p>\n	　　“影院虽然有控制成本的考虑，但不能因此就转嫁给消费者。”中国人民大学法学院教授刘俊海表示，影院3D眼镜收费实际上侵犯了消费者的知情权、自主选择权和公平交易权。电影院单方面告知顾客3D眼镜需要付费，实质上是一种不公平的条款，免除了电影院义务，侵犯了消费者的权利。</p>\n<p>\n	　　福建师范大学法学院副教授李付雷表示，观影合同是电影院为了重复使用而预先拟定的，并在订立合同时没有与观众协商，属于格式条款。是否免费提供3D眼镜对于观众来说具有重大利害关系。民法典明确规定，提供格式条款的一方未履行提示或者说明义务，致使对方没有注意或者理解与其有重大利害关系的条款的，对方可以主张该条款不成为合同的内容。“对于3D电影而言，3D眼镜是必需的观影设备，提供3D眼镜是观影服务不可分割的组成部分。电影院有义务在观众买票时提醒其注意，并充分尊重观众的自主选择权。”李付雷说。</p>\n<p>\n	　　中国消费者协会指出，3D眼镜额外收费属于不平等的格式条款，侵犯了消费者的自主选择权，涉嫌违反消费者权益保护法。“观众有权拒绝购买3D眼镜，并就是否免费提供3D眼镜与电影院沟通协商。如果双方无法达成一致意见，观众还可向有关部门投诉。监管部门应当加强监管，及时处理有关投诉和反馈，维护消费者的合法权益。”刘俊海说。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月13日 07 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0213/c14739-32622447.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (55,'2023-02-19 09:34:48.802904','2023-02-19 09:34:48.802904','19批次化妆品检出禁用原料！涉及…','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	人民网北京2月10日电 （记者孙红丽）<span style=\"text-indent: 2em;\">国家药监局10日发布的《关于19批次化妆品检出禁用原料的通告》（以下简称《通告》）显示，尚品美白祛斑精华霜、Brimles泡泡染发剂（灰色）、永九美七白二裂酵母焕颜蚕丝面膜、南京同仁堂蓝铜肽面膜、圣博薇鱼籽水灵鲜肌膜、UneYeah壹叶·摩洛哥坚果滋润修护洗发露、CD垂度头皮清爽控油洗发水、发薇奢养蛋白精华素（免冲洗）、发利源女人香留香柔顺液、豫镕倍爱多效修护霜、VANESSA MATTIA 维艾补水精华面膜、BEAVER博柔头皮芯生系列头皮去屑洗发水等19批次化妆品检出禁用原料。</span></p>\n<p style=\"text-indent: 2em;\">\n	根据《通告》，在2022年国家化妆品监督抽检工作中，经陕西省食品药品检验研究院等单位检验，标签标示为生产企业广州尚品生物科技有限公司、总经销商广州永九美生物科技有限公司生产的尚品美白祛斑精华霜等19批次化妆品，检出《化妆品安全技术规范（2015年版）》中规定的禁用原料。</p>\n<p style=\"text-indent: 2em;\">\n	其中，上述标签标示名称为Brimles泡泡染发剂（灰色）的产品检出禁用原料的同时，还存在产品成分比对不符合规定的问题；标签标示名称为古澜轩秀柠檬温和控油洗发液等4批次化妆品检出禁用原料的同时，还存在甲基氯异噻唑啉酮和甲基异噻唑啉酮与氯化镁及硝酸镁的混合物（甲基氯异噻唑啉酮:甲基异噻唑啉酮为3:1）检出结果不符合规定的问题。</p>\n<p style=\"text-indent: 2em;\">\n	记者注意到，此次通报的19批次检出禁用原料化妆品多为面膜、洗发水等，检出的禁用原料包括氯倍他索丙酸酯、三氯生等。</p>\n<p style=\"text-indent: 2em;\">\n	据了解，氯倍他索丙酸酯属于糖皮质激素类物质。长期使用含有糖皮质激素的化妆品可能导致面部皮肤产生黑斑、萎缩变薄等问题，还可能出现激素依赖性皮炎等后果。</p>\n<p style=\"text-indent: 2em;\">\n	根据《化妆品监督管理条例》《化妆品生产经营监督管理办法》，国家药监局已要求广东、浙江省药品监督管理部门对上述不符合规定的化妆品涉及的注册人、备案人、受托生产企业依法立案调查，责令相关企业立即依法采取风险控制措施并自查整改；各省（区、市）药品监督管理部门责令相关化妆品经营者立即停止经营上述化妆品，依法调查其进货查验记录等情况，对违法产品进行追根溯源；发现违法行为的，依法严肃查处；涉嫌犯罪的，依法移送公安机关。</p>\n<p style=\"text-indent: 2em;\">\n	<a href=\"https://www.nmpa.gov.cn/directory/web/nmpa/images/1676015282391047328.docx\" target=\"_blank\"><span style=\"color:#0000cd;\">附件：19批次检出禁用原料的化妆品信息</span></a></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0210/c14739-32621532.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (57,'2023-02-19 09:34:48.814851','2023-02-19 09:34:48.814851','光激活抗癌研究取得新进展','<div class=\"artDet\">\n                        \n                        <p>\n	　　科技日报北京2月16日电 （实习记者张佳欣）英国东英吉利大学（UEA）的科学家们离创造新一代光激活癌症疗法又近了一步。这一疗法通过打开嵌在肿瘤附近的“LED灯”来发挥作用，然后激活生物治疗药物。该疗法将具有高度针对性，比目前最先进的癌症免疫疗法更有效。</p>\n<p>\n	　　最新发表在《自然·化学生物学》上的论文解释了这一创新想法背后的原理，展示了研究团队是如何设计抗体片段的。这些抗体片段不仅与其目标“融合”，还能被光激活。这意味着在未来，免疫疗法可比以往任何时候都更精确地攻击肿瘤。</p>\n<p>\n	　　这项研究首席科学家、UEA化学学院博士阿米特·萨赫德瓦说：“目前的癌症治疗方法，如化疗，会杀死癌细胞，但也会损害人体内的健康细胞，如血液和皮肤细胞。这会引起副作用，包括脱发、疲倦和不适，而且它们还会增加患者感染的风险。因此，科学家们很想创造更有针对性、没有副作用的新疗法。”</p>\n<p>\n	　　目前，科学家已经开发出几种抗体和抗体片段来治疗癌症。这些抗体比化疗中使用的细胞毒性药物具有更高的选择性，但它们仍会引起严重的副作用，因为抗体靶标也存在于健康细胞上。</p>\n<p>\n	　　现在，UEA团队设计出了第一批抗体片段，它在特定波长的紫外光照射下能与其目标结合并形成共价键。研究人员表示，共价键的作用就像将两块塑料熔合在一起。这样一来，药物分子可以永久固定在肿瘤上。</p>\n<p>\n	　　研究人员希望，此次研究将促进开发一类新的高度靶向的光响应生物疗法。抗体可在肿瘤部位被激活，并在光激活时以共价方式附着在目标上。换句话说，就是可以通过照射激活抗体来攻击肿瘤细胞。对于皮肤癌，要么直接照射到皮肤上，要么使用可植入体内肿瘤部位的像小LED灯那样的发光物质。这将使癌症治疗更加有效和有针对性，因为只有肿瘤附近的分子会被激活，而不会影响其他细胞。</p>\n<p>\n	　　研究人员称，这可能会减少副作用，还会延长抗体在体内的停留时间。它对皮肤癌或实体瘤等癌症有效，但对血癌无效。如果下一阶段研究成功，他们希望在5—10年内看到下一代光激活免疫疗法被用于治疗癌症。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0217/c14739-32625726.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (58,'2023-02-19 09:34:48.816123','2023-02-19 09:34:48.816123','科学家找到疫苗中鲨鱼化合物替代品','<div class=\"artDet\">\n                        \n                        <p>\n	　　科技日报北京2月16日电 （记者张梦然）《npj·疫苗》杂志16日发表了一项研究，描述了20多种半合成角鲨烯类似物，角鲨烯是从鲨鱼肝油中提取的化合物，能用来增强特定疫苗的免疫应答。研究结果或有助于以可持续的方式获得这种化合物的替代品。</p>\n<p>\n	　　从鲨鱼中提取的角鲨烯，一直被用于流感和新冠疫苗佐剂配方，以增加部分疫苗效力或效价。同时，角鲨烯也是正在接受临床评估的结核病和疟疾等疾病候选疫苗的主要成分。但是，鲨鱼种群面临过度捕捞的威胁，全球海洋中鲨和鳐的丰富度也自1970年以来下降了71%，这些因素可能会导致角鲨烯供应出现问题。</p>\n<p>\n	　　美国高级健康研究所的科学家此次从酵母产生的β-金合欢烯中合成了20多种角鲨烯类似物，并将它们作为疫苗佐剂成分的活性与角鲨烯进行了对比。研究人员发现，他们合成的多种化合物（包括醇A、金合欢烯热二聚体、二醇B、乙醚4、乙醚5）拥有类似角鲨烯或更好的佐剂活性。</p>\n<p>\n	　　研究结论认为，这些化合物中的一些或许能成为角鲨烯的有效替代品，不过，仍需开展进一步测试推动这些化合物在疫苗产品中的使用。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0217/c14739-32625727.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (59,'2023-02-19 09:34:48.822132','2023-02-19 09:34:48.822132','合肥为群众提供更多健身场地','<div class=\"artDet\">\n                        \n                        <p>\n	　　天空飘着小雨，9日一早，送完孙子上学，71岁的安徽合肥市民周新健来到繁昌路高架桥下的体育公园，几个球友已经等在那里。</p>\n<p>\n	　　乒乓球台前，老周灵活自如，和球友你来我往，赛况激烈。他用力一挥，扣球得分！围观的人群中响起叫好声。</p>\n<p>\n	　　“自从有了体育公园，再不用为去哪儿健身发愁咯！下雨天也不怕。”中场休息，老周拧开保温杯，抿了口热水，往上指了指。头顶上的高架桥宽阔结实，把雨水挡得严严实实。</p>\n<p>\n	　　繁昌路高架桥下，过去是荒地，由于缺少阳光雨露，花草难以存活。高架桥周边，人口密集，缺少户外运动场地。去年夏天，通过调研，合肥市体育局决定将这里改造为体育公园，局里投入器材，区里协调用地，既可以盘活资源，又能满足群众需求。这是大好事，可具体怎么改、增添啥器材、后期咋运营，都得提前考虑、规划。</p>\n<p>\n	　　合肥的做法，是问计于民。老周就曾接到电话，询问他对体育公园建设有啥建议。“我家离这很近，空闲时间想打乒乓球运动一下，可以设乒乓球台吗？”周新健说。没想到，体育公园不仅设了乒乓球台，还装了不少凳子供市民休息，衣服、水壶等随身物品也有地方放置。</p>\n<p>\n	　　如今，走进繁昌路高架桥下的体育公园，篮球场、羽毛球场、乒乓球台整齐排布。空地处，舞龙队挥舞着一条条色彩鲜艳的龙绸；太极队缓缓冲拳、推掌，动作整齐；还有不少市民被单双杠、力量型健身器材等吸引。这个建在家门口的体育公园，不仅解决了群众“健身去哪儿”难题，还引进专业公司进行后期维护管理。</p>\n<p>\n	　　家住合肥市瑶海区大兴镇双圩社区的夏魁，也感受到近年来的变化。下班回家，他头一件事就是直奔社区百姓健身房。“社区健身房不但设施齐全，还免费，这阵子我通过运动减肥瘦身了10多斤。”夏魁说。</p>\n<p>\n	　　老百姓的健身需求日益旺盛，社区能否建个群众活动室？居民有想法，社区有场地，教体局有器材，于是，2021年，改造后的百姓健身房正式对外开放。</p>\n<p>\n	　　“我们根据群众意见不断改进健身房里的设施。有人反映一个人来锻炼，乒乓球打不起来，我们就购置了发球机。有人说运动后一身汗容易感冒，健身房就加装了淋浴室。”双圩社区党委书记朱兴文介绍，健身房调动了大家的运动热情，接下来，社区还会组织趣味运动会、乒乓球比赛等活动，为全民健身搭好台子。</p>\n<p>\n	　　截至目前，合肥市已打造24个体育公园，建成全民健身苑、笼式多功能健身场等各类全民健身场地设施5276个，初步形成覆盖城乡的全民健身网络，有效改善了城乡健身环境。“我们不断探索利用复合用地建设全民健身场地。高架桥下新建口袋体育公园、污水处理厂上增添体育场地设施……因地制宜用好闲置土地和空间，盘活资源，将有利于不断满足周边群众的健身需求。”合肥市体育局群体处负责人汪贻政说。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月16日 15 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624781.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (60,'2023-02-19 09:34:48.822132','2023-02-19 09:34:48.822132','传统老字号 呈现新风貌','<div class=\"artDet\">\n                        \n                        <table class=\"pci_c\" width=\"400\">\n	<tbody>\n		<tr>\n			<td align=\"center\">\n				<img src=\"http://paper.people.com.cn/rmrb/images/1/20230216/1676495929761_1.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n				<p>\n					　　图①：消费者在全聚德前门店选购商品。<br />\n					　　本报记者 贺 勇摄<br />\n					　　图②：传统手工制药传承人展示手工泛丸技艺。<br />\n					　　胡庆余堂供图<br />\n					　　图③：赤水曾氏晒醋生产现场。<br />\n					　　受访者供图<br />\n					　　图④：北京市前门大街夜景。<br />\n					　　本报记者 贺 勇摄</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	　　近日，商务部、文化和旅游部、国家文物局联合印发《关于加强老字号与历史文化资源联动促进品牌消费的通知》，提出加强老字号历史文化资源保护利用，着力提升老字号与非遗、文物和博物馆等的融合发展。</p>\n<p>\n	　　据统计，我国现有的1128家中华老字号企业中，有近800家涉及非遗项目，87家是国家级非遗代表性项目的保护单位。加强老字号与历史文化资源联动，不仅有利于老字号的创新发展，也有助于中华优秀传统文化的传承弘扬。本版约请记者探访了一个老字号商业街区、一家老字号博物馆、一名老字号非遗传承人，从中可以看出，通过加强历史文化资源保护利用，越来越多老字号呈现出新风貌。</p>\n<p>\n	　　——编  者  </p>\n<p>\n	　　</p>\n<p>\n	　　北京前门大街——</p>\n<p>\n	　　老字号+国潮  古都商街新活力</p>\n<p>\n	　　本报记者  贺  勇</p>\n<p>\n	　　北京中轴线上，有一条全国闻名的商业街——前门大街。数百年间，这条大街见证着古都的历史变迁。近年来，这条承载着老北京记忆的街道修缮后重焕光彩，全聚德、便宜坊、都一处、天兴居……全长840米的前门大街汇聚着30余家老字号，已经成为游客体验地道老北京风味的必去之地。</p>\n<p>\n	　　穿过标志性的五牌楼前行100米，路东就是全聚德前门店。如今，这家店是集“中轴线文化”和“京味饮食文化”为一体的老字号文化体验店。在体验区，3款定制糕点礼盒吸引了许多顾客驻足。礼盒包装设计采用书简卷轴的方式呈现。打开礼盒，以永定门为始、钟楼为末的12座建筑构成的中轴线跃然眼前。</p>\n<p>\n	　　“作为街区历史悠久的老字号之一，全聚德希望通过品牌创新，更好地把老字号和北京文化结合起来。”负责相关运营的经理宋诗伟说。据统计，春节假期以来，北京主要老字号餐厅堂食线上交易额同比去年增长近50%。</p>\n<p>\n	　　挖掘文化基因、收获年轻受众，尝到甜头的不仅是全聚德。就在全聚德前门店向南不到50米的吴裕泰，前来选购茶叶的顾客络绎不绝。过道柜台上的“只此青裕”袋泡茶系列，引人注意。据了解，“只此青裕”袋泡茶系列名字灵感来源于《千里江山图》。“这个‘只此青裕’袋泡茶系列卖得特别火，年轻人买的尤其多。”店员说。</p>\n<p>\n	　　“东城区老字号数量众多、历史悠久，形成了王府井和前门两大集聚地区。我们着重引导老字号与新消费相结合，在《东城区培育建设国际消费中心城市示范区实施方案（2021—2025年）》中明确老字号传承保护、集聚发展等重点任务，调动全区资源推动老字号的保护和发展。”北京东城区商务局相关负责人介绍。</p>\n<p>\n	　　前门大街77号的四联美发博物馆，图文并茂地展示了发式的历史变化、行业人物等内容；广誉远中医药文化博物馆、荣宝斋沉浸式体验研学馆等民间博物展馆，在前门大街落户亮相；朱炳仁等非遗传承人创立文创工作室、艺术体验店，形成文旅消费新亮点……鼓励传统老字号升级、推动开办民间博物馆、开发衍生文创产品，东城区让传统文化与商业生态实现更新，建设具有京味特色与现代活力的高品质步行街区，推动古都商街持续迸发生机活力。</p>\n<p>\n	　　</p>\n<p>\n	　　胡庆余堂中药博物馆——</p>\n<p>\n	　　传承不守旧  创新不忘本</p>\n<p>\n	　　本报记者  江  南</p>\n<p>\n	　　摇晃抖动竹编圆形药匾，中药研粉随之上下翻飞，经过上千次旋转翻滚，药粉和水融合形成一粒粒大小均匀、紧实圆润的药丸。</p>\n<p>\n	　　浙江杭州清河坊老街上的胡庆余堂中药博物馆，中药传统技能手工泛丸的展示现场，总是引来观众阵阵惊叹。</p>\n<p>\n	　　泛丸的老师傅叫丁光明，已在胡庆余堂工作50多年，也是传统手工制药的省级非遗代表性传承人。他用筅帚沾水刷上药匾使之湿润，再添入一定剂量磨好的药粉，开始熟习多年的操作。</p>\n<p>\n	　　“手工泛丸是个力气活，更是良心活。”丁光明说，制丸要用足用好材料，做一次丸剂，大约要用1公斤药粉，前后花费近1个小时，容不得丝毫马虎。“做药如做人”，用料精准、工艺精湛正是中药行当从业者的本分。</p>\n<p>\n	　　走进胡庆余堂中药博物馆，木结构建筑古色古香，这是这家老字号1874年创建时使用的古建筑，也是目前国内保存完好的晚清工商业类古建筑群之一。在这里，参观者能近距离看到吊蜡丸、手工熬膏、铁船磨粉、手工切片等传统中药技能展示，还可以在手工作坊体验一番。</p>\n<p>\n	　　1988年，胡庆余堂古建筑群被列入全国重点文物保护单位；2006年，胡庆余堂中药文化又被列入首批国家级物质文化遗产名录。历经百余年风雨，胡庆余堂不仅是一家药店、药厂，如今也在药材种植、成药生产、医疗保健、文化旅游等领域形成中医药产业链布局。</p>\n<p>\n	　　在胡庆余堂集团董事长、总裁刘俊看来，要加强老字号历史文化资源挖掘、促进老字号历史文化资源利用，让老字号融入新的时代，必须“传承不守旧，创新不忘本”。</p>\n<p>\n	　　胡庆余堂中药博物馆有一块特殊的匾额：它不是朝外挂，而是面朝店内，给自家员工看。这就是创始人胡雪岩在开业之初写就的“戒欺”匾。匾中所言“药业关系性命尤为万不可欺”，体现在传统制药全过程，入药的药材一定要“真”，制药更要精益求精。</p>\n<p>\n	　　让老字号更青春，有坚守，也要有发展。去年杭州市发布的“宋韵精品研学线路”中，胡庆余堂是研学点之一。以中药博物馆为依托，与药材种植基地、药膳烹饪技艺、现代化工厂等产业链深度融合，胡庆余堂将开启一条中医药文化研学之旅，吸引中小学、高职院校学生等参与体验，并开展集传承文化、研学教育、校企合作为一体的共建项目等。</p>\n<p>\n	　　</p>\n<p>\n	　　赤水晒醋非遗技艺传承人曾泉霖——</p>\n<p>\n	　　坚守匠心  探索新路</p>\n<p>\n	　　本报记者  黄  娴</p>\n<p>\n	　　冬日午后，暖阳高照。穿过“赤水曾氏晒醋”工厂厚实的大门，一阵醋曲香由远及近。拾级而上，数以千计的土黄色天然晒露缸映入眼帘，甚为壮观。晒醋制作，这项有着上百年历史的手工技艺，在这里静静沉淀、生生不息。</p>\n<p>\n	　　经古法酿造，醋醅和半成品醋都在阳光下长久曝晒而成，赤水晒醋因此得名。曾氏家族晒醋已有百年历史，“曾氏”商标也是贵州老字号。酿造好的赤水晒醋呈红棕色，醋汁黏稠、口感柔和、酸味醇厚、微甜爽口。</p>\n<p>\n	　　仅凭一双手就能把控醋醅的湿度，曾泉霖介绍起制醋的工艺，如数家珍：制曲、发酵、制醅、曝晒、取醋……看似简单的一瓶醋，从发酵到成品醋总共要经历12道工序，历经5次天然发酵，需要3年以上的时间。</p>\n<p>\n	　　“好在香、绝在药、妙在晒”，这是曾泉霖对赤水晒醋的理解。46岁的他是国家级非物质文化遗产赤水晒醋制作技艺省级代表性传承人，出生于晒醋酿造世家，祖祖辈辈都在钻研这门技艺，曾泉霖格外看中“老字号”这块招牌。“老字号意味着底蕴、匠心与传承。”曾泉霖坦言，步入新世纪，工业技术迅猛发展，产量大、周期短、自动化的新型企业对传统手工生产企业带来了挑战。</p>\n<p>\n	　　如何在传统与现代之间平衡，在不变中寻求变化，融入时代所需元素？曾泉霖尝试在文旅融合中探索出一条新路。</p>\n<p>\n	　　2012年成立的赤水市曾氏晒醋有限公司，将企业发展定位为传承赤水晒醋历史文化、建设园林式晒醋博物馆、打造非物质文化遗产传习基地，走上了传统工艺与旅游融合发展的新路子。</p>\n<p>\n	　　10年沉淀发展，曾氏晒醋酿造基地已成为国家3A级旅游景区、传统酿造文化现场教育基地和学生研学实践教育基地，通过“文化+旅游”“文化+研学”“景点+销售”等模式的探索，在擦亮老字号招牌、传承文化技艺的同时，也为企业创收。2019年工厂开放观光体验以来，接待游客20万人次以上，实现旅游销售收入2800万余元。</p>\n<p>\n	　　谈起未来的发展规划，曾泉霖挺兴奋：与江南大学等院校开展微生物酿造发酵研究，提升晒醋质量，力争将晒醋品质升级为零碳食品、绿色食品和有机食品；将赤水曾氏晒醋打造为中华老字号企业……</p>\n<p>\n	　　“非遗技艺传承让企业成为老字号，坚持传承和不断创新是老字号走下去的保障。”曾泉霖说：“如何让老字号继续充满活力地走下去，我会一直探索”。</p>\n<p>\n	　　版式设计：蔡华伟</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月16日 12 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624783.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (61,'2023-02-19 09:34:48.832764','2023-02-19 09:34:48.832764','长沙充分释放消费潜力','<div class=\"artDet\">\n                        \n                        <p>\n	　　2月11日晚，湖南长沙市芙蓉区扬帆夜市一摊位前，店主汪思琪一边向顾客演示飞饼制作过程，一边通过手机直播。零点，她关掉直播画面，盘点一天的营业收入，又有几千元进账。“过去只会埋头卖货，现在还要会讲故事、引流量。”汪思琪是扬帆夜市最早开启直播的店主之一，一年多来，她运营的直播号粉丝量已超过1万，每天的营业额也从1000多元增长到最高1万多元。</p>\n<p>\n	　　扬帆夜市总经理李天勇说，随着长沙越来越重视夜经济的消费拉动作用，扬帆夜市和其他夜市一起迎来了全方位升级。扬帆夜市实施公司化运营管理改革，成立品牌推广部门，培育直播店主，同时联动湖南丰富的媒体资源和流量渠道，拓展发展空间。</p>\n<p>\n	　　凌晨1点，汪思琪打开“扬帆夜市商户端”小程序，将打扫干净的摊位拍照上传到后台。小程序上，出摊打卡、收摊打卡、采购票据、食品安全自查、液化气使用安全等功能模块简单明了。</p>\n<p>\n	　　“这是扬帆夜市提质升级的一个方面。”李天勇说，除了商户端外，数字化管理平台还有消费者端、管理端和政府端。市场管理方可为每一名管理人员的巡逻检查情况建立台账。这些数据最终都将为政府管理部门提供基础数据依托。</p>\n<p>\n	　　从小年夜到元宵节，扬帆夜市共接待游客120万人次。面积不大的扬帆夜市，年均营业额约8亿元，带动盘活周边330多个食品加工间。2022年，扬帆夜市被评为长沙夜间消费示范点。</p>\n<p>\n	　　在长沙，类似扬帆夜市这样的夜间消费示范点已有140多个。近年来长沙持续推进夜间经济发展，以打造“24小时城市”为目标，规划一批特色夜消费街区，夜经济成为稳增长、稳就业的生力军，先后有4个消费集聚区入选国家级夜间文化和旅游消费集聚区。</p>\n<p>\n	　　巨大的银白色月球灯饰下，啤酒吧、咖啡馆、集市依次排列，游客领略步行街的繁华后，还可到码头乘游船欣赏湘江夜景。长沙着力发展具有创新引领和彰显文化特色的夜间经济消费业态。在长沙海信广场，有魔术、脱口秀表演和阳台音乐会，还有美术馆、博物馆等，这里成为城市新文化消费场所。在长沙雨花区，乐之书店、“和+”共享图书馆、雨花非遗书院等夜间营业的书店、图书馆，共同组成了夜间读书圈。长沙市政府每年投入近3000万元向长沙音乐厅、梅溪湖大剧院等购买公共服务，推出惠民票价。</p>\n<p>\n	　　今年春节假期，长沙接待游客278.94万人次，同比增长109.25%；实现旅游收入29.69亿元，同比增长93%。1月21日至2月13日，长沙主要夜经济商圈黄兴南路步行街客流同比增长68%、五一商圈客流同比增长64.9%。</p>\n<p>\n	　　1月29日，长沙市出台2023年推进经济高质量发展十大行动方案，深入挖掘消费需求，充分释放消费潜力，进一步提升城市“烟火气”。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月16日 01 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624789.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (62,'2023-02-19 09:34:48.832764','2023-02-19 09:34:48.832764','全国高校均已建成健康驿站','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	“目前，全国高校均已建成健康驿站。将根据实际需要和规范要求，执行入站标准，开展在站服务，做好出站安排。”教育部体育卫生与艺术教育司副司长、一级巡视员刘培俊在国务院联防联控机制日前召开的新闻发布会上介绍，教育部已印发《普通高等学校健康驿站建设管理指引》，明确了高校健康驿站建设要求，推动高校依托校医院、医疗机构科学统筹高校内外的医疗服务资源，建成符合规范标准的健康驿站。</p>\n<p style=\"text-indent: 2em;\">\n	教育部门已经对健康驿站实行动态监测，推动其质量达标。刘培俊说，目前开展了全国高校健康驿站自查工作，全面摸排建设情况，逐校建立了人员、物资和工作台账，支持高校按标准安排驿站的床位数，配备足量医护和服务保障人员，储备医疗药品和器材，开设校内发热诊疗点，实行24小时值班值守制度，为校内师生提供便捷的医疗和相关服务。</p>\n<p style=\"text-indent: 2em;\">\n	刘培俊指出，为了确保健康驿站运行规范，学校属地的卫健、疾控和教育等部门指导支持高校建立与相关医院的稳定对接机制，协调属地医院包联学校，安排医院医护人员驻校共同工作，健全将校内有关病例转至相关医院专业救治的绿色通道。</p>\n<p style=\"text-indent: 2em;\">\n	同时，开展专业培训，提升健康驿站管理能力。刘培俊表示，教育部组织开展了全国高校健康驿站管理能力提升培训，各地教育部门也会同属地卫健疾控部门分级开展专项培训，全面提升高校健康驿站分管校领导、校医院医护人员和驿站工作人员的疫情应对能力。</p>\n<p style=\"text-indent: 2em;\">\n	“春季开学后，教育部门将继续会同属地有关部门，支持高校巩固健康驿站建设，强化健康驿站管理，优化健康驿站服务，关心关爱师生，实行接诉即办，及时回应关切，持续保障高校师生的健康和校园的安全稳定。”刘培俊说。（本报记者 陈海波）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0214/c14739-32623294.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (63,'2023-02-19 09:34:48.842229','2023-02-19 09:34:48.842229','河北取消京津“异地就医备案”观察','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	因血液疾病导致左腿严重病变，河北邢台市民李秀格老人半年内已经三赴北京协和医院治疗。每次就医之前，她的女儿都会花近半小时进行异地就医网上备案。让她们高兴的是，从2月10日起，去北京看病不用再备案了。</p>\n<p style=\"text-indent: 2em;\">\n	2月10日起，河北省实施医保新规，取消参保人员到京津跨省异地就医备案手续。这意味着，河北参保人员在京津相关定点医疗机构看病就医，可持医保电子凭证或社会保障卡免备案直接结算。过去，如果河北参保人员到异地就医不备案，要全额垫付医疗费用，不备案产生的医疗费用原则上不能报销。</p>\n<p style=\"text-indent: 2em;\">\n	记者了解到，我国实施异地就医备案制度初期，参保人员不仅需提供医院转诊手续以及个人暂住证和工作证等材料，而且必须到医保经办机构现场办理备案手续。2019年，河北探索实施“零跑腿、不见面”的异地就医网上备案，取消一切不必要证明材料，参保人员仅凭社保卡、身份证即可在网上自主备案，极大方便了群众就医。2021年，河北提出取消省内异地就医备案。而今年取消京津就医备案手续，河北参保人员京津冀就医“无异地”进程将进一步加快。</p>\n<p style=\"text-indent: 2em;\">\n	实施“免备案”后，河北参保人员可在开通跨省异地就医直接结算的京津医保定点医疗机构看病治疗并直接结算。记者从河北省医保局了解到，截至今年1月底，北京、天津分别有760家、436家医疗机构开通跨省异地就医住院直接结算业务，3228家北京医疗机构和1143家天津医疗机构的普通门诊可直接结算。</p>\n<p style=\"text-indent: 2em;\">\n	李秀格老人2月底要去北京进行第四次治疗，让她期待的是，到时不仅报销费用有望提高，而且，由于河北将对异地就医人员执行就医地医保目录，老人治病过程中可供选择的药品种类、医疗项目也更多了。</p>\n<p style=\"text-indent: 2em;\">\n	“过去，河北对转诊转院等临时外出人员异地就医，一般降低10%报销比例。”河北省医保局医药服务管理处处长郑东花说，2月10日起，河北调整医保报销政策，参保人员在京津不同级别医疗机构就医，将可享受河北同级别医疗机构报销待遇。</p>\n<p style=\"text-indent: 2em;\">\n	此前，高血压、糖尿病、恶性肿瘤门诊放化疗、尿毒症透析、器官移植术后抗排异治疗等5个门诊慢特病从2022年开始试点实施免备案直接结算。长住天津的河北省临城县石城乡村民米爱华已切实享受到便利。米爱华是一名慢性肾功能衰竭患者，每周需要做一次透析，“1月19日在天津市人民医院做透析花了460元，直接报销了318.78元。这样直接报销实在太方便了。”</p>\n<p style=\"text-indent: 2em;\">\n	记者了解到，为确保“免备案”落地实施，河北各市医保部门已发布相关落地实施文件，明确待遇调整政策，并将信息系统中关于放开备案限制和调整待遇政策等功能调试到位。</p>\n<p style=\"text-indent: 2em;\">\n	近年来，随着京津冀协同发展的推进，三地逐步扩大异地就医定点医疗机构互认范围，京津冀医疗资源共享不断推进。2022年7月发布的《京津冀医保协同发展2022年工作要点》提出，推动三地扩大异地就医定点医疗机构互认范围，探索取消京津冀区域内异地就医门诊备案手续，推进部分医保服务事项实现跨省通办和同事同标，优化管理服务。</p>\n<p style=\"text-indent: 2em;\">\n	“河北实施京津就医免备案制度，将可以更好服务北京非首都功能疏解，为京津冀三地群众异地工作、养老和就医提供更大便利。”河北省医保局党组书记、局长赵新喜说。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0211/c14739-32621843.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (64,'2023-02-19 09:34:48.852073','2023-02-19 09:34:48.852073','加快数字服务适老化改造','<div class=\"artDet\">\n                        \n                        <p>\n	　　创造对老年人友好的数字环境，既能帮助老年人跨越“数字鸿沟”，共享科技进步的红利，也将为相关行业提供新的发展机遇</p>\n<p>\n	　　</p>\n<p>\n	　　前不久，在一些地方召开的两会上，加快数字服务适老化改造、解决老年人“最初一公里”出行难等话题引人关注。在移动互动网应用普及的当下，一些老年人需要打车服务时，由于不熟悉智能手机等移动终端的应用操作，往往很难约到网约车。而在路边招手打出租车，常常看到出租车空载而过，因为司机已经从线上接单了。</p>\n<p>\n	　　这样的情况并不只存在于老年人的出行服务中。数字化技术、智能化产品的广泛应用，极大促进了经济社会发展，给人们生活带来不少便捷。但受年龄、认知等各方面因素的影响，部分老年群体在使用互联网应用、手机应用程序时，经常面临一些问题与障碍。比如去医院看病，经常要使用手机提前预约挂号；每年的社保认证，虽不用再到现场，但要通过手机进行“人脸认证”；外出就餐，很多餐厅都要求扫码点餐……一些对于年轻人来说习以为常的操作，却对老年人造成不小的困扰，成了难以逾越的“数字鸿沟”。</p>\n<p>\n	　　加快数字服务适老化改造，切实解决老年人运用智能技术困难，是积极应对人口老龄化的重要举措。我国60岁及以上人口已超2.64亿人，占总人口的比重达18.7%，这部分人群在数字服务方面的需求不应被忽视。创造对老年人友好的数字环境，既能帮助老年人跨越“数字鸿沟”，共享科技进步的红利，同时进一步激发老年群体上网、用网的需求活力，也将为相关行业提供新的发展机遇。</p>\n<p>\n	　　近年来，我国大力推进数字技术的适老化改造，有关部门陆续出台互联网适老化及无障碍改造方案，推动老年群体融入互联网，推进民生服务便利共享。通过优化界面、简化程序、增加功能等方式，不少网站和手机应用程序更加贴近老年人的实际需求。日前，中国消费者协会针对适老化应用程序的一项研究报告显示，评测的104款常用程序中，76%的应用程序符合适老化改造的基本要求，76.2%的调查对象对APP适老化的现状整体持满意态度。</p>\n<p>\n	　　与此同时，让老年群体触网更便利，仍有不少工作需要持续推进。“数字惠老”并非一日之功，需要政府、社会、企业、家庭等方面共同发力，助力老年人数字化生活更加便捷。</p>\n<p>\n	　　政府要在宏观层面进一步加强法律、规划、政策、标准和规范等制定，不断完善老年数字鸿沟治理的政策制度体系，推动、引导相关企业加快互联网适老化改造。同时充分利用5G网络、大数据、物联网、人工智能等新一代信息技术，以数字技术打通政务服务、城市治理、公共安全等领域，进一步优化公共服务。企业则要承担起更多责任，坚持以老年人的需求为牵引，推出更多老年人能用、会用的智能产品与服务，把适老化理念融入产品设计的全生命周期，不断提升老年人使用体验。社会和家庭的作用也不可或缺，通过各种宣传、教育、培训、志愿服务等方式，加强家庭与社会数字反哺，推动数字技能代际传递，消除老年人对网络信息和智能技术的畏难、排斥心理等。</p>\n<p>\n	　　帮助老年人赶上时代步伐，既考验治理的精度，更映照社会的温度。愿数字技术的发展成果，为老年人带来更多便利，共享数字生活的美好。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月17日 19 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0217/c14739-32625642.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (65,'2023-02-19 09:34:48.852073','2023-02-19 09:34:48.852073','老字号如何持续“潮”？','<div class=\"artDet\">\n                        \n                        <p>\n	　　什么是老字号？</p>\n<p>\n	　　中秋佳节，拎一盒杏花楼月饼，品尝团圆的滋味；新年将至，提一支芝兰图毛笔，挥洒来年的期许；乔迁新居，摆一款景泰蓝，营造东方的美感……遍布大江南北的老字号，在琐碎的柴米油盐里，也在雅致的琴棋书画中，它们展示着物质的丰裕，更传承着文化的底蕴。</p>\n<p>\n	　　不久前，商务部、文化和旅游部、国家文物局联合印发《关于加强老字号与历史文化资源联动促进品牌消费的通知》，提出加强老字号文物修缮保护、鼓励老字号融入传统街区建设等。加强老字号历史文化资源挖掘，成为当下推动老字号创新发展的重要途径。</p>\n<p>\n	　　老字号一头连着千家万户的日常生活，一头也连着千姿百态的文化传统。义利食品传承“先义后利”的开店宗旨，荣宝斋装裱普通中堂有17道完整工序……精湛的传统技艺、可贵的工匠精神与独特的服务理念，让老字号成为一代代人的成长记忆。老字号坚守商业道德，追求精益求精，以匠心收获口碑，满足着人们对高品质商品的期待。</p>\n<p>\n	　　随着城市商业生态的更新，一些老字号也面临不小的挑战，如何摆脱“经得住百年风雨，经不起市场洗礼”的经营窘境，不断擦亮金字招牌？历史文化资源开发为老字号的推陈出新提供了新思路、新途径。</p>\n<p>\n	　　除了传承手艺、严格品控，老字号更应挖掘传统手艺的当下价值，增加文化创意附加值。天津劝业场大楼、黑龙江马迭尔宾馆、上海豫园……这些老字号旧址或是通过开设场景化、交互式的老字号博物馆，讲述老字号的文化故事；或是融入历史街区保护建设，成为城市更新的有机组成部分。一处处集商业、文化、旅游等于一体的老字号综合体，让人们在原址原境中品味老字号的原汁原味。</p>\n<p>\n	　　当下的老字号，亦是曾经的“新国潮”。“持中守正”与“出奇制胜”始终流淌在老字号的基因里。从与文博文创、国漫的跨界合作，到线上直播的渠道拓展，再到打造互动式、个性化新零售的创新理念，年轻人感受到了老字号在时代洪流中的无限生机，也看到了它们在传统与潮流、品质与个性中的坚守与蜕变。</p>\n<p>\n	　　当工业生产取代了手工制作、当网络直播代替了市井吆喝，老字号里有始终不变的坚守，也有锐意求变的朝气。期待老字号通过多种途径挖掘历史文化资源，擦亮招牌、做强品牌。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月16日 12 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624784.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (66,'2023-02-19 09:34:48.866619','2023-02-19 09:34:48.866619','让家庭病床放得平、安得稳','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	近日，江苏省无锡市印发实施意见，加快推进家庭病床建设，为符合条件的患者提供居家医疗服务，所发生的费用按住院标准给予医保报销。作为地方实践，无锡市的探索丰富了家庭病床改革的全国样本库。</p>\n<p style=\"text-indent: 2em;\">\n	此前有媒体报道，中国首个智慧家庭病房在广州启动，该病房应用物联网、5G等新技术，将智能穿戴设备、智能家居、健康监测设备等部署到家，有望实现智慧居家医疗、居家护理和居家康复一体化。</p>\n<p style=\"text-indent: 2em;\">\n	家庭病房是专业医疗机构诊疗、护理、康复等医疗服务的延伸。在这个场景中，家庭将成为医疗服务的主要场所，接受来自专业机构的诊疗与指导。这对于那些丧失主动行为能力或年老体衰的患者来说，无疑是一个福音，他们不仅能够便捷地得到治疗，还能待在熟悉温馨的家庭环境中，而免于奔波在路上、拥挤在医院。</p>\n<p style=\"text-indent: 2em;\">\n	特别是随着我国社会老龄化程度的提高，越来越多的老年人连续就医的需求持续增长，诸如中度、重度失能人员，中风瘫痪康复期、恶性肿瘤晚期患者，以及骨折牵引需卧床治疗的患者等，都需要长期缠绵病榻。一个良好的静养环境，对他们而言格外重要。但当下的医疗供给，仍很难完全满足这一需求，“看病难”“住院难”仍是较为突出的矛盾。</p>\n<p style=\"text-indent: 2em;\">\n	因此，扎实推进家庭病床建设，让更多的患者足不出户即可接受专业诊疗与护理，不仅可以提升患者及家庭的幸福感，也是应对人口老龄化、全面加强老年健康服务、打造健康中国的重要路径。</p>\n<p style=\"text-indent: 2em;\">\n	不过，从各地的实践看，仍有一些因素制约着家庭病床的发展。目前，家庭病床总体供给量太少，很难满足数量庞大的患者需求。一方面，报酬低、风险大导致医院积极性不高；另一方面，医保报销成为横亘在患者面前的一大门槛。</p>\n<p style=\"text-indent: 2em;\">\n	尽管在国家层面已经出台多个通知，要求大力推动家庭病床建设，如2020年12月，国家卫生健康委和国家中医药管理局印发《关于加强老年人居家医疗服务工作的通知》；2021年12月，国家卫生健康委、全国老龄办、国家中医药管理局印发《关于全面加强老年健康服务工作的通知》等。但从普遍性倡导到广泛落地，有待更具体、更规范、更周密的制度设计。</p>\n<p style=\"text-indent: 2em;\">\n	医疗服务是一个良心活，普惠式服务当然是应该提供的，但相关部门应该从收入、评价、晋升等方面多一些考量，激发医护人员的积极性。此外，目前基于到医院就诊的医保报销政策也要打通，这就需要各地出台具体规定。此番无锡市提出“所发生的费用按住院标准给予医保报销”，开辟了一条可资借鉴的新路。</p>\n<p style=\"text-indent: 2em;\">\n	进一步讲，像家庭病床这样的设计，也应该与当下的分级诊疗、智慧医疗接轨。一般的治疗、护理，可以请社区医生负责，专家可以出诊，也可以接入智慧医疗等远程服务。从大医院走进小家庭，家庭病床无疑是一种制度创新，这就要求当下的医疗服务进行体系再造、流程再造、服务再造。只有统筹兼顾各方利益诉求，从制度上明确责权利，并不断针对运行中出现的问题补漏纠偏，才有可能让家庭病床放得平、安得稳。</p>\n<p style=\"text-indent: 2em;\">\n	（作者：胡印斌，系媒体评论员）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624818.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (67,'2023-02-19 09:34:48.872185','2023-02-19 09:34:48.876196','职工医保个人账户改革影响几何','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	近日，武汉、西安、黑龙江等多个正在进行医保个人账户改革的地方，出现了不少争议。一些参保职工质疑，认为改革后个人账户划入少了，自己吃亏了。一时间，这个话题引发社会的普遍关注。为什么要开展职工医保门诊保障方式改革？个人账户划入少了，是不是真的吃亏了？针对一些网友和群众关注的问题，近日，记者采访了中国社科院经济研究所研究员、公共经济学研究室主任王震。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>为什么要推进门诊保障方式改革</strong></p>\n<p style=\"text-indent: 2em;\">\n	记者在走访中发现，不经常看病的年轻人对这次改革并没有那么关注，而老病号和老年人，应该说是此次改革中受益最多的群体，反而对政策存在不少误解。很多人在问，国家为什么要推进门诊保障方式改革？主要是想解决什么问题？</p>\n<p style=\"text-indent: 2em;\">\n	了解我国职工医保制度的人知道，职工医保实行的是统筹基金和个人账户相结合的制度，改革前，统筹基金保障住院和门诊大病，个人账户保障门诊小病。</p>\n<p style=\"text-indent: 2em;\">\n	“这在特定历史时期有力地推动了公费和劳保医疗制度向社会保险制度的平稳过渡。但随着我国社会主要矛盾的变化，基本医保体系的不断完善，‘个人账户保门诊小病’方式的局限日益凸显，出现了参保职工‘有病的不够花，没病的用不了’的情况。”王震表示，一方面，退休群众和患病群众个人账户的钱很快就会花完，出现了门诊个人自费负担重的情况。另一方面，健康状况较好的年轻人和健康群众的个人账户中大量资金在沉睡，这笔钱只能用于看病，不看病只能“趴”在账户上，这类账户占到80%以上。</p>\n<p style=\"text-indent: 2em;\">\n	王震介绍，按照中央任务部署，这次实施门诊共济保障改革，一方面保留了职工医保个人账户设置和部分功能，继续发挥其积累作用；另一方面充分发挥统筹基金保障功能，把普通门诊费用纳入统筹基金报销范围，切实帮助身患疾病有治疗需要的人群，在机制上补齐了保障短板，减负效果更明显，这也是发挥社会保障再分配功能，促进共同富裕的体现。</p>\n<p style=\"text-indent: 2em;\">\n	改革推出后，也有网民称“这次改革是因为医保基金不够用了”，对此，王震表示：“实际情况并非如此，参保群众没有必要担心。”他介绍，2022年，全国基本医疗保险基金（含生育保险）收入超3.06万亿元，支出2.44万亿元，当期结余0.63万亿元，其中职工医保基金收入2.06万亿元，支出1.51万亿元，当期结余0.55万亿元。总体上看，医保基金收支平衡，收支规模与经济发展水平相适应，基金保障能力稳健可持续。</p>\n<p style=\"text-indent: 2em;\">\n	王震说，建立普通门诊统筹，是通过基金内部的结构调整，在不另外筹资、不新增单位和个人缴费负担的前提下，提高了门诊保障水平。普通门诊统筹是一项新的保障机制，带来了保障的增量，涉及的资金来源，既有个人账户计入调整的调剂资金，也有统筹基金内部的挖潜。“从目前全国门诊费用1.3万亿元左右的规模看，按50%报销，单靠个人账户调减也是不够的，长远看统筹基金将承担更多的支出责任”。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>参保人账户划入少就是吃亏了吗</strong></p>\n<p style=\"text-indent: 2em;\">\n	改革后，大部分参保人个人账户当期计入会减少，有网民称“吃亏了”。是不是真的可以这样理解呢？</p>\n<p style=\"text-indent: 2em;\">\n	“大家既要算小账，也要算大账。既要算眼前账，又要算长远账。改革后门诊待遇更好，统筹基金会加强，生病后可以通过统筹基金报销，大家互助共济，保障会更充分。”王震表示，大家要理性看到收入机制和保险机制的不同，在社会医疗保险的大框架下，个人账户的资金是“看病钱”，不是工资收入，也不是福利。年老多病的时候，靠个人账户和个人积累总是有限的，都需要有坚实的可持续的医疗保险实现社会互助共济来化解疾病风险，长远来看大家都会从这个改革中获益的。</p>\n<p style=\"text-indent: 2em;\">\n	他说，改革后，参保人保障水平在很多方面都得到了提升。一是互助共济功能显著提高，门诊需求多、患病多的参保职工明显获益。之前不报销的门诊常见病、多发病门诊医疗费纳入统筹基金支付范围，有效减轻普通门诊就诊费用负担。特别是罹患慢病的参保职工，获益远超个人账户减计额，往往是减计数百元、报销千余元甚至数千元。二是职工医保个人账户家庭共济。个人账户的使用范围从参保人员本人拓展到本人及其配偶、父母、子女，身体健康、生病少的职工医保参保人可以把本人的个人账户与家庭成员关联，支付子女、老人的就医购药费用，保障范围更大。三是适当拓展了个人账户使用范围。改革后，个人账户可以用于支付在定点医疗机构就医发生的由个人负担的医疗费用，在定点零售药店购买药品、医疗器械、医用耗材发生的由个人负担的费用，还可以用于配偶、父母、子女参加城乡居民医保等的个人缴费，使用效率更高，保障范围更广。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>为什么有些群众觉得购药不便了</strong></p>\n<p style=\"text-indent: 2em;\">\n	此次改革政策推出之后，一些地方群众反映，虽然门诊费用能报销，但在药店享受待遇不够方便，这也反映出一些地方配套政策跟进不及时的问题。</p>\n<p style=\"text-indent: 2em;\">\n	对此，王震表示，本次改革提出将符合条件的定点零售药店用药保障服务纳入门诊保障范围，目的就是为了方便患者就近报销，减轻费用负担。原来，参保患者在定点零售药店购药只能用个人账户支付，改革后既可以使用个人账户，也可以凭外配处方享受统筹基金报销。但是，有些地区担心将药店纳入门诊统筹报销后“管不住”，没有及时按照国家文件的要求将符合条件的药店纳入门诊统筹报销，从而影响了参保人购药的便利性。</p>\n<p style=\"text-indent: 2em;\">\n	他说，医保制度从建立起，就重视发挥零售药店作用，将零售药店纳入医保定点服务管理，鼓励定点零售药店为参保人员提供购药服务，发挥药店在药品供应保障方面的功能。新医改过程中，国家采取多种形式推进医药分开，鼓励患者自主选择在医院门诊药房或凭处方到零售药店购药。本次改革重视发挥定点零售药店作用，统筹基金支付延伸到符合条件的医保定点药店就是创新的支持措施。从有关数据看，改革实施以来，个人账户在定点零售药店使用的规模是增加的，也显示了个人账户家庭共济、拓展个人账户使用范围等改革举措使药店受益。</p>\n<p style=\"text-indent: 2em;\">\n	“当然，零售药店也要根据行业发展和改革方向，通过主动创新来更好适应新的医保支付措施，积极在处方流转、配药服务、与医疗机构协同等方面共同努力，更好服务患者。相信随着改革系统集成作用进一步深化，定点零售药店将迎来更加广阔的发展空间。”王震表示。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624834.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (68,'2023-02-19 09:34:48.883067','2023-02-19 09:34:48.883067','权威专家解读职工医保改革热点','<div class=\"artDet\">\n                        \n                        <p style=\"text-indent: 2em;\">\n	近期，广东、湖北、四川等省份实施职工医保门诊共济保障改革，明确开展职工医保门诊统筹，按规定报销参保人普通门诊费用，并同步调整职工医保个人账户计入办法。围绕公众关心的焦点问题，“新华视点”记者采访了权威专家和业内人士。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>焦点一：个人账户的“钱”少了会影响医保待遇吗？</strong></p>\n<p style=\"text-indent: 2em;\">\n	随着多地政策落地，不少参保人发现自己医保账户的“钱”少了，担心会影响个人医保待遇。</p>\n<p style=\"text-indent: 2em;\">\n	据了解，近期多地出台的医保改革方案有共同点：职工医保个人账户当期计入的金额有所变化。对在职职工而言，原本由单位缴费划入的部分不再划入，被纳入统筹基金；对退休人员而言，个人账户计入从过去的与本人养老金挂钩，过渡到定额划入，定额标准与统筹地区改革当年人均养老金挂钩。</p>\n<p style=\"text-indent: 2em;\">\n	单从账面上看，个人账户新计入的“钱”的确变少了，而且有些人的降幅不小。这不禁让人疑惑，减少的“钱”去哪儿了？会影响个人医保待遇吗？</p>\n<p style=\"text-indent: 2em;\">\n	“医保个人账户上的‘钱’减少，并不意味着参保职工医保待遇的降低或损失。”中国社科院公共经济学研究室主任王震说，个人账户减少的“钱”将转化为统筹基金“大池子”的增量，用来承担以往个人账户“小池子”需要支付的普通门诊费用，且个人账户之前的累计结存仍归个人使用，实现保障“增量”。</p>\n<p style=\"text-indent: 2em;\">\n	2021年4月，国务院办公厅印发《关于建立健全职工基本医疗保险门诊共济保障机制的指导意见》，明确调整统筹基金和个人账户结构后，增加的统筹基金主要用于门诊共济保障，提高参保人员门诊待遇。这也就是说，改革后，以前不能报销的普通门诊费用可以报销了。</p>\n<p style=\"text-indent: 2em;\">\n	以武汉市退休参保职工周某为例。其年养老金收入5万元，改革前个人账户每年划入2400元。周某患有脑梗，但因当地没有门诊统筹政策，在门诊看病无法享受报销。改革后，他的个人账户年划入调整为996元，在某三级医院门诊就医发生门诊费用7150元后，按照新的门诊统筹政策报销，除去门槛费500元，按三级医院60%的报销比例，即可报销3990元。这也就是说，虽然周某改革后个人账户当年少划入1404元，但其享受待遇却增加了2586元。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>焦点二：改革职工医保个人账户是因为统筹基金没钱了吗？</strong></p>\n<p style=\"text-indent: 2em;\">\n	有质疑声认为，改革职工医保个人账户是因为统筹基金没钱了，要用个人账户“补窟窿”。对此，专家表示，这是由于对我国医保基金收支情况不清楚而产生的误解。</p>\n<p style=\"text-indent: 2em;\">\n	数据显示，2021年职工医保统筹基金收入11864亿元，支出9321亿元。也就是说，统筹基金不仅收支平衡，而且略有结余，所以“统筹基金没钱了”并不成立。</p>\n<p style=\"text-indent: 2em;\">\n	既然如此，有公众疑惑，为何把个人账户的“钱”转到统筹基金，用于门诊共济保障，让别人花自己的“钱”？</p>\n<p style=\"text-indent: 2em;\">\n	“医疗保险归根到底是一种社会保险，这就意味着它具有互助共济、责任共担、共建共享的性质。”王震说，参保人年轻时得病少，到年老时容易生病，看病吃药仅依靠个人账户积累是有限的，把大家的钱放在一起，可以实现用大数法则化解社会群体的风险，更大范围满足公众医疗需求。</p>\n<p style=\"text-indent: 2em;\">\n	王震说，调整个人账户是调减当期计入，没有动个人的历史结存。要认识到，无论是个人账户还是统筹基金，都属于已经缴纳的医疗保险费用，是用于解决公众防病、治病问题而筹集、分配和使用的“钱”。</p>\n<p style=\"text-indent: 2em;\">\n	改革前，个人账户无法给别人使用，“有病的不够用、没病的不能用”，这就逐渐出现了过度沉淀、共济性不够、欺诈骗保等弊端。</p>\n<p style=\"text-indent: 2em;\">\n	“在医保战略性购买的框架下，优化门诊保障待遇是结构性调整。”国家卫健委卫生发展研究中心医疗保障研究室主任顾雪非介绍，这是在不另外筹资、不新增单位和个人缴费负担的前提下，提高门诊保障水平。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>焦点三：个人账户改革对老年人有什么影响？</strong></p>\n<p style=\"text-indent: 2em;\">\n	有一些老年人看到改革后个人账户“钱”少了，担心看病吃药使用受限。</p>\n<p style=\"text-indent: 2em;\">\n	指导意见明确，普通门诊统筹保障水平以50%起步，并要求各地在此基础上对退休的老年人再给予倾斜支付。</p>\n<p style=\"text-indent: 2em;\">\n	以呼和浩特市为例，在开通门诊统筹保障后，退休人员门诊支付比例提高，各级医疗机构在原办法的基础上提高5%，同时进一步提高门诊统筹年度最高支付限额，退休人员待遇支付由原来的4000元提高到6000元，高于在职职工。</p>\n<p style=\"text-indent: 2em;\">\n	此外，在普通门诊保障健全之前，不少地区先行建立了门诊慢特病保障机制，用统筹基金支付常见于老年人的慢性病、特殊疾病在门诊发生的费用。近期，一些地方适当扩大门诊特殊病病种。如江苏南京在原4大类门特病种基础上，新增9类病种；武汉将高血压、糖尿病等慢性病特殊疾病病种由28类增加到37类，基本病种从32种增加到70种等。</p>\n<p style=\"text-indent: 2em;\">\n	通过对多地职工医保个人账户改革方案的对比，还可以发现一个共同点——个人账户使用范围拓宽，使用主体从个人扩宽到配偶、父母、子女等，对象范围从药品支付到医疗器械、医用耗材等。这对老年人来说更是个利好。</p>\n<p style=\"text-indent: 2em;\">\n	顾雪非表示，改革后，参保人可以把个人账户的“钱”用给老人等其他家庭成员，形成家庭内部“小共济”，提高家庭应对医疗风险能力。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>焦点四：如何提高百姓对改革的获得感？</strong></p>\n<p style=\"text-indent: 2em;\">\n	随着各地改革方案落地，也有人对改革提出意见：以前在家门口药店买药，现在为了报销还要跑到定点医疗机构门诊挂号；门诊统筹报销设置起付线、最高支付限额，保障力度不如住院报销等。</p>\n<p style=\"text-indent: 2em;\">\n	“参保人对改革感受最为直观。”顾雪非说，为了让改革红利真正惠及最广大群众，需要充分考虑对老百姓、医药机构等利益相关方的影响，多方共同发力，方便老百姓就医购药。</p>\n<p style=\"text-indent: 2em;\">\n	根据相关部署，职工医保门诊共济保障改革将通过3年完成。目前，大多数地区已经公布职工医保个人账户改革、门诊共济保障机制的实施细则，但各地经济发展水平、医保统筹力度等情况存在差异，相应的改革策略与举措不尽相同。</p>\n<p style=\"text-indent: 2em;\">\n	受访专家与业内人士建议，要切实回应群众提出的合理诉求，在提高医疗保障服务的便捷性和可及性、加强政策解读等方面着手，切实破解群众求医问药难题。</p>\n<p style=\"text-indent: 2em;\">\n	“改革过程中存在不同声音是正常的。”王震说，改革政策效益的显现也需要时间，要同步推进协同配套服务，如合理调整门诊报销的起付线与最高支付限额、尽快将符合条件的定点零售药店纳入门诊统筹、探索将符合条件的“互联网+”医疗服务纳入保障范围等。</p>\n<p style=\"text-indent: 2em;\">\n	不少地区采取分步调整，促进政策平稳过渡。也有一些地区降低门诊报销起付线、提高报销限额等，如广州在门诊报销由月度限额调整为年度限额的基础上，提高了报销金额；北京不再设置医保门诊最高支付限额。</p>\n<p style=\"text-indent: 2em;\">\n	记者了解到，截至发稿时，一些地方已进一步优化有关配套措施。武汉市医保局发布公告，在前期已经公布的1000多家零售药店试点的基础上，将第三批4065家定点零售药店纳入职工医保门诊统筹保障范围。（记者彭韵佳、邓楠、邓瑞璇、董小红、黄筱）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0216/c14739-32624839.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (69,'2023-02-19 09:34:48.885203','2023-02-19 09:34:48.885203','与时俱进筑牢消费者权益“防火墙”','<div class=\"artDet\">\n                        \n                        <p>\n	　　营造良好消费环境，维护好消费者权益，是提振消费信心、促进消费持续恢复的重要一环</p>\n<p>\n	　　</p>\n<p>\n	　　主播售假问题暴露直播带货侵权乱象，网络游戏停服删档引发虚拟财产侵权争议，“价格刺客”有违明码标价诚信原则，预制菜产品品质及应用场景存在侵权隐患……中国消费者协会联合人民网舆情数据中心，根据事件涉及人群、波及范围、安全危害三个维度，发布了2022年十大消费维权舆情热点报告，引起关注。</p>\n<p>\n	　　一段时间以来，各地有关部门、消协组织采取畅通投诉渠道、倡导经营者压实主体责任、增强消费者维权意识与能力等举措，不断织密消费者权益保护网，消费者满意度稳步提升。数据显示，2022年有近七成受访者对国内消费环境总体表示放心。但也要看到，当前市场上仍存在一些损害消费者权益的行为，既包括医美机构误导消费、不规范标价等老问题，也有直播售假、网游虚拟财产侵权等新问题。这对消费者权益保护工作提出了更高要求。</p>\n<p>\n	　　消费是畅通国内大循环的关键环节和重要引擎。在食品安全、产品质量等领域，一个消费侵权案例的发生，可能影响许多人的消费意愿。营造良好消费环境，维护好消费者权益，是提振消费信心、促进消费持续恢复的重要一环。《扩大内需战略规划纲要（2022—2035年）》着眼当前消费领域存在的新老问题，提出“建立健全适应消费新业态新模式发展特点的新型监管机制”“建立假冒伪劣产品惩罚性巨额赔偿制度”“完善多元化消费维权机制和纠纷解决机制”等具体举措，为进一步做好消费者权益保障明确了方向。</p>\n<p>\n	　　营造安全放心诚信的消费环境，需要加强全链条监管。一方面，要加强事前监管，对产品、服务质量和广告宣传等问题常抓不懈，压缩实施侵权行为的空间。另一方面，要强化社会监督，畅通维权渠道。《关于进一步释放消费潜力促进消费持续恢复的意见》提出，持续优化完善全国12315平台，充分发挥地方12345政务服务便民热线作用，进一步畅通消费者投诉举报渠道。多措并举降低消费者维权的门槛，在更大范围开展消费投诉信息公开试点工作，强化信用约束机制，形成遏制侵权行为的合力，才能持续改善消费环境。</p>\n<p>\n	　　消费者权益保护工作千头万绪，不可能毕其功于一役。对屡禁不止的老问题，要执法从严，形成震慑。去年底，最高人民法院发布《关于为促进消费提供司法服务和保障的意见》，对食品安全、预付式消费、直播电商、快递服务等领域提出16条举措，进一步明晰权责归属，为消费者维权提供坚强司法保障。对新问题，要根据行业发展特点，及时创新监管方式。修订后的《上海市消费者权益保护条例》，对“盲盒”经济、大数据杀熟、互联网广告竞价排名等作出明确规范，积极回应消费者期待，这是有益的探索。与时俱进构筑强而有效的监管堤坝，为消费者权益筑牢“防火墙”，才能消除消费者的后顾之忧，让其愿消费、敢消费。</p>\n<p>\n	　　市场经济是信用经济，在市场竞争日益激烈的今天，漠视消费者权益的行为，最终会得不偿失。随着监管不断加强、消费者安全意识和辨别能力不断提高，种种“消费陷阱”最终也会让侵权者自身陷入困境。期待广大经营者自觉秉持诚信经营的原则，抵制各类不良营销方式，将质量放在第一位，更好满足消费者高层次、多样化的需求，为释放消费活力、促进经济发展作出有益贡献。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月13日 05 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0213/c14739-32622438.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (70,'2023-02-19 09:34:48.892358','2023-02-19 09:34:48.892358','让车险消费更优惠','<div class=\"artDet\">\n                        \n                        <p>\n	　　车险是关系到我国4亿车主切身利益的险种。车险综合改革实施两年多来，不少车主享受到了车险产品“加量不加价”的改革红利。银保监会发布的数据显示，截至2022年6月底，消费者车均保费为2784元，较改革前大幅下降21%，87%的消费者保费支出下降，为车险消费者减少支出2500亿元以上；保障程度明显提高，在价格不变的情况下，交强险保障金额由12.2万元提升至20万元。</p>\n<p>\n	　　商业车险价格受自主定价系数、无赔款优待系数、交通违法系数等因素的影响。其中，前两者由相关部门和机构制定发布，供保险公司使用；自主定价系数由各财险公司根据车型、车主年龄、驾驶习惯、行驶里程、销售渠道等多个因素自主设置，是财险公司行使自主定价权的主要途径。</p>\n<p>\n	　　扩大商车险自主定价系数浮动范围，早就列在了车险综合改革的“日程表”上。2020年9月，银保监会印发《关于实施车险综合改革的指导意见》，将自主定价系数范围确定为0.65至1.35，并明确将逐步放开自主定价系数的范围。</p>\n<p>\n	　　为健全以市场为导向、以风险为基础的车险条款费率形成机制，优化车险产品供给，银保监会日前发布了《关于进一步扩大商业车险自主定价系数浮动范围等有关事项的通知》，明确商业车险自主定价系数的浮动范围扩大为0.5—1.5，进一步扩大财产保险公司定价自主权。各地将根据车险市场情况稳妥确定政策执行时间，原则上不晚于今年6月1日。</p>\n<p>\n	　　不少车主关心，自主定价系数是如何影响车险价格的？业内人士介绍，通常来说，每辆车的保费在基准保费基础上享有一定程度的折扣，而折扣率与自主定价系数有关。扩大商车险自主定价系数浮动范围，是以渐进方式深化车险综合改革，将定价权更多下放给保险公司，使保险公司获得更灵活的费率调整空间，以健全市场化条款费率形成机制，使车险定价更加精细。</p>\n<p>\n	　　中国社科院保险与经济发展研究中心副主任王向楠说，对商车险消费者来说，此次调整后，可能享受到的车险保费优惠力度将更大，而价格上浮的空间也将提高。“驾驶行为良好的车主，保险公司有望给予更大程度的保费优惠。同时，频繁违章、出险的客户，则面临保费上涨压力。通过价格杠杆，有利于助力规范车主驾驶行为。”</p>\n<p>\n	　　费率浮动范围扩大后，如何防范商车险无序底价竞争？《通知》提出，各财产保险公司要严格执行车险各项监管要求，提高费率厘定的科学性，按照监管规定做好条款费率备案工作，优化和保障车险产品供给，提升车险承保理赔服务水平。同时，保险业协会将依法合规做好行业自律，防止垄断和非理性竞争，优化行业服务标准。中国银行保险信息技术管理有限公司将升级车险信息平台，为财产保险行业提供数据和系统支持，做好费率监测与预警。</p>\n<p>\n	　　一家财险公司的有关负责人表示，从行业当前情况来看，用到自主定价系数下限0.65的保单比例并不高。自主定价系数下限调整将对财险公司风险定价能力和管理能力提出更高要求，促进市场良性竞争。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月13日 18 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0213/c14739-32622478.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (71,'2023-02-19 09:34:48.898661','2023-02-19 09:34:48.898661','劳动，学生成长的必修课','<div class=\"artDet\">\n                        \n                        <p>\n	　　新学期伊始，劳动课迎来了第一个“春耕”。</p>\n<p>\n	　　随着《义务教育劳动课程标准（2022年版）》的稳步落实，劳动课已成为各中小学不可缺少的独立课程之一。锄地栽苗、下厨烹饪、清洁卫生……许多学校将体验劳动作为“开学第一课”，让孩子们在汗水与欢笑中开启校园生活。</p>\n<p>\n	　　说起劳动课，70后、80后感触颇多。70后的劳动课，多在田地里：麦熟时，学校放起麦假，孩子回到田野帮父母收割麦子；80后的劳动课，多在课堂上：竹木扎框、糊上棉纸，自制风筝放飞在课后的欢声笑语里。“是时候补上这堂课了”“劳动课很有必要”……各大社交平台上，此起彼伏的支持与赞扬是主流意见，传递着一个共识：劳动是光荣的，也是难忘的。无论是生火做饭，或是缝衣织布，劳动在供给生活必需的同时，更让人在其中找到彼此需要的生命联结。</p>\n<p>\n	　　开好开足劳动课的呼唤，深切期待着多方联袂参与的回应。学校或将课堂搬到田间地头，或请非遗项目入校园，让学生在躬身稼穑、体验民俗之间，感受生活味、烟火气；企业、工厂等社会力量，利用提供标准化场地、定制化器具等方式，拓展校方的有限场景资源；家长通过日常交流的言传、共担家务的身教，营造热爱劳动、崇尚劳动的家风家教。如此，方能打通书本与现实、学校与社会的罅隙，把劳动课牢固地嵌入教育体系的应有位置。</p>\n<p>\n	　　劳动课虽无范式，评价更需有标准。“老师布置任务、家长完成作业、学生打卡拍照”的网络留言，折射出家长群体对劳动课该“怎么教、怎么考”的现实思量。无论是增加专业教师培养与引进之实举，还是广开课程考核、综合评价之言路，都是合理衡量学生全面发展素质的题中应有之义，也需要被给予充分的时间、耐心去摸索与确立。但从深处看，一种更健康、更可持续的评价体系的建立，更有赖于社会心态的转变。</p>\n<p>\n	　　其实，不妨把目光放得更辽阔些。劳动教育并非朝夕之得、尺寸之功，更需日将月就、久久为功。“劳则思，思则善心生”，劳动的过程本身即是一种教育。父母们更不必苛求孩子熟稔镰刀的轻重、农事的早晚，而是让他们在身体力行的劳动中，去懂得敬惜物力，去强健自立自强之精神。这些精神上的隐性收获，更能让孩子在思想、道德与品格上获得长久的滋养。</p>\n<p>\n	　　劳动是质朴的，亦是多彩的，它无比深刻地与童年交织缠绕在一起，成为人生最珍贵的回忆与永不逝去的底色。而劳动课的回归与弹性扩容，恰恰寄望于更先进的教育理念、合理的学段目标、规范的课程设计，去铺设抵达寓教于劳、五育并举的理想之路。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月09日 14 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0209/c14739-32620454.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (74,'2023-02-19 09:34:48.917292','2023-02-19 09:34:48.917292','战鼓催春丨军营庙会闹新春','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"/NMediaFile/2023/0124/MAIN202301241027000544695298509.jpg\" width=\"600\" height=\"200\" /></p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"官兵用手写春联祝福新春。归王彬摄\" src=\"/NMediaFile/2023/0124/MAIN202301240326000264855145441.jpg\" width=\"600\" height=\"394\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	武警贵州总队机动支队官兵齐贺新春。归王彬摄</p>\n<p style=\"text-indent: 2em;\">\n	春节期间，武警贵州总队机动支队营区内张灯结彩，迎新春游园活动现场人头攒动、热闹非凡。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"舞龙舞狮活动现场。吴哲明摄\" src=\"/NMediaFile/2023/0124/MAIN202301240326000476314126662.jpg\" width=\"600\" height=\"386\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	舞龙舞狮活动。吴哲明摄</p>\n<p style=\"text-indent: 2em;\">\n	伴随着喜庆的锣鼓声，一条五彩祥龙从方队中间穿梭而出，巨龙在官兵们手中舞动得活灵活现，形态变化万千。游玩的官兵纷纷驻足观看，欢呼声、掌声汇聚成一片欢快的海洋。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"官兵开展写春联活动。归王彬摄\" src=\"/NMediaFile/2023/0124/MAIN202301240327000050341669262.jpg\" width=\"600\" height=\"394\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	官兵们手写春联。归王彬摄</p>\n<p style=\"text-indent: 2em;\">\n	“喜迎新春”“建功柏腊”……在“写春联、送福字”活动现场，官兵们一笔一划在红纸上勾勒出充满军营特色的书画作品。</p>\n<p style=\"text-indent: 2em;\">\n	“心中吹过南湖春风，我们走来一路忠诚……”在“喜迎新春”舞台上，官兵们在激扬的旋律中引吭高歌，一曲《一路忠诚》气势磅礴、震撼人心。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"官兵开展剪纸活动。归王彬摄\" src=\"/NMediaFile/2023/0124/MAIN202301240327000220475783536.jpg\" width=\"600\" height=\"395\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	剪纸活动。归王彬摄</p>\n<p style=\"text-indent: 2em;\">\n	“竹板响，咱登场，说说柏腊山人的光荣与梦想……”机动五中队战士唐芝不甘示弱，他和战友们表演的快板《点赞二二唱响二三》唱出一年来全体官兵砥砺奋进、勇毅前行的精神风貌。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"官兵开展视频拜年活动。归王彬摄\" src=\"/NMediaFile/2023/0124/MAIN202301240327000465451768744.jpg\" width=\"600\" height=\"396\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	视频拜年活动。归王彬摄</p>\n<p style=\"text-indent: 2em;\">\n	“爸、妈，今天我和战友们一起给你们拜个年，我在部队一切安好，请你们放心……” 在集体视频拜年活动中，官兵们手捧对联、灯笼和福字，与远方的家人视频连线，小小的屏幕承载着浓浓思念、传递着新年祝福。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"官兵开展文化活动。吴哲明摄\" src=\"/NMediaFile/2023/0124/MAIN202301240327000589591906486.jpg\" width=\"600\" height=\"397\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	军营文化活动。吴哲明摄</p>\n<p style=\"text-indent: 2em;\">\n	“春节是万家团圆的日子，但作为军人，坚守战位、守护一方灯火，是我们义不容辞的责任。”该支队领导表示，为让官兵们过上一个愉快祥和的新春佳节，他们还开展了贴近官兵实际、符合官兵“口味”的军营文化活动。</p>\n<p style=\"text-indent: 2em;\">\n	刚下队一个多月的新兵胡同亮说：“这是我在部队过的第一个年，虽然也想跟家人团聚，但军营中热热闹闹的游园会、和谐融洽的战友情，同样让我们感到很充实、很开心，也感受到了部队大家庭的温暖。”<span style=\"text-indent: 2em;\">（黄子娟、吴哲明、归王彬摄影报道）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：陈羽、邓志慧)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            ','人民网','http://military.people.com.cn/n1/2023/0124/c1011-32611643.html',7);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (90,'2023-02-19 09:34:49.002200','2023-02-19 09:34:49.002200','第36届非盟峰会聚焦非洲大陆自贸区建设等议题','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-align:center;\">\n	<a href=\"/n1/2023/0219/c1002-32626692-2.html\"><img alt=\"\" src=\"/mediafile/pic/20230219/80/6954025085141622136.jpg\" /></a></p>\n<p align=\"center\" class=\"pictext\">\n	<span style=\"font-family:楷体;\">　　这是2月18日在埃塞俄比亚亚的斯亚贝巴拍摄的非洲联盟第36届首脑会议现场。</span></p>\n<p>\n	　　非洲联盟（非盟）第36届首脑会议18日在位于埃塞俄比亚首都亚的斯亚贝巴的非盟总部开幕，加快非洲大陆自由贸易区建设、在疫情和经济社会危机下促进发展、确保非洲和平与安全以及增强非洲在国际多边机构中的代表性，成为峰会的中心议题。</p>\n<p>\n	　　新华社记者 汪平 摄</p>\n<div class=\"zdfy clearfix\"><a href=\"/n1/2023/0219/c1002-32626692.html\">【1】</a><a href=\"/n1/2023/0219/c1002-32626692-2.html\">【2】</a><a href=\"/n1/2023/0219/c1002-32626692-3.html\">【3】</a></div><center><table border=\"0\" align=\"center\" width=\"40%\"><tr><td width=\"50%\" align=\"center\"><a href=\"/n1/2023/0219/c1002-32626692-2.html\"><img src=\"/img/next_page.jpg\" border=\"0\" /></a></td></tr></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：艾雯、燕勐)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0219/c1002-32626692.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (91,'2023-02-19 09:34:49.012038','2023-02-19 09:34:49.012038','24小时不间断搜救 为那一丝生命的希望','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p align=\"center\">\n	<img alt=\"\" src=\"/mediafile/pic/20230218/30/2760712921373870566.jpg\" /></p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"/mediafile/pic/20230218/77/3320677419246751237.jpg\" /></p>\n<p style=\"text-indent: 2em;\">\n	2月16日，中国赴土耳其开展国际救援的82名队员从阿达纳省阿达纳国际机场乘坐国航包机回国。从2月8日中国救援队抵达土耳其地震重灾区哈塔伊省的哈塔伊市，直到目送着他们踏上归国的旅程，近10天的时间里，我跟随着中国救援队从一个救援现场到另一个救援现场，记录了他们成功营救一个又一个幸存者，更见证了中国救援队为了一丝生命的希望而不眠不休、24小时不间断搜救的感人故事。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"2月16日，中国救援队队员在土耳其阿达纳国际机场乘坐国航包机回国。\" src=\"/mediafile/pic/20230218/75/9977119346192223899.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月16日，中国救援队队员在土耳其阿达纳国际机场乘坐国航包机回国。</p>\n<p style=\"text-indent: 2em;\">\n	2月6日凌晨，土耳其南部靠近叙利亚边境地区发生强烈地震，造成土耳其和叙利亚两国重大人员伤亡和财产损失。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"　2月7日，人们在土耳其卡赫拉曼马拉什省的埃尔比斯坦地区展开救援工作。\" src=\"/mediafile/pic/20230218/91/4126452067841023031.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月7日，人们在土耳其卡赫拉曼马拉什省的埃尔比斯坦地区展开救援工作。</p>\n<p style=\"text-indent: 2em;\">\n	2月8日，中国救援队抵达土耳其地震重灾区哈塔伊省安塔基亚市的哈塔伊体育馆外，搭建指挥、通信、医疗、库房、住宿等设施，建立了救援行动营区。</p>\n<p style=\"text-indent: 2em;\">\n	就在中国救援队抵达的前一天，正在地震重灾区埃尔比斯坦地区采访的我和同事连夜出发，经过近12小时的奔波，抵达哈塔伊，并于8日下午2点左右与中国救援队会合。</p>\n<p style=\"text-indent: 2em;\">\n	从这一天开始，我每天跟随中国救援队开展救援行动报道，深刻地感受了他们“生命高于一切”的敬业精神，也近距离看到他们的团结协作、吃苦耐劳。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"　2月8日，中国救援队队员在土耳其哈塔伊的一处地震废墟搜救。\" src=\"/mediafile/pic/20230218/73/11323362147938671785.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月8日，中国救援队队员在土耳其哈塔伊的一处地震废墟搜救。</p>\n<p style=\"text-indent: 2em;\">\n	中国救援队坚持生命至上的理念，每到一处地震中受损的建筑物，都立刻投入救援工作中。不少人前来诉说自己亲人被倒塌建筑物掩埋时的情景，描述房屋结构，急切盼望着救援队能将亲人从废墟中救出。中国救援队耐心认真对待每一位前来诉说的人，争分夺秒，每天24小时不间断进行各种分工作业，尽量加快救援速度。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"2月10日，中国救援队队员在土耳其哈塔伊省安塔基亚市的驻扎营区参加升旗仪式。\" src=\"/mediafile/pic/20230218/93/4743098652598801345.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月10日，中国救援队队员在土耳其哈塔伊省安塔基亚市的驻扎营区参加升旗仪式。</p>\n<p style=\"text-indent: 2em;\">\n	中国救援队自8日到达当地灾区后，按照土方给出的范围，到地震重灾区哈塔伊省执行搜救任务。在搜索中，救援队努力搜索生命迹象，不放弃一丝希望。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"2月9日，在土耳其哈塔伊省安塔基亚市，中国救援队与土耳其救援队转移一名救出的女性幸存者。\" src=\"/mediafile/pic/20230218/87/16551411100862845791.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月9日，在土耳其哈塔伊省安塔基亚市，中国救援队与土耳其救援队转移一名救出的女性幸存者。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"2月9日晚，在土耳其哈塔伊省安塔基亚市，中国救援队与土耳其救援队合力展开营救。\" src=\"/mediafile/pic/20230218/96/17147178441752974524.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月9日晚，在土耳其哈塔伊省安塔基亚市，中国救援队与土耳其救援队合力展开营救。</p>\n<p style=\"text-indent: 2em;\">\n	2月9日这一天，余震不断，在经过了20多个小时的连续救援后，队员们终于在晚上8点多返回营地进行轮休。他们的体力几乎耗尽，有的队员累到双手颤抖一度无法控制。然而这一切的付出都是值得的。中国救援队成功营救出3名被困废墟的幸存者，其中一名女性被困时间超过了80小时。绝不放弃每一个生命，绝不放松每一次救援，这是中国救援队对生命最大的尊重，对使命最真的理解。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"　2月12日，在土耳其哈塔伊省安塔基亚市，中国救援队转移一名救出的幸存者。\" src=\"/mediafile/pic/20230218/82/5302900990004302882.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月12日，在土耳其哈塔伊省安塔基亚市，中国救援队转移一名救出的幸存者。</p>\n<p style=\"text-indent: 2em;\">\n	每次得知被困人员消息后，救援队会第一时间到达现场，对倒塌建筑物进行安全评估并进行实地勘查。随后他们利用随身携带的雷达生命探测仪、蛇眼探测器等设备，进一步确认被困人员方位及生命体征。这些过程需要极其专业的知识和实践操作。每次看到探测队员不顾自身安全，在现场不停地更换位置、操作设备，争取早日找到被困人员，这样的工作节奏让我感到无比敬佩。有些现场环境十分恶劣，周围危楼多，且实施救援的建筑物也存在安全隐患。最令人担忧的是，突然发生的余震，让在场人员措手不及。我从救援队队员身上，看到了不怕苦不怕累、随时为祖国和人民奋斗的坚强意志。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"　2月12日，在土耳其哈塔伊省安塔基亚市，中国救援队转移一名救出的幸存者。\" src=\"/mediafile/pic/20230218/11/14515883591067704635.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月12日，在土耳其哈塔伊省安塔基亚市，中国救援队转移一名救出的幸存者。</p>\n<p style=\"text-indent: 2em;\">\n	我在现场同样见证了中土两国救援队相互信任、相互协同、联合救援的感人场景。虽然语言不通，但他们干起活儿来不需要翻译，通过肢体语言，马上就知道各自想说和想做的事。2月9日，正是在中土两国救援队通力合作之下，他们从一栋8层倒塌房屋的废墟中营救出一名孕妇，这样的感人故事令人振奋。</p>\n<p style=\"text-indent: 2em;\">\n	经过不断努力，中土两国救援队成功搜救的幸存者人数不断上升。两国救援队偶尔碰到一起，会相互拥抱、相互鼓励。这从另一个角度证明了中土两国的友谊。中国救援队在重灾区参与救援期间，无论是土耳其救援队还是普通百姓，见到了中国救援队队长和队员都会说:“感谢中国，感谢中国救援队。我们不会忘记中国朋友在困难时期伸出的手。”</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"2月11日，包括中国救援队队员在内的救援人员在土耳其哈塔伊省安塔基亚市参与搜救。\" src=\"/mediafile/pic/20230218/17/7859472515128171941.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月11日，包括中国救援队队员在内的救援人员在土耳其哈塔伊省安塔基亚市参与搜救。</p>\n<p style=\"text-indent: 2em;\">\n	救援队队员为了救出被困人员，常常钻进狭窄的救援洞口，为节省时间，他们用自己的身体挡住落石或土，一点一点清理障碍物。中国救援队雷厉风行的职业素养和不怕苦不怕累的精神也感动了当地百姓，他们都一致称赞中国救援队队员好样的。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"　 2月9日，中国救援队队员在土耳其哈塔伊省安塔基亚市开展救援工作。\" src=\"/mediafile/pic/20230218/6/7302335965205764678.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月9日，中国救援队队员在土耳其哈塔伊省安塔基亚市开展救援工作。</p>\n<p style=\"text-indent: 2em;\">\n	我除了每天跟随救援队采访拍摄外，偶尔也为救援队当翻译。采访回来后，就在营地帐篷里编发稿件，每天睡眠不足3、4小时。因为当地温差大，大家早晚围着火堆烤火取暖，时间长了，会一起围在火堆旁聊天，一天的乏累不知不觉就消失了。与救援队聊天时，大家最常说的话就是，我们代表国家来到土耳其参加救援。他们展现出中国人民热爱和平以及大灾大难时一方有难、八方支援的精神。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"2月12日，中国救援队队员在土耳其哈塔伊省安塔基亚市一处地震废墟开展救援工作。\" src=\"/mediafile/pic/20230218/0/13922781994444135128.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月12日，中国救援队队员在土耳其哈塔伊省安塔基亚市一处地震废墟开展救援工作。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"2月12日，中国救援队队员在土耳其哈塔伊省安塔基亚市一处地震废墟开展救援工作。\" src=\"/mediafile/pic/20230218/48/8216372290425456756.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月12日，中国救援队队员在土耳其哈塔伊省安塔基亚市一处地震废墟开展救援工作。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"月14日，在土耳其哈塔伊省安塔基亚市，中国救援队队员在地震废墟中搜救。\" src=\"/mediafile/pic/20230218/26/15449273870175308010.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月14日，在土耳其哈塔伊省安塔基亚市，中国救援队队员在地震废墟中搜救。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"2月13日，在位于土耳其哈塔伊省哈塔伊体育馆外的中国救援队营地，医生为救援队成员检查身体。\" src=\"/mediafile/pic/20230218/60/15673838764407664388.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月13日，在位于土耳其哈塔伊省哈塔伊体育馆外的中国救援队营地，医生为救援队成员检查身体。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"2月14日，中国救援队与中国香港特区救援队联合编组在土耳其哈塔伊省安塔基亚市开展救援工作。\" src=\"/mediafile/pic/20230218/38/9071405218124410778.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月14日，中国救援队与中国香港特区救援队联合编组在土耳其哈塔伊省安塔基亚市开展救援工作。</p>\n<p style=\"text-indent: 2em;\">\n	当地时间2月16日12时多一点儿，中国救援队从哈塔伊体育场营地撤离。在撤离营地前，救援队将食品、饮用水、部分帐篷等生活物资和全部医疗物资留给灾区，作为对灾区民众的又一份心意。随后，中国救援队与前来送别的土方官员和中国驻土耳其大使馆人员告别，离开哈塔伊体育场营地，经由阿达纳国际机场返回北京。</p>\n<p style=\"text-indent: 2em;\">\n	在本次执行救援任务期间，中国救援队累计共派出救援人员21批次、308人次，搜索评估建筑87栋，排查总面积超过70万平方米，共营救被困人员6人，搜寻遇难者11人。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"2月16日，在土耳其哈塔伊省安塔基亚市，中国救援队队员搬运设备。\" src=\"/mediafile/pic/20230218/21/8476185412348487801.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月16日，在土耳其哈塔伊省安塔基亚市，中国救援队队员搬运设备。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"2月16日，中国救援队队员在土耳其阿达纳国际机场办理乘机手续。\" src=\"/mediafile/pic/20230218/7/15114579607288490215.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	2月16日，中国救援队队员在土耳其阿达纳国际机场办理乘机手续。</p>\n<p style=\"text-indent: 2em;\">\n	在阿达纳国际机场，看着中国救援队像他们到来的时候一样整齐划一地列队登上飞机，我的脑海中不断回忆起这9天时间里，我见证中国救援队争分夺秒、不眠不休地工作，只为了那废墟下面一丝生命的希望，也见证了他们创造的一个又一个生命的奇迹。</p>\n<p style=\"text-indent: 2em;\">\n	中国救援队乘坐的飞机消失在茫茫夜色中，但我相信，我脚下的这片土地以及土耳其人民会永远记得，在他们最需要帮助的时候，有这样一群来自万里之外的中国朋友，来到了他们的身边，和他们一起并肩战斗。</p>\n<p style=\"text-indent: 2em;\">\n	土耳其的天空也会记得，那迎风飘扬的中国救援队的旗帜。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"这是2月10日在土耳其哈塔伊省安塔基亚市拍摄的中国救援队驻扎营区。\" src=\"/mediafile/pic/20230218/94/5846993377652777470.jpg\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	这是2月10日在土耳其哈塔伊省安塔基亚市拍摄的中国救援队驻扎营区。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"/mediafile/pic/20230218/76/11249079819601445500.png\" /></p>\n<p class=\"pictext\" style=\"text-indent: 2em;\">\n	策划：兰红光</p>\n<p style=\"text-indent: 2em;\">\n	统筹：费茂华、周大庆、刘金海</p>\n<p style=\"text-indent: 2em;\">\n	记者：沙达提</p>\n<p style=\"text-indent: 2em;\">\n	编辑：刘欣、李梦馨、张铖</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：于洋、刘叶婷)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0218/c1002-32626457.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (95,'2023-02-19 09:34:49.032089','2023-02-19 09:34:49.032089','王毅就飞艇事件向美方表明立场','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-indent: 2em;\">\n	新华社德国慕尼黑2月18日电 当地时间2月18日，中共中央政治局委员、中央外事工作委员会办公室主任王毅在出席慕尼黑安全会议期间，应美方请求，同美国国务卿布林肯进行非正式接触。</p>\n<p style=\"text-indent: 2em;\">\n	王毅表明了中方在所谓飞艇事件上的严正立场，要求美方改弦更张，正视并解决滥用武力给中美关系造成的损害。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：卫嘉、白宇)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0219/c1002-32626594.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (96,'2023-02-19 09:34:49.032089','2023-02-19 09:34:49.032089','德国总理朔尔茨会见王毅','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　本报慕尼黑2月17日电  （记者张慧中）当地时间2023年2月17日，德国总理朔尔茨在慕尼黑会见出席第五十九届慕尼黑安全会议的中共中央政治局委员、中央外事工作委员会办公室主任王毅。</p><p>　　王毅向朔尔茨转达中国领导人的亲切问候。王毅表示，中国已成功克服疫情影响，经济呈现强劲复苏。中方愿全面重启同德国及欧洲的各领域交往，扩大互利合作，增进相互了解。双方可以积极筹备新一轮政府磋商，规划两国关系发展蓝图，使中德合作继续走在世界前列。中德双方应共同支持多边主义和自由贸易，反对脱钩断链，切实维护全球产供链稳定。</p><p>　　朔尔茨表示，很高兴得知中国成功战胜疫情，赞同尽快重启各领域合作，尽早启动下一轮政府磋商。德方将坚定发展同中国的经贸关系，反对任何形式的“脱钩”。强劲的德中关系和经贸互利合作有利于世界的稳定和繁荣。</p><p>　　双方就乌克兰问题深入交换了看法。王毅表示，中德都是独立自主的大国，对维护世界和平、应对全球挑战肩负着共同责任。中国始终站在和平一边，致力于劝和促谈，主张尽快停火止战。希望德方为推动局势降温发挥建设性作用。</p><p>　　当日，王毅还会见了德国外长贝尔伯克、巴基斯坦外长比拉瓦尔。</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月19日   03 版）</span>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：卫嘉、白宇)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0219/c1002-32626577.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (97,'2023-02-19 09:34:49.042327','2023-02-19 09:34:49.042327','朝鲜发射一枚洲际弹道导弹','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-indent: 2em;\">\n	新华社首尔2月19日电 据朝中社19日报道，朝鲜18日下午在平壤国际机场高角度发射一枚“火星-15”型洲际弹道导弹。</p>\n<p style=\"text-indent: 2em;\">\n	报道说，此次发射系按照18日凌晨的紧急指示以及当天上午朝鲜劳动党中央军事委员会委员长金正恩下达的命令书紧急组织进行的。导弹精确打击了位于朝鲜东部公海的目标水域。</p>\n<p style=\"text-indent: 2em;\">\n	朝中社援引命令书内容报道说，此次突袭发射旨在再次确认和验证武器系统的可靠性，同时凸显朝鲜核武备战状态。</p>\n<p style=\"text-indent: 2em;\">\n	另据朝中社报道，朝鲜劳动党中央委员会副部长金与正19日发表谈话，抨击美国和韩国打着应对威胁的招牌大肆叫嚣扩张遏制力。金与正警告，朝方将密切关注敌人的一举一动，并将做出“与之相应的、强有力的压倒性应对”。</p>\n<p style=\"text-indent: 2em;\">\n	日本防卫省18日说，当天下午朝鲜向东发射一枚弹道导弹，推测落入北海道渡岛以西海域。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：艾雯、燕勐)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0219/c1002-32626749.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (98,'2023-02-19 09:34:49.042327','2023-02-19 09:34:49.042327','联合国宣布拨款2.5亿美元帮助脆弱人群','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>\n	　　新华社联合国2月18日电（记者王建刚）联合国秘书长古特雷斯18日宣布从联合国中央应急基金中拨款2.5亿美元，用于帮助19个国家最脆弱的人群应对饥荒等多重危机。</p>\n<p>\n	　　古特雷斯在联合国当天发布的新闻稿中说：“长期存在的发展问题往往与气候混乱、经济冲击和暴力冲突交织在一起，演变成人道主义灾难的旋风。目前，全世界有3.39亿人需要人道主义援助，比去年增加了25%以上。”</p>\n<p>\n	　　新闻稿说，此次拨款将用于帮助阿富汗、布基纳法索、海地、马里、尼日利亚、索马里、南苏丹和也门8个国家2000多万正处于饥荒边缘的民众，在乍得、哥伦比亚、刚果（金）、厄立特里亚、埃塞俄比亚、洪都拉斯、肯尼亚、黎巴嫩、马达加斯加、巴基斯坦和苏丹加强人道主义行动，以应对粮食不安全，以及气候变化等长期危机的影响。</p>\n<p>\n	　　联合国负责人道主义事务的副秘书长格里菲思表示，这笔资金将有助于及早采取行动，在包括饥荒在内的危机形成前采取行动。</p>\n<p>\n	　　新闻稿说，2022年联合国及其合作伙伴向近1.6亿人提供了援助，但人道主义需求的激增迅速超过了应对能力。联合国呼吁捐助者向中央应急基金捐款，以帮助更多有需求的人。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：白宇、胡永秋)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0219/c1002-32626567.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (99,'2023-02-19 09:34:49.052472','2023-02-19 09:34:49.052472','土耳其官员：地震已在土造成逾4万人死亡','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>\n	　　新华社安卡拉2月18日电（记者熊思浩）土耳其灾害与应急管理署署长尤努斯·塞泽尔18日下午说，6日发生在土耳其南部的强烈地震已造成该国40642人死亡，搜救工作将于当晚基本结束。</p>\n<p>\n	　　塞泽尔在记者会上说，南部哈塔伊省是此次地震死亡人数最多的省份，目前搜救工作主要集中在该省，灾害与应急管理署派出的近1.3万名搜救人员仍在争分夺秒搜寻被困民众。他说，迄今震区内共有43万人被疏散至其他安全省份。</p>\n<p>\n	　　土耳其司法部长博兹达18日对媒体说，到目前为止，共有245名对震区劣质建筑负有直接责任的承包商和工程师被起诉，另有65名嫌疑人被羁押待审。</p>\n<p>\n	　　根据土耳其政府发布的最新统计数据，地震灾区有超过8.4万栋建筑倒塌、严重受损或急需拆除。建筑质量因而受到各界质疑。</p>\n<p>\n	　　此外，据土耳其阿纳多卢通讯社报道，救援人员18日在哈塔伊省安塔基亚市救出3名幸存者，其中包括1名儿童，获救时他们已被困296小时。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：白宇、胡永秋)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0219/c1002-32626566.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (100,'2023-02-19 09:34:49.052472','2023-02-19 09:34:49.052472','以色列发动导弹袭击造成叙方5死15伤','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-indent: 2em;\">\n	新华社大马士革2月19日电 叙利亚军方19日发表声明说，当天凌晨以色列对叙首都大马士革及其周边地区发动导弹袭击，造成5人死亡，另有15人受伤。</p>\n<p style=\"text-indent: 2em;\">\n	声明说，当地时间19日0时22分许，以色列对“包括部分居民区在内的大马士革及其周边地点”发射多枚导弹，叙防空系统进行拦截并击落大部分来袭导弹。</p>\n<p style=\"text-indent: 2em;\">\n	声明说，袭击造成包括军事人员和平民在内的5人死亡、15人受伤，部分伤者伤势严重。袭击还造成多处民居和部分设施损毁。</p>\n<p style=\"text-indent: 2em;\">\n	2011年叙利亚内战爆发后，以军多次以打击伊朗军事设施为由空袭叙境内目标。叙利亚和伊朗政府均否认伊朗在叙有驻军。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：艾雯、燕勐)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0219/c1002-32626748.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (101,'2023-02-19 09:34:49.062461','2023-02-19 09:34:49.062461','国际观察：淡化处理“剧毒列车” 美国当…','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-indent: 2em;\">\n	火光冲天、黑烟翻滚、异味刺鼻……近日，美国俄亥俄州东巴勒斯坦城经历了一场“美国切尔诺贝利”之祸。2月3日，一列运载有毒化学物质的列车行使至当地时，其150节车厢中的38节脱离轨道并引发大火，另12节车厢遭烧毁。3天后，当地政府对列车中剩余有毒物质进行了“受控释放”。</p>\n<p style=\"text-indent: 2em;\">\n	事件引发大量包括氯乙烯、乙二醇丁醚、丙烯酸异辛酯和异丁烯在内的有毒化学品泄露。氯乙烯是一种致癌物质，对人的中枢神经系统、肝脏等系统都将产生影响。一战期间，氯乙烯燃烧产生的光气曾被广泛用作窒息剂。暴露在有毒的空气中，当地居民忧心忡忡。多名居民抱怨强烈的气味，出现头痛、恶心等症状，一些企业主和居民要求列车所属公司提供赔偿。</p>\n<p style=\"text-indent: 2em;\">\n	同时，危险物质泄漏后，直接流入雨水管道或被现场填埋，引发对环境破坏的担忧。据相关部门初步统计，东巴勒斯坦南部水道上发现了3500条死鱼。附近北利马的居民也说，农场母鸡在爆炸后不久突然死亡。</p>\n<p style=\"text-indent: 2em;\">\n	然而，无论是民众的担忧和诉求，还是自然环境的无声抗议，都没有得到应有的回应。“这是几十年来最致命的环境紧急情况之一，但却没有人谈论它。” 国际非营利组织气候枢机（Climate Cardinals）的创始人索菲亚·基安尼（Sophia Kianni）说。</p>\n<p style=\"text-indent: 2em;\">\n	面对批评质疑，美国当局极力淡化影响。美国地方官员坚持认为空气和饮用水目前都是安全的。美国国家环境保护局也同样表示，截至上周末，东巴勒斯坦城未发现空气中存在“任何程度的担忧”。面向东巴勒斯坦城居民的强制疏散令8日撤销，理由是样本监测显示当地空气和水体安全。2月13日，美国交通部长布蒂吉格出席全国县城联合会年会，对于受到民众普遍关注的列车脱轨事件，布蒂吉格只字未提，却对拜登政府主推的基础设施投资计划加以称赞。铁路工会的官员则称，他们早就警告过，由于铁路预算的削减妨害到安全措施，类似的事故可能发生。</p>\n<p style=\"text-indent: 2em;\">\n	在毒气泄露事件中，美国的新闻自由也受到怀疑。在俄亥俄州州长迈克·德瓦恩的新闻发布会现场，“新闻国家”(NewsNation)直播记者兰伯特被强行逮捕，原因是“非法入侵”。“我不认为这应该发生在美国任何地方，”兰伯特的同行维基拉说，“在危机当中，执法者居然如此蔑视媒体报道政府官员言行、政策的权利，这让我震惊。”</p>\n<p style=\"text-indent: 2em;\">\n	同一时间内，美国政府和媒体大肆炒作所谓“中国气球”等问题以转移视线。美媒对该事件“不闻不问”的态度，引发各界批判。议员马乔里·泰勒·格林（Marjorie Taylor Green）不止一次讽刺拜登政府，并表示针对“不明飞行物”的炒作都是对毒气泄漏事故的掩盖。俄罗斯《共青团真理报》指出，这一灾难性事件发生后的数天内，美国各媒体头版都在报道全美橄榄球比赛决赛的盛况。巴西网友奥斯瓦尔多（Osvaldo Júnior Yai）在社交媒体平台评论说，“没有在国际媒体上看到这事儿，美国只知道干涉其他国家的事情。这么严重的环境灾难没看见他们有报道。”有韩国网友留言称，美媒只对“间谍气球”进行大肆炒作，怀疑美国利用“间谍气球”来转移视线，不知道他们想掩盖什么。</p>\n<p style=\"text-indent: 2em;\">\n	（鲁扬、刘旭霞、蔡海若、李明琪、刘动、申玉环、许文金对此文亦有贡献）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：徐祥丽、常红)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0217/c1002-32625944.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (102,'2023-02-19 09:34:49.062461','2023-02-19 09:34:49.062461','国际观察：全球进入地震活跃期了吗？','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-indent: 2em;\">\n	北京时间2月6日9时17分，土耳其东南部发生7.8级地震，震源深度20公里。时隔9个多小时，当地再次发生7.8级地震，震源深度20公里。2次地震震中位置相距约96公里，位于东安纳托利亚断裂带上。时隔9小时在同一区域发生两次7级以上的强震在历史上不多见。震源机制解结果显示，两次地震均为走滑型破裂，与该地区历史地震震源机制基本一致。</p>\n<p style=\"text-indent: 2em;\">\n	研究表明，第1次7.8级地震产生的应力变化，对第2次7.8级地震有一定的触发作用。时隔9小时在同一区域发生两次7级以上的强震在历史上不多见，但并不特殊。接连发生两次强震的原因比较复杂，多发生在多断裂带或多断层的闭锁段，其地震活动特点是地震频度高，能量释放有明显起伏，衰减速度慢，余震活动持续时间较长。</p>\n<p style=\"text-indent: 2em;\">\n	土耳其位于欧亚板块、非洲板块和阿拉伯板块三大板块边界上，南边的阿拉伯板块像楔子一样插入土耳其，形成土耳其—伊朗高原。西面的非洲板块向北推挤，与北面的欧亚板块相抵。由于板块间相互作用，该地区构造活动极为强烈。1900年以来，土耳其共发生7级以上地震19次，其中，7.0-7.9级地震18次，8.0级以上地震1次。</p>\n<p style=\"text-indent: 2em;\">\n	全球主要有六大板块：太平洋板块、印度—澳大利亚板块、欧亚板块、美洲板块、非洲板块和南极洲板块。板块相互作用的边界是全球大地震发生最集中的区域。强震往往沿着板块构造边界呈带状分布，地震越大，分布的成带性越明显。据此，可以把全球地震分布划分为三大地震带，一是环太平洋地震带，二是欧亚地震带，三是在各大洋中绵延数万公里的海岭地震带，与海区大破裂带相依附。</p>\n<p style=\"text-indent: 2em;\">\n	地震的深度变化可以从几公里到700余公里，地震的深浅与地质构造也密切相关。环太平洋地震带是地球上地震活动最强烈的地带，全世界约80％的浅源地震、90％的中源地震和几乎所有的深源地震都集中在该带上。其所释放的地震能量约占全球地震能量的80％。</p>\n<p style=\"text-indent: 2em;\">\n	地震活动在时间上的分布是不均匀的，具有一定的活跃—平静特征。一段时间发生地震较多，震级较大，为地震活跃期；另一段时间发生地震较少，震级较小，为地震活动平静期。活跃期可能发生多次7级以上地震，甚至8级左右的巨大地震。地震活动周期可分为几百年的长周期和几十年的短周期；不同地震带活动周期也不尽相同，比较复杂。</p>\n<p style=\"text-indent: 2em;\">\n	从全球的地震活动特征来看，1900年以来，全球平均每年发生18次左右7级以上地震。从近几年的实际情况看，2019年以来，全球地震活动水平相对较低。2020年发生了10次7级以上地震；2021年发生了19次7级以上地震；2022年共发生7级以上地震7次。由于地震时间分布的不均匀性，全球这种年尺度的地震活动起伏，从大的时间尺度来看是正常的。</p>\n<p style=\"text-indent: 2em;\">\n	我国是世界上大陆地震最频繁、地震灾害最严重的国家之一。通过几十年地震预报探索和实践，我们对地震孕育发生的基本原理、规律有所认识，在一定条件下，能够对某些区域、某些类型的地震做出一定程度的预报，但距离真正认识地震本质，完全掌握地震活动规律还有很大的距离。因此，减轻地震灾害最有效的途径之一就是做好各类建筑物的抗震设防。</p>\n<p style=\"text-indent: 2em;\">\n	首先要做好建筑物的工程场地选址。这是建筑物抗震设防的基础和前提；其次是科学确定抗震设防要求。对一般建设工程而言，要严格执行《中国地震动参数区划图》规定的抗震设防要求。对重大工程、生命线工程和可能发生严重次生灾害的建设工程，必须依法进行地震安全性评价，并按地震安全性评价结果进行抗震设防；第三要严格按照抗震设计规范进行抗震设计；第四要强化监管，保证工程的施工质量，全面提高各类建筑物的工程质量。</p>\n<p style=\"text-indent: 2em;\">\n	此外，我们还需要提高自我的预防或自救能力，提高自身应对自然灾害的生存能力，学习更多关于如何在地震中逃生自救的知识，以便在地震来临时，保护自己、家人及朋友。</p>\n<p style=\"text-indent: 2em;\">\n	（作者为中国地震台网中心预报部高级工程师）  </p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：崔越、常红)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0216/c1002-32625062.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (103,'2023-02-19 09:34:49.072097','2023-02-19 09:34:49.072097','国际观察：中国绿色发展助力全球经济可持…','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-indent: 2em;\">\n	近日，商务部国际贸易经济合作研究院发布《中国绿色贸易发展报告（2022）》（简称“报告”），报告显示，2021年，中国绿色贸易额达到11610.9亿美元，超过欧盟成为全球第一大绿色贸易经济体。</p>\n<p style=\"text-indent: 2em;\">\n	中国绿色贸易为何能够实现规模持续增长？中国经济“含绿量”显著提升为疫后全球“绿色复苏”作出了怎样的贡献？</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"/NMediaFile/2023/0210/MAIN202302101521000241463066444.jpg\" width=\"600\" height=\"1066\" /></p>\n<p style=\"text-indent: 2em;\">\n	<strong>推进绿色低碳转型已成为全球共识</strong></p>\n<p style=\"text-indent: 2em;\">\n	当前，世界地缘政治危机不断、经济风险积聚，保护主义逆流横行，如何推动经济社会实现高质量、可持续发展，成为摆在各国面前的课题。</p>\n<p style=\"text-indent: 2em;\">\n	2月6日，联合国秘书长古特雷斯呼吁各国10年内将全球温室气体排放量减半，同时敦促较富裕国家至少要兑现去年在《联合国气候变化框架公约》第二十七次缔约方大会上做出的承诺。2月7日，欧盟竞争力部长非正式会议提出，欧盟应推动绿色转型和深化单一市场，并重点讨论了关于欧盟绿色工业计划的提案。</p>\n<p style=\"text-indent: 2em;\">\n	商务部研究院绿色经贸中心主任许英明在接受人民网记者专访时表示，推进绿色低碳转型已成为全球共识，越来越多的国家特别是大部分发达国家把低碳发展上升到国家战略高度，国际绿色贸易需求不断增强。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>国家战略指引打造绿色发展高地</strong></p>\n<p style=\"text-indent: 2em;\">\n	近年来，党和国家出台一系列战略部署和规划，为打造绿色发展高地提供良好政策环境。党的二十大报告中明确提出，推动经济社会发展绿色化、低碳化是实现高质量发展的关键环节。《中华人民共和国国民经济和社会发展第十四个五年规划和2035年远景目标纲要》提出：“坚持生态优先、绿色发展，推进资源总量管理、科学配置、全面节约、循环利用，协同推进经济高质量发展和生态环境高水平保护。”</p>\n<p style=\"text-indent: 2em;\">\n	在具体部署和政策安排指引下，中国经济社会发展全面绿色转型不断提速，环境技术创新硕果累累，清洁能源消费占比大幅提升。国务院新闻办公室发布的《新时代的中国绿色发展》白皮书数据显示，2011年至2020年，中国环境技术发明专利申请总量接近全球60%，是全球布局环境技术创新最积极的国家。截至2021年底，中国可再生能源发电装机突破10亿千瓦，其中水电、风电、光伏发电装机均超3亿千瓦，均居世界第一。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"/NMediaFile/2023/0210/MAIN202302101521000440684904696.jpg\" width=\"600\" height=\"1066\" /></p>\n<p style=\"text-indent: 2em;\">\n	许英明认为，中国在绿色产业基础、绿色流通体系、低碳技术、绿色发展国际合作等方面建设成效明显；绿色贸易市场主体持续发展壮大；绿色贸易产品，特别是环保科技类产品国际竞争力不断增强。2021年，中国是世界第一大环保科技类产品进出口国，进出口额达8752.3亿美元，同比增长23.6%，在全球占比达到17.6%。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>中国新能源产业为世界经济注入“绿色动能”</strong></p>\n<p style=\"text-indent: 2em;\">\n	中国积极推动经济社会发展绿色转型升级，为亟待复苏的世界经济带来增长新亮点。报告显示，2012-2021年，中国绿色贸易规模持续增长，总额从7934.2亿美元增长至11610.9亿美元，10年间贸易规模增长了146.3%；其中环境产品进出口规模不断扩大，年增长率达到4.3%。从全球视角看，中国在全球绿色贸易总额中的占比呈逐年扩大趋势，由2012年的9.9%提高至2021年的14.6%。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"/NMediaFile/2023/0210/MAIN202302101522000001498091072.jpg\" width=\"600\" height=\"1066\" /></p>\n<p style=\"text-indent: 2em;\">\n	随着可再生能源产业迅速发展，以电动汽车、光伏产品、锂电池的出口为代表，中国高技术、高附加值、引领绿色转型的产品成为出口新增长点。许英明指出，新能源产品国际贸易快速发展契合疫后全球经济“绿色复苏”的发展要求，不仅有利于中国的绿色低碳转型，也有利于绿色国际合作，共同应对全球气候变化。</p>\n<p style=\"text-indent: 2em;\">\n	目前，中国风电、光伏发电等清洁能源设备生产规模居世界第一，多晶硅、硅片、电池和组件占全球产量的70%以上。2月2日，商务部对外贸易司司长李兴乾在国务院新闻办公室新闻发布会上表示，2022年，中国电动汽车出口增长了131.8%，光伏产品增长了67.8%，锂电池增长了86.7%。</p>\n<p style=\"text-indent: 2em;\">\n	许英明认为，从规模看，新能源产品出口有成为我国“稳外贸”主打产品的潜力和空间。未来，应继续支持企业进行绿色设计和制造，构建绿色技术支撑体系和供应链，并采用国际先进环保标准，鼓励获得节能、低碳等绿色产品认证的产品加大出口，推动高技术、高附加值的机电产品和高新技术产品出口。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>相关链接</strong></p>\n<p style=\"text-indent: 2em;\">\n	<a href=\"http://world.people.com.cn/n1/2023/0209/c1002-32620733.html\" target=\"_blank\"><strong>海报：中国经济“含绿量”显著提升</strong></a></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：崔译戈、杨牧)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0210/c1002-32621507.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (104,'2023-02-19 09:34:49.077108','2023-02-19 09:34:49.077108','国际观察：国际社会呼吁美国立即停止对叙…','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-indent: 2em;\">\n	8日的早晨，叙利亚首都大马士革飘下了雪花，气温降到零度以下。严寒之下，更让人沉郁的是一场瞬间夺去无数人生命的大地震。由于美国等西方国家对叙实施的封锁和经济制裁，让本已严峻的人道救援形势雪上加霜。</p>\n<p style=\"text-indent: 2em;\">\n	叙利亚卫生部部长哈桑8日发表声明说，强震在叙利亚已造成1262人死亡和2285人受伤，伤亡人数可能进一步增加。由于美国拒绝解除对叙利亚的制裁，大多数国家无法向叙利亚提供人道主义援助。许多国际媒体和组织纷纷呼吁在巨大的自然灾难面前，人类应该团结一致，共同应对挑战。</p>\n<p style=\"text-indent: 2em;\">\n	近日，一个名为“Stop Sanctions On Syria”（停止制裁叙利亚）的话题在社交媒体上被大量转发，叙利亚民众和救援机构呼吁西方国家尽快解除对叙制裁，为国际救援提供便利。</p>\n<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"社交媒体上，网友留言并呼吁解除对叙利亚的制裁。网络截图\" src=\"/NMediaFile/2023/0209/MAIN202302090952000544254071967.jpg\" width=\"650\" height=\"488\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	社交媒体上，网友留言并呼吁解除对叙利亚的制裁。网络截图</p>\n<p style=\"text-indent: 2em;\">\n	网友S.说：“叙利亚几十年来一直处于战争状态，因为经济制裁，没有国际援助到达他们手中。这是不人道的，这些无辜的人们究竟做了什么，请把政治放在一边，帮助叙利亚，为他们祈祷。”</p>\n<p style=\"text-indent: 2em;\">\n	网友Keke说：“我们受够了。是时候改变了，改变必须发生。他们不能继续这样生活下去。请听听他们的声音！为他们发声。”</p>\n<p style=\"text-indent: 2em;\">\n	联合国秘书长古特雷斯强调，让我们团结一致，帮助所有受这场灾难影响的人。其中许多人已经处于急需人道主义援助的状态。</p>\n<p style=\"text-indent: 2em;\">\n	美联社8日报道称，在发生毁灭性地震之前，向饱受战争蹂躏的叙利亚各地提供援助也充满了令人生畏的政治和后勤挑战。地震灾难之后，这些障碍只会成倍增加。</p>\n<p style=\"text-indent: 2em;\">\n	美国智库昆西负责任国家政策研究所8日刊文指出，制裁严重阻碍了向叙利亚提供救援和帮助他们重建家园。美国应迅速采取行动，尽可能的暂停或取消制裁，以便援助机构和其他政府能够更有效地解决叙利亚人民的困境。</p>\n<p style=\"text-indent: 2em;\">\n	总部位于美国的美国一阿拉伯反对种族歧视委员会在6日呼吁美国“立即解除对叙利亚的制裁”，以便向叙利亚提供援助。</p>\n<p style=\"text-indent: 2em;\">\n	沙特阿拉比亚电视台称，在援助中受到歧视和被边缘化的叙利亚，可能会酿成更大的人道危机。</p>\n<p style=\"text-indent: 2em;\">\n	美国自诩为人权卫士，然而所作所为却大相径庭。多年来，美国肆意对叙实施制裁，造成叙利亚经济凋敝，平民生活困苦不堪。“大灾当前，美国应当放下地缘政治执念，立即解除对叙利亚的单边制裁，为人道主义救援敞开大门。”正如中国外交部发言人毛宁所呼吁的，请美国真正站在人道主义这边，立即停止对叙利亚的制裁。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：刘叶婷、杨牧)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0209/c1002-32620670.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (105,'2023-02-19 09:34:49.082179','2023-02-19 09:34:49.082179','国际观察：中国经济增长预期利好世界','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-indent: 2em;\">\n	全国国内旅游出游3.08亿人次、电影票房突破67亿元、揽投快递包裹量超7亿件……刚刚过去的春节期间，中华大地上展现出浓浓的烟火气和巨大流动性。春节消费也成为外界窥见中国经济韧性与活力的一扇窗口。</p>\n<p style=\"text-indent: 2em;\">\n	2月3日，国际货币基金组织（IMF）官网发表文章称，随着流动性和经济活动的上升，2023年中国经济增长将带动全球经济提振。文章指出，经济增长不仅对中国，对全世界都将是利好消息，因为“中国的经济每增长1%，其他国家的经济就增长大约0.3%”。文章预测，今年中国经济对全球增长贡献将达到1/4。此前，IMF在更新的《世界经济展望报告》中将中国2023年经济增长预期大幅上调至5.2%。</p>\n<p style=\"text-indent: 2em;\">\n	根据中国国家统计局公布的数据，2023年1月，中国制造业采购经理指数（PMI）从上年12月的47.0升至50.1。1月31日路透社发表文章指出，中国经济活动重振，国内订单和消费拉动生产上涨。同时，由于农历新年消费的助推，中国非制造业活动的反弹比经济学家预测的要更“坚决”，该指标从上年12月的41.6跃升至54.4。知名咨询机构仲量联行的经济学家庞溟在一份报告中写道：“PMI数据表明，人们对生产、经营和市场状况的信心有了大幅提高。”</p>\n<p style=\"text-indent: 2em;\">\n	外界认为，中国经济景气水平的回升将为全球经济平稳发展做出贡献。国际知名证券投资技术分析师戴若·顾比(Daryl Guppy) 2月5日在《南华早报》网站发表文章指出，全球化没有“死去”，以中国和数字经济为驱动的新的全球化正在兴起。文章指出，中国全面回归全球市场将对冲长期搅扰西方经济体的通胀压力。</p>\n<p style=\"text-indent: 2em;\">\n	《日本经济新闻》2月4日发表报道指出，中国相关政策优化带来的对旅游业和贸易重启的预期，正在拉升许多亚洲国家货币的价值，这同时也减轻了美国货币政策带来的压力。</p>\n<p style=\"text-indent: 2em;\">\n	1月30日，泰铢迎来去年3月以来最强劲的增值，对美元汇率上升到1美元兑32泰铢；新加坡元也升至1美元兑1.3新加坡元，到达2018年1月以来的最高点；韩元对美元汇率一度达到1美元兑1215韩元，这是去年4月以来最高水平。日经新闻指出，亚洲货币升值重要原因为中国游客的回归。日本第一生命经济研究所经济学家西滨彻（Toru Nishihama）说，“亚洲是从中国经济重新开放中受益最多的地区。”</p>\n<p style=\"text-indent: 2em;\">\n	更强劲的货币走势正在让资本回流到亚洲债券市场。资金流向监测和研究机构EPFR数据表明，1月份，2.64亿美元流入日本以外的亚洲债券基金，遏制了自去年2月以来连续11个月的资金外流趋势。在股票市场，1月份明晟(MSCI)中国指数上涨达到12%，超过了MSCI世界指数的6%增长。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：徐祥丽、杨牧)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0208/c1002-32619894.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (106,'2023-02-19 09:34:49.087185','2023-02-19 09:34:49.087185','国际观察：亚太不是地缘政治的角斗场','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-indent: 2em;\">\n	2023年1月11日，日美外长防长“2+2”会议召开。会后两国发表联合声明，宣布加强日美同盟，并恶意指责中国，炒作涉华问题。当前，百年未有之大变局与疫后复苏叠加，国际社会最需要的是团结合作，地区最希望的是和平稳定。然而，日美两国打着“自由开放的印太”旗号，实际上却打造了一个排他小圈子，严重违反国际关系基本准则，影响亚太稳定，造成分歧和对抗。</p>\n<p style=\"text-indent: 2em;\">\n	在这份联合声明中，日本和美国再次污蔑中国，以所谓“规则和秩序的缔造者”自居，称他们将“为自己的利益重塑国际秩序”。这恰恰是冷战思维和意识形态偏见根深蒂固的表现，是从狭隘的地缘政治角度搞集团政治，假借“国际秩序”名义，在地区和世界之间制造冲突和分裂。毫不夸张地说，这就是“颠倒黑白”。</p>\n<p style=\"text-indent: 2em;\">\n	国际社会普遍认为，世界反法西斯战争胜利奠定了当前的世界体系和国际秩序。日本犯下累累战争罪行，不仅不正确反思侵略历史，反而夸大周边安全威胁，借日美同盟为军事扩张松绑，为摆脱战后束缚制造借口。这实质上是对战后国际秩序的挑衅。</p>\n<p style=\"text-indent: 2em;\">\n	当今世界只有一个国际体系，即以联合国为核心的国际体系。中国遵守以联合国宪章为基础、为各国所广泛承认的国际关系基本准则，而不是某些国家制定的“小圈子准则”。作为负责任大国，中国始终奉行独立自主的和平外交政策，坚定不移做世界和平的建设者、全球发展的贡献者、国际秩序的维护者。全球发展倡议和全球安全倡议是中国维护国际秩序的智慧和方案，着眼于全世界特别是发展中国家的发展与安全。</p>\n<p style=\"text-indent: 2em;\">\n	此次日美“2+2”联合声明还粗暴干涉中国内政。“互不干涉内政”是中国和平共处五项原则的组成部分，1955年的万隆会议、上世纪60年代的不结盟运动、70年代的联大宣言均把和平共处五项原则包括在内。中方一贯主张主权和领土完整不容侵犯，尊重彼此核心利益。各国政府和人民应携手合作，共同应对，依法行使自身权利。不能以“基于规则的自由开放国际秩序”为幌子，歪曲国际法，侵犯他国合法权益，破坏和平。</p>\n<p style=\"text-indent: 2em;\">\n	2022年是中日邦交正常化50周年。在亚太经合组织第二十九次领导人非正式会议期间，中日双方一致同意，保持高层交往和对话沟通，增进政治互信，推进务实合作，扩大人文交流，加强国际地区事务协调合作，共同致力于构建契合新时代要求的稳定和建设性的中日关系。</p>\n<p style=\"text-indent: 2em;\">\n	当前，世界进入新的动荡变革时期，一国安全不能以损害他国安全为代价，地区安全也不能通过分裂对抗来实现。2023年是中日和平友好条约缔结45周年。45年前，两国前辈承诺肩负维护地区和平稳定的责任。站在新起点上，日方应按照中日四个政治文件原则和相关共识，有效管控矛盾对抗，防止两国关系大局受到干扰，维护两国关系的政治基础。中日两国要珍惜和平稳定，共同谋求团结合作与发展繁荣。</p>\n<p style=\"text-indent: 2em;\">\n	（作者是中国社会科学院日本研究所助理研究员）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：崔译戈、常红)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0203/c1002-32617372.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (107,'2023-02-19 09:34:49.092248','2023-02-19 09:34:49.092248','漫评美国制裁阻碍叙利亚抗震救灾：“危机…','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"漫画作者：马宏亮（人民网版权图片，未经授权，请勿转载。）\" src=\"/NMediaFile/2023/0217/MAIN202302170857000079893451558.jpg\" width=\"700\" height=\"436\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	漫画作者：马宏亮（人民网版权图片，未经授权，请勿转载。）</p>\n<p style=\"text-indent: 2em;\">\n	当地时间2月6日，土耳其南部靠近叙利亚边境地区发生强烈地震，造成土叙两国大量人员伤亡。世界卫生组织官员14日说，土耳其与叙利亚边境地震造成两国2600万人需要人道主义援助。</p>\n<p style=\"text-indent: 2em;\">\n	受多年战乱和美国制裁影响，叙利亚民生凋敝，地震更是为当地人道主义危机雪上加霜。地震发生后，美国仍拒绝解除对叙利亚的制裁，阻碍救济援助运抵。直至9日，美国才宣布临时取消部分制裁，此时已过了72小时黄金救援期。天灾加上“人祸”，叙利亚人民承受了极大代价，遭受着“危机中的危机”。</p>\n<p style=\"text-indent: 2em;\">\n	长期以来，美国为维护自身霸权，对叙利亚进行军事干预、实施单边封锁和制裁、抢掠资源，是叙利亚危机的始作俑者。在地震发生后，美西方甚至将政治化置于拯救生命之上，阻碍抗震救灾。叙利亚驻俄罗斯大使巴沙尔·贾法里近日一语道破美方的丑陋嘴脸：“试想一下，我们的救护车甚至都没有燃料来运送伤员，而美国却一直在偷窃我们的天然气和石油，并将其运送至第三国。”</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：崔越、常红)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0217/c1002-32625770.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (108,'2023-02-19 09:34:49.092248','2023-02-19 09:34:49.092248','漫评美国滥施“长臂管辖”：“害人害己”','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"漫画作者：马宏亮（人民网版权图片，未经授权，请勿转载。）\" src=\"/NMediaFile/2023/0210/MAIN202302100853000256700988910.jpg\" width=\"596\" height=\"700\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	漫画作者：马宏亮（人民网版权图片，未经授权，请勿转载。）</p>\n<p style=\"text-indent: 2em;\">\n	一直以来，美国为维护自身霸权，频繁实施“长臂管辖”，并且“手臂”越伸越长。蛮横的制裁手段给世界造成深重且恶劣的影响。</p>\n<p style=\"text-indent: 2em;\">\n	根据美国财政部报告，截至2021财年，美国已生效的制裁措施累计达9400多项。《美国滥施“长臂管辖”及其危害》报告指出，仅在2021年，美国财政部、商务部等部门就针对2000多个实体实施了各类制裁措施。美国“长臂管辖”不仅严重干涉别国内政、冲击国际秩序，并侵犯别国人民的基本人权。6日，土耳其南部靠近叙利亚边境地区发生强烈地震，造成土叙两国大量人员伤亡。可面对这一严重灾害，美国仍拒绝解除对叙利亚的制裁，阻碍救济援助运抵。</p>\n<p style=\"text-indent: 2em;\">\n	在世界迫切需要各国团结合作的当下，美国的“长臂管辖”措施及其非法单边制裁终究会反噬自身，害人害己。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：崔越、杨牧)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0210/c1002-32621296.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (109,'2023-02-19 09:34:49.103053','2023-02-19 09:34:49.103053','漫评美借俄乌冲突大发战争财：“一箭三雕”','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"漫画作者：马宏亮（人民网版权图片，未经授权，请勿转载。）\" src=\"/NMediaFile/2023/0203/MAIN202302030835000189072451513.jpg\" width=\"700\" height=\"479\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	漫画作者：马宏亮（人民网版权图片，未经授权，请勿转载。）</p>\n<p style=\"text-indent: 2em;\">\n	美国国务院近日公布数据显示，2022财年由国务院授权的美政府对外武器销售总额跃升至约519亿美元，较上一财年的348亿美元激增49.1%。其中一个重要原因是乌克兰危机升级。</p>\n<p style=\"text-indent: 2em;\">\n	自乌克兰危机爆发以来，美国一直从中牟利，大发战争财。美国还通过制造“俄罗斯威胁”使欧洲陷入安全焦虑，从而获取更多军火订单。美国《外交政策》杂志的统计显示，在美西方持续向乌克兰输送武器的情况下，2022年美国对北约成员国的军售总额与2021年相比几乎翻倍。另外，乌克兰还成为了美国及其军工复合体的武器试验场。此前，乌克兰国防部长阿列克西·列兹尼科夫说，乌克兰现在实质上是一个武器试验场，因为许多武器现在都在战场上接受了与俄罗斯军队实战的检验。</p>\n<p style=\"text-indent: 2em;\">\n	美国及其军工复合体通过乌克兰冲突实现了“一箭三雕”：他们既在战场上检验了产品，又向欧洲进行了推销展示，最后还会收获更多订单，赚得盆满钵满。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：崔越、常红)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0203/c1002-32617105.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (112,'2023-02-19 09:34:49.112094','2023-02-19 09:34:49.112094','漫评2022年的美国之经济痛苦：通胀“…','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-align: center;\">\n	<img alt=\"\" desc=\"漫画作者：马宏亮（人民网版权图片，未经授权，请勿转载。）\" src=\"/NMediaFile/2023/0113/MAIN202301130854000317966813759.jpg\" width=\"700\" height=\"502\" /></p>\n<p class=\"desc\" style=\"text-align: center;\">\n	漫画作者：马宏亮（人民网版权图片，未经授权，请勿转载。）</p>\n<p style=\"text-indent: 2em;\">\n	2022年，美国交出的成绩单恐怕并不好看。这一年，美国通胀持续升温，“高烧”不退。</p>\n<p style=\"text-indent: 2em;\">\n	通胀高企令许多美国家庭遭受严重的财务压力。美联储数据显示，2022年第三季度美国家庭债务暴增3510亿美元，为2007年以来最大增幅。为给通胀“降温”，美联储持续激进加息。从3月份开始，美联储在2022年共加息7次，累计加息425个基点，联邦基金利率水平在不足一年内从接近零快速升至4.25%至4.50%之间。</p>\n<p style=\"text-indent: 2em;\">\n	对国内，高通胀令民众苦不堪言；对国外，美联储加息为全球经济复苏带来严重冲击。通胀失控、疯狂加息，美国所作所为既损人又不利己。</p>\n<p style=\"text-indent: 2em;\">\n	<strong>相关阅读：</strong></p>\n<p style=\"text-indent: 2em;\">\n	<a href=\"http://world.people.com.cn/n1/2023/0106/c1002-32601117.html\" target=\"_blank\"><strong>漫评2022年的美国之治理乱象：“乱套了”</strong></a></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：崔越、常红)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0113/c1002-32605850.html',8);
