INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (115,'2023-02-19 09:34:49.132332','2023-02-19 09:34:49.132332','海报：中国外长连续33年年初首访非…','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p>据外交部消息，为进一步深化中非全面战略合作伙伴关系、推进中非友好合作，2023年1月9日至16日，外交部长秦刚应邀访问埃塞俄比亚、加蓬、安哥拉、贝宁、埃及和非盟总部、阿盟总部。此访也是中国外长连续33年年初首访非洲。<br />    中非关系为什么好？中非友谊为什么深？中国外长新年访非之际，让我们一起回顾中非友好合作的精彩历程，共同期待新时代中非合作向高质量、惠民生、可持续方向不断迈进。</p></div>\n            	<div class=\"box_pic\"><table width=\"450\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\"><tr><td align=\"center\"><a href=\"/n1/2023/0111/c1002-32604391-2.html\"><img src=\"/NMediaFile/2023/0111/MAIN202301111451000437334778649.jpg\" alt=\"海报：中国外长连续33年年初首访非洲 续写中非友好合作故事\" /></a></td></tr><tr><td align=\"center\"><font color=\"#000000\" /></td></tr></table></div>\n				<p style=\"text-indent: 2em; text-align: center;\">\n	<span style=\"text-indent: 2em;\">编辑：崔译戈</span></p>\n<p style=\"text-indent: 2em; text-align: center;\">\n	<span style=\"text-indent: 2em;\">设计：趣动全媒体工作室 方红</span></p>\n<p style=\"text-indent: 2em; text-align: center;\">\n	资料来源：新华社、外交部、《新时代的中非合作》白皮书等</p>\n<div class=\"zdfy clearfix\"><a href=\"/n1/2023/0111/c1002-32604391.html\">【1】</a><a href=\"/n1/2023/0111/c1002-32604391-2.html\">【2】</a><a href=\"/n1/2023/0111/c1002-32604391-3.html\">【3】</a><a href=\"/n1/2023/0111/c1002-32604391-4.html\">【4】</a><a href=\"/n1/2023/0111/c1002-32604391-5.html\">【5】</a><a href=\"/n1/2023/0111/c1002-32604391-6.html\">【6】</a></div><center><table border=\"0\" align=\"center\" width=\"40%\"><tr><td width=\"50%\" align=\"center\"><a href=\"/n1/2023/0111/c1002-32604391-2.html\"><img src=\"/img/next_page.jpg\" border=\"0\" /></a></td></tr></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：崔译戈、常红)</div>\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			','人民网','http://world.people.com.cn/n1/2023/0111/c1002-32604391.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (116,'2023-02-19 09:34:49.132332','2023-02-19 09:34:49.132332','海报：疫情防控政策调整 中国努力构…','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-indent: 2em;\">\n	<span style=\"color:#0000ff;\"><span style=\"font-family:楷体_gb2312;\">编者按：2022年12月26日，中国国家卫生健康委发布公告，将于2023年1月8日将新型冠状病毒感染从“乙类甲管”调整为“乙类乙管”。这是自2020年1月20日中国开始实施严格的传染病甲类防控措施后，疫情防控政策的又一次重大调整。政策调整之后，如何有效地防止医疗资源挤兑？如何降低重症和死亡的发生？实际上，早在2022年11月中国国务院出台的二十条疫情防控优化措施中，已经包含了多项物资储备内容。当前，中国相关机构正在进一步扩充医疗资源、增加医疗服务供给。以下这组关键数据，展示了过去三年间以及当下，中国构筑防疫屏障的努力和成果。</span></span></p>\n<p style=\"text-align: center;\">\n	<a href=\"/n1/2023/0105/c1002-32600600-2.html\"><img alt=\"\" src=\"/NMediaFile/2023/0105/MAIN202301051831000585879763341.jpg\" width=\"660\" height=\"1173\" /></a></p>\n<p style=\"text-indent: 2em; text-align: center;\">\n	 </p>\n<div class=\"zdfy clearfix\"><a href=\"/n1/2023/0105/c1002-32600600.html\">【1】</a><a href=\"/n1/2023/0105/c1002-32600600-2.html\">【2】</a><a href=\"/n1/2023/0105/c1002-32600600-3.html\">【3】</a><a href=\"/n1/2023/0105/c1002-32600600-4.html\">【4】</a><a href=\"/n1/2023/0105/c1002-32600600-5.html\">【5】</a><a href=\"/n1/2023/0105/c1002-32600600-6.html\">【6】</a></div><center><table border=\"0\" align=\"center\" width=\"40%\"><tr><td width=\"50%\" align=\"center\"><a href=\"/n1/2023/0105/c1002-32600600-2.html\"><img src=\"/img/next_page.jpg\" border=\"0\" /></a></td></tr></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：徐祥丽、常红)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0105/c1002-32600600.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (117,'2023-02-19 09:34:49.142529','2023-02-19 09:34:49.142529','谣言粉碎机：西方对华认知中的谬误和…','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p>2022年，一些国家不遗余力地在国际舆论场上制造针对中国的谣言，渲染中国威胁，抹黑中国内外政策。中国外交部发言人在例行记者会上用事实和数据及时“打假”“消毒”，有力驳斥西方对华认知中的谬误，深刻揭露造谣传谣的险恶用心。让我们通过过去一年中外交部发言人的部分精彩回应，一同回顾谣言背后的事实真相。</p></div>\n            	<div class=\"box_pic\"><table width=\"450\" cellspacing=\"0\" cellpadding=\"3\" align=\"center\"><tr><td align=\"center\"><a href=\"/n1/2022/1229/c1002-32596230-2.html\"><img src=\"/NMediaFile/2022/1229/MAIN202212291341000157635563503.jpg\" alt=\"谣言粉碎机：西方对华认知中的谬误和事实真相\" /></a></td></tr><tr><td align=\"center\"><font color=\"#000000\" /></td></tr></table></div>\n				<p style=\"text-indent: 2em; text-align: center;\">\n	设计：趣动全媒体工作室 方红</p>\n<div class=\"zdfy clearfix\"><a href=\"/n1/2022/1229/c1002-32596230.html\">【1】</a><a href=\"/n1/2022/1229/c1002-32596230-2.html\">【2】</a><a href=\"/n1/2022/1229/c1002-32596230-3.html\">【3】</a><a href=\"/n1/2022/1229/c1002-32596230-4.html\">【4】</a><a href=\"/n1/2022/1229/c1002-32596230-5.html\">【5】</a><a href=\"/n1/2022/1229/c1002-32596230-6.html\">【6】</a><a href=\"/n1/2022/1229/c1002-32596230-7.html\">【7】</a><a href=\"/n1/2022/1229/c1002-32596230-8.html\">【8】</a></div><center><table border=\"0\" align=\"center\" width=\"40%\"><tr><td width=\"50%\" align=\"center\"><a href=\"/n1/2022/1229/c1002-32596230-2.html\"><img src=\"/img/next_page.jpg\" border=\"0\" /></a></td></tr></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：崔译戈、杨牧)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2022/1229/c1002-32596230.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (118,'2023-02-19 09:34:49.142529','2023-02-19 09:34:49.142529','图解：2022年习近平主席出访盘点','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p style=\"text-align: center;\">\n	<img alt=\"\" src=\"/NMediaFile/2022/1229/MAIN202212290948000453472525227.jpg\" width=\"900\" height=\"5777\" /> <img alt=\"\" src=\"/NMediaFile/2022/1229/MAIN202212290948000525038089315.jpg\" width=\"900\" height=\"4429\" /> <img alt=\"\" src=\"/NMediaFile/2022/1230/MAIN202212301054000541269423921.jpg\" width=\"900\" height=\"4857\" /></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：刘洁妍、杨牧)</div>\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			','人民网','http://world.people.com.cn/n1/2022/1229/c1002-32596091.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (120,'2023-02-19 09:34:49.162204','2023-02-19 09:34:49.162204','金与正最新谈话提醒韩国：朝鲜不会用洲际弹道导弹瞄准首尔','<td id=\"article_content\">\r\n\r\n<div>朝中社19日刊发朝鲜劳动党中央委员会副部长金与正的谈话。金与正在讲话中敦促美收起“不敌视朝鲜、对对话持开放态度”的欺骗世界的废话，停止威胁朝鲜国家安全的一切行动。而对于韩国，她表示朝鲜无意与韩国打交道，“因他们是傻瓜才提醒一句，我们不会用洲际弹道导弹瞄准首尔”。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div>据朝中社报道，金与正在讲话中抨击美韩，称“我们的敌手又无凭无据地做出了对共和国主权赤裸裸的侵害行为”。她还称，美韩动辄打出应对威胁之招牌，大肆喧嚷扩张遏制力和联合防卫态势，公开暴露出要在朝鲜半岛地区取得军事优势、占据支配地位的十分危险、极为过度的野心和企图，这将越来越破坏地区稳定，导致形势岌岌可危。</div><div><br /></div><div>金与正敦促美国“停止威胁我们国家安全的一切行动，不要抹黑我们国家的形象，为了自己的长远安全要始终深思熟虑”。</div><div><br /></div><div>此外她还表示，韩国也最好考虑，若像现在这样装出十分“勇敢无比”、不顾一切地多管闲事，最终会招致何等灾殃。“因他们是傻瓜才提醒一句，我们不会用洲际弹道导弹瞄准首尔。”</div><div><br /></div><div>据此前报道，韩国国防部16日发行《2022国防白皮书》，时隔6年重添视朝鲜政权和朝军为“敌”的表述，同时在描述对日关系时的措辞也发生细微变化。据韩联社报道，这是尹锡悦政府首次发布国防白皮书，也是韩国1967年以来发布的第25份国防白皮书。该份白皮书指出，朝鲜在2021年修订的劳动党章程中明确提出要将朝鲜半岛全域共产主义化，并在2022年12月劳动党中央委员会全体会议上将韩国定义为“明确的敌人”，因为朝鲜没有弃核并持续施加军事威胁，韩国也将朝鲜政权和军队视为“敌人”。</div><div><br /></div><div>此外，据韩联社17日报道，韩国国防部当天表示，旨在应对朝核威胁的韩美延伸威慑手段运用演习（DSC TTX）将于本月22日在美国华盛顿五角大楼举行。当天晚些时候，朝中社发布朝鲜外务省发言人谈话警告，“若美国和韩国按照已公布的计划，将我们凭借正当忧虑和根据视为侵略战争准备的训练构想付诸于实践，那么将会面临空前的持续而强大的回应”。</div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527361.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (121,'2023-02-19 09:34:49.173958','2023-02-19 09:34:49.173958','中疾控：去年12月以来共发现本土重点关注变异株15例','<td id=\"article_content\">\r\n\r\n<div>中疾控网站18日发布最新的全国新型冠状病毒感染疫情情况。</div><div><br /></div><div><b>一、感染监测数据</b></div><div><br /></div><div>（一）全国报告人群新冠病毒核酸检测结果。 </div><div><br /></div><div>2022年12月9日以来，各省份报告人群核酸检测阳性数及阳性率呈现先增加后降低趋势，阳性人数12月22日达到高峰（694万）后波动下降，2月16日为10720例；检测阳性率12月25日达高峰（29.2%）后波动下降，2月16日为1.1%（图1-1）。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0022285n96r4gork4efknr.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-1\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0022285n96r4gork4efknr.jpg\" /></a></p></div><div><font color=\"#808080\">图1-1  全国报告人群新型冠状病毒核酸检测阳性数及阳性率变化趋势（数据来源于31个省（区、市）及新疆生产建设兵团报告） </font></div><div><br /></div><div>（二）全国报告人群新冠病毒抗原检测结果。各省份报告抗原检测量呈现波动减少趋势，从2022年12月19日的最高189万波动下降到2月16日的7.2万。抗原检测阳性数及阳性率自2022年12月9日快速上升，12月22日达高峰（33.7万、21.3%）后波动下降，2月16日阳性数降至508（阳性率为0.7%）（图1-2）。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0022311iz95gf57fb3qzid.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-2\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0022311iz95gf57fb3qzid.jpg\" /></a></p></div><div><font color=\"#808080\">图1-2  全国报告人群新型冠状病毒抗原检测阳性数及阳性率变化趋势（数据来源于31个省（区、市）及新疆生产建设兵团报告） </font></div><div><br /></div><div><b>二、全国发热门诊（诊室）诊疗情况</b></div><div><br /></div><div>（一）总体就诊人数结果。全国（不含港澳台）发热门诊（诊室）就诊人数于2022年12月23日达到峰值286.7万人次，随后连续下降；2023年1月23日后就诊人数呈现低位波动，2月16日为12.4万人次，较峰值下降了95.7%（图2-1）。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0023248he8de8tdc8etev9.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-3\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0023248he8de8tdc8etev9.jpg\" /></a></p></div><div><font color=\"#808080\">图2-1  全国发热门诊（诊室）诊疗人数变化趋势（数据来源于31个省（区、市）及新疆生产建设兵团报告） </font></div><div><br /></div><div>说明：自2022年12月9日起，监测二级以上医疗机构发热门诊诊疗量；12月21日起，增加监测社区卫生服务中心和乡镇卫生院发热诊室诊疗量（不含村卫生室和社区卫生服务站）。 </div><div><br /></div><div>（二）农村发热门诊（诊室）就诊人数结果。全国乡镇卫生院发热诊室就诊人数于2022年12月23日达到峰值92.2万人次，随后波动下降；2023年1月23日后呈现低位波动，2023年2月16日为4.6万人次，较峰值下降了95.0%（图2-2）。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0023295fzkwwz2fomkk8bp.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-4\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0023295fzkwwz2fomkk8bp.jpg\" /></a></p></div><div><font color=\"#808080\">图2-2  全国农村地区乡镇卫生院发热门诊（诊室）就诊人数变化趋势（数据来源于31个省（区、市）及新疆生产建设兵团报告） </font></div><div><br /></div><div>说明：农村发热患者诊疗量为乡镇卫生院发热诊室诊疗量（不含村卫生室） </div><div><br /></div><div>（三）城市发热门诊就诊人数结果。全国二级以上医疗机构和城市社区卫生服务中心发热门诊（诊室）就诊人数于2022年12月22日达到峰值195.4万人次，随后连续下降；1月23日后呈低位波动，2月16日为7.7万人次，较峰值下降了96.0%（图2-3）。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002336ktp0lnosrwoonvan.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-5\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002336ktp0lnosrwoonvan.jpg\" /></a></p></div><div><font color=\"#808080\">图2-3  全国城市发热门诊（诊室）就诊人数变化趋势（数据来源于31个省（区、市）及新疆生产建设兵团报告） </font></div><div><br /></div><div>说明：城市发热门诊诊疗量含二级以上医疗机构和社区卫生服务中心（不含社区卫生服务站）。 </div><div><br /></div><div>（四）哨点医院监测结果。2022年9月-12月上旬，全国流感哨点医院每周流感样病例（体温≥38℃，伴咳嗽或咽痛之一）数量稳定在10万左右，流感样病例占门（急）诊就诊人数比值在2.7%-3.6%区间波动。第50周（12月12日-18日）明显上升至8.5%，第51周达到最高12.1%，第52周起快速下降。2023年第6周（2月6日-12日）下降至1.4%（图2-4）。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0023353xzg1hhz77khk1ww.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-6\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0023353xzg1hhz77khk1ww.jpg\" /></a></p></div><div><font color=\"#808080\">图2-4  全国哨点医院报告的流感样病例数及占比变化趋势（数据来源于824家哨点医院） </font></div><div><br /></div><div>2022年第49周（12月9日），流感样病例新冠病毒阳性率开始逐渐增加，在第51和52周期间达峰值后持续下降，2023年第6周（2月6-12日）新冠病毒阳性率已下降至4.1%。2022年第49周以来，流感病毒阳性率则逐步降低，12月下旬至2月初处于极低水平（低于1.0%）；2023年第6周，流感病毒阳性率有所上升（3.4%）（图2-5）。 </div><div><br /></div><div style=\"text-align: center;\"><p /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002456m0md0gmo60919317.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-7\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002456m0md0gmo60919317.jpg\" /></a></p></div><div><font color=\"#808080\">图2-5  全国哨点医院流感样病例新冠和流感病毒阳性率变化趋势（数据来源于402家网络实验室） </font></div><div><br /></div><div><b>三、住院诊疗情况</b></div><div><br /></div><div>（一）在院新冠病毒感染者结果。全国在院新冠病毒感染者于2023年1月5日达到峰值162.5万人，随后持续下降；2月16日下降至2.0万人，较峰值减少了98.8%（图3-1）。 </div><div><br /></div><div style=\"text-align: center;\"><p /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002502stnwgk0gq08emtms.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-8\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002502stnwgk0gq08emtms.jpg\" /></a></p></div><div><font color=\"#808080\">图3-1  全国在院新冠病毒感染者每日变化情况（数据来源于31个省（区、市）及新疆生产建设兵团报告） </font></div><div><br /></div><div>（二）在院新冠病毒感染阳性重症患者结果。全国在院新冠病毒感染者中，重症患者数量于2022年12月27日至2023年1月3日期间每日增量近1万，1月4日增量明显下降，1月5日达到峰值12.8万后持续下降，2月16日下降至56例（其中新冠病毒感染重症6例、基础性疾病重症合并新冠病毒感染50例），较峰值下降了99.9%（图3-2）。  </div><div><br /></div><div style=\"text-align: center;\"><p /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002505tjt8olggxpwwhwwh.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-9\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002505tjt8olggxpwwhwwh.jpg\" /></a></p></div><div><font color=\"#808080\">图3-2  全国在院新冠病毒感染阳性重症患者变化情况（数据来源于31个省（区、市）及新疆生产建设兵团报告） </font></div><div><br /></div><div>（三）在院新冠病毒感染死亡情况。在院新冠病毒感染死亡病例数于1月4日达到每日峰值4273例，随后持续下降，2月16日下降至6例，较峰值下降99.9%（图3-3）。2月10日至2月16日，31个省（自治区、直辖市）及新疆生产建设兵团医疗机构累计在院新冠病毒感染相关死亡病例98例，其中新冠病毒感染导致呼吸衰竭死亡1例、基础疾病合并新冠病毒感染死亡97例。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002506gz32czu943t53cdi.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-10\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002506gz32czu943t53cdi.jpg\" /></a></p></div><div><font color=\"#808080\">图3-3  全国在院新冠病毒感染死亡病例变化情况（数据来源于31个省（区、市）及新疆生产建设兵团报告） </font></div><div><br /></div><div><b>四、新冠病毒感染本土病例病毒变异监测情况</b></div><div><br /></div><div>（一）总体情况。2022年9月26日至2023年2月16日，全国共报送25632例本土病例新冠病毒基因组有效序列，均为奥密克戎变异株，共存在78个进化分支，主要流行株为BA.5.2.48（53.4%）、BF.7.14（24.9%）和BA.5.2.49（13.9%）。BA.5.2等20种进化分支构成比在0.1%-2.4%之间，55个进化分支的构成比小于0.1%（共占0.6%）（图4-1）。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002733uyr7n9cn2o6cjgwn.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-11\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002733uyr7n9cn2o6cjgwn.jpg\" /></a></p></div><div><font color=\"#808080\">图4-1  全国新型冠状病毒变异株变化趋势图 </font></div><div><br /></div><div>说明：1.采样日期：2022年9月26日至2023年2月14日。 </div><div><br /></div><div>2.图中标记的数字分别为BA.5.2.48、BF.7.14和BA.5.2.49进化分支有效基因组序列数量。 </div><div><br /></div><div>3.“其它”指全国范围Omicron变异株构成比小于0.1%的进化分支。 </div><div><br /></div><div>（二）12月以来本土病例病毒变异株监测情况。2022年12月1日至2023年2月16日，全国共报送16931例本土病例新冠病毒基因组有效序列，全部为奥密克戎变异株，共存在34个进化分支。主要流行株为BA.5.2.48（60.4%）和BF.7.14（28.9%）（表4-1）。共发现本土重点关注变异株15例，其中，1例XBB.1，1例XBB.1.5，1例BQ.1，5例BQ.1.1，1例BQ.1.1.17，4例BQ.1.2和2例BQ.1.8。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002738imen0e0jun2j0upj.png\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-12\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002738imen0e0jun2j0upj.png\" /></a></p></div><div>（三）新冠病毒变异分省份情况。总体来看，北京、天津和内蒙古以BF.7及其子分支为优势株；江苏BF.7及其子分支和BA.5.2及其子分支基本持平；其他省份均以BA.5.2及其子分支为优势株（图4-2）。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002738iei6u6xjepietote.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-13\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002738iei6u6xjepietote.jpg\" /></a></p></div><div><font color=\"#808080\">图4-2  各省份新冠病毒变异监测情况 </font></div><div><br /></div><div>说明：1.采样时间：2022年12月1日-2023年2月14日； </div><div><br /></div><div>2.图中标记的数字分别为BA.5.2.48、BF.7.14和BA.5.2.49进化分支有效基因组序列数量。 </div><div><br /></div><div>五、新冠病毒疫苗接种进展</div><div><br /></div><div>截至2023年2月16日，31个省（自治区、直辖市）和新疆生产建设兵团累计完成接种新冠病毒疫苗349177.5万剂次（图5-1）。接种总人数131036.1万人，完成全程接种127685.8万人，完成第一剂次加强免疫接种82710.2万人。全人群第一剂次、全程接种覆盖比例分别达到93.0%和90.6%（图5-2）。60岁以上老年人累计报告接种新冠病毒疫苗67949.0万剂次，接种总人数24167.3万人，完成全程接种23028.0万人，完成第一剂次加强免疫接种19266.9万人。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0027410tgm0mdy0qa2tmmj.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-14\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0027410tgm0mdy0qa2tmmj.jpg\" /></a></p></div><div><font color=\"#808080\">图5-1  分月新冠病毒疫苗累计接种剂次（数据来源于31个省（区、市）及新疆生产建设兵团报告） </font></div><div><font color=\"#808080\"><br /></font></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002740jiygvq2jocojcycj.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-15\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002740jiygvq2jocojcycj.jpg\" /></a></p></div><div><font color=\"#808080\">图5-2  分月全人群中第一剂次接种、基础免疫全程接种覆盖率（数据来源于31个省（区、市）及新疆生产建设兵团报告） </font></div><div><br /></div><div>以2022年底全国老年人专项摸底调查人口数为基数统计，截至2023年2月16日，60岁以上老年人第一剂次接种覆盖人数占老年人群的96.1%，全程接种、第一剂次加强免疫接种人数分别占符合接种时间间隔老年人群的96.6%、92.3%（图5-3）。 </div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002909ndnl9jgll8ppdjv8.jpg\" target=\"_blank\"><img alt=\"中疾控：去年12月以来共发现本土重点关注变异株15例_图1-16\" title=\"中疾控：去年12月以来共发现本土重点关注变异株15例\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/002909ndnl9jgll8ppdjv8.jpg\" /></a></p></div><div><font color=\"#808080\">图5-3  基于摸底人口数的60岁以上人群新冠病毒疫苗接种率（数据来源于31个省（区、市）及新疆生产建设兵团报告） </font></div><div><br /></div><div> 注：1.第一剂次接种率测算中分子为接种目前附条件上市或紧急使用新冠病毒疫苗至少1剂次的人群，分母为2022年12月10日各省上报的老年人摸底登记人口数。2.全程接种率测算中分子为接种灭活疫苗2剂次、腺病毒载体疫苗1剂次、重组蛋白疫苗3剂次的老年人群，分母为接种灭活疫苗1剂次、腺病毒载体疫苗1剂次和重组蛋白疫苗2剂次的人群，并且接种后间隔满28天（4周）。3.第一剂次加强免疫接种率测算分子为完成第一剂次加强免疫接种老年人群，分母为接种灭活疫苗2剂次、腺病毒载体疫苗1剂次的人群，且全程接种后间隔满3个月。（由于重组蛋白疫苗实施加强免疫接种的时间短，接种3剂次重组蛋白疫苗人群目前未包括在分母中）。 </div><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;text-align:center;margin:4px auto; margin-bottom:5px;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527357.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (122,'2023-02-19 09:34:49.181993','2023-02-19 09:34:49.181993','重大事故致13死35伤 盘锦市政府主要负责人被约谈','<td id=\"article_content\">\r\n\r\n<div>据应急管理部官网2月18日消息，2月17日，针对辽宁盘锦1月15日发生的浩业化工重大爆炸着火事故，国务院安委会办公室约谈了盘锦市人民政府主要负责人。国务院安委会办公室副主任、应急管理部副部长孙广宇主约谈，应急管理部有关司局负责人参加约谈。</div><div><br /></div><div>约谈指出，盘锦浩业化工公司“1·15”重大爆炸着火事故发生在春节前夕和全国安全生产重大隐患专项整治期间， 影响恶劣、教训深刻，暴露出事故企业处理效益和安全严重失衡、风险管控存在严重漏洞、日常安全管理严重缺失，地方政府部门重大风险防控存在盲区、“三个必须”落实不到位、安全监管压力传导不足等突出问题，映射出辽宁省盘锦市在统筹发展和安全方面存在安全基础薄弱、从业人员专业能力不足、安全保障不匹配等突出矛盾。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div><br /></div><div>约谈强调，辽宁省盘锦市要坚决贯彻习近平总书记关于安全生产重要指示精神，把思想和行动统一到党的二十大精神和党中央、国务院决策部署上来，深刻认识统筹好发展和安全的重大意义，深刻反思事故教训，举一反三、狠抓落实，切实扭转被动局面。</div><div><br /></div><div>约谈要求， 辽宁省要依法依规严肃事故查处与责任追究。盘锦市要树牢安全发展理念，严格落实安全生产十五条硬措施，压紧压实责任，推动提升危化品监管合力和系统治理水平；要坚持底线思维，采取断然措施，组织对化工和危化品企业全面开展安全大诊断，逐一落实管控措施，坚决防控重大风险、遏制重特大事故；要聚焦重大风险、重大隐患严格监管执法，加强专业监管能力建设，有效运用社会共治力量，构建安全生产良性局面；要固化、深化、拓展危化品安全专项整治三年行动和风险集中治理成效，抓好年度重点工作落实，加强节后复工复产和全国两会安全防范，为经济社会发展营造良好安全环境。</div><div><br /></div><div>辽宁省应急管理厅、盘锦市人民政府有关部门负责人列席约谈。</div><div><br /></div><div><b>事故造成13人死亡、35人受伤</b></div><div><br /></div><div>据应急管理部官网此前通报，1月15日，辽宁省盘锦浩业化工有限公司烷基化装置在维修过程中发生泄漏爆炸着火事故， 造成13人死亡、35人受伤。国务院安委会已对该起事故查处实行挂牌督办。国务院安委会办公室对该起事故进行了通报。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/223317spdes3hr9geege2t.jpg\" target=\"_blank\"><img alt=\"重大事故致13死35伤 盘锦市政府主要负责人被约谈_图1-3\" title=\"重大事故致13死35伤 盘锦市政府主要负责人被约谈\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/223317spdes3hr9geege2t.jpg\" /></a></p></div><div><font color=\"#808080\">事故搜救现场（来源：新华社）</font></div><div><br /></div><div>通报指出，该起事故发生在春节前夕和岁末年初安全生产重大隐患专项整治期间，造成重大人员伤亡，影响恶劣，教训深刻。初步调查，暴露出事故企业处理效益与安全的关系严重失衡，安全生产主体责任悬空，隐患长期得不到彻底消除，高风险检维修作业安全管理存在重大漏洞，安全专项整治走过场等突出问题。</div><div><br /></div><div>通报强调，各地区、有关部门和企业要进一步树牢安全发展理念，严格落实安全生产十五条硬措施，聚焦防范化解重大安全风险，扎实推进危化品安全风险治理各项任务落地落实。要立即组织开展化工装置设备带“病”运行专项排查整治，对重点环节部位的隐患，强化防控措施并及时整改，对可能引起中毒、火灾、爆炸等事故的隐患，立即处置、彻底消除。要持续深化危化品高风险装置设施安全诊断和整治提升，强化检维修作业安全管理，提升本质安全水平。</div><div><br /></div><div>1月18日，盘锦市召开安全生产电视电话会议，盘锦市市长邢鹏出席会议并讲话。会议指出， 1·15化工企业爆炸事故的教训是惨痛的，令人警醒，发人深思。各地各部门要深刻吸取事故教训，以时时放心不下的责任感，如履薄冰、如临深渊、如坐针毡地把安全生产工作抓细抓实抓好，坚决防范遏制重特大安全生产事故。</div><div><br /></div><div>据《辽宁日报》报道，今年2月1日至2日，辽宁省委书记郝鹏到盘锦市调研督导。</div><div><br /></div><div>郝鹏首先来到盘锦浩业化工有限公司，现场察看、实地督导该公司烷基化装置爆炸事故调查处置、善后处理等工作，强调要深入贯彻习近平总书记关于安全生产的重要论述，科学精准、强化统筹，加快做好事故处置、隐患整改、恢复生产等各项工作。 对企业生产来说安全是第一位的，没有安全一切都无从谈起，要从此次事故中深刻吸取教训，严格落实安全生产主体责任，全面提高企业安全生产管理水平，严密防范化解安全生产风险，严肃追究事故责任。</div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527356.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (124,'2023-02-19 09:34:49.192053','2023-02-19 09:34:49.192053','布林肯晤王毅 双方针对“气球事件”相互指责','<td id=\"article_content\">\r\n\r\n<div><div>美国中文网报道 美国国务卿布林肯周六在德国慕尼黑与中共中央政治局委员、中央外事工作委员会办公室主任王毅会晤。对于近期引发风波的“气球事件”，双方相互指责。这也是事件发生以来美中双方高级官员的首次面对面会晤。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div><b>布林肯指“气球事件”侵犯美主权 促勿重演</b></div><div><br /></div><div>国务院发言人普莱斯在一份声明中称，在会晤中，布林肯“直接谈及进入美国领空的中华人民共和国高空监测气球对美国主权和国际法不可接受的侵犯，强调这一不负责任的行为绝不应再发生”。</div><div><br /></div><div>声明称，对于俄乌战争，布林肯警告了“如果中国向俄罗斯提供实质性援助，或助其系统性逃避制裁”将会产生的影响和结果。</div><div><br /></div><div>另外声明指，布林肯重申，美国长期的“一个中国”政策没有改变，他还强调了保持台海和平稳定的重要性。</div><div><br /></div><div>布林肯重申了拜登最近的说法，即美国将坚决捍卫自身的价值和利益，但同时不希望与中国发生冲突，也不寻求新冷战。布林肯还强调了保持外交对话、以及沟通管道随时畅通的重要性。</div></div><div><br /></div><div><b>王毅促美改弦更张 正视并解决滥用武力对中美关系损害</b></div><div><br /></div><div>另据新华社报道，王毅是应美方请求，同美国国务卿布林肯进行非正式接触。报道称，王毅表明了中方在所谓飞艇事件上的严正立场，要求美方改弦更张，正视并解决滥用武力给中美关系造成的损害。</div><div><br /></div><div>两人是在慕尼黑安全会议间隙会晤的，这也是“气球事件”发生以来，美中高官首次面对面会晤。在中国气球飘入美国领空后，布林肯取消了原定的访华计划。但中国坚称其飞艇属民用性质，并指责美国就此小题大做。</div><div><br /></div><div>周六早些时候，<a href=\"https://www.sinovision.net/portal.php?mod=view&amp;aid=527337\" target=\"_blank\"><font color=\"#0000f0\">王毅在会议上致辞时指责美国</font></a>“用导弹击落毫无威胁的飞艇的行为”“匪夷所思，近乎歇斯底里，是百分之百的滥用武力，明显违反惯例和有关国际公约”。</div><div><br /></div><div>本周早些时候，总统拜登在就起球事件讲话时表示，美国不寻求与中国展开“新冷战”，预计最近将与习近平主席通话。</div><div><br /></div><div>(编辑：张赋杰)</div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527345.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (125,'2023-02-19 09:34:49.212607','2023-02-19 09:34:49.212607','朝鲜再射远程导弹 落入日本专属经济区 华盛顿谴责','<td id=\"article_content\">\r\n\r\n<div>美国中文网报道 周六，朝鲜试射了一枚远程弹道导弹，疑似是洲际导弹。报道称这枚导弹落入了日本以西海岸的专属经济区。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div>对此，白宫周六在一份声明中予以谴责，称美国“将采取一切必要措施”取保美国本土、韩国和日本的安全。声明称，美军印太司令部评估认为，“朝鲜本次发射未对美国的人员、领土以及盟友构成威胁，但它毫无必要地升高了紧张和风险，让区域安全形势变得更加不稳定”。</div><div><br /></div><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/142104bdd33xr37bjpn79h.jpeg\" target=\"_blank\"><img alt=\"朝鲜再射远程导弹 落入日本专属经济区 华盛顿谴责_图1-3\" title=\"朝鲜再射远程导弹 落入日本专属经济区 华盛顿谴责\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/142104bdd33xr37bjpn79h.jpeg\" /></a></p></div><div><font color=\"#808080\">图为周六，首尔火车站，韩国民众观看关于朝鲜试射导弹的报道。美联社</font></div><div><br /></div><div>日本首相岸田文雄周六表示，朝鲜当日早些时候发射的是一枚洲际导弹，落入日本北海道以西海域的专属经济区。日方称，这枚导弹的飞行高度为5700公里，飞行距离900公里。</div><div><br /></div><div>韩国军方称，这枚导弹于当地时间周六傍晚5点22分发射；日本称，导弹于6点27分落入海中。这显示，导弹在空中飞行约1小时。这些参数都与之前朝鲜试射的洲际导弹类似。</div><div><br /></div><div>此前一天，朝鲜威胁称，如果美国继续与韩国举行联合军演，该国将采取“前所未有之坚定而强力的反制措施”。</div><div><br /></div><div>(编辑：张赋杰)</div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527343.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (126,'2023-02-19 09:34:49.222092','2023-02-19 09:34:49.222092','俄亥俄脱轨事故两周后  FEMA终将派员赴现场','<td id=\"article_content\">\r\n\r\n<div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/1118435on4t4z0ndcajhth.jpeg\" target=\"_blank\"><img alt=\"俄亥俄脱轨事故两周后  FEMA终将派员赴现场_图1-1\" title=\"俄亥俄脱轨事故两周后  FEMA终将派员赴现场\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/1118435on4t4z0ndcajhth.jpeg\" /></a></p></div><div><br /></div><div>美国中文网报道 俄亥俄州火车脱轨造成的环境污染，近日受到外界关注。州长德韦恩（Mike DeWine）周五晚间宣布，联邦紧急事务管理署（FEMA）将部署资源，协助善后。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div>德韦恩周五表示，FEMA周六将派遣高级官员和地区管理团队支援正在进行的任务，包括事故协调、以及潜在的长期恢复需求评估。</div><div><br /></div><div>2月3日，一列由诺福克南方铁路运营的货运火车在俄亥俄州东巴勒斯坦镇附近脱轨，导致有害化学物品泄露。</div><div><br /></div><div>本周早些时候，德韦恩曾表示，FEMA认定这起事故未达到需要联邦部署资源协助处理的标准。他当时说，FEMA告知他，该机构主要参与处理“有重大房屋或财产损失”的灾难，比如飓风或龙卷风。</div><div><br /></div><div>德韦恩周五并未说明，FEMA将要部署的资源是否包括该机构此前拒绝向俄亥俄提供的部分。</div><div><br /></div><div>事故发生后，德韦恩曾下令受影响地区的居民疏散，但5天后取消了疏散令。</div><div><br /></div><div>代表俄亥俄的2名国会议员布朗（Sherrod Brown）和万斯（J.D. Vance）都敦促德韦恩尽快宣布俄亥俄州因此事进入紧急状态。不过万斯强调，这不应意味着将负担转到和联邦政府身上，而不让诺福克南方铁路承担责任。</div><div><br /></div><div>化学品泄露事件发生后，当地媒体报道有动物死亡、居民身体不适的情况出现，引发全美关注。</div><div><br /></div><div>（编辑：张赋杰）</div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527339.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (127,'2023-02-19 09:34:49.222092','2023-02-19 09:34:49.222092','贺锦丽：美认定俄在乌克兰犯下反人类罪行','<td id=\"article_content\">\r\n\r\n<div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/101833g081o5ja0060wa0w.jpeg\" target=\"_blank\"><img alt=\"贺锦丽：美认定俄在乌克兰犯下反人类罪行_图1-1\" title=\"贺锦丽：美认定俄在乌克兰犯下反人类罪行\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/101833g081o5ja0060wa0w.jpeg\" /></a></p></div><div><font color=\"#808080\">贺锦丽周六在慕尼黑安全会议上致辞。图据其推特</font></div><div><br /></div><div>美国中文网报道 美国副总统贺锦丽周六在德国慕尼黑安全会议上致辞时表示，美国已经正式认定，俄罗斯军队在入侵乌克兰的战争中犯下了反人类罪行。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div><br /></div><div>贺锦丽在致辞中强调，俄罗斯在乌克兰犯下的“野蛮罪行”必须被绳之以法。她还强调了西方支持乌克兰的坚定决心。</div><div><br /></div><div>今年的慕尼黑安全会议正值俄罗斯入侵乌克兰将满一周年之际。俄罗斯于2022年2月24日入侵了乌克兰。在周六的致辞中，贺锦丽回顾了去年此时，她在同一场合警告俄罗斯的入侵迫在眉睫，而当时很多人对于西方将如何应对、乌克兰是否做好了准备仍然抱有疑虑。</div><div><br /></div><div>“今天，一年之后，我们知道：基辅仍屹立不倒。俄罗斯已被削弱，而跨大西洋联盟前所未有的强劲。”贺锦丽表示。</div><div><br /></div><div>贺锦丽在致辞中详细描述了俄罗斯军队在乌克兰的罪行，称这些行为是“对我们共同价值的攻击”。她还援引自己曾担任检察官的经历称，“我们考察了相关证据，毫无疑问，俄罗斯在乌克兰犯下了战争罪行”。贺锦丽说：“我要告诉那些犯下这些罪行的人，以及被这些罪行牵涉到的他们的上司：你们将被绳之以法。”</div><div><br /></div><div>不过，贺锦丽在谈及俄罗斯犯下反人类罪行时，并未直接提到普京的名字。此前拜登在公开场合曾直接称普京为“战犯”。</div><div><br /></div><div>（编辑：张赋杰）</div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527338.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (129,'2023-02-19 09:34:49.232351','2023-02-19 09:34:49.232351','北大团队揭新冠感染20个月后后遗症特征并提出干预对策','<td id=\"article_content\">\r\n\r\n<div>新冠疫情在全球肆虐，数亿的新冠感染者在急性期症状消失的数月后仍面临长期后遗症的困扰。根据2021年10月世界卫生组织（WHO）发布的新冠病毒感染后的临床病例定义，新冠后遗症指新冠感染后3个月出现症状，最短持续2个月，并且无法由其他诊断解释。症状主要包括疲劳、呼吸困难、认知问题（健忘或注意力不集中，通常被称为脑雾），还包括嗅觉和味觉丧失、肌肉疼痛、胸痛、心悸、食欲不振、抑郁、焦虑等情绪问题，累及全身多个系统。然而面对复杂的长新冠/后遗症症状，全球尚缺乏对新冠康复者长期后遗症表型特征的系统认识及相应的临床指导建议，为了总结新冠康复者的长期后遗症的临床特征，以便提供有针对性的康复干预，北京大学第六医院陆林院士团队联合武汉市武昌医院在武汉市建立了基于社区的新冠康复者随访队列。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div><br /></div><div>2023年1月23日，陆林课题组在《Molecular Psychiatry》上发表了题为《The phenotype and prediction of long-term physical, mental and cognitive COVID-19 sequelae 20 months after recovery, a community-based cohort study in China》的文章，北京大学公共卫生学院硕士研究生赵逸苗、北京大学第六医院副研究员师乐和武汉市武昌医院心理治疗科主任姜振东为论文共同第一作者，陆林、北京大学中国药物依赖研究所副研究员鲍彦平和武汉市武昌医院院长李相友为论文共同通讯作者。</div><div><br /></div><div>本研究在2021年10月至11月间对武汉市洪山区曾在2020年12月前感染新冠的社区居民进行调查，最终纳入1000名15岁以上的新冠康复者。采用潜变量分析的方法，对收集到的一般、心肺、精神、神经认知和消化五大系统的27种症状进行分类，确定了4种后遗症分型，分别为完全康复或轻度组（44.9%）、中度伴随躯体症状组（29.2%）、中度伴随认知和精神心理症状组（9.6%）和重度组（16.3%）。</div><div><br /></div><div>此外，研究团队探索了影响后遗症分型的因素，发现合并基础疾病、具有精神疾病病史、诊断为重型或危重型新冠和急性期住院时间长者，更容易出现重度后遗症；年龄小于60岁、合并基础疾病和诊断为重型或危重型新冠者，更容易出现以躯体症状为主的中度后遗症，而急性期长期住院的女性则更容易报告以认知和精神症状为主的中度后遗症。</div><div><br /></div><div>本研究针对不同严重程度后遗症的新冠康复者提出不同的康复干预措施。对于近一半的无后遗症或轻度后遗症组，不需要进行干预，只需要进行长期随访；对于近三成的以躯体症状为主的中度后遗症组，可进行物理治疗及康复训练等干预，帮助康复者改善躯体功能；而对于约1/10的以认知和精神症状为主的中度后遗症组，可以进行记忆、注意力和执行功能等的认知功能训练，改善认知问题，同时进行心理治疗或行为干预，改善抑郁、焦虑、失眠和创伤后应激障碍等心理健康问题；对于约1/6的重度后遗症组，应采取多学科协作的综合干预模式。</div><div><br /></div><div>本研究针对2020年感染致病性较强的原始毒株的新冠康复者的后遗症进行分析，发现新冠感染20个月后，部分康复者为后遗症所困扰，需要根据不同的后遗症类型开展针对性的康复干预措施，从而使康复者尽快恢复正常功能。该研究对目前致病性较低的奥密克戎株感染者后遗症的康复有一定的参考及指导意义。</div><div><br /></div><div>本研究由国家重点研发计划、国家脑科学和类脑科学研究和国家自然科学基金资助。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527336.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (130,'2023-02-19 09:34:49.242458','2023-02-19 09:34:49.242458','848.73万人次！深圳地铁全网客流创历史最高纪录','<td id=\"article_content\">\r\n\r\n<div>2月17日，深圳地铁各车站人流涌动，全网（含4号线）总计848.73万人次搭乘地铁出行，刷新历史最高客流纪录，其中有4条线路同步创下单日历史新高，2条线路均突破110万大关。当天，深圳地铁全面做好客运保障工作，确保市民出行平安通畅。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/065103bu9pkbg0e9h77n8b.png\" target=\"_blank\"><img alt=\"848.73万人次！深圳地铁全网客流创历史最高纪录_图1-3\" title=\"848.73万人次！深圳地铁全网客流创历史最高纪录\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/065103bu9pkbg0e9h77n8b.png\" /></a></p></div><div>数据显示，2月17日（周五），深圳市全网16条地铁线路总计客运量高达848.73万人次，刷新在2021年12月31日跨年夜创下的843.19万人次历史最高客流纪录。当日，11号线总客运量75.07万人次、12号线38.99万人次、14号线35.42万人次、16号线13.41万人次，共4条线路同步刷新单线历史客流新高。同时，1号线总客运量113.96万人次，5号线128.31万人次，单日客流双双突破110万大关。</div><div><br /></div><div>记者了解到，随着社会生产生活的加速回暖，市民乘客的出行意愿不断增强，近期深圳地铁日客运量保持攀升势头。本周，2月13日至2月17日工作日期间，全网累计客运量3982万人次，日均客运量接近800万人次（达796.34万人次），稳居全国第四。2月17日周五，更是在工作日通勤客流、学生客流、外出休闲游玩客流等多重因素的叠加影响下，首次在正常工作日突破单日客流840万大关，同时也首次实现连续两周工作日周五均突破800万量级。</div><div><br /></div><div>“伴随去年地铁线网里程的增长，有越来越多的沿线市民享受到了工作日搭乘地铁上班通勤的便利。而今年以来，工作日周五晚上及周末假期选择搭乘地铁外出购物、休闲游玩的出行需求更是显著提升，逐日带动了全网客流的快速增长，呈现出强劲的复苏势头。”深铁集团相关负责人介绍。</div><div><br /></div><div>据统计，昨日全网进站客运量排名前5的站点分别是深圳北站10.41万人次、车公庙站7.15万人次、高新园站6.64万人次、坪洲站6.33万人次、民治站5.99万人次。</div><div><br /></div><div>记者了解到，为确保全网出行客流安全有序，当天深圳地铁全力做好运输组织与服务保障工作。专业部门实时监测线网客流密度，重点关注枢纽车站客流情况，以便科学、动态调整列车开行方案。重点车站持续关注周边活动信息，做好人员物资储备，根据现场实际情况采取客流管控措施，保障乘客快速进站通行。</div><div><br /></div><div>据悉，伴随着升腾的“烟火气”，深圳的夜间经济也活力十足。目前，深圳地铁也正在结合全网夜间出行客流特征，通过不断优化行车组织及生产管理，助力城市高质量发展。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527334.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (131,'2023-02-19 09:34:49.242458','2023-02-19 09:34:49.242458','韩军：朝鲜向半岛东部海域发射弹道导弹','<td id=\"article_content\">\r\n\r\n据韩联社当地时间2月18日消息，朝鲜向半岛东部海域发射弹道导弹。韩军正在分析导弹飞行距离、高度和速度等信息。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;text-align:center;margin:4px auto; margin-bottom:5px;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527333.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (133,'2023-02-19 09:34:49.259548','2023-02-19 09:34:49.259548','王毅：中方要求美方拿出解决问题的诚意','<td id=\"article_content\">\r\n\r\n<div>据中国外交部网站2月18日消息，当地时间2023年2月17日，中共中央政治局委员、中央外事工作委员会办公室主任王毅在出席慕尼黑安全会议期间会见巴基斯坦外长比拉瓦尔。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/024400zos37smesfo4omoz.jpg\" target=\"_blank\"><img alt=\"王毅：中方要求美方拿出解决问题的诚意_图1-3\" title=\"王毅：中方要求美方拿出解决问题的诚意\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/024400zos37smesfo4omoz.jpg\" /></a></p></div><div style=\"text-align: center;\"><font color=\"#808080\">中国外交部网站</font></div><div style=\"text-align: center;\"><br /></div><div>王毅表示，中巴是全天候战略合作伙伴，中巴友谊坚如磐石。中方愿同巴方一道，落实好两国领导人达成的战略共识，共建新时代更加紧密的命运共同体。中方坚定支持巴方维护安全稳定，实现发展振兴，愿为巴方克服暂时困难提供力所能及的帮助。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/024359qbpdzhqghbw2c2hg.jpg\" target=\"_blank\"><img alt=\"王毅：中方要求美方拿出解决问题的诚意_图1-4\" title=\"王毅：中方要求美方拿出解决问题的诚意\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/024359qbpdzhqghbw2c2hg.jpg\" /></a></p></div><div>比拉瓦尔对巴中关系发展感到满意。强调中国是巴基斯坦的全天候朋友，巴外交政策的重要支柱就是强化与中国的关系，推进中巴经济走廊建设。巴方将坚定打击恐怖主义，全力保护在巴中国企业和公民的安全。</div><div><br /></div><div>双方还就当前国际局势下加强相互支持交换了意见。王毅指出，中国民用无人飞艇因不可抗力偏离航线进入美国上空，对于这一偶发意外事件，本应冷静、理智和专业处理。但美方无视基本事实，滥用武力、过度反应、渲染炒作，这种近乎歇斯底里的作法显示美方对中国的偏见和无知已到了荒谬的程度。中方要求美方拿出解决问题的诚意，不要再因国内政治需要做出这种荒唐无稽的事情。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527329.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (134,'2023-02-19 09:34:49.264560','2023-02-19 09:34:49.264560','世界最大打桩船“一航津桩”在宁波正式投入施工','<td id=\"article_content\">\r\n\r\n<div>2月17日，世界最大打桩船“一航津桩”在宁波象山海域正式投入施工，这也是自交付以来，“一航津桩”首次施工作业。最先施打的钢护筒为宁波象山港铁路跨海大桥沉下67#墩台首根钢护筒。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/232513jllhqx6q2r1hokhj.jpg\" target=\"_blank\"><img alt=\"世界最大打桩船“一航津桩”在宁波正式投入施工_图1-3\" title=\"世界最大打桩船“一航津桩”在宁波正式投入施工\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/232513jllhqx6q2r1hokhj.jpg\" /></a></p></div><div style=\"text-align: center;\"><font color=\"#808080\">沉桩作业完成</font></div><div style=\"text-align: center;\"><br /></div><div>据了解，“一航津桩”船长124米，型宽39米、型深8米，桩架高142米。最大可施打桩长118米+水深、重700吨、直径6米的桩基。能适应外海恶劣海况下的施工要求，能用于大型桩基施工、群桩式基础、风电导管架桩、风电单桩等施工，是中国水工建设领域名副其实的“大国重器”。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/232512uduivadlul8ui4lh.jpg\" target=\"_blank\"><img alt=\"世界最大打桩船“一航津桩”在宁波正式投入施工_图1-4\" title=\"世界最大打桩船“一航津桩”在宁波正式投入施工\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/232512uduivadlul8ui4lh.jpg\" /></a></p></div><div style=\"text-align: center;\"><font color=\"#808080\">“一航津桩”吊起钢护筒 准备进行沉桩作业</font></div><div style=\"text-align: center;\"><br /></div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527326.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (136,'2023-02-19 09:34:49.274512','2023-02-19 09:34:49.274512','香港学府首例 港大禁用ChatGPT等AI工具','<td id=\"article_content\">\r\n\r\n<div style=\"text-align: left;\">人工智能（AI）聊天机器人ChatGPT近日风靡全球，其对答与写作能力成全球热话，甚至有大学生借其撰写论文，引发热议。香港大学最新公布，所有港大课堂的作业及评估均一律禁止使用ChatGPT或其他AI工具，若学生需要使用须事先获得课程讲师的书面许可，违者将被视作潜在剽窃（plagiarism），甚至可能面临纪律处分，为香港首间大学明文禁止使用相关软件。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div><br /></div><div>港大副校长（教学）何立仁（Ian Holiday）昨日向师生发出内部电邮，表明禁止在港大所有课堂、作业和评估中使用ChatGPT或其他AI工具，除非学生事先获得有关课程讲师的书面同意豁免，否则将被校方视为剽窃个案，并指若没有书面许可，ChatGPT和其他基于AI的工具一律不能用于任何涉及学分的活动，如教师怀疑学生使用ChatGPT或其他AI工具，可要求学生就其作业进行讨论、进行额外口试及考试等措施。</div><div><br /></div><div><b>视为剽窃个案</b></div><div><br /></div><div>根据电邮所指，港大将“剽窃”（plagiarism）定义为“未经同意下采用他人作品”，作品包括但不限于任何材料、创作、想法和数据，不论有关作品是否已经发布或是否具有意图欺骗，在未经承认下采用ChatGPT或其他AI工具缴交作品或论文，是完全符合使用“他人”作品的定义。何立仁在电邮中提到，若学生违规使用ChatGPT等有关程序，等同取用他人作品，当中涉及“欺骗”（deceive）成分，因此日后会将有关个案当作潜在剽窃处理。</div><div><br /></div><div>何立仁解释，港大正计划就AI工具对教学的影响邀请师生进行广泛校园讨论，惟现时正值学期中，每周都有课堂及评估，难以突然使用新政策，故需要采取上述短期措施，同时亦会为教师安排关于ChatGPT及其他AI工具的研讨会。</div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527323.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (137,'2023-02-19 09:34:49.278972','2023-02-19 09:34:49.278972','拜登团队聘请竞选经理有点难 主要原因是...','<td id=\"article_content\">\r\n\r\n<div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/2116171l0xrc4x61cs4j74.jpg\" target=\"_blank\"><img alt=\"拜登团队聘请竞选经理有点难 主要原因是..._图1-1\" title=\"拜登团队聘请竞选经理有点难 主要原因是...\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/2116171l0xrc4x61cs4j74.jpg\" /></a></p></div><div><font color=\"#808080\">美联社资料图</font></div><div><br /></div><div>美国中文网报道  多位消息人士称，为拜登的连任寻找竞选经理是一个急迫而且棘手的问题。媒体称这突显出在白宫仍保留决策权的情况下，很难找到可以开展业务的人选。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div>据NBC报道，竞选经理和其他高级职员可能更像是执行者而不是决策者，这对一些经验丰富的政治专业人士来说是一个艰难的现实。</div><div><br /></div><div>“你想接受一份有五个老板的工作吗？”一位熟悉该过程的消息人士称。</div><div><br /></div><div>白宫发言人安德鲁•贝茨（Andrew Bates）回应称拜登团队在填补竞选经理职位时遇到问题这个说法是“不真实的”。</div><div><br /></div><div>消息人士称，拜登的高级顾问正在与几位竞选经理和其他高级领导职位的候选人进行交谈。到目前为止，接近该过程的三位消息人士表示，招募的重点是参加过一些最具竞争力的2022年中期选举的个人，以及参与过拜登2020年的竞选活动和民主党全国委员会的竞选工作。</div><div><br /></div><div>一位熟悉该过程的人士表示，在这个后期阶段，对话的非正式性质表明，拜登顾问正试图“让合适的人站出来，然后再找到最合适的”。</div><div><br /></div><div>据两位知情人士透露，拜登可能最早在4月发起一场竞选活动，人员配置斗争变得更加紧迫。周四，拜登完成了体检，这也是宣布前的一件“待办事项”。</div><div><br /></div><div>但当被问及仍然悬而未决的关键竞选决定时，贝茨回答说：“这与NBC早前对该问题的报道一样不准确。”</div><div><br /></div><div>据多名了解这一过程的民主党人说，有几个人就竞选经理的位置和拜登团队进行了接触，但他们之后又改变了谈话方向，不是推销其他角色就是承诺担任外部顾问。</div><div><br /></div><div>与此同时，据知情人士说，拜登核心团队的人认为那些自愿表示对这份工作感兴趣的人缺乏担任这一角色所需的经验。这些人说，这种经历包括监督一个庞大的竞选组织，以及在镜头前担任公众形象代言人。</div><div><br /></div><div>（编辑：张月如）</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527322.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (139,'2023-02-19 09:34:49.282026','2023-02-19 09:34:49.282026','曾经很火但消失了的APP 网友第一个想到的是“腾讯微博”','<td id=\"article_content\"><div>微博话题”曾经很火但消失了的APP“冲上了热搜第一名。在这个微博话题下，网友想到了腾讯微博，这条评论数的点赞量最高。另外还有天天动听、人人网、风行网、虾米音乐等等。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div>其中腾讯微博曾是腾讯打造的、对标新浪微博的重要产品，2020年9月4日，腾讯微博突然宣布关停，话题迅速登上新浪微博热搜榜，唤起了许多网友的青春回忆。从2010年4月1日开始小规模内测，到宣布2020年9月28日正式停运，腾讯微博这款插入新浪地盘的“钉子户”产品，用十年结束了使命。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/003348rm74xv7rv7v2yavf.jpg\" target=\"_blank\"><img alt=\"曾经很火但消失了的APP 网友第一个想到的是“腾讯微博”_图1-3\" title=\"曾经很火但消失了的APP 网友第一个想到的是“腾讯微博”\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/003348rm74xv7rv7v2yavf.jpg\" /></a></p><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/003347j4hjyl0eejt723k7.jpg\" target=\"_blank\"><img alt=\"曾经很火但消失了的APP 网友第一个想到的是“腾讯微博”_图1-4\" title=\"曾经很火但消失了的APP 网友第一个想到的是“腾讯微博”\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/003347j4hjyl0eejt723k7.jpg\" /></a></p></div><div><br /></div><div>在微博全盛时期，新浪、网易、搜狐、腾讯等互联网巨头都推出过自己的微博产品，为抢占市场，马化腾曾亲自上阵，不但邀请名人入驻，还发动腾讯高管注册。腾讯公司刘炽平曾透露，截至2011年9月30日，腾讯微博平台的注册用户数超3.1亿。但好景不长，随着新浪微博的高歌猛进，2014年7月，腾讯微博事业部被撤销。</div><div><br /></div><div>以社交产品起家的腾讯微博为何惨淡收场？业内人士表示，腾讯微博入局较晚，丧失先机是其失败的首要原因。记者注意到，腾讯微博2010年5月上线，比2009年8月诞生的新浪微博足足晚了8个月。在这段时间内，新浪微博早已获取一部分固定用户。在用户体验同质化的情况下，腾讯微博想要争夺这部分原始用户并非易事。</div><div><br /></div><div>另外，其他几个互联网巨头的微博产品，面对新浪微博用名人资源和公共话题筑起的“护城河”，也均败下阵来。2014年11月，网易微博宣布正式关闭，将用户迁移到新产品LOFTER上，搜狐微博也在2014年底被张朝阳逐渐放弃。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0033488ffyf57z5yzrzyj8.jpg\" target=\"_blank\"><img alt=\"曾经很火但消失了的APP 网友第一个想到的是“腾讯微博”_图1-5\" title=\"曾经很火但消失了的APP 网友第一个想到的是“腾讯微博”\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/0033488ffyf57z5yzrzyj8.jpg\" /></a></p></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527358.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (140,'2023-02-19 09:34:49.292526','2023-02-19 09:34:49.292526','马云现身澳大利亚 合影背后是一段43年的友情','<td id=\"article_content\"><div>近日，有网友在澳大利亚墨尔本偶遇马云在酒店，照片中他穿着布鞋玩手机喝可乐。今日，马云和外国友人一家的照片也在社交平台被发布。</div><div><br /></div><div>一位阿里巴巴内部人士向第一财经记者确认了马云在澳大利亚的消息，但并未透露关于他的行程。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/203933739jaklb9kl7zh9h.jpg\" target=\"_blank\"><img alt=\"马云现身澳大利亚 合影背后是一段43年的友情_图1-3\" title=\"马云现身澳大利亚 合影背后是一段43年的友情\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/203933739jaklb9kl7zh9h.jpg\" /></a></p></div><div><font color=\"#808080\">马云和澳洲友人合影在社交平台被发布</font></div><div><br /></div><div>在澳大利亚，这一家外国友人与马云延续了四十多年的友情，他们正是43年前在西湖边和中学生马云合影的莫利一家。</div><div><br /></div><div>1980年夏天，正在西湖边找老外练习英语的马云，偶然认识了来自澳大利亚的莫利一家。那年马云15岁，还是个中学生。自此马云和莫利一家成为了笔友，双方经常通信。老莫利给马云回信时，还经常纠正马云的语法错误。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/203932tsbbzbyldi0bfda0.png\" target=\"_blank\"><img alt=\"马云现身澳大利亚 合影背后是一段43年的友情_图1-4\" title=\"马云现身澳大利亚 合影背后是一段43年的友情\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/203932tsbbzbyldi0bfda0.png\" /></a></p></div><div><br /></div><div>1985年，老莫利在信中鼓励马云，并邀请他前往澳洲旅行。</div><div><br /></div><div>马云曾回忆说，尽管当时7次申请签证才最终成行去了纽卡斯尔，但正是年轻时期赴澳度过的这段时光，当地的文化、景色以及人民，彻底改变了自己对于世界的看法。</div><div><br /></div><div>回到杭州后，双方不仅保持了通信，老莫利还为杭州师范学院读书的马云提供支持，两年多的时间里，莫利一家总计向马云寄来了200澳元。</div><div><br /></div><div>又过了十多年，马云在2017年再赴澳大利亚，以2600万澳元（约合2000万美元）的奖学金计划回报Ken曾经给予他的指导和支持。当时，澳大利亚纽卡斯尔大学宣布，获阿里巴巴董事局主席马云通过马云公益基金，拿出2600万澳元设立 “马－莫利奖学金”项目(The Ma &amp; Morley Scholarship Program)。这是纽卡斯尔大学有史以来收到的最大规模捐赠。</div><div><br /></div><div>马云说，这项奖学金将用于“支持那些想自己看看这个世界，经历它、用自己的脑袋思考它的人们。”</div><div><br /></div><div>当时他还在纽卡斯尔大学讲述这段往事，称 “纽卡斯尔那29天，在我的生命中至关重要。没有那29天，我永远也不会像今天这样思考”。 据说，在马云家和办公室里，一直有个位置，放着他与莫利一家的合影。</div><div><br /></div><div>目前尚不知马云澳大利亚之行的具体行程。但据公开报道，近一年来，马云先后前往荷兰、日本、东南亚泰国等多个农业研究机构考察农业渔业技术，春节期间曾现身香港街头采买年货，并多次表示农业科技和乡村教育公益是其接下来的工作重心。</div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527353.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (141,'2023-02-19 09:34:49.295963','2023-02-19 09:34:49.295963','90后年轻人攻占黄金 “中国大妈”后继有人','<td id=\"article_content\"><div>今年春节，崔希回到南方一座三线城市的老家，除了和家人团聚之外，还有一件重要待办事项：逛金店，买金饰。她今年三十岁，目前在成都一家大型连锁店担任区域主管。她对于金子的偏爱源于两个方面，一是认为“年纪到了”，另外就是金子“可以抵御风险”。</div><div><br /></div><div>另一位同事的经历也影响了崔希。同事和她年纪差不多，今年生日收到了男友送的Gucci小羊皮经典款包包，价格在一万元左右。可是同事的反应居然是“不值”，今年俩人计划结婚，而且男友过去一年薪资还降了不少。于是，她的同事把包包拿去退了，直接去金店换成了价值8000多元的金块。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div>今年25岁在西宁读研二的张雯雯已“久经金场”，目前手上拥有42克和12克两个金手镯、4克左右金项链7条，金饰加在一起价值十万余元。对她而言，金饰不仅能带来美丽，“不喜欢的时候可以随时卖掉赚钱”，一举两得。</div><div><br /></div><div>对于正在西宁读大四的王魏烨而言，买金饰远不如直接买金子实在。2月15日，她翻开中国银行APP，上面显示：黄金400元/克。她果断买入了5克。之后不到两小时，金价便涨了4元/克。</div><div><br /></div><div>或抱着投资目的、或冲着保值的考虑，越来越多的90后、00后们成为了黄金消费的主力军。今年春节过后，老凤祥把流动的银楼开在了上海一个时尚地标的商务大楼。根据老凤祥首席发言人王恩生的介绍，银楼开门当天，年轻人在门口排起了长队付款，两天销售额达300万元，其中有65%是年轻人在消费。去年1月，老凤祥也在上海新地标今潮8弄开出了老凤祥“藏宝金”专卖店。老凤祥银楼诞生于1848年，是中国跨越了三个世纪的经典珠宝品牌，在近二十多年的年轻化转型中，王恩生逐渐看到了希望。</div><div><br /></div><div>2013年4月，国际金价暴跌20%，而在这之后的半个月，一群中国中年女性大量收购黄金，引发了世界金价的变动。“中国大妈”也由此一战成名。如今年轻人纷纷“入坑”黄金，也被外界调侃为“中国大妈”已后继有人。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/201408bf4bslvibf77s55f.jpg\" target=\"_blank\"><img alt=\"90后年轻人攻占黄金 “中国大妈”后继有人_图1-3\" title=\"90后年轻人攻占黄金 “中国大妈”后继有人\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/201408bf4bslvibf77s55f.jpg\" /></a></p></div><div><b><br /></b></div><div><b>要好看也要值</b></div><div><br /></div><div>崔希一回家乡就把整个城市各大商场的金店逛了个遍，最后看上了一款周大福新推出的竹节手链，13克左右加上手工费，整个手链约为一万元。这是她能够接受的价格范围。</div><div><br /></div><div>这不是她第一次与金饰结缘。十年前在家乡读大一时，她的男朋友已开始送她金饰作为礼物。对方的妈妈是生意人，从那时起就告诉他们“黄金买回来，以后等着它升值”。不过还没等到升太多值，几年后和男朋友分手，崔希就把对方送的金饰一口气卖掉了。</div><div><br /></div><div>崔希今年回家发现，身边的朋友都在讨论买金饰如何保值，而且朋友们推荐的金手镯“居然很好看”。她发现，如今的金饰完全不同于几年前“土里土气”。“现在老凤祥整体做得比较符合年轻人审美”，让崔希对黄金提起兴趣的是老凤祥去年推出的“范FAN系列”。这个系列的金饰偏哑光，设计简约大方。</div><div><br /></div><div>过往好几年的春节，崔希回家乡购入更多的是衣服、护肤品、口红，“但现在对那些根本没有需求，加上消费降级，只选择保值实用的”。她盘算着，既要好看又要在不确定的环境中“不乱花钱”，唯一仅剩的选择就是：买金饰。</div><div><br /></div><div>但好看也并非全部，虽然周大福旗下潮牌的复古黑桃吊坠设计新颖，但崔希没有将它纳入考虑，因为这个吊坠中的黄金仅1克左右，价格就接近2000元。比较之后，崔希认为按克数计算的金饰更“值得”，她不愿意为仅仅是设计的金饰付出更多价钱。</div><div><br /></div><div>张雯雯也有着一致的心理需求。年后金价趋稳的2月，张雯雯带朋友买金耳钉。走进西安水贝黄金珠宝商城，张雯雯直问，“克重区是哪边？我不买一口价。”顺着导购指出的方向，直奔克重区金柜。</div><div><br /></div><div>排除小细圈耳环，略过偏大的花朵样式，张雯雯和朋友的目标明确：要买年轻人的款式。最后他们买下了一对小兔子与胡萝卜组合的耳钉，2.34克，磨砂结合亮面工艺，精致立体，玲珑可爱，工费35元均价，今年刚好是兔年。开发票的间隙，张雯雯问道：“能送我们一个漂亮的首饰盒吗？”员工在撇见她手上戴的花丝金戒指后，欣然答应。</div><div><br /></div><div>目前张雯雯的“小金库”里有两个手镯、七条项链、一个凤钗。她叹气道：“可惜我第一个金戒指被盗了。”从那以后，她在家里安上智能锁、智能猫眼、网络监控，看护着用17元首饰盒存着的10万元金饰。</div><div><br /></div><div><b>投资、保值</b></div><div><br /></div><div>过往十年，金价处于震动上升，2010年黄金的均价约为280元/克，十年后，2022年黄金的均价涨到了410元/克。比起金饰，投资未被打磨的黄金实体，也受到了年轻人青睐。</div><div><br /></div><div>在投资黄金上，王魏烨很有心得：“黄金最重要的功能是保值，金饰工费属于浪费”。她从不在品牌店买被溢价过的黄金，而是直接在银行等渠道购入黄金。</div><div><br /></div><div>王魏烨从小就会把长辈给的压岁钱放在存钱罐里，随着存款越来越多，妈妈为她开通了银行账户“吃利息”。而利息收入让王魏烨意识到理财的重要性。</div><div><br /></div><div>2017年，金价低迷，上高二的王魏烨成了妈妈买金条的“合伙人”，过后几年金价一路飙升。在小红书上看到有大学生买金收益颇丰，他们会分享买金时机、渠道，分析金价大盘，王魏烨决定亲自入局，目前她手上拥有130黄金，等着高价卖出。</div><div><br /></div><div>囤下不少金饰后，张雯雯计划在金价降低时，入手大克重金条，用来投资。她已经做好了准备，紧盯各大微信群、平台社群，了解金价大盘，金价暴跌、暴涨时，会上微博热搜头条。她了解到，金价与美国经济挂钩，所以会密切关注美国时政新闻、美国经济形势，由此形成自己对金价涨跌的判断，“很多时候这种判断很准”。</div><div><br /></div><div>菜百股份高级黄金投资分析师李洋发现，2008年以来大众接受投资黄金后，到2013年大批消费者抢黄金浪潮，再到2013年之后越来越多年轻人开始关注投资黄金，且在近几年变得尤为明显。</div><div><br /></div><div>“年轻人更关注国际事务对国际金价的影响，例如研究美国货币政策、投资理财知识，很多年轻人逢低会分批买入，另一个就是年底客流量较大，年轻人一般在年底会选择购买金饰或者投资金条给自己或者家人一个奖励。”在李洋的观察中，年轻人现在的投资更加多元化，他们一般会拿10-15%比例的现钱去买金条投资。</div><div><br /></div><div>在美联储加息逐步进入尾声之后，在未来降息预期的影响下李洋预计金价将会有一个很好的表现，“去年金价和美联储货币政策有很大关系，过去一年超市场预期加息7次，金价自2022年3月后冲高回落。去年3月份俄乌战争金价达到历史新高后，美联储加息，导致金价不断下跌。随着美联储加息已经进入尾声，预计下半年随着市场对降息的预期，金价大概会迎来一波不错的反弹”。</div><div><b><br /></b></div><div><b>老字号也要年轻化</b></div><div><br /></div><div>李根是菜百股份首席首饰设计师。20多年前李根入职菜百股份，他一直在追问，“年轻人到底是喜欢什么？”这是他20多年到现在作为设计师最重要的工作方向，也是菜百股份作为中华老字号企业一直在摸索的方向。</div><div><br /></div><div>在二十多年的摸索中，李根发现年轻人越来越能接受传统文化。他们对具有中国传统文化寓意的金饰更有好感。菜百首饰和颐和园、故宫、天坛、首都博物馆、中国火箭联合设计的文创产品成为了大众喜爱的爆款产品之一。</div><div><br /></div><div>要赢得年轻群体的芳心，李根有一个心得，那就是现代工艺的作用。一方面，让产品更加时尚、新潮；另一方面，能让产品变得更易携带、舒适感增强，年轻人也更易接受。菜百股份新推出的黄金与珐琅工艺相结合的“东方花园”系列成为了销售爆款，满足了年轻人对“性价比”的追求，珐琅工艺与黄金的搭配，造型新颖，定价在1000-2000元左右。</div><div><br /></div><div>为了迎合新的受众，老字号的转型也越来越主动。老凤祥首席发言人王恩生说，去年无论是总部，还是近6000家门店的经销商，都有一个强烈的愿望：激发消费热情。这也由此推动着老凤祥做出更多创新的动作，推出高端系列“藏宝金”和时尚品类“范FAN系列”。这些新产品也受到了年轻群体的关注。</div><div><br /></div><div>“我们有一个口号：‘哪怕做传统的黄金，也把它做得时尚’，所以我们在工艺上也做了大量的改良，用k金工艺做足金，改变了过去黄金老土的旧面貌。”王恩生说。</div><div><br /></div><div>按数额算，有实力的中年群体是主要人群，但按款式看，年轻人已成为主力军，这是王恩生去年12月份的体会。那次他接待了一位商界成功人士，对方到店里买了300万元黄金，其中150万-200万元左右为金条，另外的100多万元金饰，全挑的是年轻市场的产品：“我估计是用来奖励年轻员工。”</div><div><br /></div><div>年轻化已是老字号毫无疑问的方向，但如何让中国的年轻群体更能为“一口价”的设计创意首饰买单，王恩生很是忧愁。</div><div><br /></div><div>一位五线城市的老凤祥代理商表示，老凤祥推出的花朵式样金项链，因为做工复杂精美，以“一口价”的形式售卖，但后来销售情况低迷，只能改成按克重加工费的形式卖，才有所好转。</div><div><br /></div><div>王恩生表示，“一口价”正是为了鼓励黄金的创意设计，但中国消费群体在购买中国的金饰时，还是习惯于偏好黄金原材料的保值。“如果我们按克价去计算，就会淡化创意的含量和设计价值”，对比市面上国际奢侈品品牌轻松赢得年轻人青睐，王恩生认为，中国的老字号品牌，在接轨国际，拉近与它们在定价上的距离，还有很长一段路要走。</div><div><br /></div><div><font color=\"#808080\">（应受访者要求，文中崔希为化名）</font></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527352.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (143,'2023-02-19 09:34:49.302031','2023-02-19 09:34:49.302031','雄安将启动首批市场化住宅项目 购房条件曝光','<td id=\"article_content\"><div>雄安新区工作委员会官方公众号近日发布消息，称河北省雄安新区管理委员会已印发《河北雄安新区承接北京非首都功能疏解住房保障实施细则（试行）》及相关配套政策，首批承接北京非首都功能疏解市场化项目住房保障工作即将正式启动。制定出台商品住房配套政策就是其中的一项工作任务。记者近日从雄安新区首批市场化住宅项目某楼盘售楼处获悉，符合与一、二、三类疏解企业(或雄安人力)签署正式合同等“3+1”条件的群体可购房。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div>雄安新区住房管理中心表示，新区实行“多主体供给、多渠道保障、租购并举”的特色住房制度，制定出台商品住房、共有产权住房、机构租赁住房和保障性租赁住房等四类住房配套政策。其中，商品住房和共有产权住房为销售型住房，机构租赁住房和保障性租赁住房为租赁型住房。同时，各居住片区还将同步配套商业服务及教育、医疗、健身等公共服务设施。</div><div><br /></div><div>据悉，住房销售政策适用于容东片区、启动区、高铁站片区等新建市场化项目。截至目前，新区已完成土地出让市场化项目13个，共计456万平方米，已开工4万余套，部分已基本竣工。其中，商品住房184万平方米，共有产权住房40万平方米，机构租赁住房154万平方米，保障性租赁住房78万平方米，可满足约13万人居住。本次住房保障工作推出的项目为华望城，后续将根据建设进度情况陆续推出其他项目。</div><div><br /></div><div>那么，哪些群体可以在雄安新区购房？雄安新区住房管理中心对此做出回应表示，此次住房保障工作面向对象为北京非首都功能疏解单位人员。具体包括符合疏解政策、新区产业发展定位、新区产业准入鼓励类目录的疏解单位，以及为实施新区规划建设管理和服务、完善城市功能、提升疏解承载能力而设立的配套服务单位，上述单位在新区工作的在编人员、与企业签订正式劳动合同的人员和引进人才。</div><div><br /></div><div>此外，记者也致电采访了雄安新区首批商品住房项目华望城的售楼处工作人员，该工作人员表示，符合“3+1”条件的群体可购房。据其介绍，“3+1”条件即必须满足3个条件，另外4个条件满足一个即可。其中，“3”指的是，与一、二、三类疏解企业(或雄安人力)签署正式合同；在雄安新区无房，能开无房证明；具有一、二类疏解企业社保或纳税在新区缴纳满一个月，三类疏解企业社保或纳税在新区缴纳满一年以上。“1”指的是，具有北京户口；新区积分落户；有雄才卡；单位开具的人才证明购房人具有本科以上学历；新区五年社保。</div><div><br /></div><div>随后，记者拨打了雄安新区的政务服务热线电话，向其确认“3+1”条件的真实性，截至记者发稿，还未有回应。</div><div><br /></div><div>与此同时，雄安新区住房管理中心表示，雄安新区实行住房限售政策。商品住房自取得不动产权证书满5年后可按规定上市交易；共有产权住房自购房之日起满5年后可按规定取得全部产权，再满5年后可上市交易；一个家庭只能购买一套商品住房或共有产权住房。</div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527327.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (144,'2023-02-19 09:34:49.312447','2023-02-19 09:34:49.312447','Costco上海浦东店3月10日将开启试营业','<td id=\"article_content\"><div>据“浦东发布”消息，2月17日，浦东店官宣即将于3月10日开启试营业。为提供良好的购物体验，浦东店试运营期间将每日限流2万人，门店建议消费者错峰出行。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div>自去年底开启线上线下会员卡办理以来，浦东店办卡数量一直在稳步增长中。浦东店新会员不仅能享受优惠会费，还能到上海闵行店、苏州店购物。随着开业临近，新会员限时优惠进入倒计时，不少消费者抓紧“入手”，提前感受起了开市客的别样消费体验。</div><div><br /></div><div>开市客浦东店位于康新公路秀浦路，紧邻地铁11号线康新公路站，是上海地区的第二家门店。开市客是美国第二大零售商、全球第七大零售商以及美国最大连锁会员制仓储式量贩超市，开市客浦东店自开工建设起就备受期待。相比上海首店，浦东店购物面积更大，达1.5万平方米，还为消费者提供近1000个停车位。门店的开业将进一步拓展浦东国际消费中心多元消费场景，激发消费新活力。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527325.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (145,'2023-02-19 09:34:49.322178','2023-02-19 09:34:49.322178','Twitter减少在印度工作：关闭两办事处裁掉九层员工','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/1545016vu9lfehxmfxdeqm.jpg\" target=\"_blank\"><img alt=\"Twitter减少在印度工作：关闭两办事处裁掉九层员工_图1-1\" title=\"Twitter减少在印度工作：关闭两办事处裁掉九层员工\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/1545016vu9lfehxmfxdeqm.jpg\" /></a></p></div><div><font color=\"#808080\">美联社资料图</font></div><div><br /></div><div>美国中文网报道  据媒体周五报道，Twitter首席执行官马斯克关闭了该公司在印度的两个办事处。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div>据悉，Twitter关闭了在新德里和孟买的办事处，只剩下在班加罗尔的一个办事处。去年底，Twitter削减了超过90%的印度员工。</div><div><br /></div><div>自从去年10月底接管Twitter以来，马斯克一直在寻求削减该公司的成本。他此前曾表示该公司处于破产的边缘。去年11月，在以440亿元收购Twitter一周后，马斯克解雇了Twitter约一半的员工。</div><div><br /></div><div>在马斯克发出最后通牒后，又有几百名员工在11月中旬辞职。马斯克的最后通牒基本上是告诉员工，要么致力于非常努力工作，要么离开。</div><div><br /></div><div>在去年11月的一封电邮中，马斯克告诉员工，他们必须决定是留下来“长时间高强度工作”，还是接受三个月遣散费。</div><div><br /></div><div>马斯克写道：“未来，要打造一个突破性的推特2.0，并在竞争日益激烈的世界中取得成功，我们需要非常坚定。这将意味着长时间高强度工作。只有出色的表现才算及格。”</div><div><br /></div><div>另据CNN报道，Twitter据称还停止支付其在旧金山和伦敦的办公室租金，面临与这两个地点有关的诉讼。</div><div><br /></div><div>（编辑：张月如）</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527308.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (146,'2023-02-19 09:34:49.322178','2023-02-19 09:34:49.322178','中国证监会发布境外上市备案管理相关制度规则','<td id=\"article_content\"><div>2023年2月17日，经国务院批准，中国证监会发布境外上市备案管理相关制度规则，自2023年3月31日起实施。</div><div><br /></div><div>此次发布的制度规则共6项，包括《境内企业境外发行证券和上市管理试行办法》（以下简称《管理试行办法》）和5项配套指引。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div><br /></div><div>《管理试行办法》共六章三十五条，主要内容：一是完善监管制度。对境内企业直接和间接境外上市活动统一实施备案管理，明确境内企业直接和间接境外发行上市证券的适用情形。二是明确备案要求。明确备案主体、备案时点、备案程序等要求。三是加强监管协同。建立境内企业境外发行上市监管协调机制，完善跨境证券监管合作安排，建立备案信息通报等机制。四是明确法律责任。明确未履行备案程序、备案材料造假等违法违规行为的法律责任，提高违法违规成本。五是增强制度包容性。结合资本市场扩大对外开放实际和市场需要，放宽直接境外发行上市在特定情形下的发行对象限制；进一步便利“全流通”；放宽境外募集资金、派发股利币种的限制，满足企业在境外募集人民币的需求。配套指引内容涵盖监管规则适用、备案材料内容和格式、报告内容、备案沟通、境外证券公司备案等方面，进一步明确和细化备案要求。</div><div><br /></div><div>国家扩大资本市场对外开放的方向不会改变，境外上市备案管理制度规则的发布实施，将更好支持企业依法合规到境外上市，利用两个市场、两种资源实现规范健康发展。</div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527288.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (148,'2023-02-19 09:34:49.332027','2023-02-19 09:34:49.332027','中国发布实施全面实行股票发行注册制制度规则','<td id=\"article_content\"><div>2023年2月17日，中国证监会发布全面实行股票发行注册制相关制度规则，自公布之日起施行。证券交易所、全国股转公司、中国结算、中证金融、证券业协会配套制度规则同步发布实施。</div><div><br /></div><div>全面实行注册制是涉及资本市场全局的重大改革。在各方共同努力下，科创板、创业板和北交所试点注册制总体上是成功的，主要制度规则经受住了市场检验，改革成效得到了市场认可。这次全面实行注册制制度规则的发布实施，标志着注册制的制度安排基本定型，标志着注册制推广到全市场和各类公开发行股票行为，在中国资本市场改革发展进程中具有里程碑意义。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div><br /></div><div>此次发布的制度规则共165部，其中证监会发布的制度规则57部，证券交易所、全国股转公司、中国结算等发布的配套制度规则108部。内容涵盖发行条件、注册程序、保荐承销、重大资产重组、监管执法、投资者保护等各个方面。主要内容包括：一是精简优化发行上市条件。坚持以信息披露为核心，将核准制下的发行条件尽可能转化为信息披露要求。各市场板块设置多元包容的上市条件。二是完善审核注册程序。坚持证券交易所审核和证监会注册各有侧重、相互衔接的基本架构，进一步明晰证券交易所和证监会的职责分工，提高审核注册效率和可预期性。证券交易所审核过程中发现重大敏感事项、重大无先例情况、重大舆情、重大违法线索的，及时向证监会请示报告。证监会同步关注发行人是否符合国家产业政策和板块定位。同时，取消证监会发行审核委员会和上市公司并购重组审核委员会。三是优化发行承销制度。对新股发行价格、规模等不设任何行政性限制，完善以机构投资者为参与主体的询价、定价、配售等机制。四是完善上市公司重大资产重组制度。各市场板块上市公司发行股份购买资产统一实行注册制，完善重组认定标准和定价机制，强化对重组活动的事中事后监管。五是强化监管执法和投资者保护。依法从严打击证券发行、保荐承销等过程中的违法行为。细化责令回购制度安排。此外，全国股转公司注册制有关安排与证券交易所总体一致，并基于中小企业特点作出差异化安排。</div><div><br /></div><div>证监会党委和中央纪委国家监委驻证监会纪检监察组将以强有力监督打造廉洁的注册制。在制度规则层面，健全覆盖发行、上市、再融资、并购重组、退市、监管执法等各环节全流程的监督制约机制，强化关键岗位人员廉政风险防范，加强行业廉洁从业监管。完善对证券交易所、全国股转公司的抵近式监督和对发行审核注册的嵌入式监督，对资本市场领域腐败“零容忍”，持之以恒正风肃纪。</div><div><br /></div><div>全面实行注册制制度规则公开征求意见期间，证监会通过邮件、信函、网络留言等多种渠道广泛听取市场机构、专家学者、社会公众等方面的意见建议。证监会对社会各界提出的意见建议高度重视，逐日汇总整理，逐条认真研究。截至2月16日，共收到意见447条，采纳89条，主要涉及加大违法违规行为惩戒力度、明确独立财务顾问的履职范围、完善非上市公众公司控股股东和实际控制人在重大资产重组中的法律责任规定等方面。未采纳的意见中，有的属于操作层面的问题，将在具体工作中予以落实；有的属于规则理解问题，将通过宣传解读、行业培训等方式予以明确；有的存在较大争议，需要进一步研究论证；还有的缺乏上位法依据，已在相关立法说明中做了解释。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527285.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (149,'2023-02-19 09:34:49.342112','2023-02-19 09:34:49.342112','5天中国3位知名企业家离世 最年轻的仅55岁','<td id=\"article_content\"><div>不到一周的时间，有三位A股上市公司实控人因病离世的消息，引发业内关注。据悉，这三位均为知名浙江企业家，最年轻的年仅55岁。</div><div><br /></div><div>2月11日，杉杉控股发布讣告，杉杉企业创始人、杉杉控股董事局主席郑永刚因病救治无效，不幸逝世，年仅65岁。</div><div><br /></div><div>2月13日，浙江上市公司三力士发布公告，公司创始人吴培生因病不幸逝世，享年75岁。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/0034569c9b9b9kwb1ac9dj.jpg\" target=\"_blank\"><img alt=\"5天中国3位知名企业家离世 最年轻的仅55岁_图1-1\" title=\"5天中国3位知名企业家离世 最年轻的仅55岁\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/0034569c9b9b9kwb1ac9dj.jpg\" /></a></p></div><div><br /></div><div>2月14日，另一家浙江上市公司中马传动发布讣告，公司实际控制人之一、原董事长吴江因病医治无效，不幸病逝，年仅55岁。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/003543i5uql5qqbszzrlrs.jpg\" target=\"_blank\"><img alt=\"5天中国3位知名企业家离世 最年轻的仅55岁_图1-2\" title=\"5天中国3位知名企业家离世 最年轻的仅55岁\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/003543i5uql5qqbszzrlrs.jpg\" /></a></p></div><div><br /></div><div><b>年仅55岁的浙江企业家吴江逝世</b></div><div><b><br /></b></div><div><b>四天前刚刚辞任董事长</b></div><div><br /></div><div>公告显示，吴江1968年出生于浙江温岭，曾担任民建台州市委会委员、温岭支部副主委、温岭市工商联副主席、温岭市汽摩配行业协会会长。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/003628yohbr1r29cl0laag.jpg\" target=\"_blank\"><img alt=\"5天中国3位知名企业家离世 最年轻的仅55岁_图1-3\" title=\"5天中国3位知名企业家离世 最年轻的仅55岁\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/003628yohbr1r29cl0laag.jpg\" /></a></p></div><div><br /></div><div>1985年，吴江的父亲吴良行放弃了国营企业中层领导的职务，下海创办了温岭县大众齿轮厂（中马集团前身）。1988年，未满20岁的吴江高中毕业后，也跟随父亲开始共同创业。</div><div><br /></div><div>公开资料显示，中马传动成立于2005年12月，2017年6月在上交所上市，主要从事汽车变速器及车辆齿轮的研发、生产和销售。公司主要产品为汽车变速器、汽车齿轮、摩托车齿轮和农机齿轮。</div><div><br /></div><div>据该公司此前公告，吴江自2016年11月起便开始担任中马传动董事长，从这家公司上市之初便掌舵了发展之路。</div><div><br /></div><div>在吴江去世消息发布前仅四天，中马传动刚刚披露了变更董事长及法人代表的公告。</div><div><br /></div><div>2月10日晚间，中马传动公告，公司实际控制人吴江、吴良行基于公司发展及个人原因，不再连任第六届董事会董事，吴江、吴良行通过提名非独立董事候选人、独立董事候选人及非职工代表监事候选人方式参与公司经营管理。</div><div><br /></div><div>中马传动表示，公司换届完成后，吴江不再担任公司任何职务。</div><div><br /></div><div><b>知名专家型企业家吴培生逝世</b></div><div><b><br /></b></div><div><b>深耕橡胶行业近40年</b></div><div><br /></div><div>吴培生是浙江绍兴人，出生于1947年，先后当选为绍兴县十一、十二届人大代表、中国橡胶工业协会胶管胶带协会副理事长。历任州山橡胶五金厂厂长、绍兴县胶带厂厂长、绍兴三力士橡胶有限公司总经理、三力士股份有限公司董事长等职务，现任三力士董事。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/003648t6rhbtyc6dy8jttq.jpg\" target=\"_blank\"><img alt=\"5天中国3位知名企业家离世 最年轻的仅55岁_图1-4\" title=\"5天中国3位知名企业家离世 最年轻的仅55岁\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/003648t6rhbtyc6dy8jttq.jpg\" /></a></p></div><div><br /></div><div>1984年，吴培生创立了三力士的前身——州山橡塑五金厂，随后带领公司飞速发展并实现了上市。1989年，吴培生注册了“三力士”的商标，并作为公司的主品牌一直沿用至今。</div><div><br /></div><div>在橡胶行业，吴培生深耕了近40年时间，是知名的专家型企业家，还曾当选为中国橡胶工业协会胶管胶带协会副理事长。2019年10月，吴培生卸任董事长职务，由其女吴琼瑛接手董事长之位。</div><div><br /></div><div>直到现在，三力士仍然专注橡胶制品业务。2021年年报显示，橡胶V带是三力士最主要的产品，营收占总营收的86.47%。2021年年报显示，吴培生和吴琼瑛2021年的税前薪酬分别为61.18万元和73.73万元。</div><div><br /></div><div><b>“服装大王”郑永刚逝世</b></div><div><b><br /></b></div><div><b>曾创立中国服装业第一家上市公司</b></div><div><br /></div><div>据报道，郑永刚1958年出生于浙江宁波。1989年5月，郑永刚赴任宁波甬港服装总厂厂长，并在宁波创立了杉杉品牌。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/0036497mik4sbrizvgmkzy.jpg\" target=\"_blank\"><img alt=\"5天中国3位知名企业家离世 最年轻的仅55岁_图1-5\" title=\"5天中国3位知名企业家离世 最年轻的仅55岁\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/0036497mik4sbrizvgmkzy.jpg\" /></a></p></div><div><br /></div><div>在郑永刚接任厂长的第二年，杉杉西服在上海的南京路、淮海路上的十家专卖店借柜上架，掀起购买狂潮。“杉杉西服，不要太潇洒”也成为当年知名的广告语。</div><div>　　</div><div>1996年，杉杉股份成为中国服装业第一家上市公司。根据杉杉企业网站介绍，到1997年，杉杉服装和杉杉品牌“达到前所未有的发展高峰”，西服的市场占有率达到了37.4%，掌舵人郑永刚亦被称作“服装大王”。</div><div>　　</div><div>就在服装业务如日中天时，杉杉集团却掉转车头，向高科技企业转型。自1999年起，杉杉股份开始进军刚刚起步的锂电池产业。从服装起家，到转型锂电材料，再到近两年来布局偏光片，如今的杉杉股份已成为全球锂电池负极材料和偏光片“双龙头”，产能持续扩张。目前，杉杉股份市值已超400亿元。</div><div>杉杉股份企业微信此前发布过一则郑永刚的创业感悟。报道称，作为一名职业企业家，郑永刚的追求是“把企业做好做精，创造大量就业岗位，对国家、对民族做贡献”。</div><div>　　</div><div>但他指出，企业家是熬出来的，杉杉从1999年开始做锂电池经历20多年的转型，直到近几年全球新能源才真正爆发。这个熬的过程，要注重人才，注重企业文化，注重研发投入。在他看来，只要战略决策正确，看清楚未来，就全身心投入。</div><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;text-align:center;margin:4px auto; margin-bottom:5px;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527277.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (150,'2023-02-19 09:34:49.342112','2023-02-19 09:34:49.342112','连续下跌一年后 1月中国70城新房价格环比止跌','<td id=\"article_content\"><div>2月16日，中国国家统计局发布2023年1月份70个大中城市商品住宅销售价格变动情况。1月，70个城市新房价格指数环比涨幅为0，同比涨幅为-2.3%，这是2022年2月以来，新房价格首次止跌。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div>一线和二线城市商品住宅销售价格分别环比上涨0.2%和0.1%，三线城市则继续下跌0.1%，但降幅较去年12月收窄0.2个百分点。</div><div><br /></div><div>此外，新房价格环比上涨的城市数量也较上月有了显著增长。2023年1月，70个大中城市中，新建商品住宅上涨城市数量36个，较2022年12月增加21城；下跌城市33个，减少22城。</div><div><br /></div><div>具体到各个城市，1月新建商品住宅价格环比涨幅前9的城市分别为上海、合肥、宁波、成都、湛江、西宁、南充、北京、杭州。而青岛、扬州、蚌埠、北海的环比涨幅并列第十位。</div><div><br /></div><div>二手住宅方面，1月份价格环比上涨城市有13个，比去年12月增加了6个。一线城市二手房价格环比上涨0.4%，为连续三个月下跌后首次止跌反弹。其中，北京、深圳、上海二手房价格分别环比上涨0.9%、0.6%、0.4%，广州二手房价格环比下滑0.2%。</div><div><br /></div><div>新房价格指数的环比止跌与上涨城市的增加也被行业内视作市场回暖迹象，不过从同比上看，市场价格暖意仍不明显。</div><div><br /></div><div>新建商品住宅销售价格同比上涨城市有15个，比上月减少了1个；二手住宅销售价格同比上涨城市仅有6个，与上月一致。并且，市场分化显著，成都、北京和上海分别以8.8%、4.3%和2.3%的同比涨幅位列前三。牡丹江、哈尔滨和吉林的二手房价格同比跌幅分别达到了11%、9.1%和8.8%</div><div><br /></div><div>从成交量看，1月楼市依旧未摆脱寒意。根据同策研究院数据，一线和二线城市成交规模同比均下降超过30%，环比降幅分别达23.18%和38.44%。三四线城市成交规模同比降39.14%，环比降32.47% 。受春节假期影响，市场整体下滑，不同城市的市场热度仍然呈现较为明显的分化；从存量方面来看，北京、广州、深圳及部分二三四线城市去化周期超过20个月，烟台高达69个月，市场去化压力持续。</div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527275.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (152,'2023-02-19 09:34:49.352384','2023-02-19 09:34:49.352384','中国居民存款破纪录 1月居民存款增加￥6.2万亿','<td id=\"article_content\"><div>　　中国人民银行近日披露的1月份金融数据显示，1月份人民币存款增加6.87万亿元，同比多增3.05万亿元。其中，住户存款增加6.2万亿元，创历史同期新高，同比多增7900亿元。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div>　　中信证券首席经济学家明明在接受人民网采访时表示，居民存款破纪录原因有三点，一是资产价值与收入水平的不确定性较大，居民倾向于更多的储蓄；二是受房地产行业风险等影响，居民部门的购房意愿有所降低，潜在的购房配置资金向储蓄转移；三是2022年末出现了理财净值受损，导致投资者纷纷将理财赎回至存款，虽然目前赎回潮基本结束，但是仍对居民的资产配置偏好有所影响。</div><div><br /></div><div>　　招联首席研究员、复旦大学金融研究院兼职研究员董希淼认为，从短期因素看，主要是新冠感染疫情、经济下行带来的冲击。与此同时，2022年以来受国际金融市场震荡影响，中国资本市场波动加剧，股票、基金收益明显下降，特别是银行理财产品出现“破净”，导致居民风险偏好有所下降，部分资金重新流向存款。</div><div><br /></div><div>　　“居民存款持续增加提高我国经济发展韧性，增强居民部门抵御风险能力，但也反映出居民对未来预期转弱、消费需求不足、投资意愿下降等问题。下一步，应采取针对性的措施，稳住居民预期和信心，进一步提振居民消费和投资。”董希淼说。</div><div><br /></div><div>　　未来，存款能否释放出来？将流向哪些领域？明明表示，考虑到经济复苏和消费修复的大趋势，2023年基本面的改善有望带动预防性储蓄的释放。配合政策引导支持和消费场景增加，一部分资金会流向消费领域，另一部分很有可能随着金融市场表现改善，转化为投资性支出，用于基金、理财等产品配置上。</div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527273.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (153,'2023-02-19 09:34:49.362034','2023-02-19 09:34:49.362034','为何此时对美国企业宣布制裁？中国商务部回应','<td id=\"article_content\"><div>中国商务部新闻发言人就不可靠实体清单有关问题答记者问。</div><div><br /></div><div><b>问：中国商务部为何此时对美国企业宣布制裁，是否与无人飞艇事件有关？</b></div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div>答：近年来，洛克希德·马丁公司、雷神导弹与防务公司不顾中方强烈反对，屡次对台实施军售，严重损害中国国家安全，严重损害中国国家主权和领土完整。我们根据《对外贸易法》《国家安全法》等有关法律，依据《不可靠实体清单规定》第二条等规定，将美洛克希德·马丁公司、雷神导弹与防务公司列入不可靠实体清单并采取相应措施，是依法采取的正常执法行为。</div><div><br /></div><div><b>问：中方首次使用“不可靠实体清单”对两家对台军售企业实施制裁，请问有何考虑？</b></div><div><br /></div><div>答：台湾问题是中国内政，事关中国核心利益，绝不容许任何外部干涉。一个中国原则是公认的国际关系基本准则和国际社会普遍共识。近年来，洛克希德·马丁公司、雷神导弹与防务公司不顾中方强烈反对，屡次对台销售导弹、战斗机等进攻性武器，严重损害中国国家安全，严重损害中国国家主权和领土完整，严重违背一个中国原则和中美三个联合公报规定，严重破坏台海和平稳定。我们对严重损害中国主权、安全、发展利益的实体，依法追究其不法责任。</div><div><br /></div><div><b>问：“不可靠实体清单”的启用是否意味着中国对外资的态度有所转变？</b></div><div><br /></div><div>答：《不可靠实体清单规定》的适用范围是严格限定的，针对的是极个别违法的外国实体，不会随意扩大范围，广大外商投资企业没有必要担心。我想强调的是，中国政府坚定不移推进高水平对外开放，坚定维护多边贸易体制，坚定维护各类市场主体合法权益。党的二十大和中央经济工作会议都明确提出要更大力度吸引和利用外资，中国政府将以更大力度欢迎世界各国企业来华投资兴业，以更大力度建设市场化、法治化、国际化的营商环境。</div><div><br /></div><div>我还想强调，中方做法符合世贸组织规则。中方始终支持以世贸组织为代表的多边贸易体制，支持自由贸易，推动世界经济贸易健康发展。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527263.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (155,'2023-02-19 09:34:49.372062','2023-02-19 09:34:49.372062','中国央行、银保监会回应房贷提前还款难问题','<td id=\"article_content\">据财联社消息，针对近期反映较多的房贷提前还款难、预约时间长等问题，中国央行、银保监会2月9日召开部分商业银行座谈会，要求商业银行强化以客户为中心的理念，保障客户合法权益，改进提升服务质量，按照合同约定做好客户提前还款服务工作。针对部分借款人违规使用经营贷、消费贷提前还款的情况，要求商业银行持续做好贷前贷后管理，加强风险警示；监管部门将加大检查处罚力度，及时查处违规中介并披露典型案例。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;text-align:center;margin:4px auto; margin-bottom:5px;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527233.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (156,'2023-02-19 09:34:49.377128','2023-02-19 09:34:49.377128','中国将洛克希德·马丁公司、雷神导弹与防务公司列入不可靠实体清单','<td id=\"article_content\"><div>2月16日，中国商务部网站发布《不可靠实体清单工作机制公告》，全文如下：</div><div><br /></div><div>为维护国家主权、安全和发展利益，根据《中华人民共和国对外贸易法》《中华人民共和国国家安全法》等有关法律，不可靠实体清单工作机制依据《不可靠实体清单规定》第二条、第八条和第十条等有关规定，<b>决定将参与对台湾地区军售的洛克希德·马丁公司（Lockheed Martin Corporation）、雷神导弹与防务公司（Raytheon Missiles &amp; Defense）列入不可靠实体清单</b>，并采取以下处理措施：<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div><br /></div><div>一、禁止上述企业从事与中国有关的进出口活动；</div><div><br /></div><div>二、禁止上述企业在中国境内新增投资；</div><div><br /></div><div>三、禁止上述企业高级管理人员入境；</div><div><br /></div><div>四、不批准并取消上述企业高级管理人员在中国境内工作许可、停留以及居留资格；</div><div><br /></div><div>五、对上述企业分别处以罚款，金额为《不可靠实体清单规定》实施以来各企业对台军售合同金额的两倍。上述企业应当自本公告公布之日起15日内，按照相关法律法规办理缴款。若逾期不履行本决定，不可靠实体清单工作机制将依法采取加处罚款等措施。</div><div><br /></div><div>本公告未尽事宜，按《不可靠实体清单规定》执行。</div><div><br /></div><div>本公告自公布之日起实施。</div><div><br /></div><div>不可靠实体清单工作机制</div><div><br /></div><div>（商务部代章）</div><div><br /></div><div>2023年2月16日</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527232.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (157,'2023-02-19 09:34:49.382139','2023-02-19 09:34:49.382139','广西南宁房贷最长可贷至100岁？多方回应','<td id=\"article_content\"><div>2月16日，网络上流传一张建发房产与广西南宁部分银行合作，购房者的贷款最长可以贷至100岁的海报。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/16/0150445s1o62lyvo405ejj.jpg\" target=\"_blank\"><img alt=\"广西南宁房贷最长可贷至100岁？多方回应_图1-1\" title=\"广西南宁房贷最长可贷至100岁？多方回应\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/16/0150445s1o62lyvo405ejj.jpg\" /></a></p></div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div>澎湃新闻以购房者的身份拨打了海报上相关楼盘的电话咨询贷款一事，楼盘销售人员表示，海报上的内容属实，目前如果来购买海报上公示出的楼盘，结合购房者的实际情况，若符合银行的贷款政策就可以操作。</div><div><br /></div><div>“实际就是接力贷，以父母的名义来购房，子女作为接力贷的共同贷款人，贷款人年龄加上贷款期限最长就能贷到100岁。这样其实还贷压力小很多的。”对于具体如何贷款，该楼盘销售称，只要征信正常，具体要贷款人提交资料后银行来判定。</div><div><br /></div><div>16日，北京商报记者致电开发商进行询问，相关人士对此事件予以否认，表示：“未出过相关宣传海报。”</div><div><br /></div><div>对此消息的真实性，北京商报记者分别致电民生银行，浦发银行南宁地区网点咨询。</div><div><br /></div><div>浦发银行某支行个贷人士表示，最长可贷至100岁信息不实，该行目前执行的业务并非“接力贷”，而是“合力贷”，该业务借款人年龄+期限可贷至70周岁，以共同借款人中年龄最小的购房者认定借款期限。</div><div><br /></div><div>民生银行相关客户经理也表示，目前该行借款人年龄+期限为可贷至70周岁。“没有能贷到100岁的情况，有点夸张。”上述客户经理说道。</div><div><br /></div><div>就在前几日，南宁已有多个楼盘的住房按揭贷款年龄可延长至80岁。</div><div><br /></div><div>2月11日，据“南国早报”微信公众号消息，2月11日，南宁多个楼盘对外宣称“住房按揭贷款年龄期限可延长至80岁”，记者从南宁多家银行了解到，确有其事，目前已有部分银行在执行。</div><div><br /></div><div>报道称，对于上述贷款年龄期限可延长至80岁的政策，大唐地产、龙光地产、银光地产、绿地集团等多家房企的相关营销负责人均称已知悉该项政策，个别房企明确表示“已在跟进”，近期旗下多个主力楼盘将与合作银行推出类似房贷政策。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527228.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (158,'2023-02-19 09:34:49.382139','2023-02-19 09:34:49.382139','史上最大担保之一 两斯坦福大学教授出手帮助他获释','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/15/191144c23t6kcc0k6mm8d0.jpg\" target=\"_blank\"><img alt=\"史上最大担保之一 两斯坦福大学教授出手帮助他获释_图1-1\" title=\"史上最大担保之一 两斯坦福大学教授出手帮助他获释\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/15/191144c23t6kcc0k6mm8d0.jpg\" /></a></p></div><div><font color=\"#808080\">美联社资料图</font></div><div><br /></div><div>美国中文网报道  据媒体周三报道，两名斯坦福大学教授共同签署了加密大亨FTX创始人班克曼-弗里德（（Bankman-Fried））的2.5亿元保释协议。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div>一名联邦法官公布了两名教授的姓名，这两人共同签署了班克曼-弗里德的保释协议，该协议允许他在等待联邦欺诈和共谋指控的审判期间被软禁。</div><div><br /></div><div>在周三下午公开的文件中，法院透露，斯坦福大学法学院前院长拉里•克雷默（Larry Kramer）和斯坦福大学计算机科学家安德烈亚斯•派普克（Andreas Paepcke）分别签署了担保书。</div><div><br /></div><div>克雷默和派普克分别签署了价值50万元和20万元的担保。班克曼-弗里德的父母都是斯坦福大学的法学教授，他们也是担保人，并利用他们在加州帕洛阿尔托的家来担保。</div><div><br /></div><div>克雷默在给媒体的一份声明中称，班克曼-弗里德的父母自上世纪90年代中期以来一直是亲密的朋友。</div><div><br /></div><div>克雷默说，“在过去的两年里，当我的家人面临与癌症的痛苦斗争时，他们一直是最亲近的朋友…反过来，我们想支持他们，因为他们正应对着自己的危机。”</div><div><br /></div><div>他补充说，他“除了帮助我们忠诚而坚定的朋友之外，在这件事上没有任何商业往来或利益。”</div><div><br /></div><div>检察官称，班克曼-弗里德的保释协议是有史以来规模最大的审前担保之一。但是，除非他不出庭，否则无论是班克曼-弗里德还是担保人都不必支付任何金额。</div><div><br /></div><div>30岁的班克曼-弗里德自12月12日在巴哈马的豪华公寓大楼被捕。之后，他的律师杰罗尼•罗伯茨 (Jerone Roberts) 说，班克曼-弗里德“急于离开”巴哈马群岛，希望尽早回美国。</div><div><br /></div><div>联邦检察官在提交给纽约市曼哈顿法官的一封信中表示，他们有证据表明班克曼-弗里德“挪用FTX客户的资金供其个人使用，包括为自己的账户进行投资，从而欺骗FTX客户，以赚取数十亿元。他还进行了大量政治捐款，以及支付Alameda Research数十亿元的开支和债务。Alameda Research是一家由班克曼-弗里德创立的加密货币对冲基金。</div><div><br /></div><div>（编辑：张月如）</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527218.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (160,'2023-02-19 09:34:49.392121','2023-02-19 09:34:49.392121','中国首次本土检出XBB.1.5 又开始发烧是否等于复阳？','<td id=\"article_content\"><div>近日，关于“国内首次本土检出XBB.1.5”的相关报道引起广泛关注。与此同时，多位网友在社交媒体称自己又开始发烧，担心是否复阳。</div><div><br /></div><div>对此，北京佑安医院呼吸与感染性疾病科主任医师李侗曾18日向媒体表示，中国现阶段免疫屏障比较强，近期不太可能暴发新一波疫情。针对网友提到的新一轮发烧现象，他表示，除了少数还没阳过的人感染新冠，大部分发热病例和新冠没有关系。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/215452uaipbz5lzqkcp2iq.jpg\" target=\"_blank\"><img alt=\"中国首次本土检出XBB.1.5 又开始发烧是否等于复阳？_图1-3\" title=\"中国首次本土检出XBB.1.5 又开始发烧是否等于复阳？\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/215452uaipbz5lzqkcp2iq.jpg\" /></a></p></div><div><font color=\"#808080\">资料图：市民接种新冠疫苗第二剂加强针。周利朔 摄</font></div><div><br /></div><div><b>未来不排除更多地方</b></div><div><b><br /></b></div><div><b>出现XBB系列毒株的感染者</b></div><div><br /></div><div>今年2月15日，中国疾控中心发布通报，2022年12月以来，全国共发现本土重点关注变异株14例，其中1例XBB.1.5，为本周期新增变异株病例。</div><div><br /></div><div>李侗曾向媒体介绍，XBB免疫逃逸能力更强，在美国等地区的新发病例中占比正逐渐升高，未来不排除更多地方出现XBB系列毒株的感染者。</div><div><br /></div><div>不过，李侗曾强调，中国现阶段免疫屏障比较强，近期不太可能暴发新一波疫情，仍以散发病例为主，目前新增确诊病例主要是还未感染过的人，被完全相同的毒株感染两次的可能性非常小。</div><div><br /></div><div>他表示，新的变异株感染者增加了，人群发生二次感染的风险会增加。面对新的变异株，人群的免疫屏障保护的时间会缩短，但不是完全没有效果，所以短期内再次出现一波疫情的概率很小。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/215451o197obaw2xow5d7d.jpg\" target=\"_blank\"><img alt=\"中国首次本土检出XBB.1.5 又开始发烧是否等于复阳？_图1-4\" title=\"中国首次本土检出XBB.1.5 又开始发烧是否等于复阳？\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/215451o197obaw2xow5d7d.jpg\" /></a></p></div><div><font color=\"#808080\">资料图：候车室内，返校大学生数量增多。王伟臣 摄</font></div><div><br /></div><div><b>近期大部分发热病例和新冠无关</b></div><div><br /></div><div>对于最近一些发热病例，李侗曾认为，除了少数是还没阳过的人感染了新冠，大部分发热病例和新冠没有关系。“当然不排除有过去3个月阳过的人因为其他疾病导致发热再次就诊的情况，但没有证据显示和新冠病毒有关。”他说。</div><div><br /></div><div>李侗曾介绍，2020年起，复阳病例就一直存在，一些人持续两三个月还是阳性，或者曾经检测阴性后再次阳性。他表示，这部分人主要是一些免疫低下人群，多见于器官移植后使用免疫抑制药物，艾滋病免疫低下，或者肿瘤放化疗阶段的免疫下降人群。</div><div><br /></div><div>李侗曾指出，复阳和再次感染是两个概念。复阳是同一次感染，一般不会有传染性，不会致病。“再次感染和大家说的‘重阳’应该是一个意思，即再次感染病毒，通常和上次感染不是同一个毒株，或者遇到一个变异株后，发生再次感染的风险更高一些。”他表示，一般认为3个月内的再次检测阳性者，属于复阳的可能性大，属于再次感染的可能性很小。</div><div><br /></div><div>李侗曾还表示，复阳的人基本没有传染性，没有病毒相关症状，只是因为其他情况就诊发现核酸阳性或者抗原阳性。</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527355.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (161,'2023-02-19 09:34:49.402500','2023-02-19 09:34:49.402500','“20元人民币老爷爷”去世 享年94岁 去年还在江上撑筏子','<td id=\"article_content\"><div>2月18日，广西桂林，阳朔县漓江景区发布视频称，20元人民币上的渔夫的原型黄全德老爷爷于18日凌晨4时离世，享年94岁。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/200611x7v33extti422kig.jpg\" target=\"_blank\"><img alt=\"“20元人民币老爷爷”去世 享年94岁 去年还在江上撑筏子_图1-3\" title=\"“20元人民币老爷爷”去世 享年94岁 去年还在江上撑筏子\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/200611x7v33extti422kig.jpg\" /></a></p></div><div><br /></div><div>据极目新闻，黄全德是广西桂林市阳朔县人，2022年7月，记者采访黄全德时，94岁的他依然“忙碌”，在漓江上跟游客拍照的间隙，跟记者通电话。</div><div><br /></div><div>据记者了解，黄全德从十二三岁起便撑着筏子在漓江上打渔，后来加入《印象刘三姐》团队，本色出演渔翁角色。2008年，黄全德跟随弟弟黄月创的脚步也加入了渔翁拍摄的行列，平时除了与游客拍照，还承接摄影师或摄影团队的拍摄邀约，是漓江上最年长的渔翁。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/200600pl68rlfuuj86jpvv.jpg\" target=\"_blank\"><img alt=\"“20元人民币老爷爷”去世 享年94岁 去年还在江上撑筏子_图1-4\" title=\"“20元人民币老爷爷”去世 享年94岁 去年还在江上撑筏子\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/200600pl68rlfuuj86jpvv.jpg\" /></a></p><p><br /></p><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/2006009oqah6hq56k622q9.png\" target=\"_blank\"><img alt=\"“20元人民币老爷爷”去世 享年94岁 去年还在江上撑筏子_图1-5\" title=\"“20元人民币老爷爷”去世 享年94岁 去年还在江上撑筏子\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/2006009oqah6hq56k622q9.png\" /></a></p></div><div><br /></div><div>一名阳朔县兴坪镇渔业居委村民曾告诉记者，黄全德虽然年纪大，但身子骨很硬朗，做渔翁“模特”十几年了，即使九十多岁高龄，每天还出去在漓江边和他人合影，竹筏也能撑得很快。</div><div><br /></div><div>记者从黄全德的儿子黄先生处了解到，去年11月开始，父亲的身体状况就不是很好，当时就没有去江上撑竹筏跟乘客合影了。离世前，父亲已经昏迷了多天。黄先生也感谢各位网友和记者朋友对父亲的关心。</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527351.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (163,'2023-02-19 09:34:49.412449','2023-02-19 09:34:49.412449','麻烦不断！同名同姓韩裔移民被分配同一社安号','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/165223d4kmkk69kjcmrrm9.jpg\" target=\"_blank\"><img alt=\"麻烦不断！同名同姓韩裔移民被分配同一社安号_图1-1\" title=\"麻烦不断！同名同姓韩裔移民被分配同一社安号\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/165223d4kmkk69kjcmrrm9.jpg\" /></a></p></div><div><font color=\"#808080\">社安卡。美联社资料图</font></div><div><br /></div><div>美国中文网报道  如果有人与你同名同姓，同日出生，你可能会惊叹这是一种缘分。但据NBC新闻报道，两名这种情况的韩裔移民因此被分配到了同一社安号（Social Security number），继而遭遇了无穷无尽的麻烦。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div><br /></div><div>在过去五年中，同样31岁、名叫金智恩（Jieun Kim，音译）但分别居住在洛杉矶和芝加哥的两位韩裔女性，遭遇了银行账户被关停、信用卡被冻结、怀疑身份欺诈等一系列囧事。</div><div><br /></div><div>她们指出，这些问题的根源是，社安局要么不愿、要么无力解决它犯下的错误——将同一组社安号分配给两个人。</div><div><br /></div><div>芝加哥的金智恩说，社安局工作人员告诉她：“这类问题在亚裔身上时有发生，因为他们的名字有时非常相似。”但在她提交申请，要求得到一个新的社安号后，社安局仍旧寄给她和之前一样的号码。她说，工作人员这样向她解释：“电脑认定你们是同一个人。”</div><div><br /></div><div>洛杉矶的金智恩的遭遇更离谱。她说，社安局工作人员警告她，不要因为这一错误小题大做，以免影响她拿到绿卡的进程。“工作人员告诉我，如果讨论社安号错误问题，可能导致原本6个月就完成的绿卡程序延长为2-3年。”她说。</div><div><br /></div><div>洛杉矶的金智恩出生在首尔，2012年来美学习，之后找到工作并结婚。芝加哥的金智恩则出生在首尔以南的小城市安养。她2017年来美留学。</div><div /></td>','美国中文网','http://www.sinovision.net/society/202302/00527347.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (164,'2023-02-19 09:34:49.412584','2023-02-19 09:34:49.412584','不再积极治疗 前总统卡特将接受临终关怀','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/154223otm4jbz4hss9mjme.jpeg\" target=\"_blank\"><img alt=\"不再积极治疗 前总统卡特将接受临终关怀_图1-1\" title=\"不再积极治疗 前总统卡特将接受临终关怀\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/154223otm4jbz4hss9mjme.jpeg\" /></a></p></div><div><font color=\"#808080\">美联社资料图</font></div><div><br /></div><div>美国中文网报道 前总统吉米·卡特（Jimmy Carter）的代表周六宣布，他将开始接受临终关怀。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div><br /></div><div>卡特中心在一份声明中称：“在一系列短期入院治疗后，前美国总统吉米·卡特今天决定，在家与家人一起度过他最后的日子，接受临终关怀，而不在进行进一步的介入治疗了。他的家人和医疗团队对此全力支持。”</div><div><br /></div><div>卡特已98岁，是仍在世的前总统中最年长的，也是美国历史上最长寿的总统。</div><div><br /></div><div>卡特在2015年被诊断出患有黑色素瘤，当时已扩散到肝脏和大脑，但他在接受治疗后不久宣布癌症痊愈。</div><div><br /></div><div>（编辑：张赋杰）</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527346.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (165,'2023-02-19 09:34:49.422395','2023-02-19 09:34:49.422395','枪击、捅人…媒体称纽约市学校附近暴力案件激增','<td id=\"article_content\"><div>美国中文网报道 《纽约邮报》周六报道称，纽约市的学校内或附近区域发生的枪击、暴力袭击等罪案近来呈现爆炸式增长。</div><div><br /></div><div>报道称，2022-23学年至今，已经有3名在读学生在暴力案件中死亡，18人被枪击或捅伤。而在前一学年，有1名学生遇害、8人在暴力案件中受伤。报道称，大多数罪案发生在学校放学后。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div><br /></div><div>生活在乔治亚州的Shateema Watson曾力劝自己在纽约市史丹顿岛读高中的弟弟Nyheem Wright搬来和自己住，因为她认为那里不安全。就在1月20日，Wright在放学后的一场打斗中被捅伤丧命，事发地离他的学校半英里远。警方认为，这起打斗因该校的一个女生而起。</div><div><br /></div><div>在这个血腥的学年中，纽约市还发生了多起类似案件，其中大多与黑帮有关。去年11月，18岁的Mark Greene在离他位于Kew Gardens Hills学校半个街区外的公交车站被枪击身亡。9月7日，15岁的Unique Smith从布鲁克林实验特许学校放学后，在4个街区外被枪杀。</div><div><br /></div><div>另外，16岁的Angellyh Yambo在从位于布朗士的学校步行回家时，在仅1个街区以外被黑帮的流弹误击中身亡。她的两个同学也受伤。</div><div><br /></div><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/1505244zs4haxzizjbxzag.jpg\" target=\"_blank\"><img alt=\"枪击、捅人…媒体称纽约市学校附近暴力案件激增_图1-3\" title=\"枪击、捅人…媒体称纽约市学校附近暴力案件激增\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/1505244zs4haxzizjbxzag.jpg\" /></a></p></div><div><font color=\"#808080\">资料图：纽约市学生参加集会，呼吁控枪。美联社</font></div><div><br /></div><div>报道指，在学校附近活跃的黑帮充斥着未成年人，而纽约州的法律让执法部门有时无能为力。据报道，根据新修改的法律，对于绝大多数犯罪，18岁以下的未成年在纽约不能被视为成年人接受审判。这意味着对于涉枪这类严重犯罪，很多青年未得到应有的惩罚。</div><div><br /></div><div>对此，皇后区地检凯兹（Melinda Katz）对《纽约邮报》指出：“尽管（提高受审年龄）的改革是早该进行的，但这也造成了威胁公共安全的副作用，迫切需要解决。</div><div><br /></div><div>报道引述匿名执法人员指，黑帮正有意利用法律的漏洞，让那些青少年持枪，因为后者即便因此被捕，也不会有严重后果。报道称，2022年，因持枪被捕的18岁以下未成年人数量激增到448人，比2017年的275人增加了64%. </div><div><br /></div><div>报道称，过去，那些年轻人首次碰枪的年龄是16或17岁，而现在已经低至12岁。</div><div><br /></div><div>据报道，更多的在校学生被卷入黑帮，他们甚至将武器带进学校。《纽约邮报》引述的数据指，截至1月8日，本学年警方在校内查获了2984件武器，包括8支枪、168支电击枪以及1192把刀。这一数字趋势和上一学年相似：2021-22学年，警方累计查获6389件武器，包括21把枪。</div><div><br /></div><div>而在疫情前的最后一个正常学年，校内查获的武器数量是2137件。</div><div><br /></div><div>但另一方面，这些孩子不愿向学校袒露自己的秘密，因为担心自己或家人因此受伤害。</div><div><br /></div><div>因应这些变化，纽约市警已经加强了在学校附近的部署。但报道指出，这对于遏制校内的暴力事件效果有限。</div><div><br /></div><div>(编辑：张赋杰)</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527344.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (168,'2023-02-19 09:34:49.437068','2023-02-19 09:34:49.437068','噩梦持续近一年…女子旅行时被绑架 终施巧计逃脱','<td id=\"article_content\"><div>美国中文网报道 新泽西州警方周五通报一起离奇案件：一名女子近1年前进行环美旅行时被一名男子绑架，在经历了几乎一整年的失去自由、颠沛流离之后，这名女子终于成功实施了逃离计划，在新泽西州的一个加油站获救。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div>官方称，绑架者是57岁的帕利罗（James W. Parrillo Jr.），受害者女性的身份未披露。整个故事要回溯到2022年2月的新墨西哥州：当时，帕利罗用假名字接近这名女子。在他的请求下，女子同意开车载他到亚利桑那州。</div><div><br /></div><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/120753gmcgeimlafyghco2.jpeg\" target=\"_blank\"><img alt=\"噩梦持续近一年…女子旅行时被绑架 终施巧计逃脱_图1-3\" title=\"噩梦持续近一年…女子旅行时被绑架 终施巧计逃脱\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/18/120753gmcgeimlafyghco2.jpeg\" /></a></p></div><div><font color=\"#808080\">嫌犯帕利罗。据新泽西警方</font></div><div><br /></div><div>两人自愿发生关系，但约一个月之后，帕利罗开始打她。那时女子已觉得不安全，想要离开；彼时他们旅行至加州。</div><div><br /></div><div>但是，帕利罗拿走了女子的手机，没收了她的信用卡，并切断了她和家人的联系。最终，两人于去年12月抵达新泽西州。在那里，帕利罗继续用假名租了房子。</div><div><br /></div><div>另一方面，女子在发现他们曾光顾的这家加油站的商店有门闩时，就开始筹划如何逃跑。</div><div><br /></div><div>2月7日，帕利罗在一次争吵中对女子锁喉，于是他决定实施逃跑计划。加油站老板马丹（Bobby Madaan）告诉CNN，当时女子跑进加油站的商店，把门锁住。随后他就听到尖叫和吼叫声。一名男子追着女子跑进加油站，但无法进入上锁的门，只好转身离开。</div><div><br /></div><div>“她当时在颤抖、哭泣；她告诉我们，自己已被绑架了近1年。”马丹回忆称。他还表示，当时女子脖子上有勒痕，她只穿着短裤和单薄上衣，没有穿鞋。商店里的一名顾客把他妻子的备用鞋给了这名女子。他们还给她找了一件夹克披上。在报警后，帕利罗当日晚些时候即被捕。</div><div><br /></div><div>官方指，这起绑架案令人“极度不安”，他们已与多个州执法部门取得联系，调查此案。另外，检方还赞扬这名女性的坚韧和勇敢，称这无异于任何英雄的行为。</div><div><br /></div><div>被捕后，帕利罗已被控一级绑架以及非法拘禁等多项罪名。</div><div><br /></div><div>（编辑：张赋杰）</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527340.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (171,'2023-02-19 09:34:49.447351','2023-02-19 09:34:49.447351','中国国产新冠口服药VV116在沪开出首张处方','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/204604axpbwbzw62aiw2wz.jpg\" target=\"_blank\"><img alt=\"中国国产新冠口服药VV116在沪开出首张处方_图1-1\" title=\"中国国产新冠口服药VV116在沪开出首张处方\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/204604axpbwbzw62aiw2wz.jpg\" /></a></p></div><div><font color=\"#808080\">VV116在复旦大学附属浦东医院开出首张处方。</font></div><div><br /></div><div>2月17日，国产新冠口服药——氢溴酸氘瑞米德韦片（商品名称：民得维®，产品代号：VV116）在复旦大学附属浦东医院开出首张处方，标志着这一沪研沪产新冠口服药正式在中国投入临床使用，为我国新冠患者带来治疗新选择。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div>民得维®由中科院上海药物所、浦东创新药企业——君实生物等单位联合研发，于2023年1月28日获得国家药品监督管理局附条件批准用于治疗轻中度新型冠状病毒感染（COVID-19）的成年患者。得益于上海试点实施的药品上市许可持有人制度（MAH），这一“张江研发+上海制造”的科技成果获批仅19天后，率先在浦东开花落地，惠及患者。</div><div><br /></div><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/204642cedgy3gz1lz3cvse.jpeg\" target=\"_blank\"><img alt=\"中国国产新冠口服药VV116在沪开出首张处方_图1-4\" title=\"中国国产新冠口服药VV116在沪开出首张处方\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/204642cedgy3gz1lz3cvse.jpeg\" /></a></p></div><div>作为一款新型口服核苷类抗病毒药物，民得维®能够以核苷三磷酸形式非共价结合到新冠病毒RNA依赖性RNA聚合酶（RdRp）的活性中心，直接抑制病毒RdRp的活性，阻断病毒的复制，从而发挥抗病毒的作用。临床前研究显示，民得维®对包括奥密克戎在内的新冠病毒原始株和突变株表现出显著的抗病毒作用，且无遗传毒性。</div><div><br /></div><div>复旦大学附属浦东医院呼吸与危重症学科兼感染科主任、上海市新冠肺炎重症专家组成员、浦东新区新冠肺炎重症专家组成员包红主任表示:“去年我们医院积极参与了VV116的临床研究。如今，看到患者能够这么快地用上这款药物，我感到非常高兴！VV116在疗效上不输进口药物，尤其是药物相互作用（DDI）可能性小，这意味着很多有合并用药的患者限制少了很多。而更多数据表明，除了伴有重症高风险因素的患者，普通患者在服药后也可获益，像发热、头痛、咳嗽、咽喉痛等十多种症状的缓解和消失速度以及转阴速度会更快。”</div><div><br /></div><div>据悉，民得维®首发报价795元/盒/疗程，按照国家相关政策要求，该药品临时性纳入基本医保基金支付范围。患者按照不同的参保人群、按照年龄段，在不同级别的医院报销比例不同。患者仅需承担医保报销后的个人自付部分，如果按个人自付10％计算，仅需不到100元。</div><div><br /></div><div>“相对于进口药物，VV116的疗效安全性完全不逊色，价格更加亲民，相信能够进一步减轻用药负担，惠及我国更广大的患者群体。”包红主任表示。</div><div><br /></div><div>据了解，民得维®一个疗程为期5天，每隔12小时服用一次，空腹或餐后服用均可。“新冠口服药应该在病毒感染初期服用，越早服用效果越显著，尤其对于老年人、有基础性疾病或具有重症高风险因素的患者，更应该在确诊后尽快服用。”包红主任补充说。</div><div><br /></div><div>目前，首批民得维®已进入浦东新区等多家医疗机构，并正式向全国供药。</div><div><br /></div><div>君实生物联席首席执行官李聪先生表示：“在民得维获批的第一时间，公司便立即着手整合各部门的资源，争分夺秒地安排投产。未来将继续努力提升民得维的可及性与可负担性，持续保障这一药物的生产供应，最大程度地为患者用药寻求便利，让更多有需要的患者及时用上高品质的国产创新药。”</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/society/202302/00527320.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (172,'2023-02-19 09:34:49.452360','2023-02-19 09:34:49.452360','星巴克解雇参与组建工会员工 法官发布禁令','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/185337u152tpub7b492t2t.jpg\" target=\"_blank\"><img alt=\"星巴克解雇参与组建工会员工 法官发布禁令_图1-1\" title=\"星巴克解雇参与组建工会员工 法官发布禁令\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/185337u152tpub7b492t2t.jpg\" /></a><font color=\"#808080\">美联社资料图   </font></p></div><div>美国中文网报道  密歇根一名联邦法官周五发布禁令，禁止星巴克解雇寻求组建工会或从事其他集体活动的员工。</div><div><br /></div><div>这是与劳工运动相关的第一个全国性的司法授权。在一年多一点的时间里，星巴克的275家门店参与了组建工会的活动。星巴克表示将对这一决定提起上诉。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div><br /></div><div>专家表示，这一禁令将允许全国劳工关系委员会（National Labor Relations Board）寻求法官的帮助，让因组织工会而被解雇的工人寻求更快的复职。正常情况下，这个过程可能需要几个月甚至几年。</div><div><br /></div><div>组织星巴克门店的工会工人联合会（Workers United）指责该公司解雇了200多名工人，因为他们参与了这项运动。在美国，一家公司因所谓的受保护的协同活动（如寻求成立工会）而解雇员工是违法的。</div><div><br /></div><div>这项禁令是由劳工委员会地区办公室在汉娜•惠特贝克（Hannah Whitbeck）一案中提出的，汉娜是一名轮班监督员，参与了工会运动。她在四月份被解雇。</div><div><br /></div><div>惠特贝克在一份声明中说，“我很高兴我的案子得到了更高层面的关注，并让人们注意到星巴克因员工组建工会而非法解雇他们。”</div><div><br /></div><div>法官马克•A•戈德史密斯（Mark A. Goldsmith）下令星巴克，在华盛顿的国家劳工关系委员会（National Labor Relations Board）对惠特贝克的案件进行裁决时，对她的工作进行复职。</div><div><br /></div><div>曾任奥巴马政府劳工委员会主席的威尔玛•利伯曼（Wilma Liebman）表示，法官最终可能会认定星巴克藐视禁令，如果有令人信服的证据表明该公司没有遵守禁令，就可能对该公司处以罚款。</div><div><br /></div><div>星巴克在一份声明中表示：“我们认为，在对此事进行全面法律审查之前采取特别禁令措施是没有根据的，并坚持认为所采取的行动是合法的，并且符合既定的员工政策。”</div><div><br /></div><div>星巴克表示，该公司之所以解雇惠特贝克，因为她在轮班期间提前离开了门店，迫使一名同事独自管理门店大约20或30分钟。这违反了公司要求至少有两名员工在场的规定。</div><div><br /></div><div>惠特贝克没有对案件的事实提出异议，但争辩说，如果她没有参与工会活动，佩戴支持工会的标志，这种违规行为就不会导致被解雇。</div><div><br /></div><div>（编辑：张月如）</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/society/202302/00527318.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (174,'2023-02-19 09:34:49.466456','2023-02-19 09:34:49.466456','调查显示：计算机专业薪水或降4% 这两类专业或涨薪','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/17271862sf7eggy3gge933.jpg\" target=\"_blank\"><img alt=\"调查显示：计算机专业薪水或降4% 这两类专业或涨薪_图1-1\" title=\"调查显示：计算机专业薪水或降4% 这两类专业或涨薪\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/17271862sf7eggy3gge933.jpg\" /></a></p></div><div><font color=\"#808080\">美联社资料图</font></div><div><font color=\"#808080\"><br /></font></div><div>美国中文网报道  近期频繁的科技大厂大规模裁员，将对2023年美国大学毕业生的就业情况产生不小的影响。一项最新调查显示，对计算机专业的学生而言，其毕业后的起薪将比去年同期下降4%，而传播和人文专业的学生起薪则会上涨接近5%。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div>最近几周，微软、Alphabet和Meta等大型科技公司裁掉了数万名员工，使得疫情期间激增的用人热潮迅速降温。疫情期间，数百万美国人不得不远程办公，使得市场对数字产品的需求猛增。</div><div><br /></div><div>然而现在，计算机专业的毕业生已开始面临薪酬窘境。根据全国大学和雇主协会（NACE）的一项最新研究，经济放缓给高科技行业带来了削减开支的压力，这导致今年的计算机科学专业的毕业生拿不到期望中的高薪。</div><div><br /></div><div>目前还不清楚这种影响是否只是短期现象，但这一问题很可能会对毕业生的整个职业生涯都产生影响。特别是如果美国像一些经济学家预测的那样，陷入衰退，影响将会更大。因为研究发现，在经济低迷期进入劳动力市场，员工的收入会受影响长达十年之久。</div><div><br /></div><div>尽管如此，这项调研了170名雇主的研究仍显示，计算机专业的毕业生仍将享受相对较高的起始年薪，接近7.3万元。而且研究还发现，超过半数的雇主打算为应届大学毕业生涨薪，涨幅一般在3%至5%之间。</div><div><br /></div><div>NACE高级研究人员安德里亚·孔茨（Andrea Koncz）说，“与2022届毕业生相比，今年毕业生的薪水似乎更稳定，除计算机和社会学这两个专业外，其他专业的薪资都有所增加。”</div><div><br /></div><div>与计算机专业的情况相反，一些文科专业今年可能出现薪资上涨的趋势。研究发现，和去年同期相比，拥有人文专业学位的毕业生薪水将提高4.5%，而传播专业毕业生将获得4.8%的薪资提升。</div><div><br /></div><div>虽如此，文科专业总体而言的收入依旧远低于计算机专业。例如，哲学、历史、文学和艺术等专业，今年的毕业起薪可能为5.3万元，比计算机专业低了近2万元。</div><div><br /></div><div>（编辑：刘坤喆）</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527312.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (175,'2023-02-19 09:34:49.472469','2023-02-19 09:34:49.472469','每周回办公室工作四天 迪斯尼员工表不满','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/181757okn5ncil4bmvkb3b.jpg\" target=\"_blank\"><img alt=\"每周回办公室工作四天 迪斯尼员工表不满_图1-1\" title=\"每周回办公室工作四天 迪斯尼员工表不满\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/181757okn5ncil4bmvkb3b.jpg\" /></a></p></div><div><font color=\"#808080\">美联社资料图</font></div><div><br /></div><div>美国中文网报道  《华盛顿邮报》周四报道，迪士尼的员工正在对每周四天到办公室上班的规定进行施压，发起请愿，要求首席执行官鲍勃•伊格尔重新考虑这一规定。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div><br /></div><div>根据这份请愿书，这一计划将于下个月生效，可能会对迪士尼造成无意的“长期伤害”。超过2300名迪士尼公司的员工在请愿书上签名，这些公司包括美国广播公司、20世纪工作室、漫威工作室，Hulu、皮克斯、FX等。</div><div><br /></div><div>迪士尼的员工总数超过20万人。</div><div><br /></div><div>据报道，那些在请愿书上签名的员工认为，这一变化将迫使一些来自弱势社区的员工以及那些难以替代的员工辞职，并将显著降低生产力、产量和效率。请愿书还指出：“这一政策将减缓甚至逆转我们在后新冠时代的复苏和增长。”</div><div><br /></div><div>特斯拉和Twitter的首席执行官马斯克去年告诉他领导的两家公司的员工，他们必须每周在办公室工作至少40小时。</div><div><br /></div><div>本周早些时候公布的一项调查显示，全国10个主要城市的办公室半满，是自2020年3月疫情开始前以来的最高水平。</div><div><br /></div><div> 《华盛顿邮报》报道称，一名员工表示，迪斯尼没有事先就返回办公室工作的政策向员工咨询意见，这也使得员工很失望。</div><div><br /></div><div>（编辑：张月如）</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/society/202302/00527316.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (176,'2023-02-19 09:34:49.477010','2023-02-19 09:34:49.477010','20名消防员受伤！纽约史丹顿岛突发大火','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/173128jdjp9rdqv90rgvy9.jpg\" target=\"_blank\"><img alt=\"20名消防员受伤！纽约史丹顿岛突发大火_图1-1\" title=\"20名消防员受伤！纽约史丹顿岛突发大火\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/173128jdjp9rdqv90rgvy9.jpg\" /></a></p></div><div><font color=\"#808080\">消防员在现场灭火。据纽约消防局推特</font></div><div><br /></div><div>美国中文网报道 纽约市消防局周五傍晚通报，当日早些时候，20名消防员在纽约史丹顿岛灭火时受伤，其中3人伤势严重。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div><br /></div><div>这场大火发生于周五下午1点半左右，地点位于史丹顿岛的Shotwell大道。现场照片和视频显示，火势很大。消防员赶到后不久，这场火已升级为4级大火。</div><div><br /></div><div>而在消防员进入屋内灭火时，房屋的后半部分突然倒塌，废墟砸到了消防员身上。消防员并未因房屋坍塌受困，但过大的火势使他们一时无法撤离。</div><div><br /></div><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/173148ekj71j8m62e85q4q.png\" target=\"_blank\"><img alt=\"20名消防员受伤！纽约史丹顿岛突发大火_图1-4\" title=\"20名消防员受伤！纽约史丹顿岛突发大火\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/173148ekj71j8m62e85q4q.png\" /></a></p></div><div><font color=\"#808080\">视频显示，起火房屋部分坍塌。纽约市消防局推特视频截图</font></div><div><br /></div><div>最终，所有进入室内的消防员都被救出。</div><div><br /></div><div>官方称，起火约1小时后，火势已被控制，但倒塌的废墟内仍有燃烧点。</div><div><br /></div><div>(编辑：张赋杰)</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527313.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (178,'2023-02-19 09:34:49.482456','2023-02-19 09:34:49.482456','被美国制裁  Facebook还为他做广告？','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/1555235hg7schs5russrr3.jpg\" target=\"_blank\"><img alt=\"被美国制裁  Facebook还为他做广告？_图1-1\" title=\"被美国制裁  Facebook还为他做广告？\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/17/1555235hg7schs5russrr3.jpg\" /></a></p></div><div><font color=\"#808080\">美联社资料图</font></div><div><br /></div><div>美国中文网报道    美联社消息，Facebook之前曾允许亲俄罗斯的摩尔多瓦政党舍尔党及其领导人伊兰·舍尔（Ilan Shor）在其社交媒体上投放广告。这位被美国政府列入制裁名单的政治人物，在广告中呼吁摩民众起来反抗和推翻该国的亲西方政府。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div>虽然这一广告最终被Facebook删除，但在此之前，广告已在摩尔多瓦被观看了数百万次。摩尔多瓦是一个位于罗马尼亚和乌克兰之间的小国，人口约260 万。</div><div><br /></div><div>去年10月，美国财政部曾宣布，对与克里姆林宫有联系的摩尔多瓦舍尔党及其领导人伊兰·舍尔实施制裁，因为后者是该国反政府抗议活动的背后支持力量。</div><div><br /></div><div>据悉，舍尔的付费广告针对的是该国亲西方总统玛雅·桑杜(Maia Sandu)的政府，后者在本周早些时候曾表示“俄罗斯阴谋利用外部破坏者推翻摩现任政府”。据报道，这些广告反映出，俄罗斯及其盟友如何使用包括Facebook在内的社交媒体平台，利用民众对通货膨胀和油价上涨的愤怒情绪，来传播虚假信息，并企图破坏现任政府。</div><div><br /></div><div>“即使他在美国制裁名单上，我仍然在Facebook上看到他的广告”，摩尔多瓦前总理加夫里利塔的外交政策顾问多林·弗拉西诺 (Dorin Frasineau) 说，舍尔的广告助长了对政府的愤怒情绪，旨在破坏摩尔多瓦的稳定并使其重返俄罗斯的势力范围。</div><div><br /></div><div>根据相关规则，美国公司被禁止与列入制裁名单的个人和团体进行任何金融交易。当被问及这些广告的情况时，负责管理制裁计划的美国财政部拒绝进行评论。</div><div><br /></div><div>在给美联社的一份声明中，拥有Facebook和Instagram的公司Meta表示，他们在发现这些广告后，已立即进行了删除。</div><div><br /></div><div>“当舍尔及其政党被列入美国制裁名单时，我们对他们的已知账户采取了行动，”公司发言人说。“当我们发现其新的关联账户时，也对这些账户采取了行动。我们坚持美国的制裁法律，并将继续检测和打击那些违反我们政策的虚假账户。”</div><div><br /></div><div>最近宣布大规模裁员的Meta没有回答有关其在摩尔多瓦的员工人数或讲摩尔多瓦语的员工人数的问题。美联社称，与许多总部设在美国的大型科技公司一样，Meta有时难以管理英语以外的其他语言的内容。</div><div><br /></div><div>（编辑：刘坤喆）</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527307.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (179,'2023-02-19 09:34:49.495565','2023-02-19 09:34:49.495565','反转？台媒曝刘文正前经纪人称其尚在人间','<td id=\"article_content\"><div>据台媒“ETtoday新闻云”2月15日消息，前经纪人夏玉顺悲痛证实，台湾歌手刘文正于2022年11月的生日前夕因心肌梗塞离世，享年70岁。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1040\"><script type=\"text/javascript\" id=\"closeAd-1038\">&lt;!--// &lt;![CDATA[\r\n    /* [id1040] 娱乐新闻详细页 Admaru */\r\n    OA_show(1040);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=c1367dd\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1040&amp;n=c1367dd\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id886] 娱乐详情页文包图广告 */\r\n    OA_show(886);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=88ea083\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=886&amp;n=88ea083\" /></a></noscript></div></div><div>16日，台媒披露上述消息出现反转。</div><div><br /></div><div>据台媒“ETtoday新闻云”消息，夏玉顺16日接受“ETtoday新闻云”访问，被问到“心肌梗塞是否是编出来的”时，他随即否认，“他（刘文正）真的有心肌梗塞，但是医好了”，而且是在刘文正过生日前夕救回来的，“反正就当他走了就好了，他永远不会出来了。”</div><div><br /></div><div>上述台媒报道表示，因为近日有业者出价20亿新台币要刘文正复出，他才会选择诈死。夏玉顺改口坦言，刘文正目前人还在拉斯韦加斯。</div><div><br /></div><div>夏玉顺在接受采访时说：“在人间啦、在人间啦！可是永远不会见人啦，所以叫我发说他已经走了，免得大家还在那边唠唠叨叨要他复出、要他怎么样，所以我是故意发的，其实是不会见任何人、也不会有任何澄清。”</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/entertainment/202302/00527226.htm',4);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (181,'2023-02-19 09:34:49.513713','2023-02-19 09:34:49.513713','台湾歌手刘文正在美去世 享年70岁','<td id=\"article_content\"><div>15日，据台媒报道，刘文正的前经纪人夏玉顺证实，刘文正已于去年11月12日因心肌梗塞病逝于美国赌城拉斯维加斯，享年70岁。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1040\"><script type=\"text/javascript\" id=\"closeAd-1038\">&lt;!--// &lt;![CDATA[\r\n    /* [id1040] 娱乐新闻详细页 Admaru */\r\n    OA_show(1040);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=c1367dd\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1040&amp;n=c1367dd\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id886] 娱乐详情页文包图广告 */\r\n    OA_show(886);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=88ea083\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=886&amp;n=88ea083\" /></a></noscript></div></div><div>刘文正是上世纪70、80年代华语流行乐坛的代表性男歌手。1975年他发行第一张个人专辑《诺言》成名，同年主演了第一部电影《门里门外》，此后又制作主持了多档收视率极高的节目，开创了电视综艺节目的新里程碑。</div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/15/023827ww0m4e4l30hee3g8.png\" target=\"_blank\"><img alt=\"台湾歌手刘文正在美去世 享年70岁_图1-3\" title=\"台湾歌手刘文正在美去世 享年70岁\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/15/023827ww0m4e4l30hee3g8.png\" /></a></p></div><div style=\"text-align: center;\"><font color=\"#808080\">微博截图</font></div><div><br /></div><div>1984年他结束了和宝丽金的合约后淡出歌坛，至美国生活。1986年底回到中国台湾，转至幕后，成立并经营飞鹰唱片，陆续培养出方文琳、伊能静、裘海正、巫启贤等艺人。</div><div><br /></div><div>1991年，刘文正结束飞鹰唱片，正式移居美国，至此全面自演艺圈隐退。</div><div><br /></div><div>刘文正的前经纪人夏玉顺证实刘文正已于去年11月离世，他表示：“以前每个月都会跟他联络，最后一次见到他是两年前”，夏玉顺说，他去年12月有一天睡不着，心里觉得不舒服，打电话给刘文正家人才知道他已离开的事情，最近在家里看到他的照片很难过、很想念他。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/entertainment/202302/00527179.htm',4);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (188,'2023-02-19 09:34:49.552236','2023-02-19 09:34:49.557258','歌手蕾哈娜周日登“超级碗”中场秀 她身价现在值这么多','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/10/174455zr7vrjlyaosr7rlr.jpg\" target=\"_blank\"><img alt=\"歌手蕾哈娜周日登“超级碗”中场秀 她身价现在值这么多_图1-1\" title=\"歌手蕾哈娜周日登“超级碗”中场秀 她身价现在值这么多\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/10/174455zr7vrjlyaosr7rlr.jpg\" /></a></p></div><div><font color=\"#808080\">美联社资料图</font></div><div><br /></div><div><br /></div><div>美国中文网报道  本周日，第57届超级碗（Super Bowl LVII）比赛将在亚利桑那州格兰岱尔的州立农业体育馆举行。歌手蕾哈娜 (Rihanna) 将在其“中场秀”环节登台表演，这将是她7年后重返该舞台。有美国媒体对这位白手起家的明星身价进行了盘点，并称她是“世界上为数不多的女亿万富翁之一”。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1040\"><script type=\"text/javascript\" id=\"closeAd-1038\">&lt;!--// &lt;![CDATA[\r\n    /* [id1040] 娱乐新闻详细页 Admaru */\r\n    OA_show(1040);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=c1367dd\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1040&amp;n=c1367dd\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id886] 娱乐详情页文包图广告 */\r\n    OA_show(886);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=88ea083\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=886&amp;n=88ea083\" /></a></noscript></div></div><div>据《福布斯》杂志报道，蕾哈娜目前的净资产为14亿元，是当今最富有的音乐家和第二富有的女艺人，仅次于身价25亿元的温弗瑞·奥普拉。</div><div><br /></div><div>蕾哈娜因音乐而出名，但她也深谙音乐之外的生财之道，她的美妆系列显然就为其财富的增加助了一臂之力。</div><div><br /></div><div>2017年，她推出了自己的彩妆系列Fenty Beauty，该品牌因提供多种肤色的粉底而赢得了用户的青睐。以她的真名Robyn Fenty命名的该品牌，与法国奢侈品集团LVMH以50/50的比例合资，后者还拥有Louis Vuitton等主要时尚品牌。《福布斯》指出，Fenty系列在2020年的销售额超过了5.5亿元。</div><div><br /></div><div>此外，蕾哈娜还拥有Savage x Fenty内衣系列30%的股份，该系列在2021年2月的估值达到10亿元。</div><div><br /></div><div>亿万富翁榜单通常都被白人男性大量占据，从白手起家的Amazon创始人杰夫·贝索斯，到继承巨额财富的沃尔玛创始人山姆·沃尔顿之子吉姆·沃尔顿。而像蕾哈娜这样的移民女性能取得如此成功，实属罕见。蕾哈娜来自加勒比岛国巴巴多斯。</div><div><br /></div><div>2021年，她告诉《纽约时报》，希望自己“亿万富翁的新身份”能激励那些和她一样的人，“能对像我一样出身卑微、父母是移民或自己是移民的年轻男女有所激励，我感觉很好”。</div><div><br /></div><div>就此次超级碗的表演，蕾哈娜对CBS News表示，希望自己的中场秀表演“能代表移民、代表黑人女性、代表巴巴多斯”。</div><div><br /></div><div>今年的“超级碗”比赛将在本周日下午开球，在美联（AFC）冠军堪萨斯城酋长队和国联（NFC）冠军费城老鹰队之间进行，由曾主裁过第51届和第55届超级碗的卡尔·切弗斯担任主裁判。</div><div><br /></div><div>在赛前表演部分，将由美国乡村音乐歌手克里斯·特普尔顿演唱国歌。中场秀环节，将由Apple Music作为新赞助商，而在过去的连续10个赛季里，赞助商一直是百事可乐。</div><div><br /></div><div>（编辑：刘坤喆）</div></td>','美国中文网','http://www.sinovision.net/entertainment/202302/00527012.htm',4);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (189,'2023-02-19 09:34:49.563352','2023-02-19 09:34:49.563352','林志颖仍开特斯拉 称车祸未给其造成阴影','<td id=\"article_content\"><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/06/221716zcc1i1r1v1k9heix.png\" target=\"_blank\"><img alt=\"林志颖仍开特斯拉 称车祸未给其造成阴影_图1-1\" title=\"林志颖仍开特斯拉 称车祸未给其造成阴影\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/06/221716zcc1i1r1v1k9heix.png\" /></a></p></div><div><br /></div><div>据最新报道，林志颖在此前驾驶特斯拉Model X车祸后，已经恢复的差不多了，甚至开始驾车外出。</div><div><br /></div><div>不过，日前他驾车出门却违规停车被罚款，因为林志颖将车辆停在货车停车位内。值得注意的是，有网友注意到了车辆的细节，发现林志颖似乎不开特斯拉了，此次被罚的车辆是宝马i3。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1040\"><script type=\"text/javascript\" id=\"closeAd-1038\">&lt;!--// &lt;![CDATA[\r\n    /* [id1040] 娱乐新闻详细页 Admaru */\r\n    OA_show(1040);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=c1367dd\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1040&amp;n=c1367dd\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id886] 娱乐详情页文包图广告 */\r\n    OA_show(886);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=88ea083\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=886&amp;n=88ea083\" /></a></noscript></div></div><div><br /></div><div>对此，也有媒体问到了他本人对特斯拉是否有新的看法。林志颖大方回应称，自己仍然在开一辆黑色的特斯拉，没有任何阴影，如果有阴影早卖了。</div><div><br /></div><div>据悉，林志颖本人一直都是特斯拉的资深粉丝，最近几年甚至多次在公开场合对特斯拉赞誉有加。</div><div><br /></div><div>在车祸前他还曾发文表示，“很多人说开了电动车就回不去了，尤其是习惯了AP自动驾驶之后，结果这是真的，又重新点燃开车的乐趣。”林志颖曾介绍，自己刚开始开Model 3时，只是拿来代步。后来因为这车的底盘低、重心在脚下，开起来像卡丁车，上赛道过弯时很顺，速度根本不输几百万的超跑，因此成为林志颖的心头好，他也把特斯拉拿来当赛车开。</div><div><br /></div><div>据报道，林志颖把车违停在货车停车格内，随后进入一旁的咖啡厅内用餐，与友人相谈甚欢。交警巡查发现，林志颖将电动车停在货车停车位内，将处罚600台币到1200台币，大约136元到272元人民币。据悉，这也是林志颖自2022年7月22日发生车祸以来，首次公开出现在媒体镜头前。</div></td>','美国中文网','http://www.sinovision.net/entertainment/202302/00526823.htm',4);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (192,'2023-02-19 09:34:49.582550','2023-02-19 09:34:49.582550','中国央视元宵晚会节目亮点公布 周深魏晨等现身彩排','<td id=\"article_content\"><div>花灿灯彩闹元宵。3日，中央广播电视总台《2023年元宵晚会》举行发布会，介绍了晚会多项技术创新和节目创作亮点。</div><div><br /></div><div>据介绍，元宵晚会以“花灯耀四海”为主线，北京灯彩、秦淮灯彩、硖石灯彩、潮州花灯等一系列入选国家级非物质文化遗产名录的中国传统民俗工艺将在晚会中惊喜亮相，与东西南北中多地灯会的实景拍摄相呼应，展现元宵佳节美丽中国的喜庆色彩。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1040\"><script type=\"text/javascript\" id=\"closeAd-1038\">&lt;!--// &lt;![CDATA[\r\n    /* [id1040] 娱乐新闻详细页 Admaru */\r\n    OA_show(1040);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=c1367dd\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1040&amp;n=c1367dd\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id886] 娱乐详情页文包图广告 */\r\n    OA_show(886);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=88ea083\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=886&amp;n=88ea083\" /></a></noscript></div></div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/03/034556suuuaolihflll7si.png\" target=\"_blank\"><img alt=\"中国央视元宵晚会节目亮点公布 周深魏晨等现身彩排_图1-3\" title=\"中国央视元宵晚会节目亮点公布 周深魏晨等现身彩排\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/03/034556suuuaolihflll7si.png\" /></a></p></div><div>晚会开场歌曲邀请今年春晚微电影《我和我的春晚》中感动了亿万观众的张建国扮演者金长勇，与现场演员共同合唱，在欢腾热烈的歌声中共迎癸卯兔年的第一个月圆之夜。由众多演奏家创新打造新国乐曲目《百凤朝阳》，奏出中华民族昂扬向上的精气神。全新演绎的《新花好月圆夜》，将河北竹板书和苏州评弹两种曲艺形式进行融合创作，表达出圆满和美的寓意。由中国人民解放军仪仗司礼大队奏唱的歌曲《强军有我》，以豪迈的激情、磅礴的气势，展现了中国军人的血性和担当，唱出了“请党放心，强军有我”的铿锵誓言……</div><div><br /></div><div>2月5日20点档，中央广播电视总台《2023年元宵晚会》将在央视综合频道、综艺频道、中文国际频道、音乐频道等电视频道，音乐之声、经典音乐广播、文艺之声、大湾区之声、台海之声、华语环球广播、南海之声等广播频率，央视频、云听、央视网、央广网、国际在线等新媒体平台同步播出。</div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/03/034613jly9gjrbogg9ogjc.png\" target=\"_blank\"><img alt=\"中国央视元宵晚会节目亮点公布 周深魏晨等现身彩排_图1-4\" title=\"中国央视元宵晚会节目亮点公布 周深魏晨等现身彩排\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/03/034613jly9gjrbogg9ogjc.png\" /></a></p></div><div>另据新浪综艺、搜狐娱乐，在昨日的彩排中，出现了希林娜依·高、汪苏泷、周深、魏晨、毛不易、张远、郭俊辰等人的身影。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/entertainment/202302/00526694.htm',4);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (193,'2023-02-19 09:34:49.582550','2023-02-19 09:34:49.582550','《狂飙》主演张颂文仅关联一家影视公司','<td id=\"article_content\"><div>近期，反黑刑侦剧《狂飙》大火，剧中人物及热梗多次登上热搜，该剧主演之一张颂文也受到关注。据悉，张颂文关联公司为厦门市思明区张颂文影视文化工作室，成立于2019年8月，企业类型为个人独资企业，投资人为张颂文，出资额50万元，经营范围包括文化、艺术活动策划；文艺创作与表演；电影和影视节目制作；演出经纪业务等。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1040\"><script type=\"text/javascript\" id=\"closeAd-1038\">&lt;!--// &lt;![CDATA[\r\n    /* [id1040] 娱乐新闻详细页 Admaru */\r\n    OA_show(1040);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=c1367dd\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1040&amp;n=c1367dd\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id886] 娱乐详情页文包图广告 */\r\n    OA_show(886);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=88ea083\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=886&amp;n=88ea083\" /></a></noscript></div></div><div><br /></div><div>早前张颂文受访时曾表示，中国只有0.5%的演员能做到衣食无忧，剩下99.5%的演员都收入极低，工作18个小时收入极低且廉价，还被不停的压榨，依旧无人关注，大家只关注那0.5%的人。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/222614evvz8eee9r3c9ovd.png\" target=\"_blank\"><img alt=\"《狂飙》主演张颂文仅关联一家影视公司_图1-3\" title=\"《狂飙》主演张颂文仅关联一家影视公司\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/222614evvz8eee9r3c9ovd.png\" /></a></p><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/222621cea4rbd4nez3iiak.png\" target=\"_blank\"><img alt=\"《狂飙》主演张颂文仅关联一家影视公司_图1-4\" title=\"《狂飙》主演张颂文仅关联一家影视公司\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/222621cea4rbd4nez3iiak.png\" /></a></p></div><div><br /></div></td>','美国中文网','http://www.sinovision.net/entertainment/202302/00526683.htm',4);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (194,'2023-02-19 09:34:49.592402','2023-02-19 09:34:49.592402','张颂文发长文告别《狂飙》：永远难忘这个春节','<td id=\"article_content\"><div>“我永远难忘这个春节，谢谢你们。”</div><div><br /></div><div>2月1日晚，电视剧《狂飙》大结局播出后，张颂文发长文告别：我永远难忘这个春节，谢谢你们。他还分享了自己第一次看《狂飙》的剧本后写下的一段文字。相关话题迅速冲上热搜第一，引发网友热议。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/015734y8oq9omdy19ttfda.png\" target=\"_blank\"><img alt=\"张颂文发长文告别《狂飙》：永远难忘这个春节_图1-1\" title=\"张颂文发长文告别《狂飙》：永远难忘这个春节\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/015734y8oq9omdy19ttfda.png\" /></a></p><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1040\"><script type=\"text/javascript\" id=\"closeAd-1038\">&lt;!--// &lt;![CDATA[\r\n    /* [id1040] 娱乐新闻详细页 Admaru */\r\n    OA_show(1040);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=c1367dd\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1040&amp;n=c1367dd\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id886] 娱乐详情页文包图广告 */\r\n    OA_show(886);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=88ea083\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=886&amp;n=88ea083\" /></a></noscript></div></div><div><br /></div><div>全文如下↓</div><div><br /></div><div>因为《狂飙》，我永远难忘这个春节，谢谢你们。</div><div><br /></div><div>大家在我这里的留言我都看到了，很多朋友也说起这个春节假期和家人一起追剧的各种趣事，这是我最欣慰的。我上小学时，曾经和父母、姐姐一起追过两部电视剧，很多年后的今天，每次看到这两个剧的某个画面，我都还能想起当时那个小小的我和家人聊得不亦乐乎的画面，它是如此清晰、如此温暖。在未来，聊起往事，聊起2023年，希望有人能记得在2023年的春节，有过一部叫作《狂飙》的电视剧……作为影视工作者，我们会深感荣幸、自豪。</div><div><br /></div><div>今天剧终，我可以回答那个大家最关心的问题了：作为高启强，最喜欢哪个阶段的自己？如果这个世界有如果，我是说如果，我好想回到旧厂街那个鱼档，和弟弟妹妹度过那年的春节。年后，我会把那台准备送给小龙的彩电留下，我会把它放在家中，我要和弟弟妹妹一起看春晚。春节后，我要去报警，我要告诉警察，在我们市场有人欺行霸市，借收卫生费之名敲诈勒索。</div><div><br /></div><div>我至今忘不了第一次看《狂飙》的剧本，看到送电视后被小龙、小虎殴打的那场戏时，心里五味杂陈，夜里辗转难眠。后来，我写过这样一段文字：</div><div><br /></div><div>马上就要过年了，阿龙刚才说年二十九那天，我们市场中午开始关门，初三再开。这两天市场每个档口都要帮忙冲洗自己门口的地面，对面猪肉档的荣哥腰不好，我会帮他把卫生也搞了，他的地面是最滑的，自己都摔过几次了，还是不愿意买块防滑垫铺在地上。我买了一大桶洗洁精，应该够我们两个档口用了。</div><div><br /></div><div>阿盛和小兰每年寒假都回来得很晚，说是买票难，都赶在年三十晚上才回来，跟我说那天的火车票容易买。他们以为我不知道，为了减轻我的负担，兄妹俩每年寒暑假都瞒着我偷偷打工，其实我也没告诉他们，这些年下来，我已经存了五万多块钱了，够用。早点儿回来休息多好，早回来我可以给他们多做些好吃的，补补身子，兄妹俩也可以给爸妈多上柱香，说说话。虽然爸妈走的时候他俩都很小，总说没什么印象，但我有：爸爸喜欢背着小兰去邻居家打牌，妈妈每天晚上都会给阿盛讲故事。如果他俩早点儿回来，我还想带他们去几个常买我东西的熟客家坐坐，我这几个熟客是很厉害的：一个是搞旅游景点开发的经理；一个是搞园林设计的部门主管；还有一个大哥的儿子，是社保局的一个科长，这些人都不是一般人，说不定毕业后能帮上忙，帮他俩落实一下工作。</div><div><br /></div><div>今天和猪肉荣提前说好了，给我留四个猪蹄、十斤五花、五斤前尖；卖面饼的翠姨今天还给了我一只老家的走地鸡，看爪子应该是果园里散养的。今年的菜单我想是这样的：粉葛猪骨汤、沙姜猪手、香菇炒滑鸡、香芋扣肉、清蒸鲈鱼、香煎鲮鱼丸、白灼罗氏虾、蒜蓉炒芥兰……阿盛上个月说，他的班主任是北方人，冬至那天请他和几个同学去家里包饺子，说北方的饺子特别好吃，饺子皮是自己手擀的，不是我们市场那种机器压出来的，煮完特别弹牙。阿盛还说，北方年三十的年夜饭也会包饺子，这次回来要给我一展手艺，这小子，从小脑子就好使，什么都是看一遍就会。我也有信心，今年我这桌饭菜一定能震惊他。今年京海特别冷，看完春晚，我还想再煮一碗汤圆给小兰吃，她喜欢花生芝麻馅的。</div><div><br /></div><div>我准备了两个红包，彩电也买了，我想年初一给阿龙阿虎兄弟送过去，可是，我真的要这样做吗？我很矛盾……</div><div><br /></div><div>不少网友留言表达不舍：</div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/0157250ai8uhwg7urwzchg.png\" target=\"_blank\"><img alt=\"张颂文发长文告别《狂飙》：永远难忘这个春节_图1-4\" title=\"张颂文发长文告别《狂飙》：永远难忘这个春节\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/0157250ai8uhwg7urwzchg.png\" /></a></p><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/0157243l3msmjzi6qcig9l.png\" target=\"_blank\"><img alt=\"张颂文发长文告别《狂飙》：永远难忘这个春节_图1-5\" title=\"张颂文发长文告别《狂飙》：永远难忘这个春节\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/0157243l3msmjzi6qcig9l.png\" /></a></p><p><br /></p></div></td>','美国中文网','http://www.sinovision.net/entertainment/202302/00526639.htm',4);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (195,'2023-02-19 09:34:49.599087','2023-02-19 09:34:49.599087','《狂飙》迎大结局 75万人将《孙子兵法》加入书架','<td id=\"article_content\"><div>刚刚，2023年首部爆款剧集《狂飙》在爱奇艺迎来大结局，剧中，高启强终于认罪伏法，被依法判处死刑，极目新闻记者留意到，法庭上列出的高启强罪行多达11项，包括组织、领导黑社会性质组织罪、故意杀人罪、故意伤害罪、绑架罪、放火罪、寻衅滋事罪、非法经营罪、强迫交易罪、组织卖淫罪、开设赌场罪、行贿罪等。而结尾处高启强与满头白发的安欣隔着铁窗，安欣面对高启强的假设说出那句“哪有那么多如果”的场景，让许多观众唏嘘。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1040\"><script type=\"text/javascript\" id=\"closeAd-1038\">&lt;!--// &lt;![CDATA[\r\n    /* [id1040] 娱乐新闻详细页 Admaru */\r\n    OA_show(1040);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=c1367dd\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1040&amp;n=c1367dd\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id886] 娱乐详情页文包图广告 */\r\n    OA_show(886);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=88ea083\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=886&amp;n=88ea083\" /></a></noscript></div></div><div><b>主演阵容实现全员“脸熟”</b></div><div><b><br /></b></div><div><b>75.2万人将《孙子兵法》加入书架</b></div><div><br /></div><div>这个春节，《狂飙》无疑成为剧集市场的最大赢家。极目新闻记者观察到，在酷云、云合等多个数据监测平台，《狂飙》以超过半数的市占率一马当先，远远甩开了同期在播剧集。</div><div><br /></div><div>在爱奇艺，《狂飙》以11000的热度刷新了爱奇艺历史热度纪录，一举超越了此前《赘婿》《人世间》《苍兰诀》保持的成绩。</div><div><br /></div><div>剧集爆红，让主演阵容更是在观众中实现全员“脸熟”。</div><div><br /></div><div>极目新闻观察到，除张译、张颂文收获大量讨论热度，高叶（饰 陈书婷）、李健（饰 李响）、贾冰（饰 徐江）令人惊喜外，就连“反派集团”的苏小玎（饰 高启盛）、林家川（饰 唐小龙）、阿如那（饰 李宏伟）、孙岩（饰 唐小虎）、冯冰（饰 老默）都收获关注度，一场直播让“狂飙反派云道歉”冲上热搜……</div><div><br /></div><div>从大众体感热度上来看，“春节返程高铁清一色狂飙”的热搜词条、仅仅只是因为同名便挂了几天热搜的“强盛集团孙红雷”等，都成为该剧爆火的佐证。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/01/073952n2502f6k191y0l2l.png\" target=\"_blank\"><img alt=\"《狂飙》迎大结局 75万人将《孙子兵法》加入书架_图1-3\" title=\"《狂飙》迎大结局 75万人将《孙子兵法》加入书架\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/01/073952n2502f6k191y0l2l.png\" /></a></p></div><div>剧集甚至还带火高启强的“枕边书”《孙子兵法》。在QQ阅读平台，累计有超过75.2万人将各版本《孙子兵法》加入书架，QQ阅读推出的“高启强同款书单”——《孙子兵法》《红楼梦》阅读量上涨12%。</div><div><br /></div><div><b>人物投射出人性的复杂</b></div><div><b><br /></b></div><div><b>“命题作文”做出了尺度突破</b></div><div><br /></div><div>《狂飙》爆火，一方面得益于该剧塑造了一个有血有肉的反派人物——张颂文饰演的高启强。</div><div><br /></div><div>接受极目新闻记者采访时，多位受访观众不约而同提到，这部剧最好的部分就是写出了“并非完全对立的好或坏，有了更多的现实性”。</div><div><br /></div><div>人物不是“非黑即白”，这一成功之处当然首推高启强的人物塑造：他是称霸一方无恶不作的黑社会老大，但他从一个老实本分的鱼贩成长为“大boss”的经历，以及他总是被裹挟在亲情之中的人设、与安欣的多次交手，都是《狂飙》热度的来源。</div><div><br /></div><div>复杂的人性，并非只体现在对于反派人物的刻画。《狂飙》里最终让人“意难平”的刑警队队长李响，就是基于人性的软弱不得不游走在黑白之间，最终守住信念又实现生命高光，由此让不少受访对象念念不忘。</div><div><br /></div><div>另一方面，《狂飙》的爆火也源于创作者在“命题作文”的框架内，作出了“尺度”上的有力突破。</div><div><br /></div><div>突破一部分源自对反面人物的刻画，另一方面则是在“扫黑除恶”主题下，正视了现实世界中同样存在的“保护伞”问题，从龚开疆到赵立冬，从曹闯、李响到张彪，“高启强”们为什么可以为所欲为？《狂飙》撕开了一角。</div><div><br /></div><div><b>最新豆瓣评分跌回9.0</b></div><div><b><br /></b></div><div><b>不少观众仍认为口碑“过誉”</b></div><div><br /></div><div>不过，因为后期剧情上的删减和台词的变动，《狂飙》的豆瓣评分已从最高的9.1分跌回了9.0。而在采访中，不少观众也提到，目前《狂飙》的豆瓣分数有点类似去年暑期的《梦华录》，“这部剧精彩肯定是精彩的，但如果及格分是6分，那9分还是虚高了。”</div><div><br /></div><div>被评得分“虚高”，当然也是因为观众对于《狂飙》部分剧情的设置“有想法”。</div><div><br /></div><div>接受采访时，全程追剧的“Young”提到，《狂飙》的故事在逻辑上有明显的硬伤，尤其是高启强的大佬成长之路，“更像是一部主角无所不能的爽剧”。</div><div><br /></div><div>同时她重点批评了主角安欣的人设，“不仅人物没有成长，更缺乏最基本的贴近现实生活的感觉，说严重点就是‘悬浮’。”她甚至提到，从不少剧情的设置上，“安欣更像是一个偶像剧傻白甜女主角的性转（注：网络用语，性别转换）版本。”</div><div><br /></div><div>除却以上，《狂飙》引发的热度也让播出平台爱奇艺更受关注。</div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/01/073956et4p9pem8hje4cmm.png\" target=\"_blank\"><img alt=\"《狂飙》迎大结局 75万人将《孙子兵法》加入书架_图1-4\" title=\"《狂飙》迎大结局 75万人将《孙子兵法》加入书架\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/01/073956et4p9pem8hje4cmm.png\" /></a></p></div><div>1日，因为限制投屏，爱奇艺被广东一用户告上法庭的消息登陆热搜。目前，爱奇艺将黄金VIP会员的投屏清晰度限制在了480P，而这一做法在该用户看来，是“单方面变更合同条款，构成违约”。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/entertainment/202302/00526599.htm',4);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (197,'2023-02-19 09:34:49.602126','2023-02-19 09:34:49.602126','春晚节目收视出炉 这个节目夺央视春晚收视率第一','<td id=\"article_content\"><div>中国视听大数据30日发布了“2023年春节主题晚会收视”情况，在包括央视、各地卫视等26台春节联欢晚会或特别节目中，6台春晚收视率破1%、11台春晚收视率超过0.5%。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1040\"><script type=\"text/javascript\" id=\"closeAd-1038\">&lt;!--// &lt;![CDATA[\r\n    /* [id1040] 娱乐新闻详细页 Admaru */\r\n    OA_show(1040);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=c1367dd\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1040&amp;n=c1367dd\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id886] 娱乐详情页文包图广告 */\r\n    OA_show(886);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=88ea083\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=886&amp;n=88ea083\" /></a></noscript></div></div><div>其中，《2023年中央广播电视总台春节联欢晚会》共有46个卫视频道转播，综合收视率达到22.230%，收视份额为72.209%。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/31/011500ulbql533bfcbtggu.jpeg\" target=\"_blank\"><img alt=\"春晚节目收视出炉 这个节目夺央视春晚收视率第一_图1-3\" title=\"春晚节目收视出炉 这个节目夺央视春晚收视率第一\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/31/011500ulbql533bfcbtggu.jpeg\" /></a></p></div><div><br /></div><div>另外，在总台春晚的节目中，圆桌脱口秀《给我一分钟》以28.617%的收视占据首位，少儿民俗表演《我和爷爷踩高跷》、公益广告《“村”晚》、歌曲《跟往事干了好几杯》、武术《演武》、舞蹈《碗步桥》、微电影《我和我的春晚》、小品《马上到》、合唱《早安，阳光》、歌曲《你好，陌生人》等分列2至10位。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/31/0115346jp5xn2p5s6jnusu.png\" target=\"_blank\"><img alt=\"春晚节目收视出炉 这个节目夺央视春晚收视率第一_图1-4\" title=\"春晚节目收视出炉 这个节目夺央视春晚收视率第一\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/31/0115346jp5xn2p5s6jnusu.png\" /></a></p></div><div><br /></div><div>在央卫视其它春晚中，收视率破1的春晚还有1月14日（腊月二十三）播出的《中央广播电视总台2023网络春晚》，综合收视率：2.093%，综合收视份额7.778%；1月20日（腊月二十九）《2023辽宁卫视春节联欢晚会》，收视率为1.621%，收视份额6.972%；1月22日（正月初一）《2023北京广播电视台春节联欢晚会》，收视率为1.031%，收视份额4.355%等。</div><div><br /></div><div>在1月22日（大年初一）晚的春晚大战中，云集了北京、东方、湖南（华侨华人春晚）、江苏、浙江、广东以及河北、河南卫视转播的《百花迎春——中国文学艺术界2023春节大联欢》等晚会，历年来都是春晚大战最为激烈的时刻。目前来看，北京电视台春晚以破1的成绩领先，东方卫视春晚以收视率0.769%，收视份额2.881%的成绩位居次席，湖南卫视的2023全球华侨华人春节大联欢以收视率0.622%，收视份额2.168%占据第3位。</div></td>','美国中文网','http://www.sinovision.net/entertainment/202301/00526537.htm',4);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (198,'2023-02-19 09:34:49.613279','2023-02-19 09:34:49.613279','宋仲基发文宣布再婚 外籍妻子已有身孕','<td id=\"article_content\"><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/30/024420hwkwggmygnw7ywn5.png\" target=\"_blank\"><img alt=\"宋仲基发文宣布再婚 外籍妻子已有身孕_图1-1\" title=\"宋仲基发文宣布再婚 外籍妻子已有身孕\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/30/024420hwkwggmygnw7ywn5.png\" /></a></p></div><div><font color=\"#808080\">宋仲基与妻子</font></div><div><br /></div><div>1月30日宋仲基通过官网发布信函宣布再婚，并透露新婚妻子已怀孕。宋仲基所属公司回应结婚一事：“宋仲基和Katy Louis Sounders将举行婚礼，但时间还未定。”</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1040\"><script type=\"text/javascript\" id=\"closeAd-1038\">&lt;!--// &lt;![CDATA[\r\n    /* [id1040] 娱乐新闻详细页 Admaru */\r\n    OA_show(1040);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=c1367dd\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1040&amp;n=c1367dd\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id886] 娱乐详情页文包图广告 */\r\n    OA_show(886);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=88ea083\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=886&amp;n=88ea083\" /></a></noscript></div></div><div>早前，有韩国媒体报道称宋仲基正在与一名英国女性热恋中，还表示宋仲基还带着女友参加一些聚会，并未隐藏自己的恋爱。随后，宋仲基所属社宣布：“宋仲基正在与一位英国女性恋爱中，对方是素人。</div><div><br /></div><div>Katy Louis Sounders1984年出生，毕业于意大利米兰的博科尼大学，曾是一名演员，现在是一名私人英语老师。此前，Katy被曝已有两个女儿，大女儿已经10岁。</div><div><br /></div><div>2017年7月5日，宋仲基公开与宋慧乔的恋情。同年10月31日，宋仲基与宋慧乔举行婚礼。2019年6月26日，宋仲基向宋慧乔提出协议离婚；6月27日，宋仲基和宋慧乔进行离婚调解；7月22日，宋仲基宋慧乔正式离婚。</div><div><br /></div><div><b>宋仲基全文：</b></div><div><br /></div><div>你好。我是仲基。今天， 我想和大家分享我生命中最珍贵最幸福的承诺。我发誓要和Katy Louise Saunders共度余生，她一直在我身边，支持我，照顾我，陪我度过宝贵的时光。 她有一颗善良的心，热情地过着自己的生活。他是一个值得尊敬的聪明而了不起的人。 感谢她，我正在成为一个更好的人。 自然而然地，我们一起梦想着创造一个幸福的家庭。我们付出了很多努力来兑现对彼此的承诺，值得庆幸的是，我们两个人走到了一起。 今天，我们即将登记结婚，开始我们基于深厚信任和爱的夫妻生活。 就像现在，在以后的日子里，我们两个人，会朝着同一个方向，一起美丽的走下去。 我们衷心感谢所有始终如一地珍惜我们的粉丝们。 特别是基尔人民的支持和祝福，是我演员生涯中最大的力量，今后我将作为个人和演员，带着更大的责任和热情的翅膀，飞得更高。我希望 每个人都健康快乐，直到我们能见面并亲自问候的那一天 。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/30/024420l7dld5vbp7ycv7vb.jpeg\" target=\"_blank\"><img alt=\"宋仲基发文宣布再婚 外籍妻子已有身孕_图1-4\" title=\"宋仲基发文宣布再婚 外籍妻子已有身孕\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/30/024420l7dld5vbp7ycv7vb.jpeg\" /></a></p></div></td>','美国中文网','http://www.sinovision.net/entertainment/202301/00526497.htm',4);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (200,'2023-02-19 09:34:49.622392','2023-02-19 09:34:49.622392','中国足协主席被查 在任三年半 陈戌源干了些什么？','<td id=\"article_content\"><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/14/204002m2hm0ii4hqp44hmh.jpg\" target=\"_blank\"><img alt=\"中国足协主席被查 在任三年半 陈戌源干了些什么？_图1-1\" title=\"中国足协主席被查 在任三年半 陈戌源干了些什么？\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/14/204002m2hm0ii4hqp44hmh.jpg\" /></a></p></div><div style=\"text-align: center;\"><span style=\"text-align: start;\"><font color=\"#808080\">中国足球协会主席、党委副书记陈戌源 资料图</font></span></div><div><br /></div><div>2月14日，湖北省相关部门宣布，中国足球协会主席、党委副书记陈戌源涉嫌严重违纪违法，目前正接受中央纪委国家监委驻国家体育总局纪检监察组和湖北省监委审查调查。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/14/203512v38kqv4v7dk40k8y.jpeg\" target=\"_blank\"><img alt=\"中国足协主席被查 在任三年半 陈戌源干了些什么？_图1-2\" title=\"中国足协主席被查 在任三年半 陈戌源干了些什么？\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/14/203512v38kqv4v7dk40k8y.jpeg\" /></a></p></div><div><br /></div><div>1956年出生的陈戌源早年曾是码头工人，历经多年，干到了上海上港集团董事长。从初入足球圈的国企高管到全票当选中国足协主席，从在中国足坛掀起大刀阔斧的改革到最终接受调查，陈戌源的落马，给了中国球迷一个很大的惊叹号。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></div><div><br /></div><div>在陈戌源在任的不到四年时间里，中国足球从当初的金元大佬到如今一地鸡毛，实在是令人不解。</div><div><br /></div><div>那么，他在这三年半到底干了什么？</div><div><br /></div><div><b>精简整风</b></div><div><b><br /></b></div><div><b>去金元足球</b></div><div><br /></div><div>公开资料显示，陈戌源是工商管理硕士学位，还是一位高级经济师，曾任上海国际港务有限公司书记、董事长，后来在2019年出任中国足协换届筹备组组长，在同一年的8月份，陈戌源出任中国足球协会主席。</div><div><br /></div><div>尽管陈戌源的上港背景遭受了不少质疑，但他在担任上港集团总裁与董事长期间，对上港足球队的打造都算得上成功，这也是他当选足协主席的先决条件。但是上港追随恒大的步伐，天价引进奥斯卡、艾克森等外援，是俱乐部成功的基础，也是不可否认的。</div><div><br /></div><div>“我在这里真诚告诫所有球员，足球是高尚运动，不要让金钱给玷污了，扭曲了。如果以金钱放在第一位，职业生涯不会有前途，一个人真正的脊梁骨是信仰和理想。”这是2019年上海上港俱乐部赛季动员大会上，陈戌源曾高谈阔论金元足球的弊病，这也是他日后改革的基调。</div><div><br /></div><div>2019年，陈戌源当选中国足协主席后，出重拳对足协进行全面整风。陈戌源除了要取消国足每逢大赛必包机的特殊待遇，还要对足协多个部门实行精简。</div><div><br /></div><div><b>强推中性化</b></div><div><b><br /></b></div><div><b>挫伤投资人信心</b></div><div><br /></div><div>俱乐部名称中性化是陈戌源的又一大改革。本来俱乐部股改方向中，名称中性化的提法是要求具备条件的俱乐部逐步实现，并非一刀切。</div><div><br /></div><div>但实际操作中，俱乐部被强制执行。在疫情期间强行推出的俱乐部、球队中性名政策，以及宣传的公益足球联赛理念，一下让中国足坛和职业联赛坠入漫漫严冬。在这样的政策“反向助力”下，山东鲁能、河南建业、天津泰达、广州恒大等俱乐部及球队名称成为了历史，加速了各级职业足球俱乐部的经营困境，金元时代树立起的中超品牌形象和商业价值被严重破坏。足协要求中性名不得打擦边球，但“上海上港”更名为“上海海港”，恰恰就被认为是打擦边球，但依然获得了足协的通过。</div><div><br /></div><div>中性名的实行，严重挫伤了俱乐部投资人的投资意愿。本来投资的退出（比如江苏苏宁），不退的也开始大幅缩减投入。本来有意进军足球圈的，望而却步。</div><div><br /></div><div><b>推行公益足球</b></div><div><b><br /></b></div><div><b>中超商业价值大缩水</b></div><div><br /></div><div>“足球本身是社会公益产品”，2021年陈戌源做客央视《新闻1+1》栏目，在节目中慷慨激昂地表示：“足球本身是社会公益产品，投资人要有社会责任感”。这句话一出，在当时就引起极大争议。被认为陈戌源过度解读了足球的公益属性，却忽视了其社会经济属性。</div><div><br /></div><div>前任国足主帅里皮在《思维的竞赛·里皮自述》一书中说：“没有投入，不可能有足够的竞争力，这是现代职业足球的经营规律。职业足球并不是商业生意，而是更加复杂的承载了千万人喜好的社会公共事务。”</div><div><br /></div><div>在2022赛季的中超联赛中，因为浙江队训练服上有球队股东的一个广告，中国足协向浙江队开出了一张罚单，于是，球队不得已在自己的训练服上贴上了胶布，挡住广告。有报道说，“在足协开出罚单之后，大家都有些不满。足协对于这样的行为就送上罚单，将极大地限制中超的一些商业化的利益。”</div><div><br /></div><div>陈戌源上任后，中超分红连年下降。2022赛季中超各队的分红约800万人民币，少于2021赛季的1000多万，更比不了“金元足球”鼎盛时期的年分红6000多万。</div><div><br /></div><div>2015年，中超的冠名版权卖出了5年80亿，年均16亿。2018年，因不断干预联赛出台各种政策导致联赛精彩程度缩水，版权费改为10年110亿，年均11亿。如今的1亿仅为当年的零头，缩水了16倍。</div><div><b><br /></b></div><div><b>两次强制降薪</b></div><div><b><br /></b></div><div><b>球员俱乐部直播带货求生存</b></div><div><br /></div><div>在陈戌源的主导下，中超的投资成本越来越低，球员的工资越来越低，但联赛水准越来越低，联赛被国足比赛的割裂也越来越严重。</div><div><br /></div><div>陈戌源认为金元足球导致球员年薪过高，使他们进取心不足，他们薪水过高的同时，也给俱乐部带来了很大负担。足协此前为了减轻俱乐部负担，已经连续两次降薪，使国内球员的顶薪从千万级别降到了现在的500万。</div><div><br /></div><div>有球员对足协的决定产生了质疑和抵触，他们表示长时间的欠薪已经让他们生活拮据，而且车贷、房贷对他压力很大，如果足协坚决降薪或者不涨薪，他们会选择退役，甚至去直播带货，这样也比他们踢球挣得多，至少不会欠薪。</div><div><br /></div><div>除了球员，中超八冠王广州队为了缓解运营压力，也开启了直播带货的先河。直播主要由两名穿着广州队球衣的女主播出镜，为观众推荐钥匙扣、马克杯、球衣等周边商品。两位年轻球员张健智和陈骏州也在直播间短暂亮相，为观众抽奖。</div><div><b><br /></b></div><div><b>国足去归化</b></div><div><b><br /></b></div><div><b>20亿投入打水漂</b></div><div><br /></div><div>尽管国足打进了2022世界杯亚洲区预选赛十二强赛，但是国足的战绩反而越来越不让球迷满意了。</div><div><br /></div><div>陈戌源上任以后，一改前任的许多对于国家队的改革做法，很明显的是，归化球员的日子变得不那么好过了。</div><div><br /></div><div>陈戌源曾公开表示，“曾经有人跟我多次讨论过，希望上港能够做外籍球员的归化工作，我是不赞成这种想法，中国足球想要取得成功，还是老老实实把基础打好，不要走太多的捷径，不要以为捷径带来的胜利和成功会是长久的。”</div><div><br /></div><div>于是对他来说是捷径的“归化”或多或少的遭到了打压。接受央视采访时，艾克森曾透露在十二强赛期间，李铁教练组“没有办法让几个巴西入籍的球员同时出场”。</div><div><br /></div><div>“没有办法”这4个字值得玩味。是战术设计上的没法子，还是有人下达指示，导致李铁不得不听话照做？据此前《足球报》报道，广州恒大在2019年为五名归化球员支付的转会、工资、安家费等高达8.7亿，此外还要每年支付约3.6亿的年薪。到2022年年底，广州恒大在这五名归化球员身上的投入已经接近20亿。算上其他归化球员的花费，这笔钱基本已经超过了20亿。总之，中国足球在“归化”方面花了大价钱。而在关键的12强赛他们却被限制了，无疑是非常让球迷们遗憾的。</div><div><br /></div><div>足球应该是公平、平等的竞赛，状态好的球员理应获得机会，而不是被以行政干预限制在板凳上。</div><div><br /></div><div>对于足协主席陈戌源被查一事，《足球报》记者李璇称：“在国家队征战期间，让国脚陪着踢球，让保障人员陪着打麻将，成绩打得跟XX一样，每天歌舞升平，分奖金。当然，这不构成他被调查的理由，还有别的事情，大家等待后续吧！”</div></td>','美国中文网','http://www.sinovision.net/sports/202302/00527168.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (202,'2023-02-19 09:34:49.632148','2023-02-19 09:34:49.632148','世界排名创新高 郑钦文横扫头号种子晋级阿布扎比四强','<td id=\"article_content\"><div>面对世界排名第8位的卡萨金娜，郑钦文拿出了极为强势的表现。</div><div><br /></div><div>北京时间2月10日，WTA500阿布扎比站女单八强战，郑钦文以6-1、6-2的比分对赛会头号种子卡萨金娜完成横扫，顺利挺进半决赛。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></div><div><br /></div><div>此前在首轮比赛中，郑钦文两盘横扫加拿大选手马里诺，次轮再度直落两盘，击败大满贯冠军选手奥斯塔彭科晋级。</div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/10/205759p5zane6arujquj02.png\" target=\"_blank\"><img alt=\"世界排名创新高 郑钦文横扫头号种子晋级阿布扎比四强_图1-3\" title=\"世界排名创新高 郑钦文横扫头号种子晋级阿布扎比四强\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/10/205759p5zane6arujquj02.png\" /></a></p><p><font color=\"#808080\">郑钦文在比赛中庆祝。WTA官网</font></p><p><br /></p></div><div>进入八强战，站在郑钦文面前的是排名更高的卡萨金娜，但郑钦文毫不怯场——全场轰出25个制胜分，4记ACE球，兑现了10个破发点中的6个，仅仅耗时78分钟就拿下了胜利。</div><div><br /></div><div>此前，郑钦文还从未与卡萨金娜有过交手，如今获胜之后，这位年仅20岁的中国金花斩获了职业生涯第三场击败TOP10的胜利，也是第三次跻身巡回赛四强。</div><div><br /></div><div>与此同时，郑钦文的即时排名也升至第24位，超越了自己此前第25位的生涯最高排名。</div><div><br /></div><div>能够对阵TOP10选手赢得速胜，郑钦文对自己的发挥十分满意：“今天的表现让我很高兴，技术非常到位，在保持进攻的同时也有稳定性。”</div><div><br /></div><div>“对手的稳定性很好，球偏上旋类型，所以想要赢下比赛，就不能去等对手的失误，必须自己去进攻。今天几个关键分也把握得很好，希望接下来继续进步。”</div><div><br /></div><div>去年9月，郑钦文曾在东京站生涯首次闯进巡回赛决赛，但最终未能夺冠，此番再度迎来进入决赛的机会，她已经做好了准备。</div><div><br /></div><div>“我希望在场上全力以赴做好自己，让结果自然而然地出现。”</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/sports/202302/00527022.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (204,'2023-02-19 09:34:49.646698','2023-02-19 09:34:49.646698','中超正式取消U23政策 6年“神操作”画上句号','<td id=\"article_content\"><div>7日上午，2023年中国足球职业联赛俱乐部负责人会议召开。据多家媒体消息，会议上宣布了新赛季的各项政策，包括中超取消U23政策，中甲实施U21政策。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/07/010811hr0rsosz9bv0t3o4.jpg\" target=\"_blank\"><img alt=\"中超正式取消U23政策 6年“神操作”画上句号_图1-3\" title=\"中超正式取消U23政策 6年“神操作”画上句号\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/07/010811hr0rsosz9bv0t3o4.jpg\" /></a></p></div><div style=\"text-align: center;\"><font color=\"#808080\">2017中超联赛第2轮，重庆当代力帆1-1河北华夏幸福。车世伟（右）的进球是足协新政实行以来U23球员的首粒进球。</font></div><div style=\"text-align: center;\"><br /></div><div>2017赛季，中国足协及职业联赛管理部门，结合各级俱乐部优秀国内球员稀缺，对职业联赛及国字号球队人才供给不力的现实，正式推出了“U23球员新政”，要求每场比赛每队场上的U23球员不得少于1人。</div><div><br /></div><div>但随之而来的，却是中超各队的神操作。</div><div><br /></div><div>2019年的中超联赛，原天津天海队门将就以前锋的身份替补登场，上场4分钟后又被另一U23球员换下；河南建业U23球员杨国元两次登场分别在2分12秒和1分30秒就被换下。</div><div><br /></div><div>上赛季的中超，还出现了大连队违规使用U23球员，导致赛后被判负的情况。</div><div><br /></div><div>此外，U23政策还导致年轻球员身价和工资水涨船高，进一步抑制了优秀年轻球员出国锻炼打拼的想法。</div><div><br /></div><div>而对于国足的成绩帮助，U23政策也是收效甚微——去年国足世预赛27人阵容的平均年龄为29.56岁，其中30岁以上的球员多达16人，球队中95后球员仅有张玉宁、韦世豪、明天3人，无一名U23适龄球员。</div><div><br /></div><div><b>新赛季中国职业联赛具体要求</b></div><div><br /></div><div>中超：报名U21球员至少5名，本俱乐部注册4年以上（取消U23球员始终在场要求）。</div><div><br /></div><div>中甲：报名U21球员至少5名，本俱乐部注册4年以上，1名U21球员始终在场（以1名U21球员始终在场取代1名U23球员始终在场的要求）。</div><div><br /></div><div>中乙（不变）：报名U21球员至少5名，本区域球员5名，1名U21球员始终在场。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/sports/202302/00526828.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (205,'2023-02-19 09:34:49.652427','2023-02-19 09:34:49.652427','中国女足留洋球员李佳悦亲历土耳其地震：感觉楼都要塌了','<td id=\"article_content\"><div>结束了北京时间5日晚与阿达纳的比赛，李佳悦与全队如往常一样回到酒店，休整一晚后再返回伊斯坦布尔。</div><div><br /></div><div>3:0战胜阿达纳女足，李佳悦所在的加拉塔萨雷女足继续势如破竹取得6连胜。从上个月初加盟球队到现在，李佳悦不仅坐稳球队主力位置，还保持着全胜战绩。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></div><div><br /></div><div>这本是一个美妙的客场之旅，但突如其来的7.8级地震打破了平静，演变成一场“逃亡之旅”。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/06/1959466y25hs4ee4rch2s4.jpg\" target=\"_blank\"><img alt=\"中国女足留洋球员李佳悦亲历土耳其地震：感觉楼都要塌了_图1-3\" title=\"中国女足留洋球员李佳悦亲历土耳其地震：感觉楼都要塌了\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/06/1959466y25hs4ee4rch2s4.jpg\" /></a></p></div><div><font color=\"#808080\">李佳悦在社交媒体上报平安。</font></div><div><br /></div><div><b>“感觉楼都要倒了”</b></div><div><br /></div><div>当地时间6日凌晨四点，土耳其发生7.8级地震，震中位于东南部的卡赫拉曼马拉什省，距离李佳悦和球队所在的客场城市阿达纳，仅有200公里的路程。</div><div><br /></div><div>“当时惊醒之后第一时间赶紧起来穿鞋，然后拿着手机跑到酒店外边空旷的地方。”李佳悦对中新网记者说。</div><div><br /></div><div>提起当时的震感，李佳悦感觉楼都要快震倒塌了，不少队友被吓哭。她虽然没哭，但还是很害怕，因为第一次感觉离死亡这么近。</div><div><br /></div><div>一向乐观地李佳悦在社交媒体上给大家报了平安：“佳哥是个坚强的女子，大哥不是白叫的，大家放心吧。”</div><div><br /></div><div><b>紧急撤离</b></div><div><br /></div><div>虽然李佳悦客场比赛所在的城市阿达纳离震中很近，但李佳悦所处的位置暂时还比较安全。</div><div><br /></div><div>“当时震感很强，但是没有看到有建筑物倒塌，因为我们当时在阿达纳的市中心，看新闻说只有靠近震中那部分区域房屋有损坏。”</div><div><br /></div><div>与中新网记者通话时，李佳悦正和球队一起离开阿达纳。</div><div><br /></div><div>“开始球队说是要去安卡拉，然后安全了再回伊斯坦布尔，但是安卡拉那边也有6.3级的地震，所以现在还不知道往哪里开。”她说。</div><div><br /></div><div>在逃离震中的路上，土耳其再一次出现7.8级强震，李佳悦在车上依旧感觉到了很强的震感。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/06/195945aq1x5y5arzbouo5b.jpg\" target=\"_blank\"><img alt=\"中国女足留洋球员李佳悦亲历土耳其地震：感觉楼都要塌了_图1-4\" title=\"中国女足留洋球员李佳悦亲历土耳其地震：感觉楼都要塌了\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/06/195945aq1x5y5arzbouo5b.jpg\" /></a></p></div><div><font color=\"#808080\">李佳悦在比赛中。图片来源：李佳悦社交媒体。</font></div><div><br /></div><div><b>“现在情绪有些不好”</b></div><div><br /></div><div>雪上加霜的是，在球队逃离震中的路上，还正好赶上了暴风雪。不过好在球队在高速休息站买了些食物，可以供来继续赶路。</div><div><br /></div><div>相比第一次7.8级强震后的乐观坚强，在经历了第二次7.8级强震后，李佳悦坦言情绪有些不好，也感到有些恐慌。</div><div><br /></div><div>“我是在手机上看新闻说又地震了，然后看到路上有很多救护车，现在队友们的情绪也不是很好。”</div><div><br /></div><div>截至发稿，李佳悦和队友们还正在紧急撤离的途中。</div></td>','美国中文网','http://www.sinovision.net/sports/202302/00526818.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (207,'2023-02-19 09:34:49.665968','2023-02-19 09:34:49.665968','张可欣夺得世界杯女子U型场地技巧冠军','<td id=\"article_content\"><div>据央视体育消息，3日的自由式滑雪世界杯猛犸山站女子U型场地技巧决赛中，资格赛第二名晋级的张可欣获得冠军。这也是张可欣在2019年9月之后再次获得世界杯冠军。决赛中，张可欣倒数第二位出场，第一轮就得到92.75分，第二轮张可欣得到93.50分，一举夺冠！英国选手佐伊·阿特金获得银牌，加拿大选手蕾切尔·卡克获得铜牌。</div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/04/045357l1vvpnd42d1i2imc.png\" target=\"_blank\"><img alt=\"张可欣夺得世界杯女子U型场地技巧冠军_图1-1\" title=\"张可欣夺得世界杯女子U型场地技巧冠军\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/04/045357l1vvpnd42d1i2imc.png\" /></a></p></div><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;text-align:center;margin:4px auto; margin-bottom:5px;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></td>','美国中文网','http://www.sinovision.net/sports/202302/00526742.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (209,'2023-02-19 09:34:49.677179','2023-02-19 09:34:49.677179','2023赛季首秀 苏炳添6秒59获得室内60米冠军','<td id=\"article_content\"><div>北京时间2月3日凌晨，在瑞典哥德堡田径室内赛男子60米比赛中，中国“飞人”苏炳添以6秒59的成绩取得冠军，用一场胜利完成个人2023赛季首秀。</div><div><br /></div><div>苏炳添上次参加正式比赛是2022年7月俄勒冈田径世锦赛，他先后参加了男子100米、男子4×100米接力，分别止步半决赛和预赛。经过冬训的调整和备战，苏炳添于1月29日奔赴欧洲，开启新赛季征程。受疫情、伤病等因素影响，苏炳添上次在赛季初赴欧洲征战室内赛还要追溯至2019赛季。当年2月，他先后在爱尔兰、英国、德国参加了3站室内赛，男子60米的比赛跑了5枪，决赛成绩分别为6秒52、6秒47、6秒49，3站比赛均夺冠。在男子60米项目上，苏炳添的个人最好成绩为6秒42，他也是该项目全国纪录、亚洲纪录保持者。2018年3月，苏炳添以6秒42获得伯明翰室内世锦赛男子60米亚军。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></div><div><br /></div><div>本次比赛男子60米报名名单显示，21位参赛选手大多来自瑞典、丹麦、挪威，只有苏炳添和日本选手东田旺洋来自亚洲。21人分成3组，先进行预赛，两个小时后展开决赛较量。在所有选手中，苏炳添的个人最好成绩是排在第一位的，东田旺洋以6秒64紧随其后，其他大部分选手的个人最好成绩在6秒90左右。</div><div><br /></div><div>有绝对实力作为保障，苏炳添比赛很顺利。在预赛第三组出场的苏炳添跑了6秒75，在预赛中排名第二位，东田旺洋以6秒71位列预赛首位。在决赛中，第一枪有人抢跑，苏炳添当时已经全力跑出，被召回后对体能有一定影响。再度发枪，排在第四道的苏炳添又是一马当先，率先冲线，成绩为6秒59秒。东田旺洋以6秒60取得第二名。惊险夺冠的苏炳添没有庆祝动作，显得特别淡定。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/223751782360hds6jfjt88.jpeg\" target=\"_blank\"><img alt=\"2023赛季首秀 苏炳添6秒59获得室内60米冠军_图1-3\" title=\"2023赛季首秀 苏炳添6秒59获得室内60米冠军\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/02/223751782360hds6jfjt88.jpeg\" /></a></p></div><div><br /></div><div>在东京奥运会上创造了9秒83的男子100米亚洲纪录后，苏炳添去年在参加俄勒冈田径世锦赛前，甚至没能参加任何一场正式比赛。这也导致他没能全部调动状态，跑出满意的成绩。苏炳添此次出征室内赛季，也是为今年的布达佩斯田径世锦赛、杭州亚运会进行热身。去年8月，世界田径协会发布了布达佩斯世锦赛的参赛标准，男子100米的达标线为10秒整。苏炳添在东京奥运会后，再未突破过10秒大关。</div><div><br /></div><div>在完成瑞典哥德堡比赛后，苏炳添还将继续在欧洲参赛。根据赛程安排，他将参加2月8日的法国蒙德维尔站比赛和2月15日的法国列万站比赛。苏炳添本赛季的前三站比赛的评级分别为D类、B类和A类，比赛竞争力将逐渐上升。以欧洲室内赛开启赛季，苏炳添能更好地向更高强度的百米过渡，为整个赛季打下基础。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/sports/202302/00526684.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (210,'2023-02-19 09:34:49.682099','2023-02-19 09:34:49.682099','&quot;这次是永远的&quot;传奇四分卫布雷迪第二次宣布退役','<td id=\"article_content\"><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/01/1051392bdgxn517sbtngb2.jpg\" target=\"_blank\"><img alt=\"&quot;这次是永远的&quot;传奇四分卫布雷迪第二次宣布退役_图1-1\" title=\"&quot;这次是永远的&quot;传奇四分卫布雷迪第二次宣布退役\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/01/1051392bdgxn517sbtngb2.jpg\" /></a></p></div><div><font color=\"#808080\">美联社</font></div><div><br /></div><div>美国中文网报道 周三，传奇四分卫汤姆·布雷迪(Tom Brady)宣布从NFL退役。一年前，他曾发表过同样声明，但随后又复出打了一个赛季。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></div><div>布雷迪在社交媒体上发布的一段53秒的视频中说:“我知道上次的过程反响很大。你只有发布一次超级感人退休宣言的机会，我去年就用完了。”</div><div><br /></div><div>去年同一天，这位45岁的海盗队四分卫在完成22个赛季后宣布退役，但在不到两个月后重返赛场，这是他的第23个赛季。然而，海盗队今年止步季后赛。</div><div><br /></div><div>布雷迪周三承诺，这次退役是“永远的”。</div><div><br /></div><div>布雷迪被广泛认为是美式橄榄球运动历史上最伟大的四分卫之一，他拥有多项NFL职业生涯记录，包括赢得比赛数量最多、最多达阵传球（624）和最多传球码数（84,250）等。</div><div><br /></div><div>布雷迪为新英格兰爱国者队效力了20个赛季，之后于2020年加入坦帕湾海盗队，并在2021年赢得了个人第7座超级碗冠军，成为NFL历史上唯一的七冠王。</div><div><br /></div><div>布雷迪在海盗队度过艰难一年之后宣布退役，这是他整个职业生涯中的第一个输球赛季。</div><div><br /></div><div>在NFL之外，布雷迪还面临着其他损失，主要与他对破产的加密货币交易所FTX的投资有关。他和当时的妻子吉赛尔·邦辰（Gisele Bündchen）共同拥有200万股加密交易所的股票，并为该公司做了公开代言。</div><div><br /></div><div>邦辰和布雷迪于去年10月完成离婚，就在这家加密公司正式倒闭之前。</div><div><br /></div><div>布雷迪面临FTX投资者的诉讼，他们认为品牌大使在推广之前应该做更多的尽职调查。</div><div><br /></div><div>（编辑：陈晓默）</div></td>','美国中文网','http://www.sinovision.net/sports/202302/00526605.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (211,'2023-02-19 09:34:49.682099','2023-02-19 09:34:49.682099','“体二代”登场 田亮女儿将战国际青少年网球赛','<td id=\"article_content\"><div>北京时间1月30日，奥运冠军田亮的女儿田雨橙堪称“国民闺女”，毕竟森碟早就以童星身份被广大观众熟知。如今根据ITF官网公布的新加坡青少年冠军赛J30签表显示，田雨橙获得外卡将首次亮相国际赛场，将正式开启网球生涯，也是令人期待她未来成为中国网球又一位希望之星。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/30/043037a41aywlaxayw99mw.jpg\" target=\"_blank\"><img alt=\"“体二代”登场 田亮女儿将战国际青少年网球赛_图1-3\" title=\"“体二代”登场 田亮女儿将战国际青少年网球赛\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/30/043037a41aywlaxayw99mw.jpg\" /></a></p></div><div><br /></div><div>田亮是中国著名体育明星，他在跳水领域取得很大成就，夺得过多次奥运金牌，并且当选国际游泳名人堂最佳跳水运动员。田亮退役后转战娱乐圈，也是拍摄了多部知名影视剧，尤其是带着女儿田雨橙登上大热综艺真人秀《爸爸去哪儿》，让他的女儿田雨橙成为“国民闺女”，也是让所有人见识到森碟的运动少女形象。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/30/043037y9ehip3iy2uxipiy.jpg\" target=\"_blank\"><img alt=\"“体二代”登场 田亮女儿将战国际青少年网球赛_图1-4\" title=\"“体二代”登场 田亮女儿将战国际青少年网球赛\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/30/043037y9ehip3iy2uxipiy.jpg\" /></a></p></div><div><br /></div><div>有着田亮的运动体育基因传承，田雨橙很小就展现体育天赋，而田亮也是着重培养女儿，很早就能看到田雨橙练习网球的视频。</div><div><br /></div><div>如今根据ITF官网公布的新加坡青少年冠军赛J30签表，田亮女儿田雨橙获得外卡，将首次亮相国际赛场征战网球赛事。田雨橙的首秀将对决另一位外卡选手董艺卓。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/30/043320n9b9boclebckuecn.jpg\" target=\"_blank\"><img alt=\"“体二代”登场 田亮女儿将战国际青少年网球赛_图1-5\" title=\"“体二代”登场 田亮女儿将战国际青少年网球赛\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/30/043320n9b9boclebckuecn.jpg\" /></a></p></div><div><br /></div><div>要知道，田雨橙在今年4月15日，才将正式年满15周岁，她如今将参加新加坡青少年冠军赛J30，无疑是她有望开启职业网球生涯的征兆。一旦田雨橙能够在网球领域展现天赋，成为中国网球又一位新星，以她的名气、颜值与国民度等，绝对有望成为中国体育又一位超级偶像。</div><div><br /></div><div>当然，除了田亮的女儿田雨橙之外，体操奥运冠军杨威与乒乓球奥运冠军刘国梁的子女，则是都在打高尔夫球，也是期待这些“体育星二代”未来能够取得成功。</div></td>','美国中文网','http://www.sinovision.net/sports/202301/00526501.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (213,'2023-02-19 09:34:49.698456','2023-02-19 09:34:49.698456','谷爱凌膝盖受伤退赛：核磁共振检测时哭了30分钟','<td id=\"article_content\"><div>进入2023赛季以来，谷爱凌的表现十分出色。但很可惜，伤病却在此时找上了她。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></div><div>北京时间28日上午，谷爱凌在个人社交媒体发文，透露自己膝盖受伤，将退出本周的Xgames比赛。谷爱凌表示，自己一度在核磁共振检测时哭了30分钟。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/28/01114987v222p8t2dw8t8i.jpg\" target=\"_blank\"><img alt=\"谷爱凌膝盖受伤退赛：核磁共振检测时哭了30分钟_图1-3\" title=\"谷爱凌膝盖受伤退赛：核磁共振检测时哭了30分钟\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/28/01114987v222p8t2dw8t8i.jpg\" /></a></p></div><div style=\"text-align: center;\"><font color=\"#808080\">谷爱凌报平安。</font></div><div style=\"text-align: center;\"><br /></div><div>“今天训练不幸摔得很重，医生开始担心是十字韧带ACL损伤。过去几个小时我很郁闷很难受，不过30分钟前检查结果刚出来了，我的膝盖没有严重损伤。”</div><div><br /></div><div>“我很幸运，只造成了膝盖的扭伤和骨头挫伤。”</div><div><br /></div><div>“但坏消息是这星期Xgames没有办法参加了，过去的几个小时让我更有清晰地感知身体健康的重要性，我会尽快回来的！”</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/28/0111494ytctttvy77vaakh.jpg\" target=\"_blank\"><img alt=\"谷爱凌膝盖受伤退赛：核磁共振检测时哭了30分钟_图1-4\" title=\"谷爱凌膝盖受伤退赛：核磁共振检测时哭了30分钟\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/28/0111494ytctttvy77vaakh.jpg\" /></a></p></div><div style=\"text-align: center;\"><font color=\"#808080\">谷爱凌社交媒体发文。</font></div><div style=\"text-align: center;\"><br /></div><div>在此前进行的自由式滑雪世界杯加拿大卡尔加里站，女子U型池场地的比赛中，谷爱凌决赛三轮均得到90+的分数，以93.50分获得冠军。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/sports/202301/00526443.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (214,'2023-02-19 09:34:49.702147','2023-02-19 09:34:49.702147','林书豪官宣新东家','<td id=\"article_content\"><div>央视网体育微信公众号消息，北京时间1月26日，前CBA外援林书豪更新社交媒体，宣布自己正式加盟中国台湾P联赛高雄钢铁人队。随后高雄钢铁人队也官宣了这一消息。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></div><div><br /></div><div>本赛季林书豪曾效力于CBA广州队，但在广州未获得太多机会，于去年年底宣布离队，提前结束CBA征程。</div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/26/033702kfkf7am4fzb0m72m.jpg\" target=\"_blank\"><img alt=\"林书豪官宣新东家_图1-3\" title=\"林书豪官宣新东家\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/26/033702kfkf7am4fzb0m72m.jpg\" /></a></p></div><div>值得一提的是，高雄钢铁人阵中的明星球员周仪翔，是林书豪在北京首钢队的昔日队友，两人将再续前缘、并肩作战。另今年加盟钢铁人的外援阿隆-哈里森，也是林书豪的昔日队友，两人2015年在夏洛特黄蜂队时曾是队友。</div><div><br /></div><div>中国台湾目前有三个职业篮球联赛，P联赛是其中之一，另外两个是T1联赛（霍华德所在联赛）和SBL联赛，三个联赛并无上下级之分。P联赛成立于2020年，目前拥有6支球队，CBA旧将林志杰就效力于该联赛球队富邦勇士，林书豪的弟弟林书纬效力于该联赛另一支球队新北国王。</div><div><br /></div><div>钢铁人目前以2胜13负的战绩排名联赛倒数第一。</div></td>','美国中文网','http://www.sinovision.net/sports/202301/00526368.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (216,'2023-02-19 09:34:49.713296','2023-02-19 09:34:49.713296','谷爱凌夺2023年首冠','<td id=\"article_content\"><div>在20日结束的2022-2023赛季国际雪联自由式滑雪U型场地技巧世界杯卡尔加里站决赛中，中国选手谷爱凌以95分的高分夺得冠军，张可欣名列第五。</div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/20/020552ovopazg3coavomcv.png\" target=\"_blank\"><img alt=\"谷爱凌夺2023年首冠_图1-1\" title=\"谷爱凌夺2023年首冠\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/20/020552ovopazg3coavomcv.png\" /></a></p><p><font color=\"#808080\">视频截图</font></p><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/20/020559yuel563we5lze1wh.png\" target=\"_blank\"><img alt=\"谷爱凌夺2023年首冠_图1-2\" title=\"谷爱凌夺2023年首冠\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/20/020559yuel563we5lze1wh.png\" /></a></p></div><div style=\"text-align: center;\"><br /></div><div><br /></div><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;text-align:center;margin:4px auto; margin-bottom:5px;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></td>','美国中文网','http://www.sinovision.net/sports/202301/00526181.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (217,'2023-02-19 09:34:49.713296','2023-02-19 09:34:49.713296','林书豪晒出婚纱照并发布长文 宣布自己已婚','<td id=\"article_content\"><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/19/033848yg8g70anu278oy0s.png\" target=\"_blank\"><img alt=\"林书豪晒出婚纱照并发布长文 宣布自己已婚_图1-1\" title=\"林书豪晒出婚纱照并发布长文 宣布自己已婚\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/19/033848yg8g70anu278oy0s.png\" /></a></p><p><font color=\"#808080\">林书豪微博</font></p><p><br /></p></div><div>19日，林书豪在社交平台上晒出与妻子的婚纱照，并发长文宣布了自己已婚的消息。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></div><div>内容如下：</div><div><br /></div><div>关于一夜成名有件很有趣的事，那就是没人会教你如何去应对。当然我很感激能拥有这么一段疯狂的旅程，但我很确定当我结婚以后，我会尽我所能的让我的私生活保持神圣且忠于自我。</div><div><br /></div><div>当我仍然坚信要守护我生活里比较私人的一面的时候，同时也很为难没有办法和大家分享我生命中如此重大的一个改变。在一起几年之后，我和我的太太在两年前完成了婚礼，那是一场温馨且神圣的仪式，也是我人生有史以来最高光的时刻。很开心把两年多前这美好的一天和大家分享。我很感激能找到一个深爱着我一切的人，也能拥抱我独特的人生，并且愿意和我一起度过。</div><div><br /></div><div>祝愿大家在新的一年里健健康康，能多和爱的人在一起，留下更多美好的记忆。我们全家在此祝大家新春快乐！</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/sports/202301/00526133.htm',5);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (218,'2023-02-19 09:34:49.722313','2023-02-19 09:34:49.722313','中国足协召开执委会 秘书长刘奕被正式免职','<td id=\"article_content\"><div>中国足球协会第十一届执委会第八次会议15日在苏州召开。</div><div><br /></div><div>国家体育总局副局长、中国足协党委书记杜兆才表示，中国足球改革发展任重道远，必须全面加强党对足球工作的领导，坚定不移贯彻落实《中国足球改革发展总体方案》。进一步加强男、女足各级国家队建设，实施“三级国家队突破行动计划”；加快落实女足改革发展方案，推动“女足重返世界一流”；坚持以青训为根，加快构建中国特色足球青训体系；坚持以联赛为本，构建中国特色职业足球发展模式。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1039\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1039] 体育新闻详细页 Admaru */\r\n    OA_show(1039);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=1ce8625\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1039&amp;n=1ce8625\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id887] 体育详情页文包图广告 */\r\n    OA_show(887);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=adf272e\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=887&amp;n=adf272e\" /></a></noscript></div></div><div><br /></div><div>杜兆才说，足球工作者要切实增强使命感、责任感、紧迫感，共同谋划中国足球发展之策，走好中国足球新的长征路，对标2035年建成体育强国目标，卧薪尝胆、真抓实干，打好中国足球翻身仗。</div><div><br /></div><div>中国足协主席陈戌源表示，中国足协2023年工作重点将聚焦于强化男女足各级国家队训练备战工作、持续推动职业联赛健康发展、加强青少年足球工作、深化足球领域改革、强化足协自身建设、加大行业风气治理等。要以时不我待、只争朝夕的精神，积极探索中国特色足球发展道路，为振兴中国足球努力奋斗。</div><div><br /></div><div>会议听取了中国足协2022年工作情况及2023年工作计划，听取了职业联赛2022年工作情况及2023年工作计划。会议决定，免去刘奕的中国足协秘书长职务。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202301/15/014708u677k1cr7strx7tt.jpg\" target=\"_blank\"><img alt=\"中国足协召开执委会 秘书长刘奕被正式免职_图1-3\" title=\"中国足协召开执委会 秘书长刘奕被正式免职\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202301/15/014708u677k1cr7strx7tt.jpg\" /></a></p></div><div><br /></div><div>据悉，作为中国足协秘书长，刘奕在被提名甚至上任之初，就因其之前担任经纪人的身份，受到中国足协内部的强烈质疑。任期之内，刘奕的相关工作，并未赢得足协内部的认可，在足协内部的工作年度测评屡次排名靠后。</div><div><br /></div><div>此前，根据著名足球记者马德兴的报道：2023年，中国国字号的管理权，又回到了中国足协。“去年年底，中国足协以及男、女国家队的管理部门召集各级国字号队伍在海口进行会议，中国足协主席陈戌源、国管部与女子部的管理人员、男女足各级国家队的主教练、领队、教练团队的相关人员等全部到会，这也是中国足球近些年以来第一次男、女国字号的教练组全部到会。能做到这一点，根本还是管理机制、管理体系的理顺。”</div><div><br /></div><div>马德兴在报道中透露：在过去四年的周期里，中国男足国家队的管理权归属几经变化。在2019年亚洲杯结束后，管理权从中国足协交到了更高一级管理部门手中，后者专门指派专门成立的管理团队负责国家队的管理，所以才有了后来的卡纳瓦罗过渡执教、之后又请回意大利人里皮。在里皮辞职后，管理权又回到了足协。12强赛前6场国足表现不佳，有关方面又“跳过”中国足协直接换帅，李霄鹏由此走上前台。同时，男足国家队的管理权又由更高一级管理部门成立一个专门的“工作领导小组”负责，由上级领导亲自担任组长、足协主席陈戌源及其他几位领导任副组长，重新开始统一指挥男足国家队事宜。此期间，李霄鹏作为主教练，事无巨细都需向上级领导报告，包括集训名单的拟定等，甚至连比赛的用人、首发阵容等也需汇报。</div><div><br /></div><div>马德兴在报道中表示：令人失望的12强赛结束后，男足国家队的重建工作被耽误下来，因为有关方面在成立了“工作领导小组”后，男足国家队事宜就完全不是由中国足协所能够左右的，而是需要等待有关方面的进一步工作指示与部署才能展开。近期以来，有关“中国男足国家队重新展开选帅”的消息与说法不断，很重要一个因素恰恰就是有关方面彻底“放权”，将男足国家队的管理权明确重新交回到足协。</div></td>','美国中文网','http://www.sinovision.net/sports/202301/00526002.htm',5);
