INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (717,'2023-02-19 11:11:04.922491','2023-02-19 11:11:04.922491','天津援建和田地区首个第三产业园区落成','<div class=\"left_ph\"><img src=\"//i2.chinanews.com.cn/simg/ypt/2023/230219/179fd55c-5e0d-478e-aff2-e92d68da81cf_zsite.jpg\" alt=\"天津援建和田地区首个第三产业园区落成\" /></div>\r\n','中国新闻网','http://www.chinanews.com/cj/2023/02-19/9956465.shtml',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (845,'2023-02-20 04:52:17.220900','2023-02-20 04:52:17.220900','首届中国非遗保护年会开幕','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　本报榆林2月18日电  （记者龚仕建）2月18日，首届中国非物质文化遗产保护年会开幕式在陕西省榆林市举办。</p><p>　　开幕式上，主办方上线了“非遗人之家”，发布了多项非遗计划和项目，包括2021年度十大最具影响力非遗传播活动、全国青年非遗传承人扶持计划、金融支持非遗“百千万”计划项目、“非遗与旅游融合发展优选项目”名录，以及2021年“非遗进校园”实践案例、《短视频直播电商助力非遗发展研究报告》、非物质文化遗产知识普及推广计划、陕北文化生态保护区（榆林市）等。</p><p>　　年会以“打造非遗年度名片、绽放非遗绚丽色彩”为主题，同步举办非遗论坛、非遗大集等活动，旨在进一步提升非遗保护传承水平，推动非遗与现代生活相连接。其间，太极拳、木偶长绸舞、陕北说书、陕北秧歌等非遗节目上演，彰显非遗魅力。</p><p>　　据了解，首届中国非遗保护年会由文化和旅游部指导，中国非物质文化遗产保护协会主办，陕西省文化和旅游厅与榆林市委、市政府联合主办，山东省文化和旅游厅、四川省文化和旅游厅、河南省文化和旅游厅为主宾省单位。</p>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：梁秋坪、马昌)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0220/c1008-32627020.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (846,'2023-02-20 04:52:17.227904','2023-02-20 04:52:17.227904','甘肃逾2万名农村人才获评职称','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　本报兰州2月19日电  （记者赵帅杰）记者从甘肃省人社厅获悉：近年来，甘肃持续加大对农村实用技术人才、农村实用文化人才和特殊人才的职称评定力度，截至目前，全省已有2万余名农民获得职称。</p><p>　　根据甘肃省农村人才职称评审相关规定，甘肃对从事种植业、养殖业、农村能源、农业机械化、农副产品加工、农业经营管理、农业环境保护以及民间戏剧、曲艺、音乐、舞蹈、美术、工艺技艺等方面的农村各类人才，按照相应条件分别评定为高级、中级和初级职称。评价突出素质、业绩、贡献导向，对学历、论文、专业等不作限制要求。针对生产型、经营型、技能型、服务型等不同人才特点，甘肃为“农把式”“土专家”“田秀才”“乡创客”量身制定符合区域特点、产业特色和发展特征的评审标准。</p><p>　　据了解，甘肃各地人社部门创新评审方式，将农村实用技术人才答辩现场设在果蔬大棚、农作物种植基地，让评委与农民走进菜地、果园、田间地头、牛棚羊圈，直观全面展示参评人员的专技水平和能力；农村实用文化人才答辩采取线上视频交流、实践操作、技艺表演、业绩展示、模拟讲课等方式。</p><p>　　通过农民职称评定，甘肃挖掘、激励了一批乡村优秀人才，形成了一批有技术、有专长、有业绩、有认证的乡村人才队伍。</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日   11 版）</span>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：赵欣悦、岳弘彬)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0220/c1008-32626854.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (847,'2023-02-20 04:52:17.232904','2023-02-20 04:52:17.233908','辽宁医药集中采购减轻群众负担','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　本报沈阳2月19日电  （记者刘佳华）记者近日从辽宁省公共资源交易中心获悉，辽宁持续推进医药集中采购，减轻群众负担，解决“看病贵”问题。2022年，辽宁省药品和医用耗材集中采购平台实现交易额442.08亿元，同比增长5.79%，落地的药品和医用耗材各类集中带量采购项目可节约采购资金20亿元，中选产品平均降价54%。</p><p>　　据悉，2022年，辽宁牵头开展“八省二区”省际联盟第四批药品集中带量采购，通过药品和医用耗材集中采购平台，汇集公立医疗机构药品需求量，由医药生产企业自愿参加、自主报价，通过公平竞争形成采购价格，与最高有效申报价相比，中选产品最高降幅89.76%，平均降幅62.67%。医疗机构与中选企业签署协议、严格落实，推动药品价格回归合理水平。</p>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：梁秋坪、马昌)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0220/c1008-32627023.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (848,'2023-02-20 04:52:17.240903','2023-02-20 04:52:17.240903','广东特困老人家庭将获适老化改造','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　本报广州2月19日电  （记者洪秋婷）日前，记者从2023年广东省民政工作会议上获悉：今年广东省各级民政部门将对符合条件的2.2万户特困老年人家庭实施适老化改造，并完善省低收入人口动态监测和救助帮扶平台，将405万低收入人口纳入动态预警范围，进一步切实织密基本民生保障网。</p><p>　　据介绍，广东将持续推进养老服务体系建设，在居家社区养老服务方面，广东全省目前共有社区养老服务设施2.1万个，为1.6万户特殊困难老年人家庭实施适老化改造，全年培训养老护理人才8.6万人次。同时，广东持续出台政策助力养老服务机构纾困，全省1012家养老机构享受机构运营补贴或一次性补贴4851万元，发放工作人员补贴1600万元。</p><p>　　会议还提出，未来一年广东将继续健全完善城市“街道—社区—小区—家庭”四级和农村“县—镇—村”三级养老服务网络，加快乡镇（街道）综合养老服务中心建设，对符合条件的2.2万户特困老年人家庭实施适老化改造；实施康养职业技能培训计划，推进多层次养老服务人才培养。突出养老服务深度合作，发挥粤港合作社会福利专班作用，支持和推动与香港、澳门地区养老服务交流合作，并持续深化跨省合作“旅居养老”。</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日   11 版）</span>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：赵欣悦、岳弘彬)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0220/c1008-32626855.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (849,'2023-02-20 04:52:17.246900','2023-02-20 04:52:17.247855','云南蒙自市湾田村推进路灯建设','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　核心阅读</p><p>　　云南蒙自市水田乡湾田村创新思路，推进乡村路灯建设：由共青团红河州委、红河州青基会协调社会公益组织，联系相关公益项目，解决资金困难，147盏路灯一个月到位；安装位置充分征求村民意见，首先保障日常出行使用，重点照顾有老人家庭；党员干部带头，发动团员青年、志愿者和村民动手安装，将保护路灯写进村规民约，发动群众做好日常管护。目前，乡村出行安全性大为增加，夜间生活更加丰富。</p><p>　　</p><p>　　夜幕降临，车子拐过一个弯，前方灯火通明的村子映入李平眼前。许久没回家的云南省红河哈尼族彝族自治州蒙自市水田乡湾田村村民李平有些惊讶，这还是那个一到晚上就“伸手不见五指”的家乡吗？</p><p>　　车驶进村子，一盏盏明亮的路灯伫立在村道两旁。在路灯的指引下，李平稳稳地将车子开进自家小院。“村里安了路灯，以后再也不用担心摸黑回家了！”一下车，李平高兴地说。</p><p>　　2021年8月，由共青团红河哈尼族彝族自治州委员会、红河州青少年发展基金会协调社会公益组织捐赠的42盏路灯“落户”水田乡湾田村。半年来，共计有147盏路灯落地水田乡4个行政村。明亮的灯光照亮了村民出行路，也温暖了村民们的心。</p><p>　　来源——</p><p>　　引进公益路灯</p><p>　　方便夜间出行</p><p>　　湾田村是蒙自市水田乡的一个自然村，村域面积5平方公里，海拔落差200多米，人口630多人。</p><p>　　湾田村群山环绕，路窄坡陡，一条2公里多长的主道，就有10多个“回头弯”和岔路口，没有路灯照明给当地村民夜间出行带来很大不便，也存在很大的安全隐患。</p><p>　　“过去，整个村子只有村委会门前有两盏路灯，一到晚上村里大部分地方一片漆黑，村民走夜路只能打着手电筒。”湾田村所在的行政村斜坡村党总支书记、村委会主任普进华告诉记者，由于村内道路未安装路灯，村民忙完农活回家只能摸黑走夜路，老人孩子一到晚上甚至不敢出门，更别提在村里开展文化活动。</p><p>　　“自然照明靠月光，人工照明靠电筒”，这一句玩笑话，在水田乡的大部分村子却曾是普遍现象。“前年乡里召开党代会，代表反映最多的问题，就是村民夜间出行难。”时任水田乡乡长，现任共青团红河州委副书记何川说。</p><p>　　乡村路灯问题，既是群众关心的民生实事，也关乎乡村振兴发展。为了摸清村庄照明情况，何川带着乡村两级干部，把没路灯的村子跑了一遍。经初步统计，全乡4个行政村近20个自然村都存在没有路灯或路灯损坏的情况。</p><p>　　想要做到路灯全覆盖，何川初步估计需要近200盏路灯，按照每盏路灯3000元的预算，一共需要近60万元。乡里财政吃紧，何川向上级部门请求帮助。但安装路灯需要协调项目和招投标，流程较长，村民们改善夜间出行的需求又很迫切。就在这时，共青团红河州委及时伸出援手，帮助协调解决。</p><p>　　红河州青基会理事长杨巧燕介绍，青基会联合人民能量公益发展基金，联系到中华思源工程扶贫基金会“乡村振兴点亮希望”公益项目。经青基会工作人员实地考察确认，水田乡农村地区照明不足问题亟须解决。于是，青基会成功协调到由公益资金捐赠的147盏路灯。从提交申请到路灯运抵村子，全过程仅用时1个月。</p><p>　　安装——</p><p>　　征求村民意见</p><p>　　满足多样需求</p><p>　　“咱们村要装路灯了！”好消息在湾田村不胫而走。</p><p>　　装太阳能灯还是电灯？装路灯后收不收电费？路灯安在哪里……在湾田村党员活动室，围绕路灯安装一事，村民代表们讨论得热火朝天。针对村民们提出的疑问，普进华和湾田村一支部书记黄进春一一记录下来，并带领村干部入户走访，征求村民意见。</p><p>　　湾田村地处干热河谷地区，光热资源充沛，安装太阳能路灯村民不用交电费，既节能环保，又经济适用。但路灯数量有限，整个湾田村有156户人家，只有42盏路灯，做不到在家家户户门前安装。</p><p>　　经过民主协商，根据村民需求和村庄规划，湾田村将大部分路灯安装在村子的主道两侧和岔路口，每盏路灯间隔50米，首先保障村民日常出行的交通安全。这时有村民代表提出建议，应该对有老人的家庭在装路灯时予以照顾。对此，湾田村决定拿出6盏路灯，装在有高龄老人和独居老人的家庭门前。</p><p>　　村民李志德的母亲年近90岁，村子将一盏路灯就装在了他家门前。“装了路灯后，老人晚饭后想出去散步，我们也更放心了。”李志德高兴地说。</p><p>　　蓝白色的LED太阳能路灯在湾田村竖立起来，不仅照亮了乡村的出行道路，也点亮了村民们的夜间生活。</p><p>　　村民李红芬晚上喜欢跳广场舞，过去没有路灯，李红芬只能在自家院子里跳。如今，路灯点亮了村庄的夜晚，李红芬索性约上几个女伴，组建了一支10人的广场舞队。吃过晚饭，李红芬和舞伴们来到村里的空地，伴着欢快的音乐跳起舞来。过去一到晚上就冷冷清清的村子也热闹了起来。</p><p>　　“村里有了路灯，老人孩子晚上出门不担心摔跤，村民们喜欢上了饭后健身散步，大家的幸福感越来越强。”黄进春笑着说。</p><p>　　维护——</p><p>　　激发村民参与</p><p>　　加强日常管护</p><p>　　路灯运进村子后，何川曾想过，是否由政府出钱找人安装。在与乡、村干部集体商量后，水田乡最终决定以党员干部带头，发动团员青年、志愿者和村民投工投劳，政府最终验收的形式开展路灯安装工作。</p><p>　　“不是乡里舍不得出钱，而是想让村民们能亲自参与到路灯的建设之中。”何川说，路灯由公益组织捐赠，村民和村集体不用出钱，只有付出劳动，村民们才会珍惜来之不易的成果，从而自发爱惜保护村里的路灯。</p><p>　　湾田村安装路灯时正值8月盛夏，气温最高逼近40摄氏度，参与搬运、安装路灯的村民们却没有人叫苦。“路灯给村子带来了光亮，买路灯没花我们一分钱，出点力气是应该的！”湾田村村民杨昆才说。</p><p>　　公益组织捐赠的这批太阳能路灯高6米，采用80瓦多晶硅太阳能板，所用的太阳能锂电池具有过充过放、自动开关等功能。杨昆才早年从事电工职业，也是村里的电工，对新路灯的质量赞不绝口：“路灯白天自动充电，天黑时自动工作，每天亮10个小时，一盏路灯的照明距离能达到50米。只要爱惜维护好，路灯能用很久！”</p><p>　　为了倡导村民们维护好路灯，水田乡各村委会将“保护路灯等公共基础设施”写进了村民公约中。</p><p>　　同时，水田乡每周二在各村例行开展“扮靓家园”行动，除了清扫道路、沟渠外，也将清洁路灯灯身小广告、擦拭太阳能板纳入行动中来，发动群众主动做好路灯的日常管护。</p><p>　　“晚上村子组织巡查，若发现有路灯没有亮，第一时间将现场情况拍照并上传至村委会工作群，由村委会安排电工及时检查维修。”普进华介绍，路灯的日常维护费用由村集体资金承担，并按规定向村民公示。</p><p>　　“乡里定期收集各村的路灯使用情况，确因质量问题出现路灯不亮，会向团州委反馈，由青基会联系捐赠方提供维修配件或重新更换。”何川说，水田乡的147盏路灯自建成投用以来均正常使用，在大家的爱护下还未出现因质量问题导致路灯损坏的情况。</p><p>　　杨巧燕表示，在团州委的领导下，2021年以来，红河州青基会已累计协调社会公益力量和爱心人士为全州6个县市的28个行政村捐赠路灯860盏，总金额近300万元，覆盖群众1万余人。</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日   11 版）</span>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：赵欣悦、岳弘彬)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0220/c1008-32626852.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (850,'2023-02-20 04:52:17.253900','2023-02-20 04:52:17.253900','安徽有序推进乡镇街道赋权工作','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　本报合肥2月19日电  （记者田先进）日前，为贯彻落实《安徽省人民政府关于赋予乡镇街道部分县级审批执法权限的决定》，安徽省司法厅会同省委编办联合印发《关于做好赋予乡镇街道县级审批执法权限有关工作的通知》（以下简称《通知》），在安徽全省范围有序推进乡镇街道赋权工作。</p><p>　　《通知》要求，各地要对照《安徽省赋予乡镇街道部分县级审批执法权限指导目录》，按照“基本盘+自选”的要求，积极稳妥做好赋权事项认领承接；要全面梳理县乡两级执法机关机构编制、人员队伍等情况，结合实际做好综合执法机构建设；要按照“一乡一策”办法，于5月底前制定公布乡镇街道审批事项清单、行政执法事项清单和配合事项清单，构建乡镇街道“一目录三清单”权责清单制度体系；要加强乡镇街道承接审批事项业务指导和执法业务培训；要建立赋权工作跟踪评估机制，及时收集掌握乡镇街道赋权相关情况，适时开展赋权事项运行情况评估。</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日   12 版）</span>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：赵欣悦、岳弘彬)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0220/c1008-32626860.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (851,'2023-02-20 04:52:17.260856','2023-02-20 04:52:17.260856','天津宁河区纪委监委加强同级监督','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　芦苇依依，鹭鸟翩翩。天津市宁河区的七里海国家湿地公园清澈美丽，这得益于生态移民工作的顺利开展。当地用有形抓手压实无形责任、不断探索破解难题的同级监督机制发挥着重要作用。</p><p>　　天津市宁河区区镇两级纪委，将党委政府“民心工程”践诺作为同级监督的重点，紧盯七里海湿地移民搬迁项目，对搬迁安置工作中推诿扯皮、延误进度、以权谋私、优亲厚友、弄虚作假、吃拿卡要等侵害民利的领导干部予以严肃查处。</p><p>　　2022年10月，造甲城镇田辛村、赵温村650余户群众通过公开摇号，不久就拿到了新房钥匙。公开透明推动移民搬迁工作妥善开展的背后，是宁河区纪委监委加强同级监督的不懈努力。</p><p>　　宁河区纪委监委紧盯同级政府诚信问题，跟进监督区政府向社会公开的2022年重大行政决策事项，实地查看工程进度、深入职能部门调研，以“全周期管理”思维开展工作。</p><p>　　常态化、全周期管理的同时，宁河区纪委监委陆续出台党员领导干部廉政谈话办法、关于进一步加强党员干部和公职人员有关重大事项报备的通知等10项制度，监督责任体系不断健全。“平台监管、日常监督、送达提示函、述评结合”的同级监督体制机制更加完善，推动“一把手”和领导班子权力运行走向更加科学化、法治化和合理化的轨道。</p><p>　　“女儿近期结婚，是否需要报告宴请名单？”“姐夫是区重点工程项目总包，组织上派我包保所在街道工作，是否应当认领《违规插手干预工程建设领域行为报告备案表》？”……《关于加强同级监督的实施意见》出台后，许多区级领导主动来电咨询。“我们强化同级监督是为抓队伍做决策拦上一条‘安全带’，确保各项决策部署始终坚持正确的政治方向，确保干部队伍始终保持清正廉洁的政治本色。”宁河区委书记白凤祥说。</p><p>　　“党内监督没有禁区、没有例外。”宁河区委常委、区纪委书记、区监委主任李炜说，我们将继续完善体制机制，引领各类监督主体找准定位、发挥作用，形成上下贯通、常态长效的监督合力。</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日   12 版）</span>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：赵欣悦、岳弘彬)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0220/c1008-32626859.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (852,'2023-02-20 04:52:17.266901','2023-02-20 04:52:17.266901','青海省格尔木市建设城市智慧管理平台','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>\n	　　核心阅读</p>\n<p>\n	　　近年来，青海省格尔木市通过建设城市智慧管理平台、划分运用单元网格，加强行政执法协调监督，实现全程留痕和可回溯管理。自平台运行以来，执法效率更高，城市治理的智能化、法治化、精细化、规范化水平不断提升。</p>\n<p>\n	 </p>\n<p>\n	　　从格尔木机场出站，沿着绵延数十公里的防护林向东行驶，大约20分钟，宽阔的街道、繁华的高楼就映入眼帘。听见记者发出“干净、美丽”的赞叹，同行的当地群众难掩自豪：“这里地处戈壁，一代代建设者们播种植绿，给城市披上了‘绿色丝巾’。现在，数字技术又为城市装上了‘智慧大脑’，咱们格尔木，美得由里到外。”</p>\n<p>\n	　　当地群众口中的“智慧大脑”，实际上就是青海省格尔木市近年来推进智慧城市建设的结果。近年来，格尔木市运用现代技术手段优化政府治理流程和方式，充分发挥智慧城管系统作用，通过人员主动巡查、问题线上监督、案件协调指挥等方式，对城市街面市容市貌、市政设施、道路交通、垃圾处置等方面进行全方位监控，城市管理工作更加精细，城市治理的智能化、法治化、精细化、规范化水平不断提升。</p>\n<p>\n	　　建好智慧平台，执法有温度又有力度</p>\n<p>\n	　　市民韩文杰等不及打包好早餐，接完电话就朝着停车的路边跑去。“想着就买几个包子的工夫，于是把车停在了路边的禁停区，没想到这么快就接到了交警的电话。”</p>\n<p>\n	　　“先生你好！我们通过全市智慧信息系统发现您的车辆占用了人行道，鉴于停放时间较短，此次暂不做处罚，下不为例！”听到处理结果，韩文杰说：“这种人性化执法让咱暖心，更长了记性。”</p>\n<p>\n	　　执法人员口中的“全市智慧信息系统”，是格尔木市运用智能化技术和集成化指挥管理平台的重要载体。在格尔木市城市管理综合执法局的智慧城管监督指挥中心里，宽大的屏幕清晰直观。屏幕前，随着电脑键盘和鼠标细微的响声，10余名工作人员正有条不紊地分析着城市管理信息。</p>\n<p>\n	　　据介绍，该平台涵盖了基础设施及运行环境、综合性城市管理数据库、智慧城管系统和视频监控系统四大板块，同时结合城市管理实际，拓展开通了户外广告管理系统、扬尘噪声监测系统等子系统，有效提升了执法效率。</p>\n<p>\n	　　视频监督员孙瑞一边演示一边介绍：“就拿渣土车治理来说，目前全市的渣土车都装上了定位系统，后台实时跟踪，车辆的行驶轨迹、运行时间都纳入严格管理，执法人员不需要像以前那样熬夜值守蹲点检查。”在执法过程中，执法工作人员通过制高点鹰眼、无人机和移动视频车等全方位立体化的信息采集，对违法建设、工地扬尘等进行360度现场巡查，并通过平台执法终端将画面、声音实时传回。</p>\n<p>\n	　　通过城市智慧管理平台，执法实现了全程留痕和可回溯管理。孙瑞说：“前几年，执法人员人手有限，对乱倒垃圾、违法占道经营等治理‘顽疾’，只能采取‘突击战’，效果不理想。有了这个平台，执法效率更高了，执法行为也更规范，提升了群众满意度。”</p>\n<p>\n	　　用好单元网格，进一步提升治理效能</p>\n<p>\n	　　穿好羽绒服，裹上大围巾，再套上带有“信息采集”4个字的绿马甲，早上8点半，网格员袁有平准时来到市区西侧的光明路，在手机上签到后，开始了一天的巡逻工作。</p>\n<p>\n	　　今年是袁有平当网格员的第四个年头了。她整天穿梭在小区之间，高原的风雨阳光在她脸上留下了明显的印记，但她对工作始终充满热情。袁有平说：“我们负责把影响城市运行的市容、秩序、安全等问题第一时间上报，像井盖丢失、路面损坏、私搭乱建等，都是我们的巡查范围。”爱说爱笑的爽朗性格，也推动袁有平把这份工作干得有声有色。袁有平告诉记者：“时间一长，大家都习惯了‘有事就找绿马甲’。看到生活的城市越来越美丽，我也是打心眼里感到自豪。”</p>\n<p>\n	　　目前，在格尔木市，像袁有平这样的网格员共有45名，当地推出的“大数据+网格化+铁脚板”运作模式提升了管理精度。格尔木市将主城区35平方公里根据社区划分成30个单元网格，每个网格配备1—2名信息采集员。当地对市区93类8.2万余个基础设施部件，进行普查、确权、编码、定位，以此为基础搭建全市智慧城管数据库。</p>\n<p>\n	　　孙瑞说：“信息采集员可通过智慧终端将日常巡检中发现的问题拍照取证后上传平台，后台将整改任务及时派发给责任单位。问题整改完毕后，采集员现场核查无误方可结案，形成了城市管理问题处置的闭环机制。”</p>\n<p>\n	　　在组织起单元网格的基础上，格尔木市进一步整合力量，提升治理效能。格尔木市城市管理综合执法局局长赵文才说：“我们发挥巡警、交警、城管执法联勤的机制优势，建立了联合执法工作联席会议制度。以全市13个便民警务站为基点，建立‘巡警+交警+城管’的执法模式，开展联合综合执法。”</p>\n<p>\n	　　在严查无牌无证摩托车、超标电动车等违法行为的同时，格尔木市相关部门推动电动车、电动摩托车上牌工作，并安排网格员开展交通安全宣传教育活动。赵文才说：“我们推动‘堵’和‘疏’并举，让‘当下治’与‘长久立’紧密结合。”</p>\n<p>\n	　　做好资源整合，部门间协调更顺畅高效</p>\n<p>\n	　　早上刚上班，一阵急促的电话铃声传到格尔木市城市智慧管理平台。“您好，是12319吗？”电话那头，家住格尔木市小岛村的村民毛迎荷挺着急：“昨晚风大，我们家门口的电线被断掉的树枝砸断了，麻烦你帮忙处理一下。”</p>\n<p>\n	　　电话这头，接线员详细询问毛迎荷的家庭住址、现场的具体情况，并做好登记。初步核实后，工作人员马上对接电力部门。不到两个小时，后台显示已经对电线进行了维修和重新检查，并附了照片，经过值班长核实后，确认“结案”。</p>\n<p>\n	　　开通12319城管热线是格尔木市在智慧城市建设中做好沟通协调，动员群众参与城市管理的重要方式。格尔木市整合多部门信息资源，打造一站式公共服务APP“市民通”，并拓展其互动功能，市民通过“市民通”举报城市管理问题可兑换现金奖励。同时，当地鼓励市民通过12319城管热线、12345市民服务热线，反映城市管理、市政设施、违法建设等方面的问题和建议。</p>\n<p>\n	　　赵文才说：“我们还充分发挥媒体监督的作用，每月定期把各单位问题受理和办结情况进行公示，并将智慧城管年终考评结果纳入全市目标责任清单式动态考核管理系统当中。”</p>\n<p>\n	　　目前，城市智慧管理平台与市直部门之间的沟通协调更加顺畅，“第一时间受理、第一时间转办、第一时间办结、第一时间反馈”的运作模式不断成熟。该平台打通各部门联动协作的“高速路”，也架起了政府与百姓的“连心桥”。据统计，自平台运行以来，智慧城市信息中心共受理城市管理案件107370件，处置100614件，处置率达93.71％，结案100485件，结案率达93.59％。</p>\n<p>\n	　　“我们把增强人民群众获得感、幸福感、安全感摆在突出位置，不断完善智慧治理体系，推动司法、公安、交通、消防、生态环境等业务系统的高效联动。”格尔木市委主要负责同志说：“接下来，我们将提升智慧城市建设品质，以此提高社会管理能力、公共服务能力以及促进经济社会发展转型的能力，更加精准高效满足群众需求。”</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日 12 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：赵欣悦、岳弘彬)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0220/c1008-32626856.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (853,'2023-02-20 04:52:17.272853','2023-02-20 04:52:17.272853','南漳发放自动履行证明为守信企业“背书”','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：南漳发放自动履行证明为守信企业“背书”</div><p>\n	　　本报讯 （记者 王 洪 丁 俐 通讯员 魏 荣）“感谢法院！这份自动履行证明书帮了我们大忙，把我们公司因涉诉造成的不良影响降到了最低，银行贷款就差最后一步了，感谢法院及时给企业出具了证明！”企业相关负责人激动地说。</p>\n<p>\n	　　为助力优化法治化营商环境，体现当事人诚信守法的良好形象，为企业从事相关业务活动提供有力帮助，近日，湖北省南漳县人民法院开出今年首份自动履行证明书，为守信企业“背书”。</p>\n<p>\n	　　2021年12月，湖北某建设工程公司因工程款给付问题，将南漳县某工业品公司诉至南漳法院，该院于2022年4月作出判决。2022年9月，双方达成和解协议，南漳县某工业品公司自动履行了判决书确定的义务。</p>\n<p>\n	　　之后，南漳县某工业品公司本以为履行完毕后就不会有任何问题，却不想在办理银行贷款时，因该公司有涉诉情形，影响其征信，贷款无法发放。该公司向法院提交了双方和解协议及原告出具的被告已自动履行完毕的证明，经研究讨论，法院依据该公司提交的材料迅速出具了自动履行证明书。最终，在法院的协助下，该公司顺利办理了银行贷款。</p>\n<p>\n	　　作为探索司法正向激励与信用修复的创新性改革举措，出具自动履行证明书，给予主动履行的被告或被执行人“信用重生”的机会，助推被告或被执行人从“要我履行”到“我要履行”的思想转变，既从源头上营造诚信守法的社会氛围，让“失信受罚”和“守信获益”成为社会共识，也从另一层面降低了企业涉诉成本，助力优化法治化营商环境。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：梁秋坪、马昌)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0220/c1008-32627038.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (854,'2023-02-20 04:52:17.279856','2023-02-20 04:52:17.279856','苏州中院发布12项举措服务保障稳就业','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<div class=\"otitle\">原标题：苏州中院发布12项举措服务保障稳就业</div><p>\n	　　本报讯 日前，江苏省苏州市中级人民法院送出构建和谐劳动关系“大礼包”，发布服务保障稳就业12项举措，旨在落实落细就业优先政策，服务就业优先战略实施和高水平人才集聚，更好服务实体经济特别是产业创新集群融合发展，不断优化苏州法治化营商环境。</p>\n<p>\n	　　12项举措包括更加强化“双保护”司法理念，支持阶段性缓缴社会保险费政策落实，支持政府吸纳农村劳动力转移就业，促进高校毕业生多渠道灵活就业创业，支持和规范发展新就业形态，妥善审理涉疫情劳动争议案件，提升劳动争议多元解纷效能，积极开展企业劳动用工合规指引，畅通诉讼服务绿色通道，善意文明司法助力稳企稳岗，健全劳动人事争议风险监测预警机制，加强稳就业服务保障工作协同联动等。</p>\n<p>\n	　　2021年7月，苏州劳动法庭揭牌成立，这是全国首家也是目前唯一一家由最高人民法院批复设立的专业化劳动法庭。近年来，全市法院以苏州劳动法庭成立为契机，充分发挥职能作用，不断擦亮劳动人事审判品牌，积极推进院庭协同机制，坚持维护劳动者合法权益与促进企业健康发展并重，为构建和谐稳定劳动关系提供了强有力的司法保障。</p>\n<p>\n	　　（沈军芳）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：梁秋坪、马昌)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://society.people.com.cn/n1/2023/0220/c1008-32627044.html',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (856,'2023-02-20 04:52:17.306901','2023-02-20 04:52:17.306901','小小社区卫生站 惠及2.3万居民','<div class=\"artDet\">\n                        \n                        <p align=\"center\">\n	<img alt=\"\" src=\"/mediafile/pic/20230220/66/17104291328280693030.jpg\" /></p>\n<p align=\"center\" class=\"pictext\">\n	　　有了社区卫生服务站，镇国寺社区的居民在家门口即可享受医疗服务</p>\n<p>\n	　　近日，丰台区马家堡街道镇国寺北街社区卫生服务站正式开诊。这是马家堡构建“15分钟医疗圈”中的重要一环，填补了街道辖区西南区域的医疗空白。再结合年底即将开设在东南角的社区卫生服务中心分中心，辖区13万名居民群众可享15分钟到达社区医院求医问药的便捷、省心和安心。</p>\n<p>\n	　　北京青年报记者了解到，镇国寺北街社区卫生服务站的顺利开诊，让家住镇国寺社区冬季星空的陈辛英阿姨一颗悬着的心终于落了地，对于她来说，2023年的春节除了喜庆祥和，更平添了一分期待与欢欣。用陈阿姨的话说：“年纪大了，身体难免有点不舒服，但大医院离家远、早去依旧排大队、在路上奔波时间长，还没到医院就站不住了。”于是，一通通急切的电话打到了12345北京市民热线，希望能够就近开设社区卫生服务站。对周边居民尤其是老年人来说，能够方便省心地病有所医就是“刚需”。</p>\n<p>\n	　　针对群众诉求，马家堡社区卫生服务中心主动向前一步，凭借自身医疗资源和在马家堡辖区扎根多年的基础，承接了开设镇国寺北街社区卫生服务站的工作。丰台区卫健委、区发改委、区财政局、属地马家堡街道办事处等多方通力合作，多次协调、沟通下，顺利完成选址、报批、审核、拨款、建设等工作，并于虎年腊月廿八日完成装修。</p>\n<p>\n	　　镇国寺北街社区卫生服务站站长刘峰回忆道：“装修开工时已临近春节，很多工人师傅都想早点回老家团聚。但一听说这是个惠民项目，二话不说跟我打了包票，一定抢在节前完工。”就这样，在所有前期建设者的坚守下，春节假期一过的正月初八，镇国寺北街社区卫生服务站得以顺利开诊。</p>\n<p>\n	　　据介绍，镇国寺北街社区卫生服务站开设了全科、中医、B超、检查化验、中西药房等专业科室，依托马家堡社区卫生服务中心的专家资源，可以享受到中心同样优质的医疗服务，一开业就为周边约2.3万居民送去家门口的便捷医疗。</p>\n<p>\n	　　步行只需6分钟，就能看诊开药和进行多项检查，签约了家庭医生就能全年减免医事服务费和“无限制”购药服务，还能开B超、生化检查、肠胃镜检查、放射检查等单子，直接前往社区卫生服务中心进行检查。这让陈辛英和像她一样的很多居民的获得感、幸福感直线上升。</p>\n<p>\n	　　比陈辛英更高兴的，要数住在镇国寺社区玺萌公馆小区的柳大叔了。服务站就开在家楼下，步行2分钟即到，就连他经常需要做的中医理疗项目也搬来了。在服务站二层设置了中医诊区、输液室、理疗室，除中医诊室外，还有中医治疗区，开展中医适宜技术和诊疗项目，如拔罐、针灸、按摩、艾灸等。“服务办到我们心坎上去了，我经常出去钓鱼，坐上半天，这肩颈、胳膊就疼，之前总骑车去嘉园二里的社区卫生服务中心按摩，虽然不到2公里，可说到底还是家楼下这个方便。”柳大爷笑着说，“刘站长前几天告诉我，楼梯上马上要安装‘爬楼神器’，我这老伴儿腿脚不方便，以后来这儿看病省去了爬楼的麻烦，这真是想到我们前面去了，更合我们心意了。”</p>\n<p>\n	　　“扎实推进社区卫生服务中心医疗资源扩容和辖区点位均衡设置，努力提升基层医疗服务能力和水平，让马家堡辖区居民充分享受‘15分钟医疗圈’带来的便捷。”马家堡社区卫生服务中心党支部书记、主任陈应军表示，未来，马家堡辖区将会陆续开设多个社区卫生服务站，进一步织密辖区医疗服务这张惠民网，为辖区居民提供疾病治疗预防、慢病康复治疗等一体化、持续性、便捷式的医疗服务。</p>\n<p>\n	　　北京青年报记者从丰台区卫健委了解到，丰台区目前设有140多个社区卫生服务站，为周边居民提供基本医疗、基本公共卫生及疫情期间的防控工作。下一步，丰台区将持续发挥社区卫生服务的网底作用，做好群众的“健康守门人”。</p>\n<p>\n	　　文/本报记者 蒲长廷</p>\n<p>\n	　　摄影/本报记者 杨益</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626942.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (857,'2023-02-20 04:52:17.311900','2023-02-20 04:52:17.311900','蔡秀军委员：将“平疫结合”病房推广全国','<div class=\"artDet\">\n                        \n                        <p>\n	　　这几天，全国政协委员、浙江大学医学院附属邵逸夫医院（浙大邵逸夫医院）院长蔡秀军的日程排得有点满。新疆、海南、浙江……他在全国各地走访调研、推广“平疫结合”快速切换病房模式、完善提案，提前开启自己的两会时间。</p>\n<p>\n	　　作为一家医院的“掌门人”，过去3年，蔡秀军时刻紧绷新冠肺炎疫情防控弦。身处疫情防控一线，他深知，我国在传染病房建设方面存在明显短板：在重大疫情发生时，传统的传染病医院病房床位有限，不能满足社会需要，而大多数综合医院的建筑布局，不具备收治呼吸道传染病病人的能力。</p>\n<p>\n	　　2020年，蔡秀军曾到上海、江苏、浙江多地调研疫情防控和复工复产工作。在调研中他发现，绝大多数地区建立了传染病大楼或公共卫生临床中心，这些建筑在疫情中发挥着定点收治重症病人的重要作用，但步入常态化防控阶段，大楼往往空置，运维成本却非常高。</p>\n<p>\n	　　针对“战时床位供不应求、平时资源无辜浪费”的尴尬局面，蔡秀军一直在思考，必须抓紧补短板、堵漏洞、强弱项。构建平常用得着、应急也能上的医疗资源和人才，打造“平疫结合”病房的想法，从那时萌发。</p>\n<p>\n	　　当时，浙大邵逸夫医院庆春院区正在筹建五期大楼，蔡秀军便决定在新大楼里“做文章”。他瞄准了病房里最常见的“门”和看不见的“气压”：在500张床位的原设计基础上，通过加装“隔离门”和“正负压装置”，调整功能布局，实现普通病房与呼吸系统传染病房的快速切换、无缝衔接。</p>\n<p>\n	　　一旦“战疫”打响，关闭隔离门、启动正负压装置，普通病房就能达到“三区两通道”的要求，快速切换成呼吸系统传染病房，相关病区马上进入隔离状态；没有重大疫情时，这些病房仍然各司其职，供日常医疗使用。</p>\n<p>\n	　　2022年8月，全国首个真正意义上的“平疫结合”快速切换病房正式投入使用。</p>\n<p>\n	　　按钮按下，气压梯度快速切换，关闭隔离门，缓冲区压力表上显示“-10Pa”。“我们启动了病房‘平疫结合’快速切换模式，普通病房已快速切换为符合‘三区两通道’的传染病房。目前，缓冲区内已实现负压状态，‘污染区’病房内同步启动独立排风装置后也实现微负压状态。负压状态区域内的‘污染’空气经过滤装置过滤后排到大气当中。”每逢同行来参观，蔡秀军都会尽量抽出时间亲自陪同，演示并介绍如何从普通病房“一键”切换为传染病房。</p>\n<p>\n	　　通过医院的创新实践经验，蔡秀军提炼总结出参政建言的要点。在全国政协双周协商座谈会上，其开创的“平疫结合”快速切换病房模式得到全国政协委员们的高度认可，也与国家卫生健康委发布的《综合医院“平疫结合”可转换病区建筑技术导则（试行）》中指出的“新建‘平疫结合’区应当从总体规划、建筑设计、机电系统配置上做到‘平疫结合’”理念相契合。</p>\n<p>\n	　　长期从事医疗管理工作，蔡秀军对医疗民生领域及改革发展方面带有普遍性的热点难点问题格外关注。今年，他的提案依然关注国家区域医疗中心建设、医疗资源均衡分布、提高生育率等问题。“政协委员不仅是荣誉，更是担当，我们责无旁贷，必须多献发展之策、多办务实之事、多为利民之举。”蔡秀军说。</p>\n<p>\n	　　记者手记</p>\n<p>\n	　　作为医疗从业者，蔡秀军始终心系民生，为医疗改革建言献策。</p>\n<p>\n	　　身处一线，他深知制约医疗行业发展的痛点、堵点所在，也能真实体会患者的疾苦。过去5年，他先后提交了17条推动行业高质量发展的建议。创新、惠民是其提案的关键词。比如他呼吁各大医院打破信息孤岛，加强互通。为解决看病难、看病贵问题，他提出均衡分配优质医疗资源，并呼吁全国医院一起努力简化看病流程。</p>\n<p>\n	　　蔡秀军总说：“要真正为百姓做事情，就要学会换位思考。”正因此，与传统传染病房集中布局缺少阳光照射不同，他开创的“平疫结合”病房创新性将病房设置于建筑采光好的一面，把阳光还给患者。</p>\n<p>\n	　　全国政协委员要高站位、多思考、干实事。这是他履职的理念和心得。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626927.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (858,'2023-02-20 04:52:17.317906','2023-02-20 04:52:17.317906','用创新技术揪出毒品“伪装者”','<div class=\"artDet\">\n                        \n                        <p>\n	　　◎本报记者 金 凤 通 讯 员 姜 晨 周天健</p>\n<p>\n	　　“当前，第一代、第二代毒品正逐步转向以新精神活性物质为代表的第三代毒品，需要为一线禁毒部门提供创新实战技术和高水平人才。”2月16日，在全国禁毒技术研讨会中，中国工程院院士、中国药科大学教授王广基将毒品问题面临的新形势和盘托出。</p>\n<p>\n	　　如何推进禁毒科技创新，成为此次会议的焦点。国家禁毒办副主任、公安部禁毒情报技术中心总工程师王优美在大会报告中指出，整类列管已被实践证明是最为有效的管制手段，针对有限目标物的检测方法已不能满足实战，覆盖整类物质的非靶向高通量筛查方法将成为发展方向。</p>\n<p>\n	　　科研创新填补了国内禁毒技术应用空白</p>\n<p>\n	　　近年来，我国的禁毒科研工作推动了毒品检验鉴定、污水监测、毛发验毒、毒品成瘾性和危害性评估等技术的发展应用，有力填补了国内禁毒技术应用空白。加强禁毒科学技术研究，提升禁毒科技水平，成为推动全国禁毒工作深入开展的重要支撑。</p>\n<p>\n	　　王优美介绍，公安部禁毒情报技术中心毒品实验室目前已累计筛查全国公安和海关报送的可疑缴获样品近1万份、核磁共振波谱图130万余份，共发现各类新精神活性物质340余种，兴奋剂50余种，其中30余种物质为世界范围内首次发现和报道，已开发覆盖全国6430家污水处理厂的污水监测毒品滥用数据平台。</p>\n<p>\n	　　在国家禁毒委员会办公室—中国药科大学禁毒关键技术联合实验室，科研人员已经构建了基于城市污水分析的毒情评估技术体系、毒品原植物定性检验鉴定及溯源技术体系、新精神活性物质快速预警筛查新方法。</p>\n<p>\n	　　中国药科大学校长郝海平认为，毒品与药品是紧密相连的，药品一旦出现滥用、危害身体健康并被国家管制就成为了毒品。目前，我国已列管449种麻醉品、精神物质，是世界上列管毒品最多、管制最严的国家。</p>\n<p>\n	　　亟须建立针对新型毒品的检验鉴定标准</p>\n<p>\n	　　当前，新的制毒化学品和制毒工艺仍不断涌现。“国内大规模非法制造冰毒、氯胺酮等毒品的情况基本消失，但多地仍存小规模制毒活动，特别是部分地下加工厂不断研究创造新的合成工艺，使用新的非列管前体化学品，企图规避法律的监管和打击。”王优美接受科技日报记者采访时表示，传统的毒品是从植物中提取而来，但随着化学合成技术的提高，现在出现了很多新型毒品或者新精神活性物质。</p>\n<p>\n	　　“制毒人员很狡猾，他们知道哪些是列管的，有时改变一个分子结构，就能合成出与列管物质不同但功能相近的毒品。我们也经常为各地检察机关、公安机关提供合成工艺鉴定，判断制毒人员设计的合成工艺能不能制出毒品。”王优美介绍，2021年7月1日，我国正式列管合成大麻素类物质和氟胺酮等18种新精神活性物质以来，毒品实验室的监测已新发现20余种非列管新精神活性物质，特别是近期，还查获了大量从国外走私入境伪装成巧克力或邮票的新精神活性物质。</p>\n<p>\n	　　他认为，毒品和制毒化学品更新换代迅速，针对新结构物质的鉴定需求不断增加，亟须建立不依赖于标准品的检验鉴定标准体系。</p>\n<p>\n	　　那么，对于列管目录之外的新型毒品，将如何监测？王优美表示，如果发现某个非列管的化学品或者新精神活性物质在某区域甚至全国增速明显，就要考虑进行列管，以及列管后如何用技术手段监测，如何按照现有毒品的危害性和成瘾性进行折算，为司法提供量刑依据。</p>\n<p>\n	　　科研人员也在尝试利用人工智能的方式抓出“伪装者”。“国家禁毒委员会办公室—中国药科大学”禁毒关键技术联合实验室专家苏梦翔教授表示：“一些制毒人员会在新型毒品的母核上，不停修饰它的外周结构，我们正试图通过人工智能算法，提取母核结构的特征，建立数学模型，一旦发现可疑的数据，就立即给予警示。”</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626929.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (859,'2023-02-20 04:52:17.324903','2023-02-20 04:52:17.324903','科普：睡眠不足影响学生学习能力','<div class=\"artDet\">\n                        \n                        <p>\n	       新华社北京2月19日电（记者李雯）美国《国家科学院学报》日前刊载的一项最新研究显示，大学生如果每晚睡眠不足6小时，会影响学习能力。</p>\n<p>\n	　　此前研究显示，睡眠是健康和绩效的重要预测因素。动物研究表明，白天形成的记忆在睡眠中得到巩固，当正常的睡眠模式被中断时，白天学习的内容就会忘记。专家建议青少年每晚睡8至10小时。但许多大学生睡眠不规律，睡眠不足。</p>\n<p>\n	　　美国卡内基-梅隆大学等机构的研究人员对3所大学的600多名大一学生开展了研究。学生们每晚佩戴睡眠追踪器，用以监测和记录睡眠情况。研究人员收集了这些学生大一期末的平均成绩，以评估睡眠时间对学生成绩的影响。</p>\n<p>\n	　　研究人员发现，参与研究的这些学生平均每晚睡6个半小时。每晚睡眠不足6小时的学生学习成绩下降明显。睡眠每增加1小时，期末平均成绩会略有提高。</p>\n<p>\n	　　研究人员指出，睡眠对学习和记忆很重要。睡眠不足可能会影响学生在大学课堂上的学习能力。长期睡眠不足6小时，人体会积累大量的睡眠债务，从而损害健康。研究人员建议大学生要重视夜间睡眠。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626931.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (860,'2023-02-20 04:52:17.329916','2023-02-20 04:52:17.329916','人民时评：让老字号不断焕发新生机','<div class=\"artDet\">\n                        \n                        <p>\n	　　老字号具有很高的经济、文化价值，是弥足珍贵的自主品牌。前不久，商务部等5部门联合印发了《中华老字号示范创建管理办法》，释放出推动老字号创新发展、促进品牌消费的积极信号。</p>\n<p>\n	　　消费是最终需求，也是经济增长的重要拉动力。当前，老字号广泛分布在食品加工、餐饮住宿、居民服务等多个领域，全国老字号年营业收入超过2万亿元。今年春节假期，老字号餐饮外卖消费同比增长13.5%，拉动商务部重点监测零售企业销售额同比增长6.8%。可以说，擦亮老字号这块“金字招牌”，不仅对增强消费能力、改善消费条件、创新消费场景等具有重要带动作用，而且有助于把扩大消费同改善人民生活品质有机结合起来。《扩大内需战略规划纲要（2022—2035年）》明确提出：“打造中国品牌，培育和发展中华老字号和特色传统文化品牌。”在此基础上，《办法》对示范创建的总体要求、基本条件、申报认定、动态管理等作出了明确规定和要求，为推动老字号实现高质量发展提供了重要的政策指引。</p>\n<p>\n	　　抓创新就是抓发展，谋创新就是谋未来。老字号的优势在于“老”，出路在于守正创新。顺应消费升级趋势，加快创新发展，不断提升老字号经营效率、管理水平，才能更好满足人民群众对美好生活的需要。如今，越来越多的老字号，借助新平台、新技术焕发新活力。有电商平台数据显示，春节期间关键词“老年货”搜索量上升120%。为更好实现规模发展，一些老字号企业引入外部力量，完善现代企业制度，利用多层次资本市场做大做强。定准群众认同、市场认可的调子，老字号们创新讲好品牌故事、搏击市场蓝海，开辟的是新领域新赛道，塑造的则是发展新动能新优势。</p>\n<p>\n	　　质量强则品牌强、企业强。老字号的产品质量，很大程度上决定了品牌竞争力。在东北制药102分厂小容量制剂生产线A上，所有药品都要经历“拍照”及红外检测、高压放电检漏等流程；上海黄浦区融合计量、认证认可、检验检测、质量管理、品牌、商标、知识产权等要素资源，为老凤祥等老字号企业提供质量基础设施“一站式”服务。各地实践表明，促进老字号品牌的持续成长，提升产品和服务质量是关键。《办法》出台后，有关部门表示将大力推动老字号企业建设全员、全要素、全过程、全数据的新型质量管理体系，引导老字号企业积极开展质量提升行动。着力突出标准引领，加强全面质量管理，强化质量监管执法，我们才能以质量跃升促进品牌持续发展，打造更多中国精品和“百年老店”。</p>\n<p>\n	　　老字号承载着丰富的生活和文化记忆，创建不易，维护更难。在市场竞争、政府监管、全社会共同监督中，相关经营主体要努力创新，发掘品牌价值，拓展更大市场，打造更加硬核的质量品牌，确保老字号“金字招牌”不褪色。将《办法》落实、落细，引导、支持老字号企业顺应市场机制、守正创新经营，我们一定能推动更多老字号走进千家万户、走进百姓生活，让老字号持续焕发新生机、涌现新活力。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日 05 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626903.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (861,'2023-02-20 04:52:17.334903','2023-02-20 04:52:17.334903','最新研究：光控药物有望精确治疗癌症','<div class=\"artDet\">\n                        \n                        <p>\n	　　西班牙科学家开发出一系列光敏分子药物，这些药物可被外部光激活，从而实现更局部化和可控的生物效应。最新研究表明，光药理学（受光控制的药物）为高度特异性治疗铺平了道路，未来有望精准治疗癌症等疾病。相关研究刊发于最近的《药物化学杂志》。</p>\n<p>\n	　　抗癌药物的一个局限性是，它们往往无法完全区分癌细胞和健康细胞，这不仅会降低治疗的有效性，也会导致副作用。</p>\n<p>\n	　　研究人员解释道，光敏药物能解决上述问题，因为其活性可借助外部光以可逆方式精确控制，这使它们能在所需时间内有效控制作用部位，从而减少副作用，提高疗效。但迄今这类药物在视网膜或疼痛方面的应用尚处于实验阶段，在肿瘤学方面取得良好结果的研究并不多。</p>\n<p>\n	　　最新研究聚焦于肿瘤学领域的一个共同靶点：组蛋白去乙酰化酶（HDAC）。当这些酶失调时，它们会阻止某些基因（如肿瘤抑制因子）表达，使细胞更有可能成为癌症细胞。鉴于此，科学家开发出了多种旨在抑制HDAC失调的药物，但其中一些具有毒副作用。</p>\n<p>\n	　　研究基于这些常规药物分子，但可通过改变光照条件来控制其活性。当这些分子被光激活时，它们能抑制HDAC。以前这些分子需要紫外光才能被激活，这可能对细胞有害且药物在生物组织中的渗透性很低。研究人员采用绿光来激活这些分子，这些分子的活性在宫颈癌、乳腺癌、白血病和结肠癌这4种癌症细胞系中得到了验证。研究结果表明，绿光照射后，癌细胞凋亡率增加。</p>\n<p>\n	　　研究团队据此开发出了一个小型光敏分子药物库，仅通过可见光照射就可控制这些药物的细胞活力，可见光更具渗透性，对细胞的损伤更小，未来有望在人体、斑马鱼或小鼠体内开展可行性研究，证明利用光药理学能更有效、更安全地治疗癌症。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626930.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (862,'2023-02-20 04:52:17.340901','2023-02-20 04:52:17.340901','睡眠影响学习能力','<div class=\"artDet\">\n                        \n                        <p>\n	       新华社北京2月19日电（记者李雯）美国《国家科学院学报》日前刊载的一项最新研究显示，大学生如果每晚睡眠不足6小时，会影响学习能力。</p>\n<p>\n	　　此前研究显示，睡眠是健康和绩效的重要预测因素。动物研究表明，白天形成的记忆在睡眠中得到巩固，当正常的睡眠模式被中断时，白天学习的内容就会忘记。专家建议青少年每晚睡8至10小时。但许多大学生睡眠不规律，睡眠不足。</p>\n<p>\n	　　美国卡内基-梅隆大学等机构的研究人员对3所大学的600多名大一学生开展了研究。学生们每晚佩戴睡眠追踪器，用以监测和记录睡眠情况。研究人员收集了这些学生大一期末的平均成绩，以评估睡眠时间对学生成绩的影响。</p>\n<p>\n	　　研究人员发现，参与研究的这些学生平均每晚睡6个半小时。每晚睡眠不足6小时的学生学习成绩下降明显。睡眠每增加1小时，期末平均成绩会略有提高。</p>\n<p>\n	　　研究人员指出，睡眠对学习和记忆很重要。睡眠不足可能会影响学生在大学课堂上的学习能力。长期睡眠不足6小时，人体会积累大量的睡眠债务，从而损害健康。研究人员建议大学生要重视夜间睡眠。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626931.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (863,'2023-02-20 04:52:17.350901','2023-02-20 04:52:17.350901','火车脱轨泄漏的氯乙烯到底有多“毒…','<div class=\"artDet\">\n                        \n                        <p>\n	　　◎实习记者 李诏宇</p>\n<p>\n	　　近期，美国俄亥俄州列车脱轨导致的毒气泄漏事件在网络上持续发酵。</p>\n<p>\n	　　此次火车脱轨事故中泄漏的氯乙烯究竟是什么？氯乙烯对人体健康和周围环境可能造成哪些危害？应该如何科学、安全地处置氯乙烯等危险化学品？科技日报记者就此采访了相关人士。</p>\n<p>\n	　　对人体与环境均有严重危害</p>\n<p>\n	　　“氯乙烯，也叫乙烯基氯，其化学式为C_2H_3Cl。”西安科技大学地质与环境学院教授修福荣介绍，“常温常压下，氯乙烯为无色有气味的气体，其密度小于水但大于空气，加压冷凝处理后可变为液体状态。”</p>\n<p>\n	　　此次火车脱轨事故中泄漏的氯乙烯并非气体状态，而是液体状态。这可能与加压冷凝处理后，液体状态的氯乙烯密度更大，每个车厢可以运输更多的氯乙烯有关。“氯乙烯具有较强的易燃易爆性。”修福荣指出。氯乙烯与空气混合时，其爆炸极限为4%—21%，在加压处理后则更易爆炸。</p>\n<p>\n	　　危险化学品，指具有毒害、腐蚀、爆炸、燃烧、助燃等性质，对人体、设施、环境具有危害的剧毒化学品和其他化学品。“氯乙烯就是一种典型的危险化学品，如果大量泄漏，对人体健康与周围环境均会造成严重的危害。”修福荣指出。</p>\n<p>\n	　　如果吸入或大量接触皮肤，氯乙烯会对人体产生十分严重的致癌和中毒作用。“氯乙烯属于1类致癌物，主要对神经系统、肝脏、消化系统及皮肤产生毒害或损伤作用。” 修福荣介绍。</p>\n<p>\n	　　氯乙烯的中毒主要可以分为急性中毒、慢性中毒和皮肤接触中毒3种。急性中毒时，轻度中毒者往往表现为眩晕、胸闷、嗜睡等。而严重中毒则表现为昏迷、抽搐、甚至死亡。慢性中毒时，中毒者会出现神经衰弱、肝功能损伤、消化功能损害等症状。皮肤接触中毒时，往往会造成中毒者红斑、湿疹、水肿甚至肢体坏死。</p>\n<p>\n	　　“氯乙烯大量泄漏到空气中，可能对周围环境造成难以逆转的损害。”修福荣指出。氯乙烯因为密度比空气大，往往会在低处扩散，其影响范围较大；氯乙烯容易与空气混合形成易燃易爆的混合物，遇热源或明火极易发生爆炸；弥散至大气中的氯乙烯会参与光化学烟雾的反应和形成，为大气污染推波助澜；此外，由于其严重的毒性，氯乙烯还会对生态环境造成严重损害，造成动植物大量死亡，进而影响生物多样性。</p>\n<p>\n	　　科学处置避免可能风险</p>\n<p>\n	　　此次发生于美国的危险化学品泄漏事故，给我国的危险化学品处置敲响了警钟。我们应该如何从中吸取教训，科学、安全地处置氯乙烯等危险化学品，避免类似的事故在我国发生呢？</p>\n<p>\n	　　“总的来说，我们要遵循我国的《危险化学品安全管理条例》，对各种危险化学品进行处置。”修福荣说，“具体来说，在生产操作环节、储存运输环节、废弃处置环节上要遵守相关安全规范，最大程度避免危险化学品造成的可能风险。”</p>\n<p>\n	　　在生产操作环节，要坚持密闭操作，做好安全通风。操作人员要佩戴防毒面具、安全防护眼镜，穿戴防化服和手套；操作场所要远离火种和热源，安装防爆、通风系统和设备。</p>\n<p>\n	　　在储存运输环节，储存时，要将各种危险化学品储存于阴凉、通风的库房；运输时，要按照危险化学品运输的相关规定进行配装和运输，远离火种和热源，与氧化剂分开，防止日光暴晒。</p>\n<p>\n	　　在废弃处置环节，要根据不同的危险化学品特点，进行对应的科学处置。“氯乙烯废弃后，一般用焚烧法处置。”修福荣表示。</p>\n<p>\n	　　需要特别指出的是，在对氯乙烯等含氯塑料的焚烧过程中，如果焚烧温度低于800℃，就会造成不完全燃烧，极易生成氯苯——剧毒物质二噁英合成的前体。“因此，我们要在专业的危险废物焚烧机构使用专业焚烧炉处置氯乙烯等含氯物质，并严格处理焚烧后产生的尾气。只有达到国家排放标准后，才能将其排放。”修福荣表示。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626926.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (864,'2023-02-20 04:52:17.360853','2023-02-20 04:52:17.360853','光控药物有望精确治疗癌症','<div class=\"artDet\">\n                        \n                        <p>\n	　　西班牙科学家开发出一系列光敏分子药物，这些药物可被外部光激活，从而实现更局部化和可控的生物效应。最新研究表明，光药理学（受光控制的药物）为高度特异性治疗铺平了道路，未来有望精准治疗癌症等疾病。相关研究刊发于最近的《药物化学杂志》。</p>\n<p>\n	　　抗癌药物的一个局限性是，它们往往无法完全区分癌细胞和健康细胞，这不仅会降低治疗的有效性，也会导致副作用。</p>\n<p>\n	　　研究人员解释道，光敏药物能解决上述问题，因为其活性可借助外部光以可逆方式精确控制，这使它们能在所需时间内有效控制作用部位，从而减少副作用，提高疗效。但迄今这类药物在视网膜或疼痛方面的应用尚处于实验阶段，在肿瘤学方面取得良好结果的研究并不多。</p>\n<p>\n	　　最新研究聚焦于肿瘤学领域的一个共同靶点：组蛋白去乙酰化酶（HDAC）。当这些酶失调时，它们会阻止某些基因（如肿瘤抑制因子）表达，使细胞更有可能成为癌症细胞。鉴于此，科学家开发出了多种旨在抑制HDAC失调的药物，但其中一些具有毒副作用。</p>\n<p>\n	　　研究基于这些常规药物分子，但可通过改变光照条件来控制其活性。当这些分子被光激活时，它们能抑制HDAC。以前这些分子需要紫外光才能被激活，这可能对细胞有害且药物在生物组织中的渗透性很低。研究人员采用绿光来激活这些分子，这些分子的活性在宫颈癌、乳腺癌、白血病和结肠癌这4种癌症细胞系中得到了验证。研究结果表明，绿光照射后，癌细胞凋亡率增加。</p>\n<p>\n	　　研究团队据此开发出了一个小型光敏分子药物库，仅通过可见光照射就可控制这些药物的细胞活力，可见光更具渗透性，对细胞的损伤更小，未来有望在人体、斑马鱼或小鼠体内开展可行性研究，证明利用光药理学能更有效、更安全地治疗癌症。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626930.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (865,'2023-02-20 04:52:17.375856','2023-02-20 04:52:17.375856','全国政协委员卢伟英：用心做好为患…','<div class=\"artDet\">\n                        \n                        <p>\n	　　  日前，在海南省妇女儿童医学中心生殖医学中心的候诊大厅里，满是前来就诊的患者。首席专家卢伟英的诊室尤为忙碌，门口排满了人。</p>\n<p>\n	　　 “环境污染、生活方式不良等原因，使这些年不孕不育的发病率越来越高了。作为长期从事这个领域的医生，我格外能理解病人们的痛苦，问得比较细，所以看诊时间经常比病人预计的要久一些。”卢伟英接诊完当天上午最后一个患者，已是下午两点。</p>\n<p>\n	　　 成为妇产科和生殖医学科医生30多年来，卢伟英帮助无数患者圆了“父母梦”，被大家亲切地称为“卢妈妈”。当选为全国政协委员后，从“首席专家”到“新任委员”，卢伟英认真履职，建言献策，做足准备。</p>\n<p>\n	　　 为了从源头上减少不孕症发病率，卢伟英从2018年开始，带领团队，走进中学和高职学校，为步入青春期的孩子们开设生殖健康科普公益讲座，介绍相关领域知识，引导孩子们建立正确的婚恋观与人生观。她和团队开展校园科普的足迹目前覆盖了海口、三亚、屯昌、五指山、陵水等省内多个市县。除了进校园开展讲座，卢伟英还大力发展网络科普视频，相关作品还曾获得2021年中国医师协会生殖医学分会的全国科普视频大赛二等奖。</p>\n<p>\n	　　 卢伟英建议要进一步建立和完善青少年生殖健康宣传体系，引导青少年树立健康积极的婚恋观，提倡适龄婚育、优生优育、性别平等、责任共担的家庭观。举办生殖健康教育师资培训班，在试点学校成立生殖健康科普教育专班，由专班针对不同年龄青少年特点及需求制定授课计划，定期进行生殖健康科普知识竞赛，总结教学成果并推广。</p>\n<p>\n	　　 卢伟英表示，我国目前育龄人口大多是独生子女一代，普遍晚婚晚育。随着育龄人口年龄增大，相关人群可能会面临更高的生育难度和不孕不育风险。因此，她认为应在提供更大范围生殖辅助技术服务方面，有更多作为。</p>\n<p>\n	　　 “希望未来，生殖健康课程能够大大方方地走进各学校，同时通过国家、社会等各方面的共同努力，人们的生殖健康得到进一步保护，从而帮助更多家庭。”卢伟英说。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626915.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (866,'2023-02-20 04:52:17.388851','2023-02-20 04:52:17.388851','质量过关网购才能红火','<div class=\"artDet\">\n                        \n                        <table class=\"pci_c\" width=\"400\">\n	<tbody>\n		<tr>\n			<td align=\"center\">\n				<img src=\"http://paper.people.com.cn/rmrbhwb/images/1/20230220/1676834033168_1.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n				<p>\n					　　在陕西省西安市灞桥区一小区内，外卖员将商品递给顾客。<br />\n					　　邹竞一摄（新华社发）</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	　　方便快捷、价格优惠、种类丰富……以网络购物、直播带货、在线外卖等为代表的数字消费新业态、新模式，成为社会大众特别是年轻人喜爱的消费方式。如何进一步规范网络购物秩序，提高网售商品及服务质量，成为许多消费者关注的话题。中共中央、国务院日前印发《质量强国建设纲要》，提出“促进网络购物、移动支付等新模式规范有序发展”“强化网络平台销售商品质量监管，健全跨地区跨行业监管协调联动机制，推进线上线下一体化监管”。专家认为，《质量强国建设纲要》的印发，意味着中国正实施消费品质量提升行动，促进定制、体验、智能、时尚等新型消费提质扩容，满足多样化、多层级消费需求。</p>\n<p>\n	　　</p>\n<p>\n	　　“中国制造”销海外</p>\n<p>\n	　　前几年，有中国游客到国外旅游时会顺便购买电饭煲。如今，同类商品国产品牌的质量已大大提升。中国的家电企业纷纷推出电饭煲的高端产品远销海外。小小电饭煲的变化，是中国制造提质升级的一个缩影，折射出建设质量强国的铿锵步伐。</p>\n<p>\n	　　以“新国货”为代表的消费品牌不断涌现。抖音电商日前发布的《2022抖音电商国货发展年度报告》显示，2022年抖音电商上的国货品牌销量同比增长110%，其中老字号品牌销量同比增长156%，新锐品牌销量同比增长84%。</p>\n<p>\n	　　专家认为，包含新国货在内的各类品牌，已经成为中国消费市场提质升级的重要引擎。随着消费者物质需求从“有没有”转向“好不好”，高品质、高附加值、高技术的商品需求明显增多。</p>\n<p>\n	　　在互联网平台助推产品质量升级的同时，还有一些行业规范方面的问题需要解决。例如，有网友通过某电商直播平台购物，在收货时遇到产品“以次充好”的骗局，想要维权却遭到商家的推诿。</p>\n<p>\n	　　专家认为，在平台企业提供服务与产品过程中，相关问题在一定程度上损害了消费者权益。推动平台经济规范健康持续发展，需要健全完善规则制度，及时弥补规则空白和漏洞，并提升监管能力和水平，实现全链条监管。</p>\n<p>\n	　　2022年底，国家市场监管总局等部门联合发布《进一步提高产品、工程和服务质量行动方案（2022—2025年）》，提出督促平台企业强化平台销售和直播带货产品的质量管控和追溯，依法承担商品和服务质量保证、食品安全保障等责任，切实维护用户个人信息权益及隐私权。专家认为，这将有利于提升平台企业的发展质量，进一步激发数字经济的内生活力。</p>\n<p>\n	　　无忧售后更安心</p>\n<p>\n	　　网络订餐日趋红火，与此相伴而生的问题也开始显现。当消费者在权益遭受损害时，时常会因为难以确定外卖平台是否应对此承担责任而陷入迷茫。</p>\n<p>\n	　　对此，最高人民法院发布的《最高人民法院关于审理食品安全民事纠纷案件适用法律若干问题的解释（一）》明确规定电商平台应承担责任，把好网购食品安全关；“黑作坊”食品的生产者和经营者都要承担惩罚性赔偿责任。</p>\n<p>\n	　　有专家认为，实行网购食品电商平台承担赔偿责任制度意义重大，有利于加强网络订餐食品安全监管，不让网络订餐平台成为“黑暗料理”的避风港，让消费者大胆消费、放心消费，促进网购市场健康有序发展。</p>\n<p>\n	　　对于外卖平台而言，加强自身合法合规经营也是应有之义。为扎实推进食品安全放心工程建设，稳步提升餐饮业质量安全水平，外卖平台升级原有食品安全保障措施，整合放心商家、安心食材、明厨亮灶、密封配送、准时送达、无忧售后等多项标准形成服务标准体系，旨在为消费者营造更加放心安全的消费环境，进而提振消费信心，助力餐饮行业全面复苏。</p>\n<p>\n	　　在一家外卖平台负责客户服务的刘伟正认为，商户使用的食材应从商场、超市、电商平台、农贸市场等正规渠道购买，并保留至少30天的发票、收据、进货清单等购买凭证，其中，肉类还应索取检验检疫合格证明，确保食品来源可追溯。食材质量应符合国家标准或行业标准，进口食材应具备出入境检验检疫证书。</p>\n<p>\n	　　常态监测不可少</p>\n<p>\n	　　强化网络平台销售商品质量监管，中国一直在行动。国家网信办、国家税务总局、国家市场监督管理总局2022年3月印发《关于进一步规范网络直播营利行为促进行业健康发展的意见》，提出加强网络直播销售产品质量安全监管，网络直播发布者、网络直播服务机构严禁利用网络直播平台销售假冒伪劣产品；不得在知道或者应当知道直播带货委托方或其他第三方存在违法违规或高风险行为的情况下，仍为其推广、引流；不得通过造谣、虚假营销宣传、自我打赏等方式吸引流量、炒作热度，诱导消费者打赏和购买商品。</p>\n<p>\n	　　各地政府也在持续响应。广东省市场监管局曾约谈十余家电商平台，并在督促各电商平台开展网售产品质量安全自查整治的基础上，发布实施《网络交易平台产品质量信息展示管控规范》团体标准。该标准解决网售产品普遍存在的厂名厂址、产品标准、商标、生产许可等基础性产品信息展示不规范不健全等问题，指引电商平台经营者更好履行产品质量安全监督和保护消费者权益职责，更好促进网络交易持续健康有序发展，具有较强的科学性和可操作性。</p>\n<p>\n	　　此外，在甘肃省定西市，安定区市场监督管理局统筹协调网络市场监管工作，强化常态化数据检测，坚持网监专员每日进行网上监测，重点加强对辖区内网站、网店、微店、直播带货等网络交易与广告宣传行为的监测、监管。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626905.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (867,'2023-02-20 04:52:17.409856','2023-02-20 04:52:17.409856','中国医疗援助造福非洲民众','<div class=\"artDet\">\n                        \n                        <table class=\"pci_c\" width=\"400\">\n	<tbody>\n		<tr>\n			<td align=\"center\">\n				<img src=\"http://paper.people.com.cn/rmrbhwb/images/2023-02/20/10/rmrbhwb2023022010p25_b.jpg\" /></td>\n		</tr>\n		<tr>\n			<td>\n				<p>\n					　　2022年11月30日，中国第25批援多哥医疗队向多哥佩莱省阿代塔镇卫生所捐赠医疗物资。<br />\n					　　新华社记者 田 耘摄</p>\n			</td>\n		</tr>\n	</tbody>\n</table>\n<p>\n	　　乌干达《新景报》网站近日刊登文章，题为《心连心的合作：中国医疗队的贡献挽救生命》。文章称，过去60多年里，中乌关系乃至中非关系愈发紧密，中国对乌干达乃至整个非洲的贡献有目共睹，其中就包括医疗卫生领域的合作。位于乌干达首都坎帕拉的中乌友好医院，就是两国医疗合作的“代言人”。这所医院由中国援建并赠送给乌干达，成千上万乌干达人从中国在该医院提供的服务中受益。自1983年以来，中国每年都支持乌干达医疗部门，派遣医疗专家团队到乌干达工作，与同行分享经验，同时向民众提供服务。在新冠疫情最严重时，一些国家奉行疫苗民族主义，中国则展现出大国担当，与包括非洲国家在内的发展中国家并肩合作，捐赠疫苗、开展联合研发等。</p>\n<p>\n	　　文章称，有句谚语说：“患难见真情。”它是对中乌两国关系的最佳描述，描绘了双方互惠互利和尽显兄弟情谊的合作。北京与非洲各国的合作关系，植根于具体而不是冠冕堂皇的空话，是构建人类命运共同体这一愿景的证明。</p>\n<p>\n	　　</p>\n<p>\n	　　今年是中国援外医疗队派遣60周年。1963年，中国政府应邀向非洲国家阿尔及利亚派出第一支援外医疗队，开启了援外医疗的历史。对非医疗援助，一直是中国援外医疗的重要内容。</p>\n<p>\n	　　作为世界上发展中国家最集中的大陆，非洲在公共卫生领域存在不少短板，一是医院、医疗设备等基础设施不足；二是专业医疗人员缺乏，而且本土医疗人员易流失；三是投入资金不足，医院、防疫及公共卫生系统、医疗人员等难以形成良性循环；四是医疗培训机构相对缺乏。</p>\n<p>\n	　　中国对非医疗援助涵盖多方面内容：</p>\n<p>\n	　　一是定期派遣援非医疗队。统计数据显示，中国已经向40多个非洲国家累计派出医疗队员约2.3万人次，诊治患者约2.3亿人次。中国许多省市都有对口的援非医疗队，形成了中非卫生合作的优良传统。</p>\n<p>\n	　　二是开展中短期援非医疗项目。例如：中国海军“和平方舟”医院船曾开展环非洲访问、提供人道主义医疗服务；多次开展“光明行”援非医疗服务，为非洲白内障患者送去光明等。</p>\n<p>\n	　　三是帮助非洲国家应对各种传染病和重大公共卫生危机。2014年西非埃博拉疫情期间，中国第一时间逆行驰援，共派出1000多人次公共卫生专家和医护人员为非洲人民提供医疗保障，并先后提供多轮紧急援助。2016年以来，中国专家赴非洲多国帮助当地防控疟疾、血吸虫病、艾滋病、结核病、鼠疫与黄热病等，有效遏制了各类疫情蔓延。2020年新冠疫情发生以来，中非守望相助、相互支持，树立了合作抗疫的典范。中方第一时间向非盟和非洲国家援助抗疫物资和疫苗，援建的非洲疾控中心总部项目提前开工。</p>\n<p>\n	　　四是开展多种形式医疗培训。中国一直秉持“授人以鱼不如授人以渔”理念，注重培养非洲本土医疗卫生人才。尤其是疫情发生后，线上线下对非医疗培训活动蓬勃开展，提升了非洲医务人员的技术水平及医疗卫生机构的管理水平。</p>\n<p>\n	　　五是帮助非洲加强医疗基础设施建设。目前，中国援建的非洲疾控中心总部项目（一期）已经竣工，成为中非合作的又一标志性项目。2000年以来，中国在非洲援建了130多个医院和诊所，改善了非洲国家公共卫生基础设施条件。</p>\n<p>\n	　　六是为非洲国家提供资金、药品、医疗器械、疫苗等，帮助非洲国家提升公共卫生系统水平，提高应对突发公共卫生事件的能力。多年来，中国复方青蒿素抗疟药品在帮助非洲抗击疟疾的过程中发挥了重要作用。</p>\n<p>\n	　　近年来，中非卫生健康合作在机制建设上迈上新台阶。从中非“十大合作计划”到中非合作“八大行动”，都包含加强中非公共卫生合作的重要倡议。2020年6月，中国国家主席习近平在北京主持中非团结抗疫特别峰会，强调共同打造中非卫生健康共同体和更加紧密的中非命运共同体。“一带一路”框架下的“健康丝绸之路”受到非洲人民欢迎。这些机制的建立，对中非卫生健康合作起到了引领和带动作用，有利于合作的常态化和机制化，也有利于将合作成果真正落到实处，造福非洲各国人民。</p>\n<p>\n	　　生命权和健康权是最基本的人权。“良好的健康与福祉”是联合国2030年可持续发展议程的目标之一。中国对包括非洲在内的广大发展中国家开展医疗援助，展现了致力于维护和保障人权的大国担当，为实现2030联合国可持续发展议程目标作出重要贡献。同时，中国提出人类卫生健康共同体理念、推动构建中非卫生健康共同体的行动，丰富和发展了人类命运共同体理念的内涵，提出了应对人类卫生健康危机的中国主张与中国方案，促进了全球公共卫生治理领域的变革与发展。          </p>\n<p>\n	　　（本报记者  李嘉宝采访整理）</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626906.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (868,'2023-02-20 04:52:17.426852','2023-02-20 04:52:17.426852','让线上就诊更便捷更规范','<div class=\"artDet\">\n                        \n                        <p>\n	　　期待互联网诊疗发挥更大作用，满足群众多样化、多层次的医疗健康服务需求　</p>\n<p>\n	　　“你从外地来一趟太远了，下次复诊可以试试远程视频看病，药品能邮寄到家。”近期在医院门诊采访时，笔者多次听到医生向患者介绍线上就诊方式，减少他们来回奔波产生的路费、时间成本，也常听到一些患者主动询问：“如何网上看病？”</p>\n<p>\n	　　近年来，在线问诊需求持续增长，医疗机构提供的线上问诊、用药指导等服务受到欢迎。新技术用于新业态，互联网诊疗既能提高医疗服务效率，也有效缓解了医疗资源供给压力，满足了群众医疗服务新需求。</p>\n<p>\n	　　互联网诊疗并非线下医疗的简单延伸，对医生、机构的服务内容、安全质量等都提出了更高要求。近年来陆续出台的《互联网诊疗管理办法（试行）》《互联网诊疗监管细则（试行）》等，鼓励医疗机构应用互联网等技术开展服务的同时，也逐渐明确了互联网诊疗在医药、医疗、技术等方面的监管要求，让其更加便捷、规范发展。</p>\n<p>\n	　　以患者为中心，充分考虑患者的互联网就诊需求。“我这个病情可以在线问诊吗？如何挂号？怎么操作？能否使用医保？”在门诊，经常有患者发出这些疑问。充分发挥互联网诊疗便捷服务的作用，要进一步提高服务平台的简便性和精准性，为患者省去寻找环节，满足大多数患者尤其是老年患者的使用需求，逐渐培养患者就医习惯。此外，还要结合供给做文章，明确服务范围和边界，探索从线上咨询、复诊开药延展到覆盖预防科普和诊前、诊中、诊后等一体化服务，促进线上线下融合，为患者带来更加便利高效的在线诊疗。</p>\n<p>\n	　　完善顶层设计，疏通互联网诊疗发展的堵点、难点。继续完善支持互联网诊疗发展的各类配套政策，比如帮助跨区域患者在互联网就诊后，实现医保报销；规范诊疗行为，互联网诊疗的过程及结果应进行完整记录，病历、处方等关键信息的流转应可追溯。需进一步完善相关法律法规，明确功能定位，实现线上线下医保支付同质化，完善线上收费体系及医务人员绩效激励机制等，促进互联网诊疗良性发展。</p>\n<p>\n	　　互联网与医疗的融合发展是大势所趋，期待它能发挥更大作用，满足群众多样化、多层次的医疗健康服务需求。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日 11 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626900.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (869,'2023-02-20 04:52:17.431901','2023-02-20 04:52:17.431901','让老字号不断焕发新生机','<div class=\"artDet\">\n                        \n                        <p>\n	　　老字号具有很高的经济、文化价值，是弥足珍贵的自主品牌。前不久，商务部等5部门联合印发了《中华老字号示范创建管理办法》，释放出推动老字号创新发展、促进品牌消费的积极信号。</p>\n<p>\n	　　消费是最终需求，也是经济增长的重要拉动力。当前，老字号广泛分布在食品加工、餐饮住宿、居民服务等多个领域，全国老字号年营业收入超过2万亿元。今年春节假期，老字号餐饮外卖消费同比增长13.5%，拉动商务部重点监测零售企业销售额同比增长6.8%。可以说，擦亮老字号这块“金字招牌”，不仅对增强消费能力、改善消费条件、创新消费场景等具有重要带动作用，而且有助于把扩大消费同改善人民生活品质有机结合起来。《扩大内需战略规划纲要（2022—2035年）》明确提出：“打造中国品牌，培育和发展中华老字号和特色传统文化品牌。”在此基础上，《办法》对示范创建的总体要求、基本条件、申报认定、动态管理等作出了明确规定和要求，为推动老字号实现高质量发展提供了重要的政策指引。</p>\n<p>\n	　　抓创新就是抓发展，谋创新就是谋未来。老字号的优势在于“老”，出路在于守正创新。顺应消费升级趋势，加快创新发展，不断提升老字号经营效率、管理水平，才能更好满足人民群众对美好生活的需要。如今，越来越多的老字号，借助新平台、新技术焕发新活力。有电商平台数据显示，春节期间关键词“老年货”搜索量上升120%。为更好实现规模发展，一些老字号企业引入外部力量，完善现代企业制度，利用多层次资本市场做大做强。定准群众认同、市场认可的调子，老字号们创新讲好品牌故事、搏击市场蓝海，开辟的是新领域新赛道，塑造的则是发展新动能新优势。</p>\n<p>\n	　　质量强则品牌强、企业强。老字号的产品质量，很大程度上决定了品牌竞争力。在东北制药102分厂小容量制剂生产线A上，所有药品都要经历“拍照”及红外检测、高压放电检漏等流程；上海黄浦区融合计量、认证认可、检验检测、质量管理、品牌、商标、知识产权等要素资源，为老凤祥等老字号企业提供质量基础设施“一站式”服务。各地实践表明，促进老字号品牌的持续成长，提升产品和服务质量是关键。《办法》出台后，有关部门表示将大力推动老字号企业建设全员、全要素、全过程、全数据的新型质量管理体系，引导老字号企业积极开展质量提升行动。着力突出标准引领，加强全面质量管理，强化质量监管执法，我们才能以质量跃升促进品牌持续发展，打造更多中国精品和“百年老店”。</p>\n<p>\n	　　老字号承载着丰富的生活和文化记忆，创建不易，维护更难。在市场竞争、政府监管、全社会共同监督中，相关经营主体要努力创新，发掘品牌价值，拓展更大市场，打造更加硬核的质量品牌，确保老字号“金字招牌”不褪色。将《办法》落实、落细，引导、支持老字号企业顺应市场机制、守正创新经营，我们一定能推动更多老字号走进千家万户、走进百姓生活，让老字号持续焕发新生机、涌现新活力。</p>\n<br />\n<p>\n	<span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日 05 版）</span></p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                        \n                        </div>\n                        ','人民网','http://health.people.com.cn/n1/2023/0220/c14739-32626903.html',6);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (890,'2023-02-20 04:52:17.569902','2023-02-20 04:52:17.569902','美国有毒化学品运载列车脱轨事件发酵','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　近期，美国一列运载有毒化学品的列车在俄亥俄州东部脱轨起火。之后，应急人员对部分有毒化学品进行了“受控释放”操作。事故发生后，方圆数公里内居民出现头疼、恶心等症状，当地鱼类、动物出现死亡现象。连日来，事发地居民对这些有毒化学品的处置及其影响忧心忡忡。</p><p>　　美国政府在此次事件中反应迟钝、处理不力，引发民众和舆论的广泛批评。美国媒体分析认为，一方面，美国铁路行业长期将利润置于安全之上，近年来事故频发，暴露了管理不善、监管缺失等弊病；另一方面，美国政府与铁路公司对于应急事故的处理“令人失望”，民众的健康安全被严重忽视。</p><p>　　“这是一场噩梦，现在仍然如此”</p><p>　　2月3日，在俄亥俄州东部的东巴勒斯坦镇，一辆运载有毒化学品的列车脱轨，造成50节车厢脱轨或损坏，20节运载危险品的车厢中11节脱轨，其中5节罐车载有氯乙烯。氯乙烯是一种具有致癌性的无色气体，其燃烧产生的光气和氯化氢也是有毒气体。此外，事故车厢里还有多种其他危险化学品。</p><p>　　东巴勒斯坦镇的人口约5000。事件发生后，当地大量居民被疏散。2月6日，应急人员以避免大规模爆炸为由，对运载氯乙烯的5节罐车进行“受控释放”，把氯乙烯从罐车内引出后燃烧。有民众拍摄的视频显示，黑色浓烟遮天蔽日，弥漫了一大片区域。两天后，在美国国家环境保护局的保证下，当地官员解除了疏散令，称事故区域及附近居民区空气质量样本中污染物读数低于需要进行安全筛查的水平，居民“可以安全回家”。</p><p>　　然而，上述说法并不能让当地居民安心。据加拿大广播公司报道，当地许多居民“仍然被恐惧、不信任和怀疑折磨”。莫拉·托德和她的丈夫曾短暂回到镇上。她说：“我们在那里待了大约15分钟，就开始觉得喉咙里有一种干涩、发紧的感觉。有点恶心，有点头晕，几乎像晕车的感觉。”第二天出门时，还是同样的感觉。于是，托德离开小镇前往肯塔基州，她说直到家乡真正安全的时候，她才敢回家。</p><p>　　当地居民埃里克·惠特宁在接受媒体采访时表示，这里的空气闻起来像一个“过度氯化的游泳池”。他的眼睛感到灼痛，但他和家人无处可去。周边已经有很多人遭受头疼等后遗症。一些居民已经提起联邦诉讼，要求事故列车所有方诺福克南方铁路公司对当地进行健康监测。当地居民梅丽莎·亨利说：“这是一场噩梦，现在仍然如此。”</p><p>　　另据媒体报道，东巴勒斯坦镇过去几天有人报告动物生病或死亡。俄亥俄州自然资源部门通报，当地一段水域约3500条鱼因化学品泄漏死亡。</p><p>　　“国会需要调查为什么会出现这种情况”</p><p>　　当地居民除了遭遇身体上的不适外，有关部门的应对措施也让他们感到担忧和不满。首先是信息不公开、不透明。目前，美国国家运输安全委员会和美国环境保护局均未提供列车运载物品的完整清单，仅提供了部分“对居民区造成最严重影响的化学品”的清单。其次是监测数据不准确。据《华盛顿邮报》援引有关人士的观点，环境保护局官员测量当地空气数据所使用的手持式检测器虽然方便使用，但是缺乏必要的灵敏度且无法完整评估风险。</p><p>　　曾在美国环境保护局任职的生态战略专家埃瓦耶洛斯·瓦利亚纳托斯表示，当局从车厢中倾倒化学品并露天焚烧是不合适的，人和自然暴露于泄漏化学品和大火会加剧危害。美国研究危险物质的专家希尔·卡贾诺很惊讶政府部门这么快就让居民回家。他认为，在进行彻底清理前，这些化学物质可能仍然残留在屋内或附着在物品上。卡贾诺建议小镇所有人进行体检，警惕这一事件的长期影响。</p><p>　　美国媒体“新闻国家”网站刊文发问：“美国政府在哪里？”文章称，小镇笼罩在黑烟和潜在危险的化学物质中，几千人的健康受到威胁，甚至不得不离开家园。更加令人担心的是，有关方面没有足够认真对待这场环境灾难。当地社区没有从政府那里得到多少实际帮助，而这些事情本应是政府的优先事项。</p><p>　　《纽约时报》评论说，东巴勒斯坦小镇上，焦虑和不信任正在加深。专家警告说，要了解事故的原因和后果，可能需要进行比目前更全面的调查。政府和铁路官员发出的信息令人困惑，而且似乎不断变化，更进一步削弱了民众的信任。</p><p>　　俄亥俄州州长迈克·德温2月16日表示，他当天同白宫进行沟通，以解决东巴勒斯坦镇需要联邦政府给予帮助等问题。德温日前还透露，上述事故列车事发前未被列为“高危险品列车”，所以没有人通知俄亥俄州它将经过。“这很荒唐。”德温说，“国会需要调查为什么会出现这种情况。”</p><p>　　“极端降本逐利商业模式造成的最新恶果之一”</p><p>　　近年来美国列车安全事故屡见不鲜。俄亥俄州有毒化学品泄漏事件发生后不到两周，一辆货运列车2月13日在得克萨斯州与一辆货车相撞，货车司机死亡，列车10余节车厢脱轨。2月16日，又一辆货运火车的3节车厢在密歇根州底特律西南郊脱轨。</p><p>　　《今日美国报》称，美国的火车越来越不安全，像俄亥俄州的火车脱轨事故可能会更加频繁地发生。劳工代表和行业专家对《今日美国报》表示，为了降低成本，铁路公司进行游说，反对成本高昂的监管，并采取增加列车长度、减少检查时间、大幅削减铁路员工等措施，使得美国的火车变得愈发不安全。</p><p>　　美国联邦参议员万斯称，列车老旧的制动系统、维修部件的耐用性，以及美国交通部对铁路系统的监管方法，都存在很多问题。美国“左翼之声”网站文章说，这场灾难本可以避免，诺福克南方铁路公司、美国联邦政府等要为这场灾难负责。“从类似工业灾难的案例来看，东巴勒斯坦镇火车脱轨事件对公共健康和环境的影响可能持续数十年。”</p><p>　　有数据显示，美国铁路从业者人数曾一度高达百万，而目前已降至20万以下，为历史最低水平。美国铁路工人联合会秘书罗恩·卡明考说，近年来，美国铁路行业持续裁员、缺乏监管，把利益置于安全之上。“最近这次脱轨事故不过是冰山一角，是美国极端降本逐利商业模式造成的最新恶果之一，如果不采取措施，情况将变得越来越糟。”</p><p>　　“俄亥俄州列车脱轨事件引发民众对美国政府监管不力的质疑，美国民众对健康的担忧正转变为对政府的信任危机。”《华盛顿邮报》文章说。美国国会两党多名人士都认为，白宫本应对这次事故作出更加迅速的回应。这起事件后，人们普遍对美国政府的危险品管理和铁路行业监管政策感到沮丧。</p><p>　　（本报华盛顿2月19日电）</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日   15 版）</span>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：赵欣悦、岳弘彬)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0220/c1002-32626870.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (891,'2023-02-20 04:52:17.575852','2023-02-20 04:52:17.575852','俄罗斯受损货运飞船坠入太平洋','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>\n	　　新华社符拉迪沃斯托克2月19日电（记者陈畅）俄罗斯国家航天集团19日宣布，因冷却剂泄漏而受损的“进步MS-21”货运飞船与国际空间站脱离后，于19日坠入太平洋。</p>\n<p>\n	　　俄罗斯国家航天集团发布的消息说，“进步MS-21”飞船于莫斯科时间18日5时26分（北京时间18日10时26分）脱离国际空间站，在宇航员远程操控下在轨飞行了一段时间。飞船于19日脱离轨道进入大气层，未燃尽的飞船构件坠入太平洋非航行水域。</p>\n<p>\n	　　俄罗斯国家航天集团11日表示，“进步MS-21”货运飞船出现冷却剂泄漏，但泄漏事故未危及国际空间站驻站宇航员的健康和安全。由于货运飞船冷却剂泄漏的原因尚未查明，俄罗斯国家航天集团13日宣布推迟“联盟MS-23”载人飞船的发射计划。</p>\n<p>\n	　　“进步MS-21”货运飞船去年10月26日从哈萨克斯坦境内的拜科努尔发射场升空，10月28日与国际空间站完成对接。飞船为空间站送去2.5吨货物，包括各类设备、燃料、压缩氮气、水和食品等。按照计划，飞船应在2月18日结束执行国际空间站的飞行任务。</p>\n<p>\n	　　俄罗斯“进步MS”系列货运飞船专门为轨道站提供服务，包括运送各种物资等。该系列货运飞船不可回收，一般完成补给任务后，宇航员会将空间站上产生的各种生活垃圾和废物填入飞船，飞船随后离轨自行销毁。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：贾文婷、刘洁妍)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0220/c1002-32627204.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (892,'2023-02-20 04:52:17.581900','2023-02-20 04:52:17.581900','土耳其官员：震区仅剩两省未完成搜救','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>\n	　　新华社安卡拉2月19日电（记者熊思浩）土耳其灾害与应急管理署署长尤努斯·塞泽尔19日晚说，除了卡赫拉曼马拉什和哈塔伊两省外，震区其他8个省的搜救工作已经结束。下一步工作重点是清除建筑废墟、搭建临时住所和恢复重建基础设施。</p>\n<p>\n	　　塞泽尔在记者会上说，在卡赫拉曼马拉什和哈塔伊两省，搜救人员正夜以继日轮班工作，在倒塌的40栋建筑中搜寻幸存者。他说，6日发生在土耳其南部的强烈地震已造成该国40689人死亡。随着搜救人员从建筑废墟中挖出更多遗体，死亡人数还会进一步增加。在过去24小时内，搜救人员在废墟中没有找到任何幸存者。</p>\n<p>\n	　　塞泽尔说，此次地震造成土耳其10个省份受灾，直接受灾人口达1350万，很多人现在无家可归。土耳其中央政府正协同地方政府和国际援助组织在灾区展开大规模安置行动，努力为灾民搭建帐篷、集装箱等临时住所。到目前为止，在灾区已有近25万顶帐篷启用，搭建集装箱住所6000多个，另有6.8万个集装箱住所即将完工，目标是在两个月内搭建10万个集装箱住所。</p>\n<p>\n	　　根据土耳其官方发布的最新统计数据，地震灾区有超过8.4万栋建筑倒塌、严重受损或急需拆除。</p>\n<div class=\"zdfy clearfix\" /><center><table border=\"0\" align=\"center\" width=\"40%\"><tr /></table></center>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：贾文婷、刘洁妍)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0220/c1002-32627182.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (893,'2023-02-20 04:52:17.586901','2023-02-20 04:52:17.586901','日本去年四季度核心机械订单额环比下降5%','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　据新华社东京电  （记者刘春燕、钟雅）日本内阁府日前公布的统计报告显示，受新冠疫情及海外经济减速等因素影响，去年第四季度，被视为日本民间设备投资先行指标的核心机械订单额环比下降5%，连续两个季度环比下降。</p><p>　　数据显示，去年四季度，不含船舶和电力的核心机械订单额经季节调整后为2.61万亿日元（1美元约合134日元）。其中，制造业订单环比减少14%至1.21万亿日元，非制造业订单环比增长4.7%至1.41万亿日元。</p><p>　　去年，日本核心机械订单额有三个季度环比下降。内阁府报告说，预计2023年一季度该数据将回升。</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日   15 版）</span>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：赵欣悦、岳弘彬)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0220/c1002-32626876.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (894,'2023-02-20 04:52:17.592900','2023-02-20 04:52:17.592900','联合国宣布拨款帮助19国最脆弱人群应对挑战','<div class=\"rm_txt_con cf\">\n				<div class=\"bza\"><span /><p /></div>\n            	<div class=\"box_pic\" />\n				<p>　　据新华社联合国2月18日电  （记者王建刚）联合国秘书长古特雷斯18日宣布从联合国中央应急基金中拨款2.5亿美元，用于帮助19个国家最脆弱的人群应对饥荒等多重挑战。</p><p>　　古特雷斯在联合国当天发布的新闻稿中说：“长期存在的发展问题往往与气候混乱、经济冲击和暴力冲突交织在一起，演变成人道主义灾难的旋风。目前，全世界有3.39亿人需要人道主义援助，比去年增加了25%以上。”</p><p>　　新闻稿说，此次拨款将用于帮助阿富汗、布基纳法索、海地、马里、尼日利亚、索马里、南苏丹和也门8个国家2000多万正处于饥荒边缘的民众，在乍得、哥伦比亚、刚果（金）、厄立特里亚、埃塞俄比亚、洪都拉斯、肯尼亚、黎巴嫩、马达加斯加、巴基斯坦和苏丹加强人道主义行动，以应对粮食不安全，以及气候变化等长期危机的影响。</p><p>　　联合国负责人道主义事务的副秘书长格里菲思表示，这笔资金将有助于及早采取行动，在包括饥荒在内的危机形成前采取行动。</p><p>　　新闻稿说，2022年联合国及其合作伙伴向近1.6亿人提供了援助，但人道主义需求的激增迅速超过了应对能力。联合国呼吁捐助者向中央应急基金捐款，以帮助更多有需求的人。</p>\n<br /><span id=\"paper_num\">　　《 人民日报 》（ 2023年02月20日   15 版）</span>\n                <div class=\"box_pic\" />\n                <div class=\"edit cf\">(责编：赵欣悦、岳弘彬)</div>\n				\n				<p class=\"paper_num\"><em class=\"section-common-share-wrap\">分享让更多人看到 <i class=\"btn_share_common\"><img src=\"/img/2020wbc/imgs/share.png\" alt=\"\" /></i> </em></p>\n			</div>\n			\n			\n            \n			','人民网','http://world.people.com.cn/n1/2023/0220/c1002-32626872.html',8);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (898,'2023-02-20 04:52:17.656893','2023-02-20 04:52:17.656893','日韩称朝鲜再发射多枚弹道导弹','<td id=\"article_content\">\r\n\r\n<div>美国中文网综合报道 日本和韩国周一表示，朝鲜向朝鲜半岛东海岸水域发射了多枚弹道导弹。</div><div><br /></div><div>日本海上保安厅在其官方网站上发表声明称，朝鲜发射了三枚疑似弹道导弹，但韩国参谋长联席会议表示，只探测到两枚短程弹道导弹。韩国说，导弹是当地时间周一早上7点至7点11分从朝鲜平安南道淑川地区发射的。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div><br /></div><div>日本海上保安厅当地时间周一上午向船只发出警报，并建议船只“密切关注进一步信息”。该机构还要求船只远离坠落物体，并向海岸警卫队报告任何目击事件。</div><div><br /></div><div>事件发生的前一天，朝鲜承认星期六进行了洲际弹道导弹试验，这是不到一年时间内已知的第三次远程导弹试验。</div><div><br /></div><div><div>在周六进行试射之前，朝鲜警告说，如果美国和韩国按计划进行军事演习，将会做出“前所未有的强烈反应”。</div></div><div><br /></div><div>【相关新闻：<a href=\"https://www.sinovision.net/politics/202302/00527371.htm\" target=\"_blank\"><font color=\"#0000f0\">朝鲜发射远程导弹一天后 美日韩举行联合空中演习</font></a>】</div><div><br /></div><div><div>周日，美国与韩国和日本举行了涉及战略轰炸机的联合空中演习。</div></div><div><br /></div><div>韩国国防部周五表示，华盛顿和首尔预计本周将在五角大楼举行应对核威胁的桌面演习。预计两国下个月还将在朝鲜半岛举行军事演习。</div><div><br /></div><div>（编辑：孙汐濛）</div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527378.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (899,'2023-02-20 04:52:17.662890','2023-02-20 04:52:17.662890','布林肯：美国毫不怀疑中国通过气球进行监视','<td id=\"article_content\">\r\n\r\n<div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/132937ukkgjrgsgruutuj1.jpeg\" target=\"_blank\"><img alt=\"布林肯：美国毫不怀疑中国通过气球进行监视_图1-1\" title=\"布林肯：美国毫不怀疑中国通过气球进行监视\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/132937ukkgjrgsgruutuj1.jpeg\" /></a></p></div><div><br /></div><div>美国中文网综合报道 美国国务卿布林肯周日明确表示，美国毫不怀疑中国试图通过本月早些时候在南卡海岸被击落的气球来监视美国。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div>“我不能肯定地说它的最初意图是什么，但这并不重要，因为我们在美国上空看到它的时候，它显然是在试图监视非常敏感的军事地点。”布林肯在接受ABC《本周》节目采访时说。</div><div><br /></div><div>“这个气球从这些地点中间飞过。在某些情况下，它是游荡的，”他补充说。“我们采取了措施保护这些信息。我们采取措施获取有关气球的信息。我想，当我们拿到残骸时，我们会知道更多。”</div><div><br /></div><div>在击落疑似中国间谍气球后，美国军方随后又击落了三个高空物体，总统拜登上周表示，美方现在相信它们与任何国家的监视计划无关。相反，它们可能被私人实体用于天气或研究目的。</div><div><br /></div><div>美国官员表示，相比之下，中国气球的有效载荷(或其携带的设备)大约有三辆公共汽车大小，能够收集信号情报和拍照。官员们说，该气球飞越了蒙大拿州的敏感地点，但拜登政府表示，它追踪了气球的路径，并努力将其情报收集能力降至最低。</div><div><br /></div><div>美国表示，该气球是中国军方控制的一个庞大项目的一部分，该项目近年来对五大洲至少40个国家进行了监视。</div><div><br /></div><div>“在我们看来，这个气球在美国上空试图做什么是毫无疑问的。我们同样也认为，中国已经在全球40多个国家使用了这个监视气球项目，”布林肯告诉ABC。</div><div><br /></div><div>（编辑：孙汐濛）</div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527372.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (900,'2023-02-20 04:52:17.667899','2023-02-20 04:52:17.667899','朝鲜发射远程导弹一天后 美日韩举行联合空中演习','<td id=\"article_content\">\r\n\r\n<div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/1232198uikljkvki999qbt.png\" target=\"_blank\"><img alt=\"朝鲜发射远程导弹一天后 美日韩举行联合空中演习_图1-1\" title=\"朝鲜发射远程导弹一天后 美日韩举行联合空中演习\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/1232198uikljkvki999qbt.png\" /></a></p></div><div><font color=\"#808080\">在2月19日举行的联合空中演习中，一架美国空军B-1B轰炸机与美国空军F-16战斗机编队在朝鲜半岛上空飞行。图片来自美联社</font></div><div><br /></div><div>美国中文网综合报道 周日，在朝鲜发射“火星-15”洲际弹道导弹的第二天，美国与韩国和日本举行了涉及战略轰炸机的联合空中演习。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div><br /></div><div>韩国参谋长联席会议表示，在这次演习中，韩国的F-35A, F-15K和美国的F-16战斗机护送美国的B-1B轰炸机，展示了盟国“压倒性的”防御能力和战备态势。</div><div><br /></div><div>“(这次演习)加强了联合作战能力，确认了美国对保卫朝鲜半岛和实施延伸威慑的坚定承诺。”韩国军方在一份声明中表示。</div><div><br /></div><div>“这次双边演习重申了日美两国对任何情况作出反应的强烈意愿，(日本自卫队)和美国武装部队的准备状态，并进一步加强日美盟友联合军演的威慑和反应能力。”</div><div><br /></div><div>此次军演的前一天，朝鲜向日本西海岸附近海域发射了一枚远程弹道导弹，此前朝鲜警告称，将对韩国和美国即将举行的军事演习做出强烈回应。</div><div><br /></div><div>朝鲜官方媒体朝中社表示，朝鲜周六进行了一次“突然发射演习”，以“实际证明”其将“对敌对势力进行致命核反击的能力转变为不可抵抗的能力”。</div><div><br /></div><div>朝鲜国家通讯社称，导弹飞行了1小时6分55秒，飞行高度达到3584英里，然后准确击中了614英里外的开放水域。2017年，朝鲜首次试射了“火星-15”。</div><div><br /></div><div>周六的导弹发射是朝鲜今年以来的首次发射，此前平壤在周五威胁要做出“前所未有的持续、强烈”回应。韩国和美国正在为年度军事演习做准备，以抵御朝鲜日益增长的核武器和导弹威胁。</div><div><br /></div><div>（编辑：孙汐濛）</div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527371.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (902,'2023-02-20 04:52:17.677900','2023-02-20 04:52:17.678900','被问及“你和川普有什么不同” 海莉这样回答','<td id=\"article_content\">\r\n\r\n<div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/103133ii2nmn66to7husuh.jpg\" target=\"_blank\"><img alt=\"被问及“你和川普有什么不同” 海莉这样回答_图1-1\" title=\"被问及“你和川普有什么不同” 海莉这样回答\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/103133ii2nmn66to7husuh.jpg\" /></a></p></div><div><font color=\"#808080\">美联社资料图</font></div><div><br /></div><div>美国中文网报道  周日，共和党总统参选人海莉(Nikki Haley)在接受采访时回避了有关她与前总统川普有何不同的问题，而是说她专注于击败拜登。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div>当被问及她与川普有何不同时，海莉在接受福克斯采访时说，“我不关注川普。令人惊讶的是，媒体竟然这么喜欢谈论这个问题。我关注的是拜登。“</div><div><br /></div><div>但曾担任南卡州长和前美国驻联合国大使的海莉将在某个时候与川普交锋，因为两人都在竞选2024年的共和党总统候选人提名。这两人是迄今为止唯一宣布的候选人。海莉大多时候都回避有关川普的问题，但也呼吁党内新一代领导人的出现。</div><div><br /></div><div>海莉周日说，“我认为我们需要新的力量加入到共和党中。我们需要抛弃过去，开始向前看。”</div><div><br /></div><div>去年11月，川普在2022年中期选举后立即宣布参选。他抨击海莉之前说过如果川普在2024年参选，她不会参选。海莉在2021年4月说，“如果川普总统参选，我不会参选。”</div><div><br /></div><div>但川普也欢迎她加入竞选，称参选人“越多越好”。</div><div><br /></div><div>川普上周在接受福克斯新闻采访时说，“我很高兴她参选。我希望她做自己想做的事情，尽管她承诺永远不会与她称之为一生中最伟大的总统竞选。”</div><div><br /></div><div>海莉说，上周在新罕布什尔和南卡举行的发布会上，没有人向她问起川普的问题。相反，她说他们担心拜登政府。海莉说，“没有一个人问我关于川普的事。每个人都想谈论拜登。”</div><div><br /></div><div>（编辑：张月如）</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527368.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (903,'2023-02-20 04:52:17.683900','2023-02-20 04:52:17.683900','王毅会见日外相林芳正：脱钩断链不符合任何一方利益','<td id=\"article_content\">\r\n\r\n<div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/101635pj0ljzuveuplgz96.jpeg\" target=\"_blank\"><img alt=\"王毅会见日外相林芳正：脱钩断链不符合任何一方利益_图1-1\" title=\"王毅会见日外相林芳正：脱钩断链不符合任何一方利益\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/101635pj0ljzuveuplgz96.jpeg\" /></a></p></div><div><font color=\"#808080\">当地时间2023年2月18日，中共中央政治局委员、中央外事工作委员会办公室主任王毅在出席慕尼黑安全会议期间会见日本外相林芳正。中国外交部网站 图</font></div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div>当地时间2月18日，中共中央政治局委员、中央外事工作委员会办公室主任王毅在出席慕尼黑安全会议期间会见日本外相林芳正。</div><div><br /></div><div>王毅表示，中日是一衣带水的近邻，以史为鉴、开辟未来是两国的正确相处之道。日方表示两国是伙伴，互不构成威胁，致力于发展建设性的中日关系，希望日方将这些表态落到实处。今年是中日和平友好条约缔结　45周年，日方应以此为契机，总结经验教训，排除内外干扰，同中方相向而行，确保中日关系在正确轨道上稳定前行。单边主义、脱钩断链不符合任何一方利益，日方应认清形势，做出独立自主的选择。</div><div><br /></div><div>王毅强调，核污染水排海事关海洋环境和各国民众健康，日方务必慎重处理，在同周边邻国等利益攸关方和国际机构充分协商前，不得擅自启动核污染水排海。</div><div><br /></div><div>林芳正表示，日中合作对于实现各自发展繁荣具有重要意义。去年双方共同庆祝邦交正常化50周年，今年是日中和平友好条约缔结45周年，日方愿同中方加强各层级沟通对话，加强经贸、青少年交流等领域的合作，落实好两国领导人达成的重要共识。</div><div><br /></div><div>双方还就一些国际地区问题交换了意见。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527367.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (904,'2023-02-20 04:52:17.688855','2023-02-20 04:52:17.688855','王毅会见乌克兰外长库列巴：不愿看到乌克兰危机长期化扩大化','<td id=\"article_content\">\r\n\r\n<div>当地时间2月18日，中共中央政治局委员、中央外事工作委员会办公室主任王毅在出席慕尼黑安全会议期间应约会见乌克兰外长库列巴。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1036\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1036] 时政新闻详细页 Admaru */\r\n    OA_show(1036);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=8175007\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1036&amp;n=8175007\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id883] 时政详情页文包图广告 */\r\n    OA_show(883);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=16ec5dc\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=883&amp;n=16ec5dc\" /></a></noscript></div></div><div>王毅表示，中国和乌克兰是战略伙伴，两国人民有着长久友好交往。我们再次感谢乌方在紧急状况下协助中方顺利开展撤侨行动。赞赏乌方始终坚持一个中国原则。希望两国关系继续稳定发展。在乌克兰问题上，中方始终站在和平一边、站在对话一边，始终坚持劝和促谈。我们不愿看到乌克兰危机长期化、扩大化，愿同国际社会一道，避免形势进一步恶化，持之以恒地争取和平。</div><div><br /></div><div>库列巴表示，一年来，乌中一直保持着沟通。没有任何国家比乌克兰更希望尽快实现和平，乌方重视中国的国际地位和重要影响，重视中方关于政治解决危机的立场，期待中方继续发挥建设性作用。</div></td>','美国中文网','http://www.sinovision.net/politics/202302/00527366.htm',1);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (908,'2023-02-20 04:52:17.737885','2023-02-20 04:52:17.737885','北京楼市小阳春：回暖太突然 门店里一个闲着的经纪人都没有','<td id=\"article_content\"><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/214130aeaza4ar6qoq5srf.png\" target=\"_blank\"><img alt=\"北京楼市小阳春：回暖太突然 门店里一个闲着的经纪人都没有_图1-1\" title=\"北京楼市小阳春：回暖太突然 门店里一个闲着的经纪人都没有\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/214130aeaza4ar6qoq5srf.png\" /></a></p></div><div><br /></div><div>准备在北京买房的郭鹏（化名）有点郁闷，他春节前看中一套并不算优质的房源，当他思索再三准备入手时，业主却不卖了，因为业主觉得市场正在好转。房产经纪人也告诉他，现在看房的人正在增多。</div><div><br /></div><div>春节过后，北京、上海、深圳、南京、武汉等多地都传出房地产市场回暖的消息，楼市似乎在一夜间就由冷转热。市场的急剧变化，让很多从业者都有些措手不及，北京一位房产经纪人对中新经纬说：“回暖速度太快，我们也觉得有些突然。”</div><div><br /></div><div>中新经纬采访发现，春节假期后，北京二手房带看量的增长比较明显，在这一先导指标的带动下，2月1日-15日，二手房网签量环比1月同期增幅超30%。目前看，房价依然比较稳定，没有出现大幅上涨的情况。业内人士认为，北京楼市已进入“小阳春”状态，但能否持续则要看购房政策是否会变化。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></div><div><br /></div><div><b>有业主反悔、加价</b></div><div><br /></div><div>春节后，陆萍（化名）开始集中看房，感受到了北京市场逐渐变热的过程。2月12日上午，北京下起小雨，但当她赶到石景山区一个刚开始蓄客的改善型楼盘时，售楼部已经有不少看房的人。她对中新经纬说：“虽然没有坐满，人还是比较多。”</div><div><br /></div><div>陆萍称，当天，该楼盘的置业顾问都有些忙不过来，要在售楼处等一会儿才有人接待与讲解。而且，即便是正在接待顾客，置业顾问的手机也会时不时响一下，接听其他顾客的电话咨询。接待她的置业顾问说，11日当天，该楼盘的排卡量就有100多位。</div><div><br /></div><div>“这个楼盘的置业顾问比较自信，不太担心客源不足，楼盘也没有什么折扣或优惠活动。置业顾问介绍，如果排卡客户超过房源量，还要摇号。”陆萍称，该楼盘的均价达到7.5万元/平方米，最小户型是113平方米，单价和总价都不低。不过，并非所有楼盘都火热，她看的另一个楼盘是由同一个开发商开发，虽然已经开盘半年，目前还是有不少房源可供选择。</div><div><br /></div><div>除了新房，陆萍也看了不少二手房。她从经纪人口中得知，春节假期后，他们的带看量增加不少，成交量也有一定增长。目前，陆萍看了大概30套二手房，其中有10套她比较中意，也是她认为比较优质的房源。她发现，这10套房源中，已经有两三套在近几天成交，“我的感受是，交易速度加快了。”</div><div><br /></div><div>优质房源交易速度加快的情况，李敬（化名）也感受到了。在某房产交易平台，李敬关注了100多套北京的二手房，这些房子中有很多都是被该平台列入所在片区“捡漏榜”“热搜房源榜”“成交小区榜”，在一定意义上说，属于价格合适、关注度高的好房源。</div><div><br /></div><div>他发现，关注的二手房源中，有几套已经在近期集中“停售”。根据该平台的规则，“停售”的原因有两种，一种是被卖出，一种是业主不再出售，而且，前者的可能性更大。李敬对中新经纬说：“我关注的房源中，断断续续都会有一些被标注为‘停售’，而这种情况在近段时间变得越来越频繁。”</div><div><br /></div><div>郭鹏已经看了两年的房子，从环北京地区的燕郊、固安，到北京的石景山、丰台、海淀，至今没有定下来。2022年年底，他看中丰台区泥洼地铁站的一套二居室，这套房源优缺点都比较明显，优点在于总价低、带电梯、位置较好，缺点是顶层、西户型、小区老旧。年前，他与业主谈过一次价格，没有成功。</div><div><br /></div><div>经过2023年春节假期的考虑，他决定购买这套不算优质的房子。但是，当他再找到经纪人询问这套房源时却被告知，业主已经不打算出售，原因是他觉得北京房价正在走强。</div><div><br /></div><div>今年2月5日，朝阳区的一位经纪人向郭鹏推荐了弘善家园一套420万元二手房。没想到的是，因为采光、装修好，这个“两年不出一套”的房子被购房人抢购。2月8日，经纪人告诉他，该房源最终加价11万元，以431万元成交了。</div><div><b><br /></b></div><div><b>“回暖太突然”</b></div><div><br /></div><div>对于北京楼市的变化，中新经纬于近日进行了走访。</div><div><br /></div><div>房山区，是北京房价相对较低、距离主城区较近的区域，但由于多种原因，外界对该区的房价并不是很看好，因此，该区近几年的市场相对冷淡。即便是这样，专注于房山区房产交易的经纪人宋娟娟（化名），也察觉到近期的市场动态。</div><div><br /></div><div>宋娟娟说：“我们之前也估计，这么多利好楼市的政策落地后，北京市场会回暖，但没想到的是，刚过完春节，咨询的客户明显增多。回暖速度太快，我们也觉得有些突然。”她称，目前只是带看量起来了，价格没有什么变化。</div><div><br /></div><div>中新经纬还走访了朝阳区十里堡区域，该区域的房产经纪人白天明（化名）称，由于当天是工作日，看房的客户不算多，但在晚上6点他还要接待一位顾客，“现在一些客户都是下了班之后看房。”</div><div><br /></div><div>白天明称，春节后的两个周末，他们都忙不过来，门店里一个闲着的经纪人都没有，都要出去带客户看房。他说：“我们公司要求每个经纪人每月带看量不低于8组客户，而截止到2月16日，我已经接待了17组。我也没想到带看量会增加得这么快。”</div><div><br /></div><div>我爱我家研究院向中新经纬提供的数据显示，春节假期后20天（2023年1月28日-2月16日），我爱我家北京区域二手住宅买卖带看量环比节前20天（2023年1月1日-1月20日）增长超八成，同比去年节后20天（2022年2月7日-2月26日）增长超一成，节后带看需求较节前回升速度较快。</div><div><br /></div><div>白天明所在公司是北京最大的房产经纪公司，在上周日（2月12日），一天就成交了652套二手房，加上上周六（2月11日）的成交，两天合计超过1100套。要知道，北京2022全年二手房共成交约14.24万套，日均不足400套。</div><div><br /></div><div>我爱我家研究院数据显示，春节假期后20天（2023年1月28日-2月16日），我爱我家北京区域二手住宅交易量环比节前20天（2023年1月1日-1月20日）增长超一成，同比去年节后20天（2022年2月7日-2月26日）增长超三成。</div><div><br /></div><div>中原地产首席分析师张大伟对中新经纬分析，目前北京房地产市场并未出台刺激政策，本轮市场回暖多是因为学区房交易驱动，家长要赶在入学前办理好房源过户，因此需要提前购房，推动了换房需求，进而让整个市场的交易活跃。</div><div><br /></div><div>据北京市住建委网站数据，2月1日-15日，北京二手房共计网签6779套，环比1月同期的5152套增加了约31.58%。张大伟称，北京是一个以二手房交易为主的市场，二手房月网签1.2万套是楼市“枯荣线”，月网签高于1.2万套，市场会保持平稳，高于1.5万套的话，价格可能会走强；月网签低于1.2万套，市场会比较低迷，更低的话，价格则会下调。</div><div><br /></div><div><b>价格依旧平稳</b></div><div><br /></div><div>上述受访的购房人、经纪人均表示，虽然北京带看量、成交量有所增加，但价格没有变化。</div><div><br /></div><div>看了一些新房、二手房后，陆萍觉得，房价暂时没有上涨，整体比较稳定。对于买卖双方的议价空间，她认为，现在有关北京楼市回暖的消息比较多，业主们的底气可能更足了，谈价空间变小。陆萍并不着急马上入手，她要继续看更多区域的房源，对北京市场有了整体了解再买入。</div><div><br /></div><div>白天明说：“去年市场最冷淡的时候，每月二手房成交量只有八九千套，客户谈价空间比较大，350万元的房子，谈下来10万元甚至更多也是有可能的。现在就没有那么大，可能就5万元左右。”</div><div><br /></div><div>白天明判断，在带看量增加的刺激下，北京市场进入到了“量增价稳”的状态，如果交易量能一直维持较高的水平，涨价也是有可能的。“当然，价格的变化，要看未来一段时间的市场热度。”我爱我家研究院分析，随着经济持续恢复向好，春节假期后北京二手住宅市场正在逐渐复苏，回暖速度有所加快。市场回温较快的同时，二手住宅交易价格十分平稳。</div><div><br /></div><div>2022年以来，全国有相当多的城市都出台了促进房地产市场健康发展的举措，取消或放宽限购、限贷的政策也是层出不穷。到目前为止，北京成为少数没有在购房政策上出台过大刺激政策的城市之一。</div><div><br /></div><div>不过，在今年1月，北京市政府印发了《2023年市政府工作报告重点任务清单》，在232条提到，完善“一区一策”调控措施。该表述引发外界关注。到2月，北京市场就传出消息，房山区要放宽楼市调控举措，实施“认房不认贷”政策。2月17日，房山区房屋交易中心对媒体表示，消息不属实，目前没有落实该政策，不要听信市场机构过度解读。</div><div><br /></div><div>宋娟娟早就听到了这则传言，她也真假难辨。她认为，一些要购买二套房的客户对放松调控政策比较期待，若能落地，他们的首付比例就会明显降低，对市场也会产生很大的刺激作用。</div><div><br /></div><div>对于北京要放宽楼市调控政策的消息，白天明认为，这不一定是好事。“北京并不缺购房需求，如果放宽二套房购房政策，房价可能就要上涨了。个人认为，调控政策不能大幅松动。”</div><div><br /></div><div>张大伟称，从当前成交情况看，北京房地产市场已经进入“小阳春”状态，预计2月份二手房网签量能达到1.5万套，到3月份可能还会进一步增加。至于之后能否继续持续，则要看政策的优化程度，如果二套房政策出现调整，市场可能会持续升温，反之则转为平淡。“为了不过于刺激市场，北京二套房政策应该不会大幅度松动，存在一定程度的调整空间。”</div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527381.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (909,'2023-02-20 04:52:17.743856','2023-02-20 04:52:17.743856','北京证券交易所正式启动股票做市交易业务','<td id=\"article_content\"><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/211108u6z6dzvfu6zjy07y.jpg\" target=\"_blank\"><img alt=\"北京证券交易所正式启动股票做市交易业务_图1-1\" title=\"北京证券交易所正式启动股票做市交易业务\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/211108u6z6dzvfu6zjy07y.jpg\" /></a></p></div><div><br /></div><div>2月20日，北京证券交易所正式启动股票做市交易业务。首批13家券商将参与北交所股票做市交易，共涉及做市标的股票36只。</div><div><br /></div><div>北交所表示，北交所采用成熟证券市场通行做法，在竞价交易的基础上引入做市交易，有助于缩小市场买卖价差，降低投资者交易成本。同时，通过引入增量资金，能够增加市场订单深度，提升市场流动性和市场稳定性。</div><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1037\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1037] 财经新闻详细页 Admaru */\r\n    OA_show(1037);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=74a0f69\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1037&amp;n=74a0f69\" /></a></noscript></div><div style=\"max-width:300px;text-align:center;margin:4px auto; margin-bottom:5px;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id884] 财经详情页文包图广告 */\r\n    OA_show(884);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=d60518a\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=884&amp;n=d60518a\" /></a></noscript></div></td>','美国中文网','http://www.sinovision.net/finance/202302/00527380.htm',2);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (910,'2023-02-20 04:52:17.779901','2023-02-20 04:52:17.779901','杭州通报多名学生新冠阳性：均为首次感染，非二次流行到来','<td id=\"article_content\"><div>2月19日，一则“杭州两学校班级多人发热，下周停课”的消息在网络上引发关注。记者从杭州市疫情防控指挥部了解到，目前涉及到杭州三所学校，分别是西湖区某小学（共出现10例，均为新冠首次感染）；拱墅区某中学（共出现5例，均为新冠首次感染）。另外 ，上城区某小学12名学生有发热症状，为流感聚集性传播。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div><br /></div><div>上述三所学校均已根据相应预案对涉及班级教学课程暂行调整。市疫情防控指挥部相关负责人介绍，杭州个别学校班级出现数名学生感染新冠病毒（均为首次感染），属于正常现象，并不代表新一波流行的开始。另外，目前甲流、诺如病毒感染也已经进入多发期，即便孩子出现发烧等症状，也不一定是由新冠病毒引起，建议家长第一时间帮助孩子进行抗原检测。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/2048165k1155i27r64psi5.jpeg\" target=\"_blank\"><img alt=\"杭州通报多名学生新冠阳性：均为首次感染，非二次流行到来_图1-3\" title=\"杭州通报多名学生新冠阳性：均为首次感染，非二次流行到来\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/2048165k1155i27r64psi5.jpeg\" /></a></p></div><div><br /></div><div>杭州平稳有序实施“乙类乙管”各项工作以来，各职能部门均已做好了相应的预案措施。教育部门为例，开学前，各校药品和抗原检测试剂储备已全部达标。校园内出现感染者后，中小学、幼儿园将以班级或年级为单位，根据区教育局会同卫健、疾控等部门提出的防控措施加以落实，确保学生的健康安全和维护正常教学秩序。</div><div><br /></div><div>市疫情防控指挥部呼吁广大市民，个人及家庭防护仍要重视。勤洗手、正确佩戴口罩、多通风，保持良好个人习惯，做好自己的健康第一责任人。</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527379.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (911,'2023-02-20 04:52:17.785899','2023-02-20 04:52:17.785899','旅日大熊猫“香香”最后一天在日见游客 民众送祝福','<td id=\"article_content\"><div>按照中日两国之间达成的相关协议，在东京上野动物园生活了5年多的雌性大熊猫“香香”即将在2月21日踏上回国之旅。2月19日，是“香香”在上野动物园最后一天与游客见面的日子，许多日本民众来到动物园与“香香”道别，为它送上美好祝福。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div><br /></div><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/1826165u3qlpp13wxxpexg.png\" target=\"_blank\"><img alt=\"旅日大熊猫“香香”最后一天在日见游客 民众送祝福_图1-3\" title=\"旅日大熊猫“香香”最后一天在日见游客 民众送祝福\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/1826165u3qlpp13wxxpexg.png\" /></a></p></div><div><br /></div><div>只有部分游客通过事先抽签得到了19日与“香香”道别的机会。在大熊猫馆内，人们抓紧时间拍照，不时挥手，依依不舍地和“香香”道别。</div><div><br /></div><div>据园方介绍，由于“香香”进入检疫期，过去一个月中，每天参观游客人数上限为2600人。19日的参观因为申请人数过多，中签概率只有七十分之一左右。尽管如此，许多没有中签的日本民众，当天还是来到了动物园。他们说，即便见不到“香香”，也希望能够表达出自己对“香香”的喜爱与祝福。</div><div><br /></div><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/1826168xoucn7ckti3czk3.png\" target=\"_blank\"><img alt=\"旅日大熊猫“香香”最后一天在日见游客 民众送祝福_图1-4\" title=\"旅日大熊猫“香香”最后一天在日见游客 民众送祝福\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/1826168xoucn7ckti3czk3.png\" /></a></p></div><div><br /></div><div>“香香”2017年6月在上野动物园出生。从出生那天起，“香香”便成了上野动物园的明星。许多游客都表示，他们是看着“香香”一点点长大的，很感谢它这些年的陪伴，非常舍不得它离开。</div><div><br /></div><div>连日来，上野动物园还面向游客和动物园工作人员开展了对“香香”的祝福征集活动。许多人在对“香香”依依不舍的同时，也都表示希望“香香”能够在新的生活环境中健康成长。</div><div><br /></div><div>按计划，“香香”将于21日启程回国。“香香”的父母，旅日大熊猫“比力”和“仙女”以及它们的龙凤胎宝宝“晓晓”和“蕾蕾”将继续留在上野动物园。</div><div><br /></div></td>','美国中文网','http://www.sinovision.net/society/202302/00527377.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (912,'2023-02-20 04:52:17.790900','2023-02-20 04:52:17.790900','枪杀天普大学警察后还试图抢劫 18岁冷血杀手被捕','<td id=\"article_content\"><div>美国中文网综合报道 周日，一名18岁男子因枪杀天普大学一名警察而被捕，这名枪手在开枪后还试图抢劫这名奄奄一息的警察。</div><div><br /></div><div><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/165747532sx2656232gslr.png\" target=\"_blank\"><img alt=\"枪杀天普大学警察后还试图抢劫 18岁冷血杀手被捕_图1-1\" title=\"枪杀天普大学警察后还试图抢劫 18岁冷血杀手被捕\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/165747532sx2656232gslr.png\" /></a></p><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div><font color=\"#808080\">CNN视频截图</font></div><div><br /></div><div>警方称，枪手迈尔斯•普费弗(Miles Pfeffer)在枪击事件发生后，还在附近冷静地实施了一起劫车案。</div><div><br /></div><div>巴克斯郡地方检察官办公室说，周日上午7点左右，警方在普费弗位于巴克斯郡白金汉镇的家中逮捕了他。</div><div><br /></div><div>据费城地方检察官办公室称，周六晚上7点左右，31岁的校警克里斯托弗•菲茨杰拉德(Christopher Fitzgerald)在天普大学校园附近响应一起报警时被枪击中头部。</div><div><br /></div><div>菲茨杰拉德后来在医院被宣布死亡。学校表示，他是天普大学第一位因公殉职的警察。</div><div><br /></div><div>检察官办公室说，普费弗面临谋杀、谋杀执法人员、抢劫、劫车和非法持有武器的指控。</div><div><br /></div><div>检察官拉里•克拉斯纳在一份声明中说，枪击发生后，菲茨杰拉德躺在地上，普费弗则试图抢劫他。</div><div><br /></div><div>“普费弗还被指控试图抢劫菲茨杰拉德警官的枪，并在菲茨杰拉德警官躺在地上受了致命伤时翻了他的口袋。”克拉斯纳周日说。</div><div><br /></div><div>根据当局获得的视频，普费弗随后参与了一起劫车案，劫车地点距离菲茨杰拉德被发现的地点大约一个街区。据称，普费弗在巴克斯郡有青少年犯罪记录。</div><div><br /></div><div>菲茨杰拉德自2021年10月以来一直在该大学警察部门工作。学校称，他已婚，是四个孩子的父亲。</div><div><br /></div><div>大学校长杰森•温加德称这起枪击事件“令人痛心地提醒我们，我们的警察每天都在勇敢地保护我们的学生、教职员工和社区”，对抗“前所未有的暴力泛滥”。</div><div><br /></div><div>（编辑：孙汐濛）</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527375.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (914,'2023-02-20 04:52:17.800900','2023-02-20 04:52:17.800900','迪士尼乐园发生事故 一女子从停车场坠落身亡','<td id=\"article_content\"><div>美国中文网综合报道 周六晚上，加州迪士尼乐园发生一起致命事故。一名女子从高层停车场坠落后死亡。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div>阿纳海姆警方表示，周六晚上6点50分左右，在接到有人“跳下去或摔下去”的报告后，阿纳海姆警方和阿纳海姆消防救援队赶到迪士尼度假区的米奇和朋友停车场。</div><div><br /></div><div>警方到达现场后在地上发现了一名成年女子，并进行了紧急医疗救助，直到急救人员到达现场。警方说，这名女子被送往医院后被宣布死亡。</div><div><br /></div><div>该女子的身份尚未公布。目前还不清楚她是否是主题公园的顾客。</div><div><br /></div><div>阿纳海姆警方表示，调查正在进行中。</div><div><br /></div><div>这座七层楼高的停车场建于2000年。这个车库在建成时是同类车库中最大的，可以停放一万多辆车。</div><div><br /></div><div>这座多层停车场位于阿纳海姆迪士尼乐园的西北边缘。去年12月，一名50多岁的男子在该停车场坠楼身亡，疑似自杀。自该停车场开放以来，至少另有三人跳楼身亡。</div><div><br /></div><div>（编辑：孙汐濛）</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527373.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (915,'2023-02-20 04:52:17.804859','2023-02-20 04:52:17.805880','孟菲斯爆大规模枪案 已致1死10伤枪手在逃','<td id=\"article_content\"><div>美国中文网综合报道 孟菲斯警察局表示，当局正在寻求公众的帮助，寻找在两起枪击案中造成1人死亡、10人受伤的凶手，调查人员认为这两起枪击案有关联。</div><div><br /><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div>警方说，他们于周日凌晨12点43分左右抵达孟菲斯郊区的一家酒吧，在那里发现了两名遭到枪击的人，他们被送往医院，情况危急。</div><div><br /></div><div>警方表示，另有5名伤势不严重的枪击伤者被私人车辆送往当地医院。</div><div><br /></div><div>在现场，警方了解到附近有更多的枪击受害者。</div><div><br /></div><div>警方之后又发现了一名死亡的男性受害者和另外三名伤者，他们被送往医院，情况危急。</div><div><br /></div><div>警方说，他们认为这两起事件有关联，但目前还没有逮捕嫌疑人。</div><div><br /></div><div>“目前还没有对嫌疑人的确切描述。”孟菲斯警方在推特上写道。</div><div><br /></div><div>目前尚不清楚枪击事件的起因，也不清楚枪手是否认识受害者。</div><div><br /></div><div>根据枪支暴力档案，在不到两个月的时间里，美国发生了78起大规模枪击事件。该非营利组织将大规模枪击事件定义为四人或四人以上被枪击的事件，不包括枪手在内。</div><div><br /></div><div>（编辑：孙汐濛）</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527369.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (916,'2023-02-20 04:52:17.810903','2023-02-20 04:52:17.810903','中国每日新增阳性5000例以上 曾光：新冠并没有消失','<td id=\"article_content\"><div>新冠“消失”了吗？根据中国疾控中心2月18日最新发布的《全国新型冠状病毒疫情情况》，新冠感染者虽然以大幅降低，但是新冠感染人数仍然存在，每天新增感染者在5000例以上。著名公共卫生与流行病学专家曾光教授在接受人民日报健康客户端记者采访时表示，新冠并没有完全消失，只是进入低水平流行阶段，还需要加强监测，每个个体仍需要继续做好必要的防护措施。<div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1038\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id1038] 社会新闻详细页 Admaru */\r\n    OA_show(1038);\r\n// ]]&gt; --&gt;</script><noscript><a href=\"https://ad.sinovision.net/www/delivery/ck.php?n=a1348663&amp;cb=INSERT_RANDOM_NUMBER_HERE\" target=\"_blank\"><img src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1038&amp;cb=INSERT_RANDOM_NUMBER_HERE&amp;n=a1348663\" border=\"0\" alt=\"\" /></a></noscript></div><div style=\"max-width:300px;float:left;margin:5px 15px 5px 0;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id885] 社会详情页文包图广告 */\r\n    OA_show(885);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=f1724a3\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=885&amp;n=f1724a3\" /></a></noscript></div></div><div><br /></div><div><b>每日阳性数仍在5000例以上</b></div><div><br /></div><div>根据中国疾病预防控制中心2月份以来发布的6次《全国新型冠状病毒感染疫情情况》，1月30日核酸检测阳性数2.4万例，2月6日核酸检测阳性数为0.9万例，2月13日为8847例，2月16日为10720例。虽然核酸检测阳性数及阳性率已大幅下降，但是感染者人数仍然存在。2月份以来，31个省（区、市）及新疆生产建设兵团报告的核酸检测阳性数日新增仍在5000例以上。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/071235qfworm8mofk3w3z3.jpg\" target=\"_blank\"><img alt=\"中国每日新增阳性5000例以上 曾光：新冠并没有消失_图1-3\" title=\"中国每日新增阳性5000例以上 曾光：新冠并没有消失\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/071235qfworm8mofk3w3z3.jpg\" /></a></p></div><div><font color=\"#808080\">中国疾病预防与控制中心 图</font></div><div><br /></div><div>此外，根据2月18日中疾控发布的最新《全国新型冠状病毒疫情情况》，2月16日，全国（不含港澳台）发热门诊（诊室）就诊人数有12.4万人次，在院新冠病毒感染者有2万人，其中重症患者56例。</div><div><br /></div><div><b>近期新增2例变异毒株</b></div><div><br /></div><div>根据2月18日中疾控发布的《全国新型冠状病毒疫情情况》，2022年12月1日至2023年2月16日，全国共发现本土重点关注变异株15例，其中，1例XBB.1，1例XBB.1.5，1例BQ.1，5例BQ.1.1，1例BQ.1.1.17，4例BQ.1.2和2例BQ.1.8。</div><div><br /></div><div>对比此前中疾控发布的《全国新型冠状病毒疫情情况》，本次新增1例BQ.1变异毒株病例。而在2月15日发布的通报中还新增了1例XBB.1.5毒株。</div><div><br /></div><div><b>新冠对生活的影响没有完全消失</b></div><div><br /></div><div>“目前来看大形势是比较好，但是是否有新的变异株出现和流行、是否感染率有增高回升、是否从境外传入新的毒株等问题，仍然是我们监测的重点。”曾光表示。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/071238sc9rsjwvcew7j7zj.png\" target=\"_blank\"><img alt=\"中国每日新增阳性5000例以上 曾光：新冠并没有消失_图1-4\" title=\"中国每日新增阳性5000例以上 曾光：新冠并没有消失\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/19/071238sc9rsjwvcew7j7zj.png\" /></a></p></div><div><font color=\"#808080\">2月17日，游客在陕西榆林老街的非遗大集游览。新华社图</font></div><div><br /></div><div>“新冠对于人们生活的影响并没有完全消失。”曾光表示，对于公众而言，还是应该保持谨慎，做好基本的防护措施。</div></td>','美国中文网','http://www.sinovision.net/society/202302/00527365.htm',3);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (919,'2023-02-20 04:52:17.847899','2023-02-20 04:52:17.847899','女星王丽坤方否认被捕：收受豪车豪宅等系虚假信息和谣言','<td id=\"article_content\"><div>2月15日，王丽坤工作室发声明就近期风波辟谣。</div><div><br /></div><div>15日，针对网传王丽坤涉嫌违法、被抓、收受豪车豪宅等，王丽坤工作室发声明回应，称相关信息是谣言，已严重损害王丽坤的名誉权，王丽坤不涉及不存在任何违法行为，也未损害过任何人的合法权利，请社会公众勿信谣传谣，将通过法律途径追究侵权者责任。</div><div><br /></div><div style=\"text-align: center;\"><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/15/074454syan5oz48zpye65q.jpg\" target=\"_blank\"><img alt=\"女星王丽坤方否认被捕：收受豪车豪宅等系虚假信息和谣言_图1-1\" title=\"女星王丽坤方否认被捕：收受豪车豪宅等系虚假信息和谣言\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/15/074454syan5oz48zpye65q.jpg\" /></a></p><p><a href=\"//cdnwww.sinovision.net/data/attachment/portal/202302/15/0744538ccltffrslsisllo.jpg\" target=\"_blank\"><img alt=\"女星王丽坤方否认被捕：收受豪车豪宅等系虚假信息和谣言_图1-2\" title=\"女星王丽坤方否认被捕：收受豪车豪宅等系虚假信息和谣言\" src=\"//cdnwww.sinovision.net/data/attachment/portal/202302/15/0744538ccltffrslsisllo.jpg\" /></a></p></div><div><br /></div><div style=\"text-align:center; margin-bottom:5px;\" id=\"closeAd-1040\"><script type=\"text/javascript\" id=\"closeAd-1038\">&lt;!--// &lt;![CDATA[\r\n    /* [id1040] 娱乐新闻详细页 Admaru */\r\n    OA_show(1040);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=c1367dd\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=1040&amp;n=c1367dd\" /></a></noscript></div><div style=\"max-width:300px;text-align:center;margin:4px auto; margin-bottom:5px;\"><script type=\"text/javascript\">&lt;!--// &lt;![CDATA[\r\n    /* [id886] 娱乐详情页文包图广告 */\r\n    OA_show(886);\r\n// ]]&gt; --&gt;</script><noscript><a target=\"_blank\" href=\"https://ad.sinovision.net/www/delivery/ck.php?n=88ea083\"><img border=\"0\" alt=\"\" src=\"https://ad.sinovision.net/www/delivery/avw.php?zoneid=886&amp;n=88ea083\" /></a></noscript></div></td>','美国中文网','http://www.sinovision.net/entertainment/202302/00527187.htm',4);
INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (999,'2023-02-20 04:52:18.364393','2023-02-20 04:52:18.364393','天合光能青海基地首根单晶硅棒下线','<div class=\"left_ph\"><img src=\"//i2.chinanews.com.cn/simg/ypt/2023/230219/e493fc93-cf66-419d-96d0-7f78ff2e52c1_zsite.jpg\" alt=\"天合光能青海基地首根单晶硅棒下线\" /></div>\r\n','中国新闻网','http://www.chinanews.com/cj/2023/02-19/9956505.shtml',2);
