INSERT INTO tb_news (id,create_time,update_time,title,passage,news_from,url,category_id) VALUES (1075,'2023-02-20 04:52:18.808209','2023-02-20 04:52:18.808209','中央民族乐团附属少年剧团启动 让世界听到中国未来之音','<div class=\"left_ph\"><img src=\"//i2.chinanews.com.cn/simg/ypt/2023/230220/6d1f0426-cf2e-4e1a-b667-e2db8d34f9b1_zsite.jpeg\" alt=\"中央民族乐团附属少年剧团启动让世界听到中国未来之音\" /></div>\r\n','中国新闻网','http://www.chinanews.com/cul/2023/02-20/9956683.shtml',4);
